_G["NpcAbilitiesAbilityData"]['de'] = {
[45] = {name = "Kriegsdonner", description = "[q]Stößt in der Nähe befindliche Feinde zurück."},
[96] = {name = "Zerstückeln", description = "[q]Erhöht den Schaden Eures nächsten Nahkampfangriffes um 100 und lässt das Ziel bluten."},
[113] = {name = "Eisketten", description = "[q]Schlingt eisige Ketten um das Ziel, welche es 6 sec lang an Ort und Stelle festhalten."},
[116] = {name = "Frostblitz", description = "[q]Schleudert einen Frostblitz auf den Feind, der 17 Frostschaden verursacht und das Bewegungstempo 9 sec lang um 40 % verringert."},
[118] = {name = "Verwandlung", description = "[q]Verwandelt den Feind in\n\nGlyphe 'Pinguin'\neinen Pinguin\n\nGlyphe 'Affe'\neinen Affen\n\n ein Schaf und zwingt ihn, 50 sec lang so herumzuwandern. Während\n\nGlyphe 'Pinguin'\nder Pinguin herumwandert, kann er weder angreifen noch Zauber wirken, regeneriert sich aber sehr schnell. Jeglicher erlittene Schaden wandelt ihn\n\nGlyphe 'Affe'\nder Affe herumwandert, kann er weder angreifen noch Zauber wirken, regeneriert sich aber sehr schnell. Jeglicher erlittene Schaden wandelt ihn\n\n das Schaf herumwandert, kann es weder angreifen noch Zauber wirken, regeneriert sich aber sehr schnell. Jeglicher erlittene Schaden wandelt es in seine normale Gestalt zurück. Es kann immer nur jeweils ein Ziel verwandelt werden. Wirkt nur bei Wildtieren, Humanoiden und Tieren."},
[122] = {name = "Frostnova", description = "[q]Fügt nahen Gegnern 408 Frostschaden zu und friert sie bis zu 8 sec lang fest. Erlittener Schaden kann den Effekt unterbrechen."},
[133] = {name = "Feuerball", description = "[q]Schleudert eine glühende Kugel, die 1035 Feuerschaden verursacht."},
[134] = {name = "Feuerschild", description = "[q]Umgibt einen Verbündeten mit einem Flammenschild, der alle 3 Sek. in der Nähe befindlichen Feinden 9 Feuerschaden zufügt. Hält 1 min lang an."},
[139] = {name = "Erneuerung", description = "[q]Regeneriert 12 sec lang alle 3 Sek. 1333 Gesundheit des Ziels."},
[172] = {name = "Verderbnis", description = "[q]Verdirbt das Ziel und verursacht 18 sec lang insgesamt 1033 Schattenschaden."},
[184] = {name = "Feuerschild", description = "[q]Umgibt einen Verbündeten mit einem Flammenschild, der allen nahen Feinden alle 3 Sek. Feuerschaden zufügt. Hält 1 min lang an."},
[228] = {name = "Verwandlung: Huhn", description = "[q]Verwandelt den Feind in ein Huhn, das nicht zaubern kann und bis zu 10 sec lang 66% weniger Schaden zufügt."},
[246] = {name = "Verlangsamen", description = "[q]Verlangsamt 6 sec lang die Bewegung des Feindes um 35% und sein Angriffstempo um 35%."},
[324] = {name = "Blitzschlagschild", description = "[q]Der Zaubernde wird von 3 Kugelblitzen umgeben. Wenn der Zaubernde von einem Nahkampf-, Distanz- oder Zauberangriff getroffen wird, erleidet der Angreifer 350 Naturschaden. Dies kostet einen Kugelblitz\n\nGlyphe 'Blitzschlagschild'\nwird die Anzahl der Aufladungen jedoch nicht unter 3 senken\n\n Es kann immer nur ein einzelner Kugelblitz alle paar Sekunden ausgelöst werden. Hält 10 min lang an. Es kann immer nur ein Elementarschild auf dem Schamanen aktiv sein."},
[348] = {name = "Feuerbrand", description = "[q]Verbrennt den Feind. Verursacht 1 Feuerschaden und fügt zusätzlich im Verlauf von 15 sec insgesamt 5 Feuerschaden zu.\n\nInstabiles Gebrechen\nEs kann pro Hexenmeister nur ein Effekt von 'Instabiles Gebrechen' oder 'Feuerbrand' auf einem Ziel gleichzeitig aktiv sein"},
[370] = {name = "Reinigen", description = "[q]Reinigt das feindliche Ziel und entzieht ihm so 1 nützlichen magischen Effekt."},
[403] = {name = "Blitzschlag", description = "[q]Schleudert einen Blitz auf das Ziel und fügt ihm 771 Naturschaden zu."},
[421] = {name = "Kettenblitzschlag", description = "[q]Schleudert einen Blitzschlag auf den Feind, der 1093 Naturschaden verursacht und zu in der Nähe befindlichen Feinden weiterspringt. Jeder Sprung verringert den Schaden um 30%. Wirkt auf insgesamt 3 Ziele."},
[465] = {name = "Aura der Hingabe", description = "[q]Erhöht die Rüstung aller Gruppen- und Schlachtzugsmitglieder in einem Umkreis von 40 Metern um zusätzlich 4076 . Spieler können pro Paladin immer nur jeweils eine Aura besitzen."},
[467] = {name = "Dornen", description = "[q]Aus dem befreundeten Ziel sprießen Dornen, die Angreifern bei einem Treffer\n\nZerfleischen\n[ 0 + (Attack power * .168)]\n [( 0 + (Spell power * .168)) * 1 ] Naturschaden zufügen.\n\nRache\nDurch den Effekt 'Rache' gewonnene Angriffskraft erhöht den Schaden von 'Dornen' nicht weiter\n\n Hält 20 sec lang an."},
[512] = {name = "Eisketten", description = "[q]Schlingt eisige Ketten um das Ziel, die es 5 sec lang am Platz festhalten."},
[518] = {name = "Wurm beschwören", description = "[q]Beschwört eine Ausgeburt von Fankriss aus den Tiefen von Ahn'Qiraj."},
[527] = {name = "Magiebannung", description = "[q]Bannt auf das Ziel wirkende magische Effekte. Entfernt 2 auf\n\nAbsolution\neinen Verbündeten\n\n Euch wirkende Schwächungszauber oder 1 auf einen Gegner wirkenden Stärkungszauber.\n\nGlyphe 'Magiebannung'\nWenn dieser Zauber erfolgreich einen Schwächungszauber von Euch oder einem Verbündeten bannt, heilt er das Ziel zusätzlich um 3% seiner maximalen Gesundheit"},
[568] = {name = "Arkane Wucht", description = "[q]Verursacht bei allen Feinden innerhalb von 10 Metern 4050 to 4950 Arkanschaden, verringert ihr Bewegungstempo um 70% und stößt sie zurück."},
[585] = {name = "Heilige Pein", description = "[q]Zerschmettert einen Feind und fügt ihm 755 Heiligschaden zu.\n\nGlyphe 'Heilige Pein'\nWenn das Ziel von Eurem Zauber 'Heiliges Feuer' betroffen ist, wird der Schaden von 'Heilige Pein' um 20% erhöht"},
[588] = {name = "Inneres Feuer", description = "[q]Der Zaubernde wird von einem Schub heiliger Energie erfüllt, der seinen durch Gegenstände erhaltenen Rüstungswert um 60 % und seine Zaubermacht um 0 erhöht.\n\nInnerer Wille\nEs kann nur entweder 'Innerer Wille' oder 'Inneres Feuer' aktiv sein"},
[686] = {name = "Schattenblitz", description = "[q]Sendet einen schattigen Blitz zum Gegner, der 698 Schattenschaden verursacht."},
[720] = {name = "Umschlingen", description = "[q]Die Brutstätte Fankriss' des Unnachgiebigen zieht das Ziel nahen Silithidjungtieren entgegen und wurzelt es 8 sec lang fest."},
[744] = {name = "Gift", description = "[q]Fügt einem Feind 15 sec lang alle 3 Sek. Naturschaden zu."},
