[851] = {name = "Verwandlung: Schaf", description = "[q]Verwandelt den Feind bis zu 10 sec lang in ein hilfloses Schaf, das weder Zauber wirken noch angreifen kann und sich um 60% langsamer bewegt."},
[879] = {name = "Exorzismus", description = "[q]Fügt einem feindlichen Ziel [(( 2483 + 2771 ) / 2) + (0.344 * Attack power)] Heiligschaden\n\nGlyphe 'Exorzismus'\nsowie zusätzlich im Verlauf von 6 sec alle 2 Sek. [( 2483 + 2771 ) / 2 * 0.0688] Schaden\n\n zu. Ist das Ziel untot oder dämonisch wird der Zauber stets kritisch treffen."},
[883] = {name = "Begleiter 1 rufen", description = "[q]Beschwört Euer erstes Tier zu Euch."},
