[1126] = {name = "Mal der Wildnis", description = "[q]Belegt ein befreundetes Ziel mit einem Mal, welches seine Stärke, Beweglichkeit, Ausdauer und Intelligenz um 5% sowie alle Magiewiderstände um [( 85 <= 70 ? 85 / 2 : ( 85 <= 80 ? 85 / 2 + ( 85 - 70) / 2 * 5 : 85 / 2 + ( 85 - 70) / 2 * 5 + ( 85 - 80) / 2 * 7)) - 0.5] erhöht. Hält 1 hour lang an. Wenn sich das Ziel in Eurer Gruppe oder Eurem Schlachtzug befindet, werden alle Mitglieder den Effekt erhalten."},
[1161] = {name = "Herausforderungsruf", description = "[q]Zwingt alle Feinde in einem Umkreis von 10 Metern, 6 sec lang ihre Angriffe auf Euch zu konzentrieren."},
[1459] = {name = "Arkane Brillanz", description = "[q]Erfüllt das Ziel mit Brillanz, welche sein maximales Mana um 2126 und seine Zaubermacht um 6 % erhöht. Hält 1 hour lang an. Wenn sich das Ziel in Eurer Gruppe oder Eurem Schlachtzug befindet, werden alle Mitglieder den Effekt erhalten."},
