[7120] = {name = "Prachtmeers Verteidigung", description = "Rüstung um 40 erhöht.\nAusdauer um 10 erhöht."},
[7121] = {name = "Antimagischer Schild", description = "[q]Lässt einen Schild aus Antimagie um den Zaubernden herum entstehen und gewährt ihm 6 sec lang Immunität gegen Magie."},
[7124] = {name = "Arugals Gabe", description = "[q]Fügt einem Feind nach Ablauf von 15 sec 100 Schattenschaden zu."},
[7125] = {name = "Toxinspeichel", description = "[q]Fügt einem Feind alle 3 Sek. 20 Naturschaden zu. Entzieht außerdem alle 3 Sek. 20 Mana. Hält 30 sec lang an."},
[7127] = {name = "Wankelmut", description = "[q]Erhöht die Zeit zwischen den Angriffen eines Feindes um 25%, verlangsamt sein Zaubertempo um 20% und sein Bewegungstempo um 20%. Hält 10 sec lang an."},
[7137] = {name = "Schattenaufladung", description = "[q]Erhöht das Tempo und verursacht beim ersten Angriff zusätzlichen Schaden."},
[7139] = {name = "Teufelsstampfen", description = "[q]Fügt nahen Feinden normalen Schaden plus 1000 zu, betäubt sie und erhöht ihnen erlittenen körperlichen Schaden um 10. Hält 3 sec lang an."},
[7140] = {name = "Schwäche aufdecken", description = "[q]Erhöht 5 sec lang den durch einen Gegner erlittenen körperlichen Schaden."},
[7154] = {name = "Dunkles Angebot", description = "[q]Heilt einen Verbündeten unter Verwendung von dunkler Magie."},
[7159] = {name = "Meucheln", description = "[q]Fügt einem Gegner zusätzlichen Schaden zu, allerdings nur, wenn von hinten angegriffen wird."},
[7160] = {name = "Hinrichten", description = "[q]Versucht, einen verwundeten Feind ganz zu erledigen und verursacht dabei 120% normalen Waffenschaden. 'Hinrichten' kann nur bei Feinden angewendet werden, die über 20% oder weniger Gesundheit verfügen."},
[7162] = {name = "Erste Hilfe", description = "[q]Heilt ein Ziel um den gleichen Wert wie den der maximalen Gesundheit des Zaubernden."},
[7164] = {name = "Verteidigungshaltung", description = "[q]Nimmt eine Verteidigungshaltung ein. Hält 3 min lang an."},
[7165] = {name = "Kampfhaltung", description = "[q]Nimmt eine ausbalancierte Gefechtshaltung ein, die Wut erzeugt, wenn der Krieger getroffen wird, und gleichzeitig Wutabfall verringert, wenn der Krieger sich nicht im Kampf befindet. Hält until cancelled lang an."},
[7272] = {name = "Staubwolke", description = "[q]Eine donnernde Staubwolke fliegt ins Gesicht der Gegner in der Nähe und verringert 5 sec lang ihre Trefferchance um 20%."},
[7279] = {name = "Schwarzer Schlick", description = "[q]Erhöht die Zeit zwischen Angriffen in der Nähe befindlicher Feinde um 25% und verlangsamt ihr Bewegungstempo um 30%. Hält 10 sec lang an."},
[7289] = {name = "Schrumpfen", description = "[q]Verringert 20 sec lang die Stärke und Ausdauer eines Gegners."},
[7321] = {name = "Kälte", description = "[q]Verringert das Bewegungstempo des Angreifers um 30% und erhöht die Zeit zwischen seinen Angriffen um 20%."},
[7342] = {name = "Weiter Streich", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden normalen Schaden plus zusätzlichen Schaden zu."},
[7357] = {name = "Giftiger Stich", description = "[q]Verursacht alle 3 Sek. Naturschaden bei einem Feind, erhöht die Zeit zwischen seinen Angriffen um 10% und verringert 10 sec lang sein Bewegungstempo."},
[7365] = {name = "Flasche Gift", description = "[q]Wirft eine Flasche voll Gift auf den Feind und fügt ihm so 15 sec lang alle 3 Sek. Naturschaden zu."},
[7366] = {name = "Berserkerhaltung", description = "[q]Nimmt eine offensive Haltung ein, die Wut erzeugt, wenn der Krieger einen Gegner schlägt. Hält 4 min lang an."},
[7367] = {name = "Infizierter Biss", description = "[q]Verursacht alle 3 Sek. Naturschaden bei einem Feind und erhöht 15 sec lang seinen erlittenen körperlichen Schaden."},
[7386] = {name = "Rüstung zerreißen", description = "[q]Zerreißt die Rüstung Eures Ziels\n\nGlyphe 'Rüstung zerreißen'\nsowie eines zweiten nahen Ziels\n\n , wodurch der Rüstungswert um 4% verringert wird. Diese Fähigkeit verursacht ein hohes Maß an Bedrohung, die durch Angriffskraft noch erhöht wird. Bis zu 3-mal stapelbar. Hält 30 sec lang an."},
[7393] = {name = "Bruder heilen", description = "[q]Heilt das Ziel um 30000."},
[7395] = {name = "Todesminendynamit", description = "[q]Fügt allen Gegnern in einem ausgewählten Bereich Feuerschaden zu."},
[7399] = {name = "Erschrecken", description = "[q]Erschreckt einen Feind und lässt ihn 3 sec lang voller Furcht flüchten. Es kann immer nur jeweils ein einzelnes Ziel mit Schrecken erfüllt werden."},
[7645] = {name = "Gedanken beherrschen", description = "[q]Kontrolliert 10 sec lang die Gedanken eines Humanoiden bis Stufe 25, erhöht jedoch die Zeit zwischen seinen Angriffen um 100%."},
[7656] = {name = "Verhexung von Rabenklaue", description = "Verursachter körperlicher Schaden um 8 verringert.\nVerursachter Magieschaden um 13 verringert."},
[7713] = {name = "Klagender Toter", description = "[q]Verringert 6 sec lang alle Attribute naher Feinde."},
[7739] = {name = "Infernoschild", description = "[q]Fügt in der Nähe befindlichen Feinden 147 to 177 Feuerschaden zu und umgibt den Zaubernden mit einem Flammenschild, der 10 sec lang anhält und Nahkampfangreifer für 50 zusätzlichen Schaden verbrennt."},
[7741] = {name = "Beschworener Dämon", description = "[q]Beschwört einen Dämon aus dem Nether."},
[7764] = {name = "Weisheit von Agamaggan", description = "[q]Erhöht die Intelligenz um 10."},
[7765] = {name = "Projektion", description = nil},
[7769] = {name = "Gebäude von Jotunheim bombardieren", description = "[q]Bombardiert das Gebäude von Jotunheim vor Euch, um es in Brand zu stecken. Die Reichweite beträgt 60 Meter."},
[7812] = {name = "Opferung", description = "[q]Opfert einen Teil der Gesundheit des Leerwandlers und gewährt seinem Besitzer einen Schild, der 30 sec lang 10921 Schaden absorbiert. Solange der Schild hält, wird das Zauberwirken durch erlittenen Schaden nicht unterbrochen."},
[7814] = {name = "Schmerzenspeitsche", description = "[q]Ein sofortiger Angriff, der das Ziel peitscht und ihm so [ 219 + (0.612 * (Spell power * 0.5))] Schattenschaden zufügt."},
[7855] = {name = "Wasserschrecken beschwören", description = "[q]Beschwört einen Wasserschrecken, der den Zaubernden bis zur Freigabe begleitet."},
[7870] = {name = "Geringe Unsichtbarkeit", description = "[q]Verleiht Eurem beschworenen Dämon maximal 5 min lang geringe Unsichtbarkeit. Der Zauber kann nicht während eines Kampfes verwendet werden."},
[7891] = {name = "Ragnaros-Gabe", description = "[q]Verursacht normalen Schaden plus 40 bei einem Feind."},
[7896] = {name = "Explosivgeschoss", description = "[q]Fügt einem Feind und seinen nächsten Verbündeten normalen Schaden plus 25 zu. Jeglicher zugefügte Schaden basiert auf Feuermagie."},
[7901] = {name = "Verfallene Beweglichkeit", description = "[q]Verringert 5 min lang die Beweglichkeit eines Gegners."},
[7947] = {name = "Lähmendes Toxin", description = "[q]Fügt einem Gegner 9 sec lang alle 3 Sek. Naturschaden zu."},
[7948] = {name = "Wilde Regeneration", description = "[q]Heilt den Zaubernden 16 sec lang alle 2 Sek. um 38 to 62 Schaden."},
[7951] = {name = "Toxinspucke", description = "[q]Verursacht bei einem Feind Naturschaden."},
[7952] = {name = "Klonen", description = nil},
[7964] = {name = "Rauchbombe", description = "[q]Wirft eine Rauchbombe, die in der Nähe befindliche Feinde 3 sec lang betäubt."},
[7965] = {name = "Schlangengestalt von Kobrahn", description = "[q]Ermöglicht 5 min lang Gestaltwandlung in eine Schlange, erhöht Angriffsschaden um 50 die Zeit zwischen Angriffen um 30%."},
[7966] = {name = "Dornen-Aura", description = "[q]Verursacht 10 Naturschaden bei jeder Kreatur, die ein in der Nähe befindliches Gruppenmitglied schlägt. Spieler können pro Paladin immer nur jeweils eine Aura besitzen. Die Aura hält 1 min lang an."},
[7967] = {name = "Naralex' Alptraum", description = "[q]Versetzt das feindliche Ziel bis zu 8 sec lang in Schlaf. Jegliche feindliche Aktion wird das Ziel wieder aufwecken. Es kann immer nur jeweils ein Ziel in Schlaf versetzt werden."},
[7978] = {name = "Dynamit werfen", description = "[q]Fügt allen Gegnern in einem ausgewählten Bereich Feuerschaden zu."},
[7979] = {name = "Kompakter Ernteschnitter", description = "[q]Beschwört 1 kompakten Ernteschnitter, um dem Zaubernden 1 min lang im Kampf zu helfen. Der Schnitter kann nicht geheilt werden, aber wenn er zerstört wird, kann er geplündert werden."},
[7992] = {name = "Verlangsamendes Gift", description = "[q]Erhöht die Zeit zwischen Angriffen eines Gegners und verlangsamt sein Bewegungstempo. Hält 10 sec lang an."},
[7993] = {name = "Treantverbündeten beschwören", description = "[q]Beschwört 1 Treantverbündeten als Begleitung für den Zaubernden bis zur Freigabe."},
[7997] = {name = "Stärke entziehen", description = "[q]Verringert die Stärke eines Feindes um 6, seine Beweglichkeit um 6 und seine Ausdauer 5 min lang um 3."},
[8004] = {name = "Heilende Woge", description = "[q]Heilt ein befreundetes Ziel um 4804 ."},
[8014] = {name = "Tetanus", description = "[q]Verringert 15 sec lang die Ausdauer eines Feindes."},
[8016] = {name = "Stärkeverfall", description = "[q]Verringert 15 sec lang die Stärke eines Feindes."},
[8040] = {name = "Druidenschlummer", description = "[q]Versetzt das feindliche Ziel bis zu 6 sec lang in Schlaf. Jegliche feindliche Aktion wird das Ziel wieder aufwecken. Es kann immer nur jeweils ein Ziel in Schlaf versetzt werden."},
[8041] = {name = "Schlangenform", description = "[q]Ermöglicht 10 sec lang Gestaltwandlung in eine Schlange, erhöht körperlichen Schaden um 25, Zauber wirken ist allerdings nicht mehr möglich."},
[8056] = {name = "Frostschock", description = "[q]Schockt das Ziel unmittelbar mit Frost, verursacht Frostschaden und verringert das Bewegungstempo des Ziels. Hält 8 sec lang an. Verursacht ein hohes Maß an Bedrohung."},
[8078] = {name = "Donnerknall", description = "[q]Fügt nahen Gegnern Naturschaden zu, erhöht die Zeit zwischen ihren Angriffen und verringert ihr Bewegungstempo. Hält 6 sec lang an."},
[8092] = {name = "Gedankenschlag", description = "[q]Überzieht das Ziel mit 1603 Schattenschaden."},
[8137] = {name = "Silithidenpocken", description = "[q]Erhöht bei einem Feind den erlittenen körperlichen Schaden 30 sec lang um 10%."},
[8138] = {name = "Mirkfallonfungus", description = "[q]Verringert 5 sec lang die Ausdauer eines Gegners."},
[8139] = {name = "Fieberhafte Erschöpfung", description = "[q]Verringert die Intelligenz eines Feindes 20 sec lang um 5% und seine Stärke um 5%."},
[8141] = {name = "Druidentrank", description = "[q]Benutzt ein Elixier, um den Zaubernden um 312 to 417 zu heilen."},
[8142] = {name = "Greifende Ranken", description = "[q]Verursacht körperlichen Schaden bei in der Nähe befindlichen Feinden, schlägt sie zu Boden und macht sie 5 sec lang bewegungsunfähig."},
[8147] = {name = "Donnerknall", description = "[q]Fügt Gegnern in der Nähe Naturschaden zu, erhöht die Zeit zwischen ihren Angriffen um 40% und verlangsamt ihre Bewegungen um 40%. Hält 6 sec lang an."},
[8148] = {name = "Dornen-Aura", description = "[q]Verursacht 20 Naturschaden bei jeder Kreatur, die ein in der Nähe befindliches Gruppenmitglied schlägt. Spieler können pro Paladin immer nur jeweils eine Aura besitzen. Die Aura hält 1 min lang an."},
[8150] = {name = "Donnerkracher", description = "[q]Verursacht 106 to 118 Naturschaden bei in der Nähe befindlichen Feinden und betäubt sie 2.50 sec lang."},
[8190] = {name = "Totem des glühenden Magmas", description = "[q]Beschwört ein Totem des glühenden Magmas mit 5 Gesundheit zu Füßen des Zaubernden. Das Totem fügt Gegnern in einem Umkreis von 8 Metern alle 2 Sek. 268 Feuerschaden zu. Hält 1 min lang an."},
[8209] = {name = "Explodierendes Schaf", description = "[q]Erzeugt 1 explosives Schaf, das den nächsten Feind angreift und dann explodiert. Explosive Schafe sind instabil und zerstören sich nach 1 min selbst."},
[8210] = {name = "Savannenjunge", description = "[q]Beschwört 3 Savannenjunge, um dem Zaubernden 30 sec lang im Kampf zu helfen."},
[8211] = {name = "Kettenbrand", description = "[q]Trifft einen Feind mit einem Antimanablitz, der auf einen anderen in der Nähe befindlichen Feind überspringt. Für jeden vom Blitz verbrauchten Manapunkt erleidet das Ziel 1 Schaden. Der Zauber wirkt auf maximal 3 Ziele."},
[8242] = {name = "Schildschlag", description = "[q]Fügt einem Gegner Schaden zu und betäubt ihn 2 sec lang."},
[8245] = {name = "Ätzende Säure", description = "[q]Verringert die Rüstung von in der Nähe befindlichen Feinden 20 sec lang um 16."},
[8246] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm 161 to 171 Naturschaden zu."},
[8255] = {name = "Starkes Spalten", description = "[q]Fügt einem Feind und seinen nächsten Verbündeten normalen Schaden plus 55 zu, wirkt sich auf bis zu 5 Ziele aus und erhöht die Zeit zwischen ihren Angriffen 10 sec lang um 33%."},
[8257] = {name = "Giftstachel", description = "[q]Fügt einem Feind Naturschaden zu und dann zusätzlich 15 sec lang alle 3 Sek. Naturschaden."},
[8258] = {name = "Aura der Hingabe", description = "[q]Verleiht Gruppenmitgliedern in der Nähe 4 min lang zusätzliche Rüstung."},
[8259] = {name = "Wirbelndes Sperrfeuer", description = "[q]Verursacht normalen Schaden plus 50 bei in der Nähe befindlichen Feinden."},
[8260] = {name = "Sturmattacke", description = "[q]Erhöht das Bewegungstempo des Zaubernden 4 sec lang um 178%, und lässt ihn bei seinem ersten Angriff zusätzlich 600 Schaden zufügen."},
[8262] = {name = "Totem des Elementarschutzes", description = "[q]Beschwört ein Totem des Elementarschutzes zu Füßen des Zaubernden. Das Totem hält 30 sec lang an und erhöht den Widerstand von in der Nähe befindlichen Gruppenmitgliedern gegen schädliche Magie."},
[8264] = {name = "Totem des Lavaschwalls", description = "[q]Beschwört ein Totem des Lavaschwalls zu Füßen des Zaubernden. Das Totem bleibt 20 sec lang bestehen und fügt Gegnern in der Nähe alle 3 Sek. Feuerschaden zu."},
[8267] = {name = "Verfluchtes Blut", description = "[q]Verringert 10 min lang die Intelligenz von Gegnern in der Nähe."},
[8269] = {name = "Raserei", description = "[q]Erhöht 2 min lang das Angriffstempo des Zaubernden um 60%."},
[8270] = {name = "Erdenrumpler beschwören", description = "[q]Beschwört 1 Steinrumpler als Begleitung für den Zaubernden bis zur Freigabe."},
[8271] = {name = "Windheuler beschwören", description = "[q]Beschwört 1 Windheuler als Begleitung für den Zaubernden bis zur Freigabe."},
[8272] = {name = "Gedankenbeben", description = "[q]Verringert das Zaubertempo eines Feindes 15 sec lang um 20%."},
[8274] = {name = "Gezähmten Kampfeber beschwören", description = "[q]Beschwört 1 gezähmten Kampfeber als Begleitung für den Zaubernden bis zur Freigabe."},
[8275] = {name = "Vergifteter Schuss", description = "[q]Fügt einem Feind normalen Schaden plus 30 zu und dann 15 sec lang alle 3 Sek. 50 Naturschaden."},
[8276] = {name = "Gezähmte Hyäne beschwören", description = "[q]Beschwört 1 gezähmte Hyäne als Begleitung für den Zaubernden bis zur Freigabe."},
[8281] = {name = "Schallexplosion", description = "[q]Fügt in der Nähe befindlichen Feinden normalen Schaden plus 3 zu und hindert sie 10 sec lang am Wirken von Zaubern."},
[8282] = {name = "Blutfluch", description = "[q]Erhöht den erlittenen körperlichen Schaden eines Feindes 20 sec lang um 1."},
[8285] = {name = "Toben", description = "[q]Fügt nahen Gegnern normalen Schaden plus 7 zu und betäubt sie 2.50 sec lang."},
[8286] = {name = "Ebergeist beschwören", description = "[q]Beschwört 1 Ebergeist, um dem Zaubernden 1 min lang im Kampf zu helfen."},
[8292] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 3 Ziele aus und verursacht bei jedem 162 to 181 Naturschaden."},
[8293] = {name = "Blitzschlagwolke", description = "[q]Lässt eine Blitzschlagwolke entstehen, die 15 sec anhält und allen Feinden im ausgewählten Gebiet Naturschaden und alle 5 Sek. zusätzlichen Schaden zufügt."},
[8314] = {name = "Felshaut", description = "Erhöht die Rüstung und Ausdauer."},
[8355] = {name = "Schwäche ausnutzen", description = "[q]Fügt einem Feind normalen Schaden plus 28 to 32 zu, aber nur, wenn von hinten angegriffen wird."},
[8358] = {name = "Manastachel", description = "[q]Stellt Mana des Zaubernden wieder her."},
[8361] = {name = "Reinheit", description = "[q]Macht den Zaubernden 2 sec lang gegen alle Angriffe und Zauber immun."},
[8362] = {name = "Erneuerung", description = "[q]Heilt einen Verbündeten 15 sec lang alle 3 Sek."},
[8364] = {name = "Blizzard", description = "[q]Ruft einen Blizzard herbei, der 8 sec lang anhält und Feinden im ausgewählten Gebiet alle 2 Sek. Frostschaden zufügt."},
[8365] = {name = "Vergrößern", description = "[q]Erhöht den von einem Verbündeten zugefügten körperlichen Schaden 10 sec lang um 3."},
[8372] = {name = "Wasserwächter heraufbeschwören", description = "[q]Beschwört 1 Wasserwächter als Begleitung für den Zaubernden bis zur Freigabe."},
[8374] = {name = "Bogenzerkracher", description = "[q]Schlägt in einem Bogen wild um sich und fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Schaden zu."},
[8376] = {name = "Totem des Erdengriffs", description = "[q]Beschwört ein Totem des Erdengriffs, das 30 sec lang bestehen bleibt und in der Nähe befindliche Feinde in regelmäßigen Abständen bewegungsunfähig macht."},
[8377] = {name = "Erdengriff", description = "[q]Lässt alle Gegner in einem Umkreis von 10 Metern 4 sec lang festwurzeln."},
[8379] = {name = "Entwaffnen", description = "[q]Entwaffnet den Gegner 10 sec lang."},
[8382] = {name = "Egelgift", description = "[q]Entzieht einem Feind 15 sec lang alle 3 Sek. 2 Gesundheit und überträgt sie auf den Zaubernden."},
[8385] = {name = "Hurtiger Wind", description = "Erhöht Bewegungstempo um 40%\nErhöht Angriffstempo um 30%."},
[8391] = {name = "Verheeren", description = "[q]Fügt einem Feind normalen Schaden plus 44 zu und betäubt ihn 3 sec lang."},
[8398] = {name = "Frostblitzsalve", description = "[q]Fügt nahen Feinden Frostschaden zu und verringert 4 sec lang ihr Bewegungstempo um 50%."},
[8399] = {name = "Schlaf", description = "[q]Versetzt in der Nähe befindliche Feinde bis zu 6 sec lang in Schlaf. Jeglicher erlittene Schaden führt dazu, dass sie aufwachen."},
[8435] = {name = "Gabelblitzschlag", description = "[q]Fügt Gegnern in einem kegelförmigen Bereich vor dem Zaubernden Naturschaden zu."},
[8555] = {name = "Totgeglaubt zurückgelassen", description = "[q]Fügt einem Feind 200 Schaden zu und zwingt ihn bis zu 5 min lang, Tod vorzutäuschen."},
[8598] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm 86 to 93 Naturschaden zu."},
[8599] = {name = "Wutanfall", description = "[q]Wird wütend, erhöht 2 min lang den verursachten körperlichen Schaden und das Angriffstempo."},
[8600] = {name = "Fieberseuche", description = "[q]Fügt einem Feind 250 Naturschaden zu und dann 30 sec lang alle 3 Sek. zusätzlich 11 Schaden."},
[8602] = {name = "Rache", description = "Schaden um 100% erhöht.\nImmun gegen Furcht."},
[8611] = {name = "Phasenverschiebung", description = "[q]Der Zaubernde wird unsichtbar, aber handlungsunfähig.\n Immun gegen alle Angriffe und Zauber."},
[8646] = {name = "Schnapptritt", description = "[q]Fügt einem Gegner Schaden zu und betäubt ihn 2 sec lang."},
[8656] = {name = "Kriecher beschwören", description = "[q]Beschwört 1 Kriecher als Begleitung für den Zaubernden bis zur Freigabe."},
[8699] = {name = "Unheilige Raserei", description = "[q]Erhöht das Angriffstempo eines Verbündeten, fügt diesem Verbündeten aber auch alle 2 Sek. Naturschaden zu."},
[8715] = {name = "Schreckliches Geheul", description = "[q]Lässt in der Nähe befindliche Feinde 3 sec lang voller Furcht flüchten."},
[8716] = {name = "Niedriger Prankenhieb", description = "[q]Fügt körperlichen Schaden zu und betäubt einen Feind. Verringert außerdem das Bewegungstempo 12 sec lang um 50%."},
[8722] = {name = "Sukkubus beschwören", description = "[q]Beschwört 1 Sukkubus, der den Zaubernden bis zur Freigabe begleitet."},
[8788] = {name = "Blitzschlagschild", description = "[q]Umgibt einen Verbündeten mit 3 Kugelblitzen, die eine Chance von 50% haben, Nahkampf- oder Distanzangreifer zu treffen und 83 Schaden zu verursachen. Jedes Mal, wenn der Blitzschlagschild zuschlägt, löst sich ein Kugelblitz auf. Auf diese Weise erlischt der Schild nach 10 min oder nachdem er 3-mal zugeschlagen hat."},
[8800] = {name = "Dynamit", description = nil},
[8806] = {name = "Vergifteter Schuss", description = "[q]Fügt einem Gegner normalen sowie zusätzlichen Schaden zu und verursacht anschließend 1 min lang alle 10 Sek. zusätzlichen Naturschaden."},
[8813] = {name = "Kurzens Gedankensklaven beschwören", description = "[q]Beschwört einen von Kurzens Gedankensklaven als Begleitung für den Zaubernden bis zur Freigabe."},
[8814] = {name = "Flammenstachel", description = "[q]Lässt eine Flammensäule niedergehen, die alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen zunächst Feuerschaden und dann 9 sec lang alle 3 Sek. zusätzlichen Schaden zufügt."},
[8852] = {name = "Moosfell", description = "[q]Ein moosiges Fell gibt dem Zaubernden eine Chance, Schaden zu absorbieren, wann immer er Schaden nimmt."},
[8858] = {name = "Bombe", description = "[q]Bombardiert alle Feinde in einem ausgewählten Gebiet und fügt Feuerschaden zu."},
