[34076] = {name = "Fürchtet die Worgs", description = nil},
[34079] = {name = "Totem der Erdung", description = "[q]Beschwört ein Totem der Erdung mit 5 Gesundheit zu Füßen des Zaubernden, das alle 5 Sek. einen schädlichen Zauber, der auf ein in der Nähe befindliches Gruppenmitglied gewirkt wurde, auf das Totem umleitet. Zauber mit Flächenwirkung werden nicht umgeleitet. Hält 45 sec lang an."},
[34080] = {name = "Ripostehaltung", description = "[q]Nimmt eine Verteidigungshaltung an, die Angriffe zurückschlägt, wenn der Krieger getroffen wird. Hält 10 sec lang an."},
