[34268] = {name = "Säureatem", description = "[q]Fügt jedem Feind in einem kegelförmigen Bereich vor dem Zaubernden 200 Naturschaden und zusätzlich alle 3 Sek. 20 Schaden zu. Hält 20 sec lang an."},
