[34694] = {name = "Blenden", description = "[q]Blendet das Ziel und lässt es bis zu 10 sec lang verwirrt umherirren. Jeglicher erlittene Schaden entfernt den Effekt."},
[34697] = {name = "Allergische Reaktion", description = "[q]Infiziert einen Gegner 18 sec lang mit einer Krankheit, die seinen erlittenen Schaden erhöht. Fügt dem Ziel 18 sec lang alle 5 Sek. Naturschaden zu. Es besteht die Chance, dass das Ziel die Krankheit auf seine nahen Verbündeten überträgt."},
[34699] = {name = "Zeitmantel", description = "[q]Als Wachmann von Lordaeron verkleidet."},
