[38924] = {name = "Sporenexplosion", description = "[q]Eine Sporenexplosion fügt den Gegnern in einem Umkreis von 6 Metern unmittelbar 1388 to 1612 Naturschaden zu. Zudem werden sie infiziert, wodurch sie alle 3 Sek. weitere 694 to 806 Naturschaden erleiden."},
[38926] = {name = "Dunkler Stoß", description = "[q]Durchtränkt die Waffe des Zaubernden mit Schatten, sodass sie beim nächsten Angriff zusätzlichen Schaden verursacht. Der gesamte verursachte Schaden wird als Schattenschaden angesehen."},
[38932] = {name = "Blinzeln", description = "[q]Teleportiert den Zaubernden hinter ein Ziel, das sich bis zu 20 Meter entfernt befindet, es sei denn, etwas steht im Weg. Befreit den Zaubernden auch von jeglichen Fesseln."},
[38939] = {name = "Inferno", description = "[q]Beschwört einen Meteor aus dem Wirbelnden Nether, der 34 Punkt(e) Feuerschaden verursacht und alle feindlichen Ziele in dem Gebiet 2 sec lang betäubt. Aus dem Krater erhebt sich eine Höllenbestie, die 1 min lang unter dem Befehl des Zaubernden steht. Sobald die Kontrolle verloren ist, muss die Höllenbestie unterworfen werden, um sie unter Kontrolle zu halten. Kann nur draußen verwendet werden."},
[38945] = {name = "Furchterregender Schrei", description = "[q]Schreit in der Nähe befindliche Feinde an und lässt sie 8 sec lang voller Furcht fliehen."},
[38947] = {name = "Raserei", description = "[q]Erhöht das Angriffstempo des Zaubernden um 60% und den verursachten körperlichen Schaden um 50. Hält 10 min lang an."},
