[38976] = {name = "Sporenbeben", description = "[q]Kanalisiert ein seismisches Beben, das allen nahen Gegnern Naturschaden zufügt."},
[38980] = {name = "Wichtel der Zwielichthöhe beschwören", description = "[q]Beschwört einen Wichtel als Begleitung für den Zaubernden bis zur Freigabe."},
