[39588] = {name = "Totem der Verbrennung", description = "[q]Beschwört für 30 sec ein Totem der Verbrennung mit 1 Gesundheit zu Euren Füßen, das wiederholt 3 Feinde im Umkreis von 40 Metern angreift und 1980 to 2420 Feuerschaden anrichtet."},
[39589] = {name = "Totem des Wirbelsturms", description = "[q]Beschwört ein Totem des Wirbelsturms."},
[39591] = {name = "Totem der Verbrennung", description = "[q]Beschwört für 1 min ein Totem der Verbrennung zu Euren Füßen, das wiederholt einen Feind im Umkreis von 20 Metern angreift und Feuerschaden verursacht."},
[39592] = {name = "Sengen", description = "[q]Versengt das Ziel mit Feuerschaden."},
