[39647] = {name = "Fluch der Besserung", description = "[q]Verflucht einen Gegner für 1.50 min, wodurch dieser sein Ziel bei Zauber- oder Nahkampfangriffen mit einer Wahrscheinlichkeit von 20% um 5% heilt."},
[39649] = {name = "Schattengeister beschwören", description = "[q]Erschafft einen schattenhaften Geist, der das Ziel angreift. Vom Schattengeist verursachter Schaden gewährt dem Zaubernden Mana. Hält until cancelled lang an."},
[39665] = {name = "Wundgift", description = "[q]Vergiftet den Feind und verringert alle auf ihn angewandten Heileffekte 15 sec lang um 20%."},
[39666] = {name = "Mantel der Schatten", description = "[q]Entfernt sofort alle schädlichen Zaubereffekte und erhöht Eure Chance, jeder Art von Zauber zu widerstehen, 5 sec lang um 90%. Effekte, die die Benutzung von 'Mantel der Schatten' verhindern, werden nicht entfernt."},
[39667] = {name = "Verschwinden", description = "[q]3 sec lang aus der Sicht verschwinden."},
[39670] = {name = "Teufelsfeuerbrand", description = "[q]Verbrennt sofort einen Feind und fügt ihm dann 15 sec lang alle 3 Sek. zusätzlichen Feuerschaden zu."},
[39671] = {name = "Regen des Chaos", description = "[q]Ruft einen Schmelzregen herbei, der alle Gegner in einem ausgewählten Gebiet verbrennt und ihnen 6 sec lang alle 3 Sek. 138 to 161 Feuerschaden zufügt."},
[39672] = {name = "Fluch der Pein", description = "[q]Verflucht einen Feind mit Pein und fügt 30 sec lang alle 3 Sek. Schattenschaden zu. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein."},
[39674] = {name = "Verbannen", description = "[q]Verbannt einen Feind und verhindert damit jegliche Aktion, macht es aber bis zu 15 sec lang unverwundbar. Es kann immer nur jeweils ein Ziel verbannt werden."},
[39699] = {name = "Kampfflußregulator", description = nil},
