[39941] = {name = "Inferno", description = "[q]Beschwört einen Meteor aus dem Wirbelnden Nether, der 34 Punkt(e) Feuerschaden verursacht und alle feindlichen Ziele in dem Gebiet 2 sec lang betäubt. Aus dem Krater erhebt sich eine Höllenbestie, die 1 min lang unter dem Befehl des Zaubernden steht. Sobald die Kontrolle verloren ist, muss die Höllenbestie unterworfen werden, um sie unter Kontrolle zu halten. Kann nur draußen verwendet werden."},
[39942] = {name = "Aasschwarm", description = "[q]Eine Welle chaotischer Magie schlägt über Feinde vor dem Zaubernden hinweg und fügt 800 to 1200 Schattenschaden zu."},
[39945] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 5 Ziele aus und verursacht bei jedem folgenden Ziel immer größeren Naturschaden."},
