[47482] = {name = "Sprung", description = "[q]Springt hinter ein feindliches Ziel."},
[47602] = {name = "Uralten König der Drakkari beschwören", description = nil},
[47611] = {name = "Ätzender Speichel", description = "[q]Speit hochwirksame Säurespucke auf einen Eisriesen."},
[47629] = {name = "Tecahunas Giftspucken", description = "[q]Spuckt Gift auf einen Gegner, fügt Naturschaden sowie 15 sec lang alle 5 Sek. zusätzlichen Schaden zu."},
[47668] = {name = "Heilung", description = "[q]Ruft heilige Magie herbei, um einen Verbündeten zu heilen."},
[47675] = {name = "Kürzlich geimpft", description = "Kürzlich geimpft\nKürzlich geimpft. Vor der Geißelseuche sicher."},
[47688] = {name = "Chaotischer Energieschlag", description = "[q]Schleudert einen chaotischen Energieschlag auf einen Gegner, der ihm 244 to 406 Arkanschaden zufügt und dann auf weitere Gegner in der Nähe überspringt. Jeder Sprung verringert den Schaden um 50%. Insgesamt werden 3 Ziele angegriffen."},
[47696] = {name = "Arkane Nova", description = "[q]Sendet einen Energiestrahl aus, der den Gegnern in einem Umkreis von 10 Metern 943 to 1057 Arkanschaden zufügt. Arkanwesen, die von diesem Effekt betroffen sind, werden in Höhe von 5% ihrer maximalen Gesundheit geheilt."},
[47697] = {name = "Schattenwort: Tod", description = "[q]Verursacht 49 to 58 Schattenschaden bei einem Gegner, fügt dem Zaubernden aber den gleichen Schaden zu."},
[47698] = {name = "Kristallketten", description = "[q]Bildet eisige Ketten um den Feind, die Frostschaden verursachen und das Ziel 6 sec lang am Platz festhalten."},
[47699] = {name = "Kristallborke", description = "[q]Umgibt den Zaubernden mit einer Kristallborkenschicht, die 5 min lang bestehen bleibt. Die dicke Borke verursacht Arkanschaden und hat eine Chance, Angreifer zu betäuben."},
[47700] = {name = "Kristallfrost", description = "[q]Umgibt den Zaubernden mit einer Kristallborkenschicht, die 3 sec lang bestehen bleibt. Die dicke Borke verursacht Arkanschaden und hat eine Chance, Angreifer zu betäuben."},
[47721] = {name = "Feuerschlag", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[47723] = {name = "Versengen", description = "[q]Versengt den Feind und fügt ihm Feuerschaden zu."},
[47727] = {name = "Blizzard", description = "[q]Ruft einen Blizzard herbei, der 6 sec lang anhält und Feinden im ausgewählten Gebiet alle 2 Sek. 1000 Frostschaden zufügt."},
[47729] = {name = "Eisdorn", description = "[q]Fügt einem feindlichen Ziel 438 to 562 Frostschaden zu. Bei eingefrorenen Zielen wird dreifacher Schaden zugefügt."},
[47731] = {name = "Tier", description = "[q]Verwandelt einen Feind in ein Tier und zwingt ihn, 3 sec lang so herumzuwandern. Während das Tier herumwandert, kann es weder angreifen noch Zauber wirken."},
[47736] = {name = "Zeit anhalten", description = "[q]Betäubt 2 sec lang alle Feinde innerhalb von 50 Metern."},
[47737] = {name = "Geladener chaotischer Energieschlag", description = "[q]Schleudert einen chaotischen Energieschlag auf einen Gegner, der ihm 357 to 593 Arkanschaden zufügt und dann auf weitere Gegner in der Nähe überspringt. Jeder Sprung verringert den Schaden um 50%. Insgesamt werden 3 Ziele angegriffen."},
[47743] = {name = "Riss erschaffen", description = "[q]Zerreißt Raum und Zeit und öffnet einen chaotischen Riss nahe Anomalus."},
[47747] = {name = "Risse aufladen", description = "[q]Anomalus kanalisiert seine chaotische Energie in Risse in der Nähe, wodurch ihre Angriffsreichweite und die Geschwindigkeit erhöht werden, mit der sie chaotische Geister erzeugen."},
[47748] = {name = "Rissschild", description = "[q]Kanalisiert Energie des Zaubernden in chaotische Risse in der Nähe und lädt diese damit mit Energie auf. Der Zaubernde ist während der Kanalisierung immun gegen Schaden."},
[47750] = {name = "Sühne", description = "[q]Schleudert eine Salve heiligen Lichts, die einem feindlichen Ziel 811 Heiligschaden zufügt oder ein befreundetes Ziel sofort um 3274 heilt sowie 2 sec lang alle 1 Sek. um einen zusätzlichen Betrag."},
[47751] = {name = "Funke", description = "[q]Schleudert einen magischen Blitz auf einen Feind und verursacht Arkanschaden."},
[47769] = {name = "Selbstmord", description = nil},
[47772] = {name = "Eisnova", description = "[q]Verursacht bei allen Feinden innerhalb von 60 Metern 2357 to 2643 Frostschaden und betäubt sie 3 sec lang."},
[47773] = {name = "Feuerbombe", description = "[q]Verursacht Feuerschaden bei einem Feind und seinen nahen Verbündeten."},
[47774] = {name = "Raserei", description = "[q]Erhöht 2 min lang das Angriffstempo des Zaubernden um 15%. Stapelbar."},
[47777] = {name = "Brandschuss", description = "[q]Ein Schuss, der 959 to 1171 Feuerschaden verursacht sowie 12 sec lang alle 3 Sek. 238 zusätzlichen Feuerschaden."},
[47779] = {name = "Arkaner Strom", description = "[q]Bringt alle Gegner im Umkreis von 10 Metern 4 sec lang zum Schweigen."},
[47780] = {name = "Zauberbrecher", description = "[q]Verursacht 100% Waffenschaden und verwundet das Ziel, verringert außerdem die Wirksamkeit von Schadenszaubern 6 sec lang um 75%."},
[47782] = {name = "Verderbnis", description = "[q]Verdirbt in der Nähe befindliche Feinde und fügt ihnen 15 sec lang alle 3 Sek. Schattenschaden zu."},
[47784] = {name = "Feuerbrandfalle", description = "[q]Platziert eine Feuerfalle, die dem ersten sich nähernden Gegner 1 min lang 144 Feuerschaden zufügt. Die Falle besteht 1 min lang. Es kann immer nur jeweils eine Falle aktiv sein."},
[47789] = {name = "Arkanfalle", description = "[q]Platziert eine arkane Falle, die eine magische Schockwelle aussendet und den Gegnern in der Nähe 2313 to 2687 Arkanschaden zufügt. Die Falle besteht 1 min lang."},
[47791] = {name = "Arkane Hast", description = "[q]Erhöht 5 sec lang das Zaubertempo von nahen Freunden um 15% sowie den Zauberschaden um 120."},
[47849] = {name = "Fleischsäge", description = "[q]Entfesselt die Fleischsäge! Kleine Reichweite, maximales Gemetzel!"},
[47911] = {name = "EMP", description = "[q]Unterbricht den Amboss und somit Eisenthan Zornhammers Schutzschild. Kann nur nahe des Amboss' aktiviert werden. (10 Sek. Abklingzeit)"},
[47916] = {name = "Benzin", description = nil},
[47921] = {name = "Sägeklingen verschießen", description = "[q]Verschießt frontal vor dem Zaubernden Sägeklingen, verursacht bei bis zu 5 Zielen 7500 to 12500 Schaden."},
[47922] = {name = "Zornhammers Immunität", description = "[q]Vor jeglichem magischen und körperlichen Übel geschützt."},
[47938] = {name = "Klingendreh", description = "[q]Fügt einem einzelnen Ziel 4500 to 7500 Schaden zu."},
[47939] = {name = "Holz sammeln", description = "[q]Sammelt Holz von markierten Bäumen auf den Kaltwindanhöhen."},
[47958] = {name = "Kristallstachel", description = "[q]Beschwört eine Reihe Kristallstachel, die sich in alle Richtungen um den Zaubernden ausrichten. Nach kurzer Zeit werden sie aus dem Boden hervorschießen, bei allen nahen Feinden 3770 to 4230 Schaden verursachen und sie in die Luft stoßen."},
[47962] = {name = "Verletzten Soldaten retten", description = "[q]Greift nach einem verletzten Soldaten des Kriegshymnenklans und zieht ihn hinein."},
[47966] = {name = "Reparaturset für den Notfall", description = "[q]Ein Reparaturset für den Notfall, das 4500 to 5500 Gesundheit von Xinks Schredder wiederherstellt."},
[47968] = {name = "Soldat gerettet", description = nil},
[47969] = {name = "Aufmotzen!", description = "Der Belagerungspanzer der Horde kann dank des geretteten Belagerungsingenieurs schneller fahren!"},
[47975] = {name = "Bollwerk des Kriegsherrn", description = "Vom verletzten Krieger beschützt. Rüstung um 30% erhöht!"},
[47981] = {name = "Zauberreflexion", description = "[q]Zauber werden reflektiert. 4 Aufladungen."},
[48016] = {name = "Trampeln", description = "[q]Fügt nahen Gegnern 2357 to 2643 körperlichen Schaden zu."},
[48017] = {name = "Raserei", description = "[q]Erhöht den verursachten körperlichen Schaden des Zaubernden um 100%."},
[48053] = {name = "Fesseln", description = "[q]Verursacht bei einem Feind 20% Waffenschaden und verringert sein Bewegungstempo 5 sec lang um 60%."},
[48054] = {name = "Manabrand", description = "[q]Trifft einen Feind mit einem Antimanablitz. Für jeden vom Blitz verbrauchten Manapunkt erleidet das Ziel 0.5 Schaden."},
[48058] = {name = "Kristallblüte", description = "[q]Erhöht den zugefügten Schaden um 2%."},
[48082] = {name = "Samenknospe", description = "[q]Umschließt den Zaubernden mit einer Samenknospe, beschützt ihn vor jedem Schaden und erlaubt ihm so, seine Gesundheit zu regenerieren."},
[48094] = {name = "Intensive Kälte", description = "[q]Verursacht im Laufe der Zeit regelmäßig Schaden und verlangsamt das Angriffs- und Zaubertempo naher Gegner. Bewegung entfernt diesen Effekt."},
[48096] = {name = "Kristallfeueratem", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Frostfeuerschaden zu, verlangsamt ihr Bewegungstempo und verursacht Schaden über Zeit."},
[48098] = {name = "Mehrfachschuss", description = "[q]Feuert eine Salve von Geschossen auf einen Feind und seine nächsten Verbündeten ab, fügt normalen Schaden plus 2000 zu, wirkt auf bis zu 3 Ziele."},
[48105] = {name = "Schreckliche Wunde", description = "[q]Stürmt auf einen Gegner zu und verursacht 15 sec lang pro Sekunde 750 Schaden. Der Effekt endet, wenn das Ziel seine volle Gesundheit erreicht."},
[48130] = {name = "Aufspießen", description = "[q]Verursacht 6598 to 7402 Schaden und pro Sekunde zusätzlich 1000 Schaden. Hält 6 sec lang an."},
[48131] = {name = "Stampfen", description = "[q]Erschüttert den Boden, verursacht bei in der Nähe befindlichen Feinden normalen Schaden und stößt sie 2 sec lang zu Boden."},
[48132] = {name = "Säurespucke", description = "[q]Speit eine Säurekugel auf einen Feind, verursacht 2828 to 3172 Naturschaden und verringert seine Rüstung um 1000. Bis zu 50-mal stapelbar. Hält 20 sec lang an."},
[48133] = {name = "Giftatem", description = "[q]Verursacht 1 min lang alle 3 Sek. 500 Naturschaden bei einem Feind."},
[48136] = {name = "Säuretropfen", description = "[q]Beschwört 6 Jormungarwürmer, die bis zum Ende des Kampfes bestehen bleiben."},
[48137] = {name = "Tödliche Wunde", description = "[q]Verursacht 75% Waffenschaden beim Feind und verwundet ihn, verringert außerdem die Wirksamkeit jeglicher Heilung 20 sec lang um 5%."},
[48138] = {name = "Wutanfall", description = "[q]Erhöht das Angriffstempo 8 sec lang um 100%."},
[48139] = {name = "Wahnsinnig", description = "[q]Der Zaubernde verfällt in Raserei, sein Bewegungstempo wird um 100% erhöht und er greift 10 sec lang zufällige Ziele an."},
[48140] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 3 Ziele aus und verursacht bei jedem Naturschaden."},
[48144] = {name = "Erschreckendes Gebrüll", description = "[q]Lässt in der Nähe befindliche Feinde 5 sec lang voller Furcht flüchten."},
[48147] = {name = "Wahnsinnig", description = "[q]Der Zaubernde verfällt in Raserei, sein Bewegungstempo wird um 100% erhöht und er greift 10 sec lang zufällige Ziele an."},
[48168] = {name = "Inneres Feuer", description = "[q]Der Zaubernde wird von einem Schub heiliger Energie erfüllt, der seine Rüstung um 10% und seine Zaubermacht um 10% erhöht."},
[48179] = {name = "Kristallisieren", description = "[q]Verursacht Frostschaden bei in der Nähe befindlichen Feinden und macht sie bis zu 10 sec lang bewegungsunfähig."},
[48181] = {name = "Heimsuchung", description = "[q]Ihr entsendet eine verderbte Seele in das Ziel, die sowohl [((Spell power * 0.5577) * 1.25) + 1078 ] Schattenschaden verursacht als auch 12 sec lang den Schaden aller Eurer regelmäßigen Schatteneffekte auf dem Ziel um 20 % erhöht. Endet die Heimsuchung oder wird sie gebannt, kehrt die Seele zu Euch zurück, um Euch um 100 % des von ihr verursachten Schadens zu heilen."},
[48191] = {name = "Flinkschuss", description = "[q]Lässt den Zaubernden sich 8 sec lang fokussieren und in schneller Folge auf nahe Gegner schießen."},
[48193] = {name = "Wutanfall", description = "[q]Erhöht 10 sec lang das Angriffstempo des Zaubernden um 30% und den von ihm verursachten körperlichen Schaden um 10%."},
[48201] = {name = "Seuche verbreiten", description = nil},
[48208] = {name = "Bitterbräu", description = "[q]Fügt dem Ziel 425 to 475 Naturschaden zu."},
[48211] = {name = "Seuchenbombe", description = "[q]Schleudert eine Seuchenbombe in die Ferne."},
[48245] = {name = "Kopfhieb", description = "[q]Verursacht bei einem Ziel sofort 75% des normalen Waffenschadens und lässt es 8 sec lang alle 2 Sek. weitere 3% seiner maximalen Gesundheit als Schaden erleiden."},
[48246] = {name = "Flammenkugel", description = "[q]Verursacht bei einem zufälligen feindlichen Ziel Feuerschaden."},
[48249] = {name = "Braufestbräu werfen", description = "[q]Wirft dem Ziel ein Braufestbräu zu."},
[48250] = {name = "Riskante Finte", description = "[q]Verringert 5 sec lang die Chance eines Feindes, auszuweichen um 1%, erhöht jedoch den vom Zaubernden erlittenen Schaden um 15%."},
[48256] = {name = "Welkendes Gebrüll", description = "[q]Verursacht bei umstehenden Feinden 2000 Schaden und verringert ihre Gesundheit um 500. Bis zu 50-mal stapelbar. Hält 30 sec lang an."},
[48258] = {name = "Flammen rufen", description = "[q]Beschwört aus nahen Feuern einige Flammenstrahlen, die auf die Feinde des Zaubernden zielen."},
[48260] = {name = "Bogenzerkracher", description = "[q]Schlägt in einem Bogen wild um sich und fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Schaden zu."},
[48261] = {name = "Durchbohren", description = "[q]Wirft einen Speer auf das Ziel, verursacht 1885 to 2115 Schaden und alle 3 Sek. zusätzlich 2000 Schaden. Hält 9 sec lang an."},
[48262] = {name = "Rune von Asvior", description = "[q]Fügt einem Feind nach Ablauf von 5 sec 1800 Feuerschaden zu."},
[48264] = {name = "Rune von Ubbi", description = "[q]Heilt einen Verbündeten um 2500 Schaden nach 5 sec."},
[48267] = {name = "Ritualvorbereitung", description = "[q]Bereitet das Ziel zur Opferung an den Lichkönig vor."},
[48276] = {name = "Ritual des Schwertes", description = "[q]Führt das Ritual des Schwerts durch und opfert das Ziel dem Lichkönig."},
[48277] = {name = "Ritualstoß", description = "[q]Ein mächtiger Schlag, der beim Gegner 6650 to 7350 Schattenschaden verursacht und ihn verdirbt, wodurch im Verlauf von 8 sec zusätzlicher Schaden verursacht wird."},
[48278] = {name = "Paralysieren", description = "[q]Kanalisiert einen paralysierenden Strahl auf das Ziel, der jegliche seiner Aktionen verhindert."},
[48280] = {name = "Wirbelwind", description = "[q]Greift in der Nähe befindliche Feinde mit einem Wirbel aus Stahl an, der until cancelled lang anhält und doppelten Schaden verursacht."},
[48283] = {name = "Harpune werfen", description = nil},
[48286] = {name = "Schrecklicher Hieb", description = "[q]Ein hinterhältiger Angriff, der das Ziel stark bluten lässt, bis es vollständig geheilt wird. Fügt bis zur vollständigen Heilung alle 2 Sek. 200 Schaden zu."},
[48287] = {name = "Kraftvoller Biss", description = "[q]Verursacht 100% des normalen Schadens und verringert die Rüstung des Gegners 5 sec lang um 20%."},
[48288] = {name = "Kolbenzerkrachen", description = "[q]Fügt einem Feind und seinem nächsten Verbündeten 110% des normalen Nahkampfschadens zu und betäubt sie 2 sec lang."},
[48291] = {name = "Eitriges Verrotten", description = "[q]Belegt das Ziel mit 'Eitriges Verrotten', verursacht alle 3 Sek. 1414 to 1586 Naturschaden und verringert seine erhaltene Heilung um 25%. Hält 9 sec lang an."},
[48292] = {name = "Dunkler Streich", description = "[q]Schlägt das Ziel mit Dunkelheit, verursacht Schaden in Höhe von 50% der aktuellen Gesundheit des Ziels."},
[48294] = {name = "Dunkle Macht", description = "[q]Umgibt den Zaubernden mit einer Aura dunkler Macht, durch die alle nahen Feinden 1414 to 1586 Schattenschaden erleiden, wenn am Zaubernden direkt Schaden verursacht wird. Hält 5 sec lang an."},
[48297] = {name = "Zügel übergeben", description = "[q]Überreicht die Zügel des Schlachtrosses an Hänsel Bauer, den Stallmeister von Gallgrimm."},
[48325] = {name = "Runenschild", description = "[q]Schützt den Zaubernden 30 Sek. lang vor jeglichem Schaden."},
[48363] = {name = "Dorfbewohner retten", description = "[q]Entreißt einen hilflosen Dorfbewohner von Wintergarde den Klauen der hungrigen Toten!"},
[48374] = {name = "Stichwunde", description = "[q]Fügt einem Gegner 5 sec lang alle 1 Sek. 144 to 156 körperlichen Schaden zu."},
[48376] = {name = "Hammerschlag", description = "[q]Verursacht bei einem Feind 100% des normalen Waffenschadens und verringert 4 sec lang sein Bewegungstempo auf 65% des Normalwertes."},
[48380] = {name = "Geisterquelle", description = "[q]Verursacht bei umstehenden Feinden alle 1 Sek. 1414 to 1586 Schattenschaden."},
[48397] = {name = "Dorfbewohner absetzen", description = "[q]Setzt einen geretteten Dorfbewohner von Wintergarde an der Greifenstation in der Feste Wintergarde ab."},
[48400] = {name = "Frostgrab", description = "[q]Schließt das Ziel in einem Eisblock ein."},
[48416] = {name = "Runendetonation", description = "[q]Macht den Zaubernden für eine Weile bewegungsunfähig und fügt nach 4 sec in der Nähe befindlichen Feinden 1800 to 2200 Feuerschaden zu."},
[48423] = {name = "Geisterstoß", description = "[q]Verursacht 925 to 1075 Schaden und erhöht den erlittenen körperlichen Schaden um 250. Bis zu 100-mal stapelbar. Hält 10 sec lang an."},
[48529] = {name = "Geistexplosion", description = "[q]Fügt allen nahen Feinden 2357 to 2643 Schattenschaden zu."},
[48530] = {name = "Magischen Pfeil verschießen", description = "[q]Feuert einen magischen Pfeil auf das Ziel und verursacht 900 to 1500 Schaden."},
[48548] = {name = "Kurzschluss", description = "[q]Schleudert einen Blitzschlag auf den Feind, der 728 to 844 Naturschaden verursacht und zu in der Nähe befindlichen Feinden weiterspringt."},
[48558] = {name = "Rückschlag", description = "[q]Blendet nahe Feinde und lässt sie bis zu 10 sec lang desorientiert umherlaufen. Jeglicher erlittene Schaden wird den Effekt aufheben."},
[48562] = {name = "Prankenhieb", description = "[q]Schlägt in der Nähe befindliche Feinde und verursacht körperlichen Schaden."},
[48583] = {name = "Steinschlag", description = "[q]Verursacht 100% des normalen Waffenschadens und stößt den Feind zurück."},
[48585] = {name = "Welken", description = "[q]Schwächt das Fleisch des Ziels, verringert alle Attribute um 2%. Bis zu 50-mal stapelbar. Hält 10 sec lang an."},
[48594] = {name = "Sprinten", description = "[q]Erhöht das Bewegungstempo des Zaubernden 15 sec lang um 50%. Hebt Verstohlenheit nicht auf."},
[48599] = {name = "Rune der Bindung", description = "[q]Markiert einen Feind 5 sec lang mit einer schattigen Rune, welche ihn nach Ablauf von 5 bewegungsunfähig macht."},
[48604] = {name = "Überdrehen", description = "[q]Erhöht das Bewegungstempo des Schredders 5 sec lang um 300%."},
[48610] = {name = "Schredderauslieferung", description = "[q]Gibt den eingesammelten Schredder in Eurem Basislager ab."},
[48616] = {name = "Obsidianessenz", description = "[q]Erhöht 20 sec lang den verursachten Schaden aller Verbündeten im Umkreis von 30 Metern um 10%."},
[48619] = {name = "Flammenatem", description = "[q]Fügt Than Torvald Eriksson 2250 to 2750 Feuerschaden zu."},
[48620] = {name = "Flügelstoß", description = "[q]Betäubt Than Torvald Eriksson 5 Sekunden lang und stößt ihn zu Boden."},
[48639] = {name = "Kniesehne", description = "[q]Verursacht 50% Waffenschaden am Feind und verlangsamt sein Bewegungstempo 5 sec lang um 60%."},
[48640] = {name = "Stoß", description = "[q]Schlägt einen Feind und fügt Waffenschaden plus 150 zu."},
[48694] = {name = "Warnung", description = "[q]Flammenbringer versucht, seinen Reiter abzuwerfen. Kehrt nach Voldrune zurück, oder riskiert die Kontrolle zu verlieren."},
[48696] = {name = "Einschüchterndes Gebrüll", description = "[q]Brüllt laut einen Feind an, lähmt ihn 6 sec lang mit Schrecken und lässt alle anderen in der Nähe befindlichen Feinde voller Furcht flüchten."},
[48697] = {name = "Mächtiges Zuschlagen", description = "[q]Fügt einem Gegner 200% des normalen Schadens zu und stößt ihn zurück."},
[48698] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm Naturschaden zu."},
[48699] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 3 Ziele aus und verursacht bei jedem folgenden Ziel immer größeren Naturschaden."},
[48700] = {name = "Welle der Heilung", description = "[q]Ruft Naturmagie herbei, um einen Verbündeten zu heilen."},
[48702] = {name = "Inbrunst", description = "[q]Erhöht das Bewegungstempo des Zaubernden um 100% und seinen Schaden um 100%. Solange der Zaubernde unter dem Effekt der Inbrunst steht, kann er nicht verlangsamt oder aufgehalten werden. Hält 10 sec lang an. Wenn dieser Effekt zu Ende geht, wird der Zaubernde 4 sec lang betäubt."},
[48703] = {name = "Inbrunst", description = "[q]Erhöht das Bewegungstempo des Zaubernden um 100% und seinen Schaden um 100%. Solange der Zaubernde unter dem Effekt der Inbrunst steht, kann er nicht verlangsamt oder aufgehalten werden. Hält 10 sec lang an. Wenn dieser Effekt zu Ende geht, wird der Zaubernde 4 sec lang betäubt."},
[48733] = {name = "Zerstörerischer Puls", description = "[q]Verursacht bei einem Feind 30 sec lang alle 5 Sek. 600 to 1400 Feuerschaden."},
[48734] = {name = "Zerstörerisches Sperrfeuer", description = "[q]Verursacht bei nahen Feinden 4200 to 9800 Feuerschaden."},
[48766] = {name = "Frostatem", description = "[q]Verursacht 2397 to 2603 Frostschaden. Nur nützlich gegen Greifenreiter des Ansturms."},
[48768] = {name = "Galoppieren", description = "[q]Erhöht das Bewegungstempo 2 sec lang um 60%."},
