[50105] = {name = "Reißender Hurrikan", description = "[q]Erzeugt einen wütenden Sturm im Zielgebiet, der allen in der Nähe befindlichen Feinden alle 3 Sek. 1665 to 1935 Naturschaden zufügt und die Zeit zwischen Angriffen betroffener Feinde um 67% erhöht. Hält 20 sec lang an."},
[50106] = {name = "Krankheitswolke", description = "[q]Eine faulige Krankheitswolke umgibt den Zaubernden, die nahen Gegnern Naturschaden zufügt."},
[50131] = {name = "Magie ziehen", description = "[q]Entzieht Gegnern in der Nähe Magie, erhöht den verursachten Schaden um 50% und verringert den verursachten Schaden und die Heilung durch Gegner in der Nähe um 225."},
[50155] = {name = "Schwanzfeger", description = "[q]Fügt Feinden in einem kegelförmigen Bereich hinter dem Zaubernden 600 to 1000 Schaden zu und stößt sie zurück."},
[50161] = {name = "Schutzsphäre", description = "[q]Umgibt den Zaubernden mit einer schützenden Sphäre, die ihn vor allem Schaden schützt, so lang sie aufrecht erhalten wird."},
[50163] = {name = "Schlächter", description = "[q]Wenn Ihr einen Feind tötet, der Erfahrung oder Ehre gewährt, erzeugt Ihr Runenmacht. Zudem erzeugt Ihr pro Sekunde zusätzliche Runenmacht."},
[50169] = {name = "Schwimmflossenklatscher", description = "[q]Ein heftiger Angriff, der den Nahkampfschaden um 120 erhöht."},
[50182] = {name = "Aura der arkanen Hast", description = "[q]Erhöht 1 min lang das Zaubertempo des Zaubernden um 15% sowie den Zauberschaden um 150."},
[50188] = {name = "Wildes Dreschen", description = "[q]Drescht wild. Trefferchance um 50% verringert."},
[50195] = {name = "Levitieren", description = "[q]Ermöglicht dem Zaubernden zu levitieren und damit wenige Schritte über dem Boden zu fliegen. Beim Levitieren fallt Ihr mit reduziertem Tempo und könnt wasserähnliche Oberflächen überwinden."},
[50196] = {name = "Verwesende Berührung", description = "[q]Verwest einen Feind bei Kontakt, verursacht 10 sec lang alle 1 Sek. 88 to 112 Schaden."},
[50198] = {name = "Magischer Brand", description = "[q]Verbrennt einen Gegner, verursacht beim Einschlag 950 to 1050 Arkanschaden und 21 sec lang alle 3 Sek. 285 to 315 Arkanschaden."},
[50204] = {name = "Stahlkiefer", description = "[q]Erhöht die Rüstung 10 sec lang um 1500%."},
[50206] = {name = "Siedender Dampf", description = "[q]Überzieht das Ziel mit 210 to 270 Feuerschaden und steckt sein Fleisch in Brand, verursacht so zusätzliche 12 sec lang alle 3 Sek. 120 Feuerschaden."},
[50207] = {name = "Kochendes Blut", description = "[q]Verursacht 219 to 281 Feuerschaden.\n Erhöht den erlittenen Schaden des Ziels um 20% und seinen verursachten Schaden um 10%."},
[50215] = {name = "Zephyr", description = "[q]Ihr wurdet von einem Zephyr direkt aus dem Sturm eingehüllt.\n Bewegungstempo um 50% erhöht."},
[50228] = {name = "Wirbelwind", description = "[q]Verursacht bei nahen Feinden pro Sekunde 4713 to 5287 Schaden, das Bewegungstempo ist jedoch um 50% verringert."},
[50232] = {name = "Sengende Rache", description = "[q]Speit einen Feuerschwall auf einen feindlichen Drachen, verursacht 6800 to 9200 Feuerschaden. Der Schwall springt auf weitere Drachen im Umkreis von 30 Metern über. Jeder Sprung erhöht den verursachten Schaden um 50%. Betrifft bis zu 5 Ziele insgesamt."},
[50234] = {name = "Zermalmen", description = "[q]Verursacht beim Ziel 150% Nahkampfschaden und stößt ihn 2 sec lang zu Boden."},
[50240] = {name = "Ausweichmanöver", description = "[q]Ermöglicht es Eurem Drachen, allen eintreffenden Angriffen und Zaubern auszuweichen. Benötigt Ausweichladungen. Jedes Ausweichen eines Angriffs oder Zaubers, während diese Fähigkeit aktiv ist, verbrennt eine Ausweichladung. Hält 30 sec lang an, oder bis alle Ladungen verbraucht sind."},
[50241] = {name = "Ausweichladungen", description = "[q]Ausweichladungen werden erzeugt, wenn der Rubindrache von einem gegnerischen Angriff oder Zauber getroffen wird."},
[50253] = {name = "Märtyrertum", description = "[q]Lenkt 10 sec lang alle auf freundliche Drachen gewirkte schädliche Zauber auf sich selbst um."},
[50255] = {name = "Vergifteter Speer", description = "[q]Verursacht 4713 to 5287 körperlichen Schaden und 12 sec lang alle 3 Sek. 1885 to 2115 zusätzlichen Naturschaden."},
[50257] = {name = "Kochender Schlag", description = "[q]Trifft das Ziel mit siedendem Wasser. Verursacht 744 to 956 Feuerschaden."},
[50262] = {name = "Stürmischer Angriff", description = "[q]Erhöht das Bewegungstempo des Zaubernden 10 sec lang um 60% und lässt ihn bei seinen ersten drei Angriffen zusätzlich 400 Schaden zufügen."},
[50267] = {name = "Mmmrrrggglll demoralisieren", description = "[q]Verringert 10 sec lang die Nahkampfangriffskraft von Gegnern in der Nähe um 250.\n Verursachter Magieschaden wird 10 sec lang um 146 verringert."},
[50269] = {name = "Abfallöl", description = "[q]Erhöht die Chance Eures Ziels zu verfehlen um 10% und verringert sein Bewegungstempo um 10%."},
[50272] = {name = "Instabile Magie", description = "[q]Erhöht allen von nahen Feinden verursachten Zauberschaden um bis zu 150.\n Erhöht allen von nahen Feinden erlittenen Zauberschaden um bis zu 300. Hält 2 min lang an."},
[50273] = {name = "Arkanbeschuss", description = "[q]Schleudert mehrere Geschosse auf das feindliche Ziel und verursacht Arkanschaden."},
[50275] = {name = "Stabilisierte Magie", description = "[q]Erhöht allen von nahen Feinden verursachten Zauberschaden um bis zu 150."},
[50276] = {name = "Krug werfen", description = "[q]Fügt einem Gegner körperlichen Schaden zu."},
[50280] = {name = "Ölfilm", description = "[q]Eine dicke Ölschicht bedeckt die Kreatur, welche die Chance, dass feindliche Angriffe und Zauber verfehlen, um 20% erhöht."},
[50283] = {name = "Blendender Schwarm", description = "[q]Ein Insektenschwarm, der bei Zielen, die in seinem Bereich stehen, 263 to 337 Naturschaden verursacht.\n Trefferchance im Einflussbereich um 75% verringert."},
[50293] = {name = "Ätzgift", description = "[q]Verringert die Rüstung eines Feindes 12 sec lang um 900 und fügt alle 3 Sek. 100 Naturschaden zu."},
[50302] = {name = "Scherbenregen", description = "[q]Lässt in es einem kegelförmigen Bereich vor dem Zaubernden kristalline Scherben auf Feinde regnen, verursacht Arkanschaden."},
[50303] = {name = "Schweinepest", description = "[q]Von der Schweinepest infiziert. Angriffe haben eine Chance, einen Ausbruch auszulösen."},
[50306] = {name = "Schlagtritt", description = "[q]Wirbelnde Tritte, von denen jeder körperlichen Schaden verursacht."},
[50312] = {name = "Unheilige Raserei", description = "[q]Erfüllt einen Diener mit unheiligem Furor."},
[50313] = {name = "Auftauchen der Maulwurfmaschine", description = nil},
[50319] = {name = "Seelenschinden", description = nil},
[50323] = {name = "Geschärfter Knochen", description = "[q]Ein spitzer Knochen, der 438 to 562 körperlichen Schaden verursacht."},
[50324] = {name = "Knochenrüstung", description = "[q]Umgibt den Zaubernden mit einer Knochenrüstung, die 1 min lang 1203 to 1397 Schaden absorbiert."},
[50328] = {name = "Entziehendes Gift", description = "[q]Vergiftet einen feindlichen Drachen und überträgt 12 sec lang alle 2 Sek. 1300 Gesundheit auf den Zaubernden. Bis zu 3-mal stapelbar."},
[50332] = {name = "Fluch des Narren", description = "[q]Fluch wird wütend. 10 sec lang verursachter körperlicher Schaden um 350 erhöht, erlittener Schaden um 25% verringert."},
[50335] = {name = "Geißelhaken", description = "[q]Verursacht körperlichen Schaden und zieht das Ziel zum Zaubernden."},
[50341] = {name = "Alptraumberührung", description = "[q]Zehrt 20000 der maximalen Gesundheit des Zaubernden auf, um einem feindlichen Drachen 25000 Naturschaden zuzufügen und 30 sec lang seinen verursachten Schaden um 25% zu verringern."},
[50344] = {name = "Traumkanalisierung", description = "[q]Solang der Zaubernde kanalisiert, werden 10 sec lang pro Sekunde 5% der maximalen Gesundheit des Zaubernden auf einen freundlichen Drachen übertragen."},
[50347] = {name = "Rausch", description = "[q]Erhöht den Schaden des nächsten Nahkampfangriffs des Zaubernden um 200%."},
[50348] = {name = "Flammenfuror", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden pro Sekunde 4375 to 5625 Feuerschaden zu."},
[50349] = {name = "Eisige Berührung", description = "[q]Verursacht 662 to 738 Frostschaden und verringert 8 sec lang das Angriffs-, Distanzangriffs- und Zaubertempo des Ziels um 15%."},
[50356] = {name = "Seuche injizieren", description = "[q]Fügt einem Feind 8 sec lang alle 2 Sek. 324 to 376 Schattenschaden zu."},
[50361] = {name = "Rüstungsverfall", description = "[q]Verringert die Rüstung eines Feindes 10 sec lang um 25%."},
[50363] = {name = "Felsschild", description = "Erlittener Schaden um 50% verringert."},
[50364] = {name = "Felsschild", description = "Erlittener Schaden um 1% verringert."},
[50366] = {name = "Seuchenwolke", description = "[q]Eine faulige Seuchenwolke umgibt den Zaubernden, die nahen Gegnern Schattenschaden zufügt."},
[50370] = {name = "Rüstung zerreißen", description = "[q]Hackt nach der Rüstung des Feindes und verringert sie mit jedem Einsatz von 'Rüstung zerreißen'. Kann bis zu 5-mal angewendet werden. Hält 15 sec lang an."},
[50375] = {name = "Dampfschlag", description = "[q]Verursacht bei einem Gegner 875 to 1125 Feuerschaden und stößt ihn zurück."},
[50378] = {name = "Frostblitz", description = "[q]Fügt einem Feind Frostschaden zu und verringert 4 sec lang sein Bewegungstempo."},
[50379] = {name = "Verkrüppeln", description = "[q]Erhöht die Zeit zwischen Angriffen eines Feindes um 100%, verringert sein Bewegungstempo um 50% und zusätzlich seine Stärke um 50%. Hält 10 sec lang an."},
[50380] = {name = "Blutsporendunst", description = "[q]Die Blutsporenschwaden verursachen eine Euphorie. Das Tempo wird 1 min lang um 5% erhöht.\n Verursacht 324 to 376 Naturschaden.\n Bis zu 5-mal stapelbar."},
[50402] = {name = "Rösten", description = "[q]Verbrennt sofort einen Feind und fügt ihm dann 12 sec lang alle 3 Sek. zusätzlichen Feuerschaden zu."},
[50403] = {name = "Knochen werfen", description = "[q]Wirft einen Knochen auf einen Gegner und verursacht 525 to 675 Schaden."},
[50405] = {name = "Geschmiedeter Donner", description = "[q]Verursacht 2625 to 3375 Naturschaden bei einem Gegner."},
[50410] = {name = "Hauerstoß", description = "[q]Fügt einem Gegner 150 normalen Schaden zu und stößt ihn zurück."},
[50413] = {name = "Magnataurenansturm", description = "[q]Erhöht das Bewegungstempo des Zaubernden 10 sec lang um 60%, und lässt ihn bei seinen ersten drei Angriffen zusätzlich 350 Schaden zufügen."},
[50414] = {name = "Blutfluch", description = "[q]Erhöht den erlittenen körperlichen Schaden von in der Nähe befindlichen Feinden 8 sec lang um 500."},
[50416] = {name = "Frostbeißerwaffe", description = "[q]Verzaubert Eure Waffe mit 'Frostbeißer', der 95 to 105 Frostschaden verursacht und das Bewegungstempo des Ziels 3 sec lang um 30% verringert, wenn Ihr Nahkampfschaden verursacht. Hält 8 sec lang an."},
[50420] = {name = "Wutanfall", description = "[q]Erhöht 5 min lang das Angriffstempo des Zaubernden um 30% und den von ihm verursachten körperlichen Schaden um 10%."},
[50430] = {name = "Ghul verschlingen", description = "[q]Verschlingt einen Ödnisplünderer im Ganzen! Euer Drache muss fressen, um seine Gesundheit und sein Mana aufzufüllen."},
[50456] = {name = "Thiassis Sturmblitz", description = "[q]Verursacht 1050 to 1350 Naturschaden bei einem Gegner."},
[50461] = {name = "Antimagisches Feld", description = "[q]Platziert ein großes, ortsgebundenes, antimagisches Feld, das den erlittenen Zauberschaden aller darin befindlichen Gruppen- oder Schlachtzugsmitglieder um 75% verringert. Das antimagische Feld hält 10 sec lang an oder bis es (10000 + 2 * Attack power) Zauberschaden absorbiert hat."},
[50476] = {name = "Menagerie beschwören", description = "[q]Beschwört eine Anzahl Kreaturen und teleportiert den Zaubernden in Sicherheit."},
[50494] = {name = "Tuch des Blitzschlags", description = "[q]Macht den Zaubernden immun gegen alle Arten von Schaden."},
[50497] = {name = "Schrei des Chaos", description = "[q]Versetzt 10 sec lang alle nahen Gegner in Furcht."},
[50501] = {name = "Fleischernte", description = "[q]Reißt Fleisch von den Knochen."},
[50502] = {name = "Dickes Fell", description = "[q]Erhöht die Rüstung 2 min lang um 1000."},
[50504] = {name = "Arkaner Schock", description = "[q]Verursacht 350 Arkanschaden und unterbricht 2 sec lang das Zauberwirken."},
[50505] = {name = "Frostatem", description = "[q]Verursacht in einem kegelförmigen Bereich von 20 Metern vor dem Zaubernden 438 to 562 Frostschaden, zusätzlich wird das Angriffstempo der Ziele 6 sec lang um 100% verringert."},
[50506] = {name = "Mal der Detonation", description = "[q]Umgibt den Zaubernden mit arkaner Energie. Durch jeglichen erlittenen Schaden explodiert die Energie vom Zaubernden aus und verursacht bei allen Feinden innerhalb von 10 Metern 943 to 1057 Arkanschaden. Hält 2 sec lang an."},
[50512] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[50517] = {name = "Schreckenskorsar", description = "[q]Macht ein Ziel zum Ehrenmitglied der Mannschaft von Schreckenskapitänin DeMeza."},
[50520] = {name = "Tiefenbaggerer", description = "[q]Greift in der Nähe befindliche Feinde mit einem Wirbelangriff an, der 2 sec lang anhält und normalen Schaden plus 110 verursacht."},
[50522] = {name = "Gorlocstampfen", description = "[q]Verursacht 71 to 79 Naturschaden bei in der Nähe befindlichen Feinden, erhöht die Zeit zwischen ihren Angriffen um 33% und verlangsamt ihr Bewegungstempo um 40%. Hält 10 sec lang an."},
[50523] = {name = "Paralysierender Schleim", description = "[q]Verursacht alle 1 Sek. 111 to 129 Naturschaden und verringert das Zauber- und Angriffstempo um 25%."},
[50533] = {name = "Drehattacke", description = "[q]Eine Schwanzattacke, die bei Feinden Waffenschaden plus 150 verursacht."},
[50535] = {name = "Machtübertragung", description = "[q]Entzieht dem Ziel pro Sek. Kraft, verringert den durch das Ziel erlittenen Schaden um 4% und erhöht Euren verursachten Schaden um 4%. Dieser Effekt ist bis zu 20-mal stapelbar. Hält 20 sec lang an."},
[50538] = {name = "Rülpser", description = "[q]Ein Rülpser, der 417 to 483 Naturschaden verursacht."},
[50542] = {name = "Giftwolke", description = "[q]Fügt 10 sec lang in der Nähe befindlichen Feinden alle 2 Sek. 140 Naturschaden zu."},
[50550] = {name = "Fallschirm", description = "[q]Ein tragbarer Fallschirm. Öffnet sich automatisch, wenn der Nutzer fällt."},
[50573] = {name = "Arkanes Spalten", description = "[q]Verursacht bei einem Ziel und seinen nahen Verbündeten Arkanschaden gleichwertig zu 75% des normalen Nahkampfschadens, betrifft bis zu 3 Ziele."},
[50578] = {name = "Eissplitter", description = "[q]Verursacht 48 to 52 Frostschaden bei einem Feind."},
[50588] = {name = "Eisbrocken", description = "[q]Schleudert einen Brocken auf einen Feind und fügt körperlichen Schaden zu."},
[50597] = {name = "Eisstalagmit", description = "[q]Verursacht 463 to 537 Frostschaden und betäubt einen Feind, sodass er sich 3 sec lang weder bewegen, noch angreifen kann."},
[50636] = {name = "Gequältes Brüllen", description = "[q]Das wahnsinnige Gebrüll erhöht 8 sec lang den vom Zaubernden verursachten Schaden um 25%."},
[50642] = {name = "Kannibalismus", description = "[q]Regeneriert Gesundheit. Hält 4 sec lang an."},
[50648] = {name = "Blinzeln", description = "[q]Teleportiert den Zaubernden 15 Meter nach vorn, es sei denn, etwas steht im Weg. Befreit den Zaubernden auch von jeglichen Fesseln."},
[50652] = {name = "Rammen", description = "[q]Rammt alle Feinde, die sich vor dem Verwüster befinden, verursacht 3000 Schaden und stößt sie zurück. Fügt nahen Strukturen zudem 1500 Belagerungsschaden zu."},
[50653] = {name = "Flammenatem", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden 1700 to 2300 Feuerschaden zu."},
[50655] = {name = "Frostschnitt", description = "[q]Angriffe verursachen einen Frostschnitt, der 2 sec lang das Angriffs- und Zaubertempo um 5%, das Bewegungstempo um 5% verringert.\n Bis zu 50-mal stapelbar."},
[50658] = {name = "Fokusstrahl", description = "[q]Verursacht pro Sekunde 100 Arkanschaden.\n Erneuert pro Sekunde den Fokuseffekt."},
[50659] = {name = "Konversionsstrahl", description = "[q]Fügt einem Feind alle 2 Sek. 185 to 215 Schattenschaden zu und verursacht 'Schwindende Entschlossenheit'."},
[50661] = {name = "Schwindende Entschlossenheit", description = "[q]Erhöht den von einem Gegner erlittenen Schattenschaden 8 sec lang um 10%.\n Bis zu 10-mal stapelbar."},
[50665] = {name = "Ausbluten", description = "[q]Wenn er zu viel Blut verloren hat, wird der Patient sterben."},
[50666] = {name = "Jonathan stirbt", description = "[q]Hat Kreuzfahrer Jonathan zu viel Blut verloren, wird er sterben."},
[50668] = {name = "Todesmantel", description = "[q]Verursacht 12 to 14 Schattenschaden. Der Zaubernde erhält 100% des verursachten Schadens als Gesundheit."},
[50672] = {name = "Der Demoralisierer", description = "[q]Hat da jemand nach mehr verlangt? Die Geißeldiener auf der Ebene von Nasam können von diesem Projektil gar nicht genug bekommen. Alle Geißeldiener im Explosionsbereich erleiden 8640 to 10560 Schaden."},
[50677] = {name = "Fleischklopfer", description = "[q]Entfesselt den Fleischklopfer! Kleine Reichweite, maximales Gemetzel!"},
[50679] = {name = "Lamoof stirbt", description = "[q]Hat Kreuzfahrer Lamoof zu viel Blut verloren, wird er sterben."},
[50681] = {name = "Ausbluten", description = "[q]Wenn er zu viel Blut verloren hat, wird der Patient sterben."},
[50688] = {name = "Seuchenstoß", description = "[q]Ein bösartiger Schlag, der Waffenschaden verursacht, das Ziel verseucht und im Verlauf von 12 sec 568 Schattenschaden hervorruft."},
[50689] = {name = "Blutpräsenz", description = "[q]Stärkt den Todesritter mit der Präsenz des Blutes, erhöht seinen Schaden um 15% und heilt den Todesritter um 4% des verursachten Schadens. Es kann jeweils nur eine Präsenz aktiv sein."},
[50690] = {name = "Immobilisierungsfeld", description = "[q]Macht nahe Gegner 4 sec lang unbeweglich."},
[50693] = {name = "Josephine stirbt", description = "[q]Hat Kreuzfahrerin Josephine zu viel Blut verloren, wird sie sterben."},
[50695] = {name = "Ausbluten", description = "[q]Wenn sie zu viel Blut verloren hat, wird die Patientin sterben."},
[50701] = {name = "Mörtel werfen", description = "[q]Wirft Mörtel in die Augen eines Feindes, sodass dessen Trefferchance 6 sec lang um 50% verringert wird."},
[50702] = {name = "Arkane Salve", description = "[q]Schleudert magische Blitze auf nahe Feinde und verursacht Arkanschaden."},
[50705] = {name = "Arkanblitz", description = "[q]Schleudert einen magischen Blitz auf einen Feind und verursacht Arkanschaden."},
[50713] = {name = "Unerbittlicher Ansturm", description = "[q]Jeder Angriff verstärkt den Ansturm, erhöht 30 sec lang den verursachten körperlichen Schaden des Zaubernden um 10."},
[50715] = {name = "Blizzard", description = "[q]Ruft einen Blizzard herbei, der 6 sec lang anhält, Feinden im ausgewählten Gebiet alle 2 Sek. 903 to 997 Frostschaden zufügt und ihr Bewegungstempo verringert."},
[50717] = {name = "Geladene Haut", description = "[q]Lädt die Haut des Zaubernden mit Energie auf, die bei nahen Feinden alle 3 Sek. 760 to 840 Arkanschaden verursacht. Hält 10 min lang an."},
[50721] = {name = "Frostblitz", description = "[q]Fügt einem Feind Frostschaden zu."},
[50728] = {name = "Wutgeheul", description = "[q]Freunde in einem Umkreis von 15 Metern erhalten einen zusätzlichen Schadensbonus auf ihren nächsten körperlichen Angriff. Hält 10 sec lang an."},
[50729] = {name = "Biss des Fleischfressers", description = "[q]Fügt einem Feind 15 sec lang alle 3 Sek. körperlichen Schaden zu."},
[50730] = {name = "Kampfrausch", description = "[q]Erhöht Nahkampf-, Distanzangriffs- und Zaubertempo aller Gruppenmitglieder 20 sec lang um 35%."},
[50731] = {name = "Kolbenzerkrachen", description = "[q]Verursacht 110% des normalen Nahkampfschadens und betäubt das Ziel 2 sec lang."},
[50732] = {name = "Wassergrab", description = "[q]Schließt das Ziel 6 sec lang in einem Wassergrab ein, fügt ihm damit alle 2 Sek. 900 Frostschaden zu und verringert sein Bewegungstempo um 30%."},
[50739] = {name = "Bedrohliches Knurren", description = "[q]Verringert 30 sec lang die Stärke eines Feindes um 10% und seine Beweglichkeit um 10%."},
[50740] = {name = "Rabenschwarm", description = "[q]Verursacht 740 to 860 Schattenschaden."},
[50743] = {name = "Unheilige Raserei", description = "[q]Erhöht das Angriffstempo eines Verbündeten 15 sec lang um 35%."},
[50744] = {name = "Lohe", description = "[q]Fügt einem Gegner 850 to 1150 Feuerschaden zu."},
[50750] = {name = "Rabenheilung", description = "[q]Die Macht des Raben heilt den Zaubernden 5 sec lang alle 1 Sek. um 400."},
[50752] = {name = "Sturm der Trauer", description = "[q]Erzeugt einen Schattensturm, der 20 sec lang vorhält. Alle Feinde im angewählten Gebiet werden mit 1665 to 1935 Schattenschaden eingedeckt und erleiden alle 1 Sek. 602 to 698 zusätzlichen Schaden."},
[50760] = {name = "Trauerschock", description = "[q]Schockt das Ziel sofort, verursacht 1750 to 2250 Schattenschaden und versetzt alle nahen feindlichen Ziele 6 sec lang in einen meditativen Zustand, der sie handlungsunfähig macht. Wird von Schaden unterbrochen."},
[50761] = {name = "Säule des Leids", description = "[q]Deckt einen Feind mit Schatten ein und verursacht im Verlauf von 10 sec alle 2 Sek. zusätzlichen Schattenschaden."},
[50774] = {name = "Donnerndes Stampfen", description = "[q]Erschüttert den Boden, verursacht bei Feinden in einem großen Umkreis körperlichen Schaden und schleudert sie in die Luft."},
[50785] = {name = "Kerne energetisieren", description = "[q]Schleudert einen magischen Blitz auf einen Feind und verursacht Arkanschaden."},
[50795] = {name = "Tor von Zul'Drak", description = nil},
[50796] = {name = "Chaosblitz", description = "[q]Schleudert einen Blitz chaotischen Feuers auf den Feind und verursacht 1741 Feuerschaden. Dem Chaosblitz kann nicht widerstanden werden und er durchschlägt alle Absorptionseffekte."},
[50804] = {name = "Arkanbeschuss", description = "[q]Verursacht bei einem Feind Arkanschaden."},
[50810] = {name = "Zertrümmern", description = "[q]Zerschmettert alle in Stein verwandelten Gegner und fügt Schaden basierend auf Abstand zu."},
[50829] = {name = "Schlachtross beschwören", description = "[q]Beschwört ein Kriegsross."},
[50830] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 3 Ziele aus und verursacht bei jedem folgenden Ziel immer größeren Naturschaden."},
[50831] = {name = "Blitzschlagschild", description = "[q]Umgibt den Zaubernden mit 10 Kugelblitzen, die eine Chance von 50% haben, Nahkampf- oder Distanzangreifer zu treffen und 1000 Schaden zu verursachen. Jedes Mal, wenn der Blitzschlagschild zuschlägt, löst sich ein Kugelblitz auf. Auf diese Weise erlischt der Schild nach 10 min oder nachdem er 10-mal zugeschlagen hat."},
[50832] = {name = "Fackel werfen", description = "[q]Wirft eine Fackel auf einen Gegner, verursacht 232 to 268 Feuerschaden sowie 30 sec lang alle 3 Sek. bleibenden Feuerschaden von 280."},
[50834] = {name = "Statische Aufladung", description = "[q]Belegt das Ziel mit einer statischen Aufladung, die regelmäßig bei seinen nahen Verbündeten und Feinden 1000 Naturschaden verursacht. Hält 10 sec lang an."},
