[50868] = {name = "Stampfen", description = "[q]Verursacht bei nahen Feinden 2375 to 2625 körperlichen Schaden."},
[50895] = {name = "Blitzverbindung", description = "[q]Fügt einem Gegner 5 sec lang Naturschaden zu."},
[50896] = {name = "Brocken schleudern", description = "[q]Schleudert einen flammenden Brocken, der Feinden bis zu 2999 to 3001 Schaden zufügt sowie an nahen Strukturen 900 to 1100 Belagerungsschaden verursacht."},
