[50921] = {name = "Tollkühner Ansturm", description = "[q]Jeder Angriff verstärkt den tollkühnen Ansturm und erhöht 12 sec lang den verursachten und erlittenen Schaden des Zauberers um 5%."},
[50933] = {name = "Raserei", description = "[q]Erhöht das Angriffstempo des Zaubernden um 30% und den verursachten körperlichen Schaden um 75. Hält 2 min lang an."},
