[50983] = {name = "Titanische Woge", description = "[q]Regeneriert 10 sec lang pro Sekunde 10% Mana. Während der Hervorrufung wird der erlittene Schaden um 10% erhöht."},
[50985] = {name = "Rekonstruktion", description = "[q]Stellt alle Gesundheit wieder her."},
[50989] = {name = "Flammenatem", description = "[q]Ziele in einem kegelförmigen Bereich vor dem Zaubernden erleiden im Verlauf von 3 sec 3000 Feuerschaden."},
