[50994] = {name = "Haut verfestigen", description = "[q]Erhöht die Rüstung um 3000 sowie den Magiewiderstand um 60. Hält 15 sec lang an."},
[50995] = {name = "Erhöhte Blutpräsenz", description = "[q]Stärkt den Todesritter und seine Gruppenmitglieder innerhalb von 30 Metern mit der Präsenz des Blutes, erhöht Zauberschaden und Heilung um 15%. Es kann jeweils nur eine Präsenz aktiv sein."},
[50997] = {name = "Kristallketten", description = "[q]Verursacht Frostschaden und macht das feindliche Ziel für bis zu 10 sec bewegungsunfähig."},
[51001] = {name = "Dunkle Materie", description = "[q]Eine Wolke dunkler Materie umhüllt das Ziel, erhöht den erlittenen Schaden um 50%, verringert das Bewegungstempo um 30% und verursacht bei nahen Feinden 1885 to 2115 Schattenschaden."},
[51002] = {name = "Azurfarbenen Ringhauptmann rufen", description = "[q]Ruft einen Azurfarbenen Ringhauptmann hinab, um Eure Feinde zu verheeren."},
[51009] = {name = "Seelenabwehr", description = "[q]Eine ungeheuerliche Kraft zerrt an Eurer Seele und reflektiert 3 sec lang 100% des von Euch verursachten Schadens auf Euch selbst."},
