[51074] = {name = "Schattensperrfeuer", description = "[q]Fügt nahen Gegnern Schattenschaden zu."},
[51103] = {name = "Frostbombe", description = "[q]Verschießt eine Frostbombe, die Frostschaden verursacht und den Boden einfriert. Dies verursacht regelmäßigen Schaden und verringert das Bewegungstempo von Gegnern im betroffenen Gebiet."},
[51110] = {name = "Mächtige arkane Explosion", description = "[q]Lässt eine Druckwelle aus Magie entstehen und fügt in der Nähe befindlichen Feinden Arkanschaden zu."},
