[51153] = {name = "Arkane Salve", description = "[q]Verursacht bei einem Feind Arkanschaden."},
[51162] = {name = "Planarverschiebung", description = "[q]Verschiebt den Zaubernden 18 sec lang auf die Astralebene. Während dieser Zeit ist er gegen Magieschaden immun, kann sich jedoch weder bewegen, noch angreifen."},
