[51222] = {name = "Taxi Path Flavor", description = nil},
[51240] = {name = "Furcht", description = "[q]Erfüllt einen Feind mit Furcht, woraufhin er bis zu 6 sec lang voller Schrecken flüchtet. Es kann immer nur jeweils ein einzelnes Ziel mit Furcht erfüllt werden."},
