[51678] = {name = "Rammen", description = "[q]Rammt alle Feinde, die sich vor dem Verwüster befinden, verursacht 3500 Schaden und stößt sie zurück. Verursacht zudem bei Gebäuden 2000 Belagerungsschaden."},
