[51732] = {name = "Arkane Infusion", description = "[q]Erfüllt das Ziel mit arkaner Energie. Nach 4 Sekunden erleidet das Ziel eine Überladung, die Gegnern in der Nähe alle 2 Sek. 220 Arkanschaden zufügt."},
[51740] = {name = "Brandfalleneffekt", description = "Alle 3 Sek. 713 to 787 Feuerschaden."},
[51742] = {name = "Arkaner Schuss", description = "[q]Ein sofortiger Schuss, der (Ranged attack power * 0.15 + 523) Arkanschaden verursacht und 1 Magieeffekt bannt."},
