[51805] = {name = "Kristalliner Wuchs", description = "[q]Erhöht 15 sec lang den verursachten körperlichen Schaden des Ziels um 25%."},
[51817] = {name = "Taifun", description = "[q]Ihr beschwört einen wilden Taifun, der feindlichen Zielen bei Kontakt 602 to 698 Naturschaden zufügt und sie 5 Meter zurückstößt."},
[51818] = {name = "Schattenwort: Tod", description = "[q]Fügt einem Gegner 24 to 29 Schattenschaden zu, jedoch erleidet der Zaubernde den gleichen Schaden."},
[51819] = {name = "Potenter Schock", description = "[q]Erhöht das Angriffstempo des Zaubernden um 15% und den durch ihn verursachten körperlichen Schaden um 15%."},
[51820] = {name = "Arkane Explosion", description = "[q]Lässt eine Druckwelle aus Magie entstehen und fügt in der Nähe befindlichen Feinden Arkanschaden zu."},
[51832] = {name = "Kurzschluss", description = "[q]Setzt das Gebiet um den Zaubernden unter Strom. Dadurch werden allen Gegnern in der Nähe 4625 to 5375 Naturschaden zugefügt und der Zaubernde getötet."},
[51842] = {name = "Sturmangriff", description = "[q]Stürmt auf einen Gegner zu und verursacht 1850 to 2150 körperlichen Schaden."},
[51857] = {name = "Frostspalten", description = "[q]Fügt einem Gegner und seinen nächsten Verbündeten Waffenschaden plus 40 zu und verringert Angriffs- sowie Zaubertempo um 30%. Trifft bis zu 5 Ziele."},
[51859] = {name = "Lage einschätzen", description = "[q]Sammelt taktische Informationen über ein Gebäude des Scharlachroten Kreuzzugs und seine Umgebung."},
[51873] = {name = "Blutwurm", description = "[q]Erschafft einen Blutwurm, der nahe Ziele angreift. Der Zaubernde erhält Gesundheit, wenn der Blutwurm Schaden verursacht. Hält 8 sec lang an."},
[51875] = {name = "Zermalmen", description = "[q]Erhöht den Schaden des nächsten Angriffs um 5."},
[51876] = {name = "Sturmschlag", description = "[q]Greift sofort mit allen Waffen an. Zusätzlich wird der Schaden der nächsten 2 Naturschadensquellen um 20% erhöht. Hält 12 sec lang an."},
[51877] = {name = "Kalter Windstoss", description = "[q]Trifft einen Feind mit einem kräftigen Windstoß, der Naturschaden verursacht."},
[51878] = {name = "Eisschlitzer", description = "[q]Verursacht Frostschaden und verringert 10 sec lang das Bewegungstempo des Gegners um 50%."},
[51879] = {name = "Fleisch korrodieren", description = "[q]Korrodiert das Fleisch des Gegners und verursacht danach 15 sec lang alle 3 Sek. Naturschaden."},
[51897] = {name = "Kreischen der Banshee", description = "[q]Bringt Gegner in der Nähe zum Schweigen und entwaffnet sie. Dadurch werden sie 4 sec lang davon abgehalten, Zauber zu wirken."},
[51899] = {name = "Bansheefluch", description = "[q]Verringert die Chance eines Feindes zu treffen 10 sec lang um 40%."},
[51901] = {name = "Traumpeitscher", description = "[q]Verursacht 602 to 698 Naturschaden."},
[51904] = {name = "Ghule auf die Scharlachroten beschwören", description = "[q]Ruft Ghule herbei, um die Angreifer aufzuhalten. Dies gibt Euch Zeit, das Auge von Acherus in Sicherheit zu bringen."},
[51917] = {name = "Spalten", description = "[q]Fügt einem Feind und seinem nächsten Verbündeten 150% des normalen Nahkampfschadens zu."},
[51918] = {name = "Wiederbeleben", description = "[q]Belebt einen gefallenen Todesritter wieder."},
[51922] = {name = "Zerrissener Traum", description = "[q]Im Traum zu sein, erhöht das Angriffs- und Zaubertempo um 100%."},
[51926] = {name = "Korsarenkostüm", description = "[q]Zieht dem Ziel ein Korsarenkostüm an."},
[51937] = {name = "Krallenschlag", description = "[q]Verursacht bei einem Feind normalen Schaden plus zusätzlichen Schaden."},
[51938] = {name = "Flügelschlag", description = "[q]Trifft Gegner nahe dem Zaubernden, stößt sie zurück und verlangsamt ihr Bewegungstempo 8 sec lang um 50%."},
[51944] = {name = "Trampeln", description = "[q]Verursacht körperlichen Schaden bei in der Nähe befindlichen Feinden."},
[51946] = {name = "Ausweichmanöver", description = "[q]Erhöht 4 sec lang die Ausweichchance des Zaubernden um 75%."},
[51951] = {name = "Tollwut", description = "[q]Die Tollwut bewirkt, dass 15 sec lang alle 3 Sek. 210 Naturschaden verursacht wird. Im Gegenzug erhöht sie 15 sec lang die kritische Trefferchance um 30% und den kritischen Schaden um 30%."},
[51959] = {name = "Hühnernetz", description = "[q]Wirft ein dünnes Netz aus, um unkooperative entflohene Hühner in bis zu 20 Meter Entfernung einzufangen."},
[51963] = {name = "Gargoylestoß", description = "[q]Verursacht bei einem Feind Naturschaden."},
[51972] = {name = "Gelassenheit", description = "[q]Regeneriert bei allen in der Nähe befindlichen Gruppenmitgliedern 10 sec lang alle 2 Sek. 1388 to 1612 Gesundheit. Kanalisiert."},
[52006] = {name = "Verhüllen", description = "[q]Schützt das Auge von Acherus davor, entdeckt zu werden. Kampf und Aktionen unterbrechen die Verhüllung."},
[52011] = {name = "Erneuernder Strahl", description = "[q]Heilt 6 sec lang einen Verbündeten alle 2 Sek. um 4% Gesundheit."},
[52022] = {name = "Eisengestalt", description = "[q]Gewährt Immunität gegenüber den Effekten 'Blutung', 'Gift' und 'Krankheit', wenn aktiviert. Zusätzlich wird die Rüstung um 20% erhöht. Hält 8 sec lang an."},
[52026] = {name = "Zerschmettern", description = "[q]Zerschmettert den Gegner und fügt ihm 120% Waffenschaden zu."},
[52027] = {name = "Wirbelwind", description = "[q]Greift Gegner in der Nähe in einem Wirbelwind aus Stahl an, der 8 sec lang anhält und bei jeder Drehung 75% des normalen Schadens verursacht."},
[52029] = {name = "Wegstoßen", description = "[q]Fügt Gegnern in der Nähe 20% des normalen Schadens zu und stößt sie zurück."},
[52058] = {name = "Erde erschüttern", description = "[q]Erschüttert den Boden, fügt Gegnern in der Nähe 925 to 1075 körperlichen Schaden zu und schleudert sie nach oben."},
[52060] = {name = "Unsichtbarkeit", description = "[q]Macht den Zaubernden unsichtbar, bis der Effekt aufgehoben wird."},
[52067] = {name = "Aura der Regeneration", description = "[q]Solange sie wächst, regeneriert die Samenkapsel bei nahen Einheiten Gesundheit und Mana."},
[52071] = {name = "Mordende Wut", description = "[q]Erhöht das Angriffstempo 6 sec lang um 100%."},
[52080] = {name = "Knochen anbrechen", description = "[q]Stampft auf den Boden, was nahen Gegnern 200% normalen Schaden zufügt und ihren erlittenen Schaden 20 sec lang um 100% erhöht."},
[52086] = {name = "Fangnetz", description = "[q]Kanalisiert einen Netzstrahl, der das Ziel 3 sec lang bewegungsunfähig macht. Wenn der Effekt endet, ist das Ziel in Spinnennetze eingewoben, die es 10 sec lang betäuben. Wenn die Spinnennetze um das Ziel entfernt werden, wird das Ziel befreit."},
[52087] = {name = "Fangnetz", description = "[q]Kanalisiert einen Netzstrahl, der das Ziel 3 sec lang bewegungsunfähig macht. Wenn der Effekt endet, ist das Ziel in Spinnennetze eingewoben, die es 10 sec lang betäuben. Wenn die Spinnennetze um das Ziel entfernt werden, wird das Ziel befreit."},
[52088] = {name = "Magnataurenansturm", description = "[q]Stürmt auf den Gegner zu und verursacht 200% Waffenschaden."},
[52097] = {name = "Temporäre elektrische Ladung", description = "[q]Lädt freundliche Einheiten elektrisch auf, die dem Zaubernden näher als 25 Meter kommen. Erhöht dadurch ihre Größe und den verursachten Schaden um 30%."},
[52098] = {name = "Aufladung", description = "[q]Lädt das Ziel mit Elektrizität auf."},
[52101] = {name = "Brennende Fäuste", description = "[q]Die Fäuste werden von Flammen umzüngelt. Nahkampfangriffe fügen Gegnern in einem Radius von 5 Metern 292 to 328 Feuerschaden zu."},
[52117] = {name = "Pulsierende Splitter", description = "[q]Verursacht alle 5 Sek. 30 Frostschaden und verringert 15 sec lang das Angriffs- sowie Zaubertempo von nahen Gegnern.\n Bis zu 10-mal stapelbar."},
[52126] = {name = "Maden anstacheln", description = "[q]Stachelt Maden an, Ziele in der Nähe anzugreifen. Der Zaubernde gewinnt Gesundheit, wenn die Maden Schaden verursachen. Hält 7 sec lang an."},
[52139] = {name = "Gigantischer Schlag", description = "[q]Verursacht 200% des normalen Schadens und stößt den Feind zurück."},
[52164] = {name = "Scourge Invasion Blood Machine Beam, Vat", description = nil},
[52166] = {name = "Donner", description = "[q]Ihr ruft einen Blitzschlag hernieder, der bei allen Feinden im Umkreis von 10 Metern Schaden verursacht. Alle Feinde erleiden 1587 to 1813 Naturschaden und werden 181.3 Meter zurückgestoßen."},
[52167] = {name = "Magnataurenansturm", description = "[q]Stürmt auf den Gegner zu und verursacht 0% Waffenschaden."},
[52174] = {name = "Heldenhafter Sprung", description = "[q]Springt durch die Luft, schmettert auf alle Feinde im Umkreis von 8 Metern um das Zielgebiet hinab und fügt ihnen (1 + 0.5 * Attack power) Schaden zu."},
[52182] = {name = "Grab der Herzlosen", description = "[q]Schließt das Ziel in einem scheinbar undurchdringlichen Eisgrab ein. Der Zauber kann nur vom Zaubernden aufgehoben werden."},
[52185] = {name = "Bindungen der Unterwerfung", description = "[q]Verbindet den Zaubernden mit dem Ziel und schützt den Zaubernden so vor allem Schaden. Stirbt das Ziel, bricht der Effekt ab."},
[52206] = {name = "Eingesprungener Gesichtszermalmer", description = "[q]Sprungangriff ins Gesicht. Äußerst schmerzhaft!"},
[52207] = {name = "Humanoiden verschlingen", description = "[q]Alle 1.5 Sek. 263 to 337 Schaden."},
[52208] = {name = "Flammenfeld", description = "[q]Das Flammenfeld verursacht 8 sec lang alle 1 Sek. 200 Feuerschaden."},
[52219] = {name = "Der Atem von Har'koa", description = "[q]Verleiht dem Ziel die Fähigkeit, am Altar von Quetz'lun in die Unterwelt überzugehen."},
[52221] = {name = "Heldenhafter Stoß", description = "[q]Ein heftiger Angriff, der den Nahkampfschaden um 11 erhöht und ein hohes Maß an Bedrohung erzeugt."},
[52230] = {name = "Seucheninfiziert", description = "[q]Die Wirkung der Seuche verursacht 15 sec lang alle 3 Sek. 250 Naturschaden. Zusätzlich erhöht sie 15 sec lang die kritische Trefferchance um 30% und den kritischen Schaden um 30%."},
[52237] = {name = "Zerschmetterndes Stampfen", description = "[q]Verursacht bei allen Feinden 2313 to 2687 Schaden und zerschmettert alle brüchigen Golems im Umkreis von 99 Metern."},
[52238] = {name = "Schmieden", description = "[q]Schlägt Volkhans Amboss, erschafft eine Anzahl an geschmolzenen Golems."},
[52254] = {name = "Luft-Luft-Rakete", description = "[q]Auf fliegende Feinde abfeuern."},
[52257] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[52262] = {name = "In die Ecke gedrängt und wütend!", description = "[q]Erhöht das Angriffstempo des Zaubernden um 100% und den verursachten körperlichen Schaden um 200. Hält 2 min lang an."},
[52265] = {name = "Repo", description = "[q]Wo wollt Ihr mit dem Pferd hin?"},
[52268] = {name = "Galoppieren", description = "[q]Erhöht das Lauftempo 2 Sek. lang um 100%."},
[52270] = {name = "Mehrfachschuss", description = "[q]Feuert eine Salve von Geschossen auf einen Feind und seine nächsten Verbündeten ab, fügt normalen Schaden plus 75 zu, wirkt auf bis zu 3 Ziele."},
[52271] = {name = "Gewaltiges Krachen", description = "[q]Springt in die Luft, schmettert auf alle nahen Feinde nieder, verursacht massiven Schaden und betäubt sie 4 sec lang."},
[52272] = {name = "Brockenwurf", description = "[q]Wirft einen Brocken, verursacht hohen Schaden und unterbricht vom Ziel gewirkte Zauber."},
[52274] = {name = "Gorloc fressen", description = "[q]Verschlingt einen nahen Gorloc und regeneriert 10 Sekunden lang alle 2 Sek. 5% Gesundheit."},
[52281] = {name = "Flamme des Sehers", description = "[q]Ruft die Macht des Sehers herbei, um 2 min lang den Zauberschaden des Zaubernden um 400 zu erhöhen."},
[52282] = {name = "Feuerball", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[52283] = {name = "Brüllen des Kriegsherrn", description = "[q]Das Brüllen des Kriegsherrn verleiht Immunität gegen Verlangsamungs-, Gruppenkontroll- und Betäubungseffekte."},
[52290] = {name = "Elementarmeisterschaft", description = "[q]Die Aktivierung dieses Zaubers verleiht Eurem nächsten Schadenszauber einen Schadensbonus von 100% und verringert die Manakosten um 100%."},
[52307] = {name = "Hydrasputum", description = "[q]Verursacht 20 sec lang alle 3 Sek. 262 to 288 Naturschaden."},
[52309] = {name = "Kriegerwillen", description = "[q]Erhöht die Rüstung um 2000 und den verursachten Schaden um 15%. Hält 1 min lang an."},
[52311] = {name = "Wütender Ansturm", description = "[q]Stürmt nach vorn, verursacht massiven Schaden und betäubt alle nahen Feinde."},
[52316] = {name = "Kopfzerkracher", description = "[q]Greift mit einem gewaltigen Kopfstoß an, verursacht in einem kegelförmigen Bereich vor dem Zaubernden 145% der normalen Schadens."},
[52317] = {name = "Verteidigen", description = "[q]Verringert erlittenen Schaden 4 sec lang um 50%."},
[52321] = {name = "Beharrlichkeit der Götter", description = "[q]Der Zaubernde wird 20 Sekunden lang immun gegen Betäubungseffekte und alle Angriffe heilen ihn."},
[52331] = {name = "Weiter Prankenhieb", description = "[q]Fügt Feinden vor dem Zaubernden 1080 to 2520 Schaden zu."},
[52334] = {name = "Kruder Schlamm", description = "[q]Verursacht 602 to 698 Schattenschaden."},
[52338] = {name = "Brocken schleudern", description = "[q]Schleudert einen massiven Brocken in die Ferne. Der Brocken verursacht im Umkreis von 10 Metern Feuerschaden sowie 1000 Belagerungsschaden."},
[52340] = {name = "Har'koas Geist", description = "Erfüllt die Fragmente unterweltlicher Macht mit ihrer Seele."},
[52341] = {name = "Elektrische Überladung", description = "[q]Lässt eine Druckwelle aus Magie entstehen und fügt in der Nähe befindlichen Feinden Naturschaden zu."},
[52342] = {name = "Brodelndes Böses", description = "[q]Angriffe haben eine Chance, 324 to 376 Schattenschaden zu verursachen."},
[52352] = {name = "Fleisch verschlingen", description = "[q]Entzieht einem Gegner 1980 to 2420 Gesundheit sowie 5% Ausdauer und überträgt sie auf den Zaubernden."},
[52356] = {name = "Werfen", description = "[q]Wirft Eure Waffe und fügt dem Ziel 150% ihres Schadens zu."},
[52358] = {name = "Schnelle Pfoten", description = "[q]Erhöht das Bewegungstempo 15 sec lang um 20%."},
[52362] = {name = "Ruf des Reiters", description = "[q]Bringt das Todesstreitross zur Todesbresche zurück und ruft Salanar den Reiter. Kann nur in der Todesbresche genutzt werden."},
[52372] = {name = "Eisige Berührung", description = "[q]Verursacht 217 bis 235 Frostschaden (modifiziert durch Angriffskraft) und verringert 20 sec lang das Nahkampfangriffs-, Distanzangriffs- und Zaubertempo des Ziels um 15%."},
[52373] = {name = "Seuchenstoß", description = "[q]Ein bösartiger Schlag, der Waffenschaden plus 37 verursacht (modifiziert durch Angriffskraft) und das Ziel mit einer Krankheit ansteckt, die im Verlauf von 12 sec 292 Schattenschaden hervorruft."},
[52374] = {name = "Blutstoß", description = "[q]Schlägt den Feind sofort, verursacht 60% Waffenschaden plus für jede auf das Ziel wirkende Krankheit zusätzlich 55 Schaden."},
[52375] = {name = "Todesmantel", description = "[q]Feuert einen unheiligen Energieschlag, fügt einem gegnerischen Ziel 184 durch Angriffskraft modifizierten Schattenschaden zu oder heilt 276 Gesundheit einer nicht gegnerischen untoten Einheit."},
[52383] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 3 Ziele aus und verursacht bei jedem Naturschaden."},
[52387] = {name = "Hitze", description = "[q]Erhitzt einen geschmolzenen Golem und erhöht seine Lebensdauer um 20%."},
[52400] = {name = "Wilder Wutanfall", description = "[q]Erhöht das Angriffstempo des Zaubernden um 60% und den verursachten körperlichen Schaden um 50. Hält 2 min lang an."},
[52401] = {name = "Magenhieb", description = "[q]Schneidet das Ziel, verursacht 800 to 1200 Schaden und lässt es im Verlauf von 9 sec für 1500 zusätzlichen Schaden bluten."},
[52402] = {name = "Betäubende Macht", description = "[q]Betäubt einen nahen Feind 6 sec lang. Dem kann nicht ausgewichen werden."},
[52425] = {name = "Kopftrauma", description = "[q]Ein schwerer Schlag auf den Kopf eines Feindes verursacht unmittelbar 324 to 376 körperlichen Schaden und zusätzlich 5 sec lang alle 1 Sek. 139 to 161 körperlichen Schaden."},
[52429] = {name = "Zertrümmern", description = "[q]Zerschmettert den abgekühlten Golem, verursacht bei nahen feindlichen Zielen 4625 to 5375 Schaden."},
[52430] = {name = "Verzerrter Geist", description = "[q]Verringert 6 sec lang das Zaubertempo um 75%."},
[52433] = {name = "Brandstoß", description = "[q]Verbrennt den Feind und fügt 450 Feuerschaden sowie 9 sec lang insgesamt 1350 zusätzlichen Feuerschaden zu."},
[52435] = {name = "Scharlachrote Kanone", description = "[q]Geringe Treibstoffkosten machen diese Mittel- bis Langstreckenwaffe zu einem absoluten Muss im Feld. Zielt niedrig und lasst es krachen!"},
[52436] = {name = "Scharlachroter Kanonenangriff", description = "[q]KAWUMMM! Alle Feinde in einem Radius von 12 Metern um den Einschlag erleiden 13613 to 19387 Schaden."},
[52442] = {name = "Aura der Hingabe", description = "[q]Erhöht die Rüstung naher Freunde um 1200."},
[52444] = {name = "Heiliges Licht", description = "[q]Ruft heilige Magie herbei, um einen Verbündeten zu heilen."},
[52445] = {name = "Exorzismus", description = "[q]Verursacht bei einem untoten oder dämonischen Gegner 2375 to 2625 Heiligschaden."},
[52446] = {name = "Säurespritzer", description = "[q]Ätzendes Blut bricht aus dem Zaubernden hervor, verursacht bei nahen Verbündeten und Feinden pro Sekunde 200 Naturschaden. Hält 10 sec lang an."},
[52451] = {name = "Ghule beschwören", description = "[q]Beschwört nahe des Zaubernden zwei Ghuldiener."},
[52457] = {name = "Drak'aguuls Soldaten", description = "[q]Wartet auf Drak'aguuls Befehle."},
[52460] = {name = "Berserkeransturm", description = "[q]Attackiert einen Feind, stößt ihn zurück und fügt ihm normalen Schaden plus 150 zu."},
[52461] = {name = "Unaufhaltsamer Wutanfall", description = "[q]Erhöht das Angriffstempo des Zaubernden um 30% und den durch ihn verursachten körperlichen Schaden um 10%. Hält 8 sec lang an. Der Zaubernde kann nicht aufgehalten werden."},
[52466] = {name = "Drakkarifluch", description = "[q]Fügt einem Gegnern 10 sec lang alle 2 Sek. Schattenschaden zu."},
[52467] = {name = "Fleisch verschlingen", description = "[q]Entzieht einem Gegner 1980 to 2420 Gesundheit sowie 5% seiner Werte und überträgt sie auf den Zaubernden."},
[52468] = {name = "Prophezeiung des Blutes", description = "[q]Erhöht 10 sec lang den Schaden, den das Ziel erleidet, um 30 und den Schaden, den es verursacht, um 15."},
[52469] = {name = "Infizierter Biss", description = "[q]Beißt das Ziel und verursacht 1885 to 2115 Naturschaden. Zusätzlich erleidet das Ziel alle 3 Sek. 1000 Naturschaden und sein erlittener körperlicher Schaden wird um 20% erhöht. Hält 9 sec lang an."},
[52470] = {name = "Wutanfall", description = "[q]Erhöht 8 sec lang das Angriffstempo des Zaubernden um 300%, verringert jedoch seinen verursachten körperlichen Schaden um 50%."},
[52480] = {name = "Ghul explodieren", description = "[q]Lässt einen nahen Ghul explodieren, verursacht bei feindlichen Zielen innerhalb von 10 Metern 3800 to 4200 Feuerschaden."},
[52491] = {name = "Gespinstexplosion", description = "[q]Macht in der Nähe befindliche Feinde 4 sec lang bewegungsunfähig."},
[52493] = {name = "Giftschauer", description = "[q]Schießt Gift auf den Feind, fügt ihm Naturschaden und dann 10 sec lang alle 3 Sek. zusätzlichen Schaden zu."},
[52495] = {name = "Elementare Erleuchtung", description = "[q]Erhöht den durch Feuer- und Frostzauber verursachten Schaden 2 min lang um 20%."},
[52496] = {name = "Gruftskarabäen", description = "[q]Beschwört mehrere Gruftskarabäen, die das Ziel des Zaubernden angreifen."},
[52497] = {name = "Flatulenz", description = "[q]Stößt nahe Schädelberster der Drakkari zu Boden und verursacht bei einem Feind 2 sec lang alle 1 Sek. Schaden."},
[52501] = {name = "Zorn", description = "[q]Schleudert einen Blitzschlag auf einen Feind und verursacht Naturschaden."},
[52502] = {name = "Mondfeuer", description = "[q]Verbrennt den Feind und fügt ihm Arkanschaden sowie 12 sec lang zusätzlichen Schaden zu."},
[52503] = {name = "Machterfüllte Aura des Mondkin", description = "[q]Erhöht die kritische Trefferchance um 10%."},
[52504] = {name = "Aufschlitzen", description = "[q]Schlitzt das gegnerische Ziel auf, verursacht 463 to 537 Blutungsschaden und lässt das Ziel 15 sec lang für 750 bluten, wodurch ein hohes Maß an Bedrohung verursacht wird. Schaden wird durch Angriffskraft erhöht. Dieser Effekt ist bis zu 5-mal auf dem selben Ziel stapelbar."},
[52506] = {name = "Zermalmen", description = "[q]Erhöht den Schaden des nächsten Angriffs um 150."},
[52507] = {name = "Wutprankes Präsenz", description = "[q]Verringert den erlittenen Schaden um 10%."},
[52508] = {name = "Aufbrechende Nähte", description = "[q]Lässt eine Monstrosität explodieren, wodurch alle in der Nähe befindlichen Schädelberster der Drakkari getötet werden."},
[52519] = {name = "Ghulplosion", description = "[q]Tötet einen ungehorsamen Ghul auf der Stelle. Das betrifft auch Ghule, die meinen, dass sie Gothik eine freche Antwort geben dürfen."},
[52521] = {name = "Ghulzap", description = "[q]Zappt einen unfolgsamen Ghul zum Gehorsam."},
[52522] = {name = "Hämotoxin", description = "[q]Vergiftet das Ziel und fügt ihm alle 2 Sek. Schaden in Höhe von 8% seiner eigenen Gesundheit zu."},
[52524] = {name = "Blendende Netze", description = "[q]Schleudert Spinnweben in die Augen umgebender Feinde, die sie 4 sec lang erblinden lassen und desorientieren."},
[52525] = {name = "Krankheitswolke", description = "[q]Eine faulige Krankheitswolke umgibt den Zaubernden, die Gegnern in der Nähe alle 2 Sek. 1000 Naturschaden zufügt."},
[52527] = {name = "Gallenschleim", description = "[q]Bedeckt das Ziel mit einer ekligen Schleimschicht, die 15 sec lang Schaden und Heilung des Ziels um 35% senkt und alle 3 700 Naturschaden zufügt."},
[52531] = {name = "Dampfschlag", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Feuerschaden zu."},
[52532] = {name = "Stoß", description = "[q]Schlägt einen Feind und fügt erhöhten Nahkampfschaden zu."},
[52534] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[52535] = {name = "Schattennova", description = "[q]Fügt nahen Gegnern Schattenschaden zu."},
[52538] = {name = "Sturmangriff", description = nil},
[52539] = {name = "Kanonenkugel", description = "[q]Schleudert eine glühende Kugel hinaus, die dem Feind 1 Feuerschaden zufügt."},
[52540] = {name = "Meucheln", description = "[q]Fügt einem Feind normalen Schaden plus 300 zu, aber nur, wenn von hinten angegriffen wird."},
[52551] = {name = "Blühendes Leben", description = "[q]Heilt pro Sekunde um 232 to 268 sowie um 1573 bis 1827, wenn der Effekt endet oder entfernt wird."},
[52553] = {name = "Machterfüllter Baum des Lebens", description = "[q]Überträgt eine vorteilhafte Aura auf nahe Verbündete und erhöht die erhaltene Heilung um 10%."},
[52554] = {name = "Pflege", description = "[q]Ruft Naturmagie herbei, um einen Verbündeten zu heilen."},
[52560] = {name = "Blut des Alten Gottes beschwören", description = "[q]Beschwört zu Ursocs Hilfe das Blut des Alten Gottes."},
[52564] = {name = "Verderbnis des Alten Gottes", description = "[q]Verursacht 1110 to 1290 Schattenschaden und 6 sec lang alle 1 Sek. 400 Schattenschaden."},
[52566] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[52576] = {name = "Elektromagnetischer Puls", description = "[q]Mittels der Energie der Scharlachroten Kanone wird ein kraftvoller elektromagnetischer Puls emittiert. Alle Feinde in näherer Umgebung werden ausgelöscht."},
[52581] = {name = "Rüstung zermalmen", description = "[q]Verringert die Rüstung eines Feindes 6 sec lang um 75%."},
[52583] = {name = "Einfluss des Alten Gottes", description = "[q]Unter dem Einfluss des Alten Gottes. Betäubt das Ziel regelmäßig 2 sec lang und verursacht 1018 to 1182 Schattenschaden."},
[52586] = {name = "Gedankenschinden", description = "[q]Fügt einem Feind Schattenschaden zu und verringert 3 sec lang sein Bewegungstempo."},
[52592] = {name = "Fluch der Ermüdung", description = "[q]Verflucht alle Feinde in einem Radius von 5 Metern um das Ziel und verursacht 2828 to 3172 Schattenschaden. Verringert das Bewegungstempo um 30%, das Angriffstempo um 30% und das Zaubertempo um 30%. Hält 10 sec lang an."},
[52601] = {name = "Sturmattacke", description = "[q]Stürmt einen Jünger von Mam'toth an, der bis zu 5 to 45 Meter entfernt ist. Kann nicht in einem Umkreis von 5 Metern um das Ziel verwendet werden."},
[52603] = {name = "Trampeln", description = "[q]Verursacht bei bis zu vier nahen Jüngern von Mam'toth 4375 bis 5625 Schaden."},
[52604] = {name = "Skorpidgift", description = "[q]Ein Gift, welches die 15 sec lang die Trefferchance des Ziels um 5% verringert. Es kann immer nur jeweils ein Gift pro Jäger auf einem beliebigen Ziel aktiv sein."},
[52606] = {name = "Feuerbrandfalle", description = "[q]Platziert eine Feuerfalle, die dem ersten sich nähernden Gegner 30 sec lang 36 Feuerschaden zufügt. Die Falle besteht 30 sec lang. Es kann immer nur jeweils eine Falle aktiv sein."},
[52608] = {name = "Laterne werfen", description = "[q]Wirft eine Laterne nach einem Feind, verursacht 213 to 287 Feuerschaden und erzeugt eine brennende Lohe unter den Füßen des Ziels."},
[52611] = {name = "Skelette beschwören", description = "[q]Beschwört nahe dem Zaubernden zwei Skelettdiener."},
[52613] = {name = "Eisbrocken", description = nil},
[52624] = {name = "Nachbrennen", description = "[q]Erhöht 10 sec lang die kritische Trefferchance des Zaubernden bei Nahkampfangriffen um 50%."},
[52626] = {name = "Rüstungsschmelze", description = "[q]Verringert die Rüstung von in der Nähe befindlichen Feinden 30 sec lang um 1000."},
[52628] = {name = "Rune der Vergeltung", description = "[q]Erhöht bei erlittenem Schaden den verursachten Schaden um 5%. Dieser Effekt kann nur einmal alle 2 Sek. ausgelöst werden und ist bis zu 5-mal stapelbar."},
[52629] = {name = "Vergeltung", description = "[q]Erhöht den verursachten Schaden um 5%. Bis zu 5-mal stapelbar."},
[52630] = {name = "Kampfrunen", description = "[q]Verursacht bei Angreifern 150 körperlichen Schaden."},
[52633] = {name = "Leerenstoß", description = "[q]Fügt einem Gegner 125% Waffenschaden zu und ignoriert dabei seine Rüstung."},
[52634] = {name = "Leerenschild", description = "[q]Eine Schutzblase der Leere geht aus dem Schild des Zaubernden hervor und verringert 8 sec lang jeden erlittenen Schaden um 50%."},
[52635] = {name = "Beschleunigen", description = "[q]Erhöht Nahkampfangriffs- und Zaubertempo des Zaubernden um 10%. Bis zu 20-mal stapelbar."},
[52636] = {name = "Zeitverschiebung", description = "[q]Teleportiert den Zaubernden an einen zufälligen Ort und entfernt alle negativen Effekte von ihm."},
[52640] = {name = "Kraft der Schmiede", description = "[q]Verursacht in einem kegelförmigen Bereich vor dem Zaubernden 1203 to 1397 körperlichen Schaden und betäubt die Gegner 3 sec lang."},
[52643] = {name = "Stumpfe Waffen", description = "[q]Gewährt die Chance, dass bei einem Treffer die Waffen des Nahkampfangreifers stumpf werden und der verursachte Schaden dadurch verringert wird."},
[52651] = {name = "Blitzschlagschild", description = "[q]Umgibt den Zaubernden mit 20 Kugelblitzen, die eine Chance von 25% haben, Nahkampf- oder Distanzangreifer zu treffen und 250 Schaden zu verursachen. Jedes Mal, wenn der Blitzschlagschild zuschlägt, löst sich ein Kugelblitz auf. Auf diese Weise erlischt der Schild nach 30 sec oder nachdem er 20-mal zugeschlagen hat."},
[52657] = {name = "Temporalvortex", description = "[q]Kanalisiert 5 sec lang einen Vortex auf den Gegner, was alle 1 Sek. 600 Arkanschaden verursacht und das Bewegungs-, Angriffs- und Zaubertempo um 70% verringert."},
[52658] = {name = "Statische Überladung", description = "[q]Lädt einen Gegner mit Energie auf und verursacht im Verlauf von 10 sec 1249 to 1451 Naturschaden. Während das Ziel davon betroffen ist, verursacht es durch die Aufladung bei seinen nahen Verbündeten ebenfalls regelmäßig 1249 to 1451 Naturschaden. Nach 10 sec verursacht eine elektrische Explosion bei allen Feinden im Umkreis von 8 Metern zusätzliche 2313 to 2687 Naturschaden und stößt sie zurück."},
[52660] = {name = "Arkane Welle", description = "[q]Schleudert eine Welle magischer Energie auf den Gegner und verursacht 2590 to 3010 Arkanschaden."},
[52666] = {name = "Kranker Auswurf", description = "[q]Fleischhaken stößt eine Krankheitswolke aus, verursacht 713 to 787 Naturschaden und unterbricht 4 sec lang das Wirken von Zaubern naher Gegner."},
[52671] = {name = "Bogenbrand", description = "[q]Verursacht beim Ziel 602 to 698 Naturschaden und erhöht den erlittenen Naturschaden um 2%."},
[52672] = {name = "Ghulplosion", description = "[q]Tötet sofort einen geringen Geißeldiener nach Wahl des Zaubernden - oftmals spektakulär!"},
[52694] = {name = "Auge von Acherus zurückrufen", description = "[q]Ruft das Auge von Acherus zurück und kehrt heim."},
[52695] = {name = "Voodoopuppe", description = "[q]Verursacht 833 to 967 körperlichen Schaden."},
[52696] = {name = "Fesselnde Ketten", description = "[q]Umschlingt das Ziel mit Ketten, verursacht alle 1 Sek. 1000 körperlichen Schaden und betäubt das Ziel 5 sec lang."},
[52699] = {name = "Rune schmelzen", description = "[q]Erfüllt das Ziel mit der Rune von Tordon."},
[52701] = {name = "Blitzgeladen", description = "[q]Angriffe haben eine Chance, Naturschaden zu verursachen."},
[52702] = {name = "Runenschlag", description = "[q]Ein massiver Schlag, der 300% Waffenschaden verursacht und eine Rune in die Haut des Ziels einprägt."},
[52703] = {name = "Aufgeladenes Totem des Wachens", description = "[q]Beschwört ein Blitzschlagtotem, das 14 sec lang anhält und in der Nähe befindlichen Feinden in regelmäßigen Abständen Schaden zufügt."},
[52705] = {name = "Späherschock", description = "[q]Fügt Gegnern in einem kegelförmigen Bereich vor dem Zaubernden Naturschaden zu."},
[52708] = {name = "Fleisch stehlen", description = "[q]Stiehlt das Fleisch des feindlichen Ziels, verringert seinen verursachten Schaden um 75% und erhöht den verursachten Schaden des Zaubernden um 75%."},
[52710] = {name = "Blitzgeladen", description = "[q]Angriffe haben eine Chance, 24 to 29 Naturschaden zu verursachen."},
[52713] = {name = "Runenweben", description = "[q]Fügt alle 1 Sek. 195 to 225 Schattenschaden zu und macht das Ziel 4 sec lang bewegungsunfähig."},
[52714] = {name = "Belebende Rune", description = "[q]Heilt den Zaubernden nach Ablauf von 5 sec um 2500."},
[52715] = {name = "Rune der Zerstörung", description = "[q]Markiert einen Feind 5 sec lang mit einer feurigen Rune, welche nach Ablauf von 5 Sek. Schaden verursacht."},
[52716] = {name = "Panik", description = "[q]In Panik!"},
[52717] = {name = "Gewitter", description = "[q]Ihr ruft einen Blitzschlag hernieder, der bei allen Feinden im Umkreis von 10 Metern Schaden verursacht. Alle Feinde erleiden 747 to 853 Naturschaden und werden 85.3 Meter zurückgestoßen."},
[52719] = {name = "Erschütternder Schlag", description = "[q]Ein brutaler Schlag, der Waffenschaden zufügt und den Gegner 3 sec lang betäubt."},
[52720] = {name = "Aasschwarm", description = "[q]Eine Welle chaotischer Magie schlägt über Feinde vor dem Zaubernden hinweg, verursacht 3230 to 3570 Schattenschaden und 15 sec lang alle 3 Sek. 380 to 420 Schattenschaden."},
[52721] = {name = "Schlaf", description = "[q]Versetzt einen Feind bis zu 10 sec lang in Schlaf. Jeglicher erlittene Schaden führt dazu, dass das Ziel erwacht."},
[52722] = {name = "Gedankenschlag", description = "[q]Fügt einem Gegner 4163 to 4837 Schattenschaden zu."},
[52723] = {name = "Vampirberührung", description = "[q]Heilt den Zaubernden um die Hälfte des vom Nahkampfangriff verursachten Schadens."},
[52740] = {name = "Waffe werfen", description = "[q]Wirft Eure Waffe und fügt dem Ziel unmittelbar 2040 to 2760 körperlichen Schaden sowie 21 sec lang 5425 zusätzlichen Schaden zu."},
[52743] = {name = "Kopfklatscher", description = "[q]Verursacht bei einem Feind 13 to 16 Schaden und betäubt ihn bis zu 8 sec lang. Ihr werdet Angriffe automatisch abbrechen. Das Ziel muss Euch zugewandt sein. Erleidet das Ziel Schaden wird es wieder erwachen."},
[52744] = {name = "Durchdringendes Heulen", description = "[q]Verursacht, dass 6 sec lang alle Feinde nahe dem Krieger benommen werden."},
[52754] = {name = "Einschlagender Schuss", description = "[q]Schießt auf einen Gegner und fügt ihm körperlichen Schaden zu.\n\n Das Opfer wird zurückgestoßen."},
[52755] = {name = "Multischusseinschlag", description = "[q]Feuert eine Salve von Geschossen auf einen Gegner und seine Verbündeten in der Nähe ab. Die Salve trifft bis zu 3 Ziele, fügt ihnen 2040 to 2760 körperlichen Schaden zu und stößt sie zurück."},
[52758] = {name = "Durchstechender Pfeil", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[52761] = {name = "Hakennetz", description = "[q]Macht einen Feind 6 sec lang bewegungsunfähig und verursacht körperlichen Schaden."},
[52766] = {name = "Zeitkrümmung", description = "[q]Die Zeit wird verlangsamt, verringert 6 sec lang das Angriffs-, Bewegungs- und Zaubertempo um 70%."},
[52770] = {name = "Dispersion", description = "[q]Streut sich zu Funken von Ionar."},
[52771] = {name = "Verwundender Schlag", description = "[q]Verletzt das Ziel schwer, verursacht 100% Waffenschaden, verringert seine erhaltenen Heileffekte um 25% und lässt es alle 1 Sek. 750 körperlichen Schaden erleiden. Hält 4 sec lang an."},
[52772] = {name = "Fluch der Strapazen", description = "[q]Erhöht 10 sec lang die Kosten von Zaubern und Fähigkeiten um 100%."},
[52773] = {name = "Hammerschlag", description = "[q]Verursacht bei einem Feind 125% des normalen Waffenschadens und verringert 5 sec lang sein Bewegungstempo auf 65% des Normalwertes."},
[52774] = {name = "Stahl erneuern", description = "[q]Repariert 6 sec lang alle 2 Sek. 10% des Schadens. Nur auf einen eisernen Vrykul anwendbar."},
[52778] = {name = "Schmelzstrahl", description = "[q]Fügt einem Gegner 5 sec lang Naturschaden zu."},
[52780] = {name = "Kugelblitz", description = "[q]Schleudert einen Kugelblitz auf das Ziel und verursacht beim Gegner 2013 to 2587 Naturschaden."},
[52791] = {name = "Geburtsflamme", description = "[q]Die starke Hitze der Flammen lässt das urzeitliche Drachenei schlüpfen."},
[52792] = {name = "Geburtsflamme", description = "[q]Bereit zu schlüpfen."},
[52812] = {name = "Glühender Zorn", description = "[q]Verursacht unmittelbar 1143 to 1397 Feuerschaden und 5 sec lang alle 1 Sek. 833 to 967 zusätzlichen Feuerschaden. Der Schaden verdoppelt sich, wenn das Ziel betäubt ist."},
[52813] = {name = "Mehrfachschuss", description = "[q]Feuert eine Salve von Geschossen auf einen Feind und seine nächsten Verbündeten ab, fügt normalen Schaden plus 278 to 322 zu, wirkt auf bis zu 3 Ziele."},
[52814] = {name = "Arktische Winde", description = "[q]Ein kalter Windstoß verursacht 2035 to 2365 Naturschaden."},
[52835] = {name = "Spalten", description = "[q]Fügt einem Feind und seinem nächsten Verbündeten Waffenschaden plus 438 to 562 zu, wirkt auf bis zu 4 Ziele."},
[52836] = {name = "Heiliger Zorn", description = "[q]Schleudert Blitze heiliger Macht in alle Richtungen und verursacht dabei 818 to 1597 Heiligschaden bei allen Untoten und Dämonen im Umkreis von 20 Metern."},
[52856] = {name = "Sturmangriff", description = "[q]Stürmt auf einen Gegner zu, verursacht normalen Schaden und betäubt ihn 2 sec lang, zudem werden alle weiteren Ziele in Reichweite zurückgestoßen."},
[52859] = {name = "Unterdrückung", description = "[q]Zwingt Akali, sich zu unterwerfen und zu beruhigen, sodass er an seinen Platz über dem Gundrakgitter zurückkehrt."},
[52862] = {name = "Wind verschlingen", description = "[q]Verschlingt einen verletzten Sturmwiedergänger und nimmt dessen Macht auf."},
[52864] = {name = "Wasser verschlingen", description = "[q]Verschlingt einen Aqueousgeist und nimmt dessen Macht auf."},
[52869] = {name = "Hervorrufung", description = "[q]Regeneriert 10 sec lang pro Sekunde 10% Mana. Während der Hervorrufung wird der erlittene Schaden um 10% erhöht."},
[52870] = {name = "Windschere", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden 1125 to 1875 Schaden zu und stößt sie zurück."},
[52873] = {name = "Offene Wunde", description = "[q]Fügt einem Gegner alle 3 Sek. körperlichen Schaden zu und erhöht seinen erlittenen Schaden 15 sec lang um 15%."},
[52878] = {name = "Schwindelig", description = "[q]Fühlt sich schwindelig."},
[52879] = {name = "Abwehr", description = "[q]Erhöht Eure Chance, Angriffe zu parieren, um 25%."},
[52883] = {name = "Gegenangriff", description = "[q]Ein Angriff, der die Verteidigung des Ziels umgeht und 8 sec lang bewegungsunfähig macht. Kann nur eingesetzt werden, nachdem Ihr einen Angriff erfolgreich pariert habt."},
[52884] = {name = "Kraft entziehen", description = "[q]Entzieht die Überreste von Akalis Macht. Die meiste davon wird zu seinem Meister in Gundrak unter das Gitter fließen, der Prophet wird aber auch einen Teil für sich nehmen. Wenn der Zauber endet, stirbt Akali."},
[52885] = {name = "Tödlicher Wurf", description = "[q]Wirft einen Dolch auf ein gegnerisches Ziel und verursacht 2400% Waffenschaden. Unterbricht außerdem das Wirken von Zaubern und verhindert 3 sec lang, dass diese Art Zauber gewirkt werden kann."},
[52886] = {name = "Stachelfalle", description = "[q]Platziert eine Stachelfalle, die bei Näherung bei allen Feinden erheblichen Schaden verursacht."},
[52889] = {name = "Vergifteter Schuss", description = "[q]Macht einen Feind benommen, verringert sein Bewegungstempo 5 sec lang um 50%."},
[52890] = {name = "Durchdringender Stoß", description = "[q]Ein Stoß, der Rüstung ignoriert und 100% Waffenschaden verursacht."},
[52891] = {name = "Klingenparade", description = "[q]Verringert 4 sec lang den erlittenen Nahkampf- und Distanzschaden um 1000."},
[52893] = {name = "Antimagisches Feld", description = "[q]Platziert ein großes, ortsgebundenes, antimagisches Feld, das den erlittenen Zauberschaden aller darin befindlichen Gruppenmitglieder um 75% verringert. Das antimagische Feld hält entweder 10 sec lang an oder bis es 10000 Zauberschaden absorbiert hat."},
[52895] = {name = "Bandagieren", description = "[q]Heilt im Verlauf von 6 sec 3000 Schaden."},
[52896] = {name = "Zauberdämpfung", description = "[q]Verringert nach einem kritischen Zaubertreffer 4 sec lang den erlittenen Zauberschaden um 10%."},
[52904] = {name = "Werfen", description = "[q]Wirft Eure Waffe auf ein Ziel und verursacht 3500 körperlichen Schaden."},
[52905] = {name = "Donnerblitz", description = "[q]Schleudert einen Blitzschlag auf einen Feind, der auf in der Nähe befindliche Gegner überspringt. Der Zauber wirkt auf den Feind und ihm nahe stehende Feinde und fügt jedem Ziel 2775 to 3225 Naturschaden zu."},
[52909] = {name = "Wasseratmung", description = "[q]Gewährt 30 min lang allen Gruppenmitgliedern die Fähigkeit der Wasseratmung."},
[52919] = {name = "Giftige Wolke", description = "[q]Verursacht alle 2 Sek. Naturschaden."},
[52921] = {name = "Bogenblitzschlag", description = "[q]Schockt das Ziel unmittelbar und verursacht 3238 to 3762 Naturschaden. Es besteht die Chance, dass das Ziel Freunde in der Nähe schockt."},
[52922] = {name = "Die Sühne des Inquisitors", description = "[q]Ein heiliger Blitz, der alle Feinde in seinem Einschlagsradius zurückstößt."},
[52926] = {name = "Valroths Pein", description = "[q]Zerschmettert einen Feind und fügt ihm Heiligschaden zu."},
[52931] = {name = "Toxinspucke", description = "[q]Schießt Gift auf einen Feind, verursacht Naturschaden und 10 sec lang alle 5 Sek. zusätzlichen Schaden."},
[52940] = {name = "Zeit zum Schlafen!", description = "[q]Heilt im Verlauf von 6 sec 600 Schaden."},
[52943] = {name = "Blitzschlagwirbel", description = "[q]Wirbelt um einen Feind herum und trifft ihn mit schmerzvollen Blitzschlägen."},
[52944] = {name = "Blitzeinschlag", description = "[q]Ruft Blitzschlag hernieder, verursacht Naturschaden und stößt alle Feinde zurück."},
[52960] = {name = "Blitzschlagnova", description = "[q]Sendet einen Energiestrahl aus, der den Gegnern in einem Umkreis von 20 Metern 6013 to 6987 Naturschaden zufügt."},
[52964] = {name = "Funkenfuror", description = "[q]Funken sprühen vom Zaubernden in alle Richtungen, verursachen pro Funke 18 to 21 Naturschaden."},
[52968] = {name = "Herzblutzorn", description = "[q]Erhöht 25 sec lang das Angriffstempo der Zaubernden um 60% und seinen verursachten körperlichen Schaden."},
[52969] = {name = "Energieentzug", description = "[q]Entzieht Energie aus den Körpern naher Feinde und stellt die Gesundheit des Zaubernden wieder her."},
[52972] = {name = "Zerstreuung", description = "[q]Schützt 4 Sek. lang gegen jegliche körperlichen Angriffe und reflektiert alle Zauber."},
[52973] = {name = "Frostatem", description = "[q]Verursacht bei Feinden vor dem Zaubernden Frostschaden und macht sie 6 sec lang bewegungsunfähig."},
[52974] = {name = "Heimtückisches Heulen", description = "[q]Verringert 15 sec lang die Angriffskraft naher Feinde."},
[52977] = {name = "Wirbelwind", description = "[q]Greift nahe Feinde in einem Wirbelwind an, der normalen Schaden verursacht und hohe Bedrohung erzeugt."},
[52979] = {name = "Eisige Absorption", description = "[q]Regeneriert 8 Sekunden lang pro Sekunde 5% Gesundheit und zusätzliche 5% Gesundheit für jeglichen während der Dauer erlittenen Schaden."},
[52982] = {name = "Akalis Immunität", description = "[q]Verzehrt einen kleinen Teil von Akalis Kraft, um Euch gegen jeglichen Schaden und Effekte immun zu machen."},
[52989] = {name = "Akalis Betäubung", description = "[q]Nutzt Akalis Macht, um alle Eure Feinde hilflos werden zu lassen."},
[52993] = {name = "Akalis Ritual der Vernichtung", description = "[q]Ein Ritual, das die Körper und Seelen der Ziele unwiederbringlich vernichten wird. Von dort gibt es kein Zurück."},
[53004] = {name = "Har'koas Zwang", description = "[q]Zwingt das Ziel, Har'koas Willen zu gehorchen."},
[53029] = {name = "Har'koas Herauforderung", description = "[q]Har'koa fordert den Propheten von Akali heraus."},
[53030] = {name = "Egelgift", description = "[q]Zieht pro Sek. 500 Gesundheit von Gegnern in der Nähe ab und überträgt sie auf den Zaubernden. Falls eines dieser Ziele stirbt, während es betroffen ist, wird der Zaubernde für 10% geheilt. Hält 10 sec lang an."},
[53032] = {name = "Klauenwirbel", description = "[q]Entfesselt Soo-holus Zorn auf nahe Gegner, welche 10 sec lang unnachgiebig angegriffen werden."},
[53039] = {name = "Fallschirm benutzen", description = "[q]Fallschirm einsetzen, um sicher zu landen."},
[53043] = {name = "Elektroschock", description = "[q]Schockt einen Gegner und fügt ihm dann 12 sec lang alle 3 Sek. zusätzlichen Naturschaden zu."},
[53044] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm Naturschaden zu."},
[53045] = {name = "Schlaf", description = "[q]Versetzt das gegnerische Ziel bis zu 4 sec lang in Schlaf. Jeglicher zugefügte Schaden wird das Ziel wieder aufwecken. Es kann immer nur jeweils ein Ziel schlafen."},
[53047] = {name = "Stürmisches Brüllen", description = "[q]Ein wütendes Gebrüll, das den verursachten Schaden um 50% erhöht."},
[53048] = {name = "Aufschreckendes Gebrüll", description = "[q]Entfernt Verlangsamungs-, Gruppenkontroll- und Betäubungseffekte von allen nahen Verbündeten."},
[53049] = {name = "Geladener Hagel", description = "[q]Greift nahe Gegner mit einem Hagel aus Axthieben an, der 6 sec lang andauert."},
[53052] = {name = "Phase verschieben", description = "[q]Macht den Zaubernden unverwundbar gegen Angriffe und Zauber des Gegners, allerdings kann er sich nicht bewegen oder angreifen. Hält bis zu 20 sec lang an."},
[53059] = {name = "Giftbenetzter Speer", description = "[q]Verursacht 12 sec lang alle 3 Sek. körperlichen Schaden."},
[53068] = {name = "Runenschlag", description = "[q]Ein massiver Schlag, der 3150 to 3850 Schaden verursacht und der Haut des Ziels eine Rune einprägt."},
[53070] = {name = "Befehl des Worgen", description = "[q]Kontrolliert die Gedanken des Ziels. Hält 20 sec lang an oder bis die Gesundheit des Ziels unter 35% fällt."},
[53071] = {name = "Gewitter", description = "[q]Ihr ruft einen Blitzschlag hernieder, der Gegnern im Umkreis von 15 Metern Schaden zufügt. Alle Gegner in der Nähe erleiden 3700 to 4300 Naturschaden und werden zurückgestoßen."},
[53072] = {name = "Sturmblitz", description = "[q]Fügt einem Gegner 3238 to 3762 Schaden zu."},
[53086] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[53093] = {name = "Echtsilberschuss", description = "[q]Schießt mit einer Echtsilberkugel auf einen Feind."},
[53094] = {name = "Infizierter Worgenbiss", description = "[q]Infiziert ein gegnerisches Ziel und verursacht alle 5 Sek. 150 Naturschaden bei einem Verbündeten in dessen Nähe. Bis zu 3-mal stapelbar. Auf Infektionsstufe 3 wird das Ziel zu einem Worgen."},
[53096] = {name = "Quetz'luns Richturteil", description = "[q]Befindet einen Drakkari als des Lebens unwürdig. Zumindest, bis sein Geist in den Körper zurückkehrt."},
[53110] = {name = "Humanoiden verschlingen", description = "[q]Verschlingt einen Scharlachroten Kreuzzügler, um das Mana des Frostbrutbezwingers wieder aufzufüllen."},
[53114] = {name = "Gefrorener Todesblitz", description = "[q]Feuert einen gefrorenen Todesblitz auf gegnerische Ziele und fügt allen Menschen 13125 to 16875 Schaden und allen Ballisten 35000 to 45000 Schaden zu."},
[53117] = {name = "Ballistenangriff", description = "[q]Verschießt eine Lanze, die beim Auftreffen explodiert und bei allen Feinden im Zielgebiet 9901 to 14099 Schaden verursacht."},
[53167] = {name = "Gabelblitzschlag", description = "[q]Fügt Gegnern in einem kegelförmigen Bereich vor dem Zaubernden Naturschaden zu."},
[53177] = {name = "Türen einweben", description = nil},
[53185] = {name = "Seitentür einweben", description = nil},
[53210] = {name = "Apokalypse", description = "[q]Lässt den Himmel herabstürzen."},
[53218] = {name = "Gefrorener Griff", description = "[q]Verringert das Bewegungstempo erheblich."},
[53229] = {name = "Gewaltiger Sturm", description = "[q]Erzeugt einen wütenden Sturm im Zielgebiet, der allen in der Nähe befindlichen Feinden alle 2 Sek. 3700 to 4300 Naturschaden zufügt und die Zeit zwischen Angriffen der betroffenen Feinde um 67% erhöht. Hält 15 sec lang an."},
[53239] = {name = "Axtsalve", description = "[q]Schleudert eine Salve von Äxten auf einen Feind."},
[53273] = {name = "Explosiver Lockvogel", description = "[q]Schleudert Schrapnelle und fügt allen nahen Gegnern so 1440 to 2160 körperlichen Schaden zu."},
[53278] = {name = "Ghulplosion", description = "[q]KABUMM!"},
[53314] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm Naturschaden zu."},
[53317] = {name = "Verwunden", description = "[q]Fügt einem Feind 15 sec lang alle 3 Sek. körperlichen Schaden zu."},
[53318] = {name = "Zerkrachen", description = "[q]Zerschmettert das Ziel, fügt ihm 5550 to 6450 Schaden zu und schleudert es in die Luft."},
[53322] = {name = "Erdrückende Netze", description = "[q]Zaubert ein Netz über einen Gegner, macht ihn 6 sec lang bewegungsunfähig und fügt ihm pro Sek. 200 Schaden zu."},
[53330] = {name = "Infizierte Wunde", description = "[q]Erhöht 20 sec lang den durch einen Gegner erlittenen körperlichen Schaden."},
[53333] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[53334] = {name = "Knochen beleben", description = "[q]Schleudert Knochen empor und erweckt sie zum Leben. Erschafft 9 sec lang alle 3 Sek. ein Gebilde mit belebten Knochen."},
[53345] = {name = "Pfeilangriff", description = "[q]Feuert einen Explosivpfeil, der bei allen Feinden innerhalb von 4 Metern 438 to 562 Schaden verursacht."},
[53350] = {name = "Kühlender Nebel", description = "[q]Kalter Nebel zieht auf, verringert die Wirkung von Flammeneffekten und heilt Freunde in der Nähe."},
[53361] = {name = "Raserei", description = "[q]Erhöht 12 sec lang das Angriffstempo um 50%."},
[53363] = {name = "Flügelstoß", description = "[q]Fügt Gegnern in einem kegelförmigen Bereich vor dem Zaubernden 9000 to 15000 Schaden zu, stößt sie zurück und verringert ihr Bewegungstempo."},
[53394] = {name = "Zuschlagen", description = "[q]Schlägt auf einen Feind ein, fügt ihm 943 to 1057 Schaden zu und unterbricht 5 sec lang den gewirkten Zauber."},
[53395] = {name = "Heldenhafter Stoß", description = "[q]Ein starker Angriff, der 2625 to 3375 körperlichen Schaden verursacht."},
[53399] = {name = "Weitreichendes Zerschmettern", description = "[q]Verursacht bei Feinden in einem kegelförmigen Bereich vor dem Zaubernden normalen Schaden plus 50 und stößt sie zurück."},
[53400] = {name = "Säurewolke", description = "[q]Versprüht Säure an den Aufenthaltsort des Ziels und erzeugt dadurch eine Wolke, die Gegnern in der Wolke pro Sek. 707 to 793 Naturschaden zufügt. Hält 1.50 min lang an."},
[53406] = {name = "Netz ergreifen", description = "[q]Verschießt klebrige Gespinste, die 2357 to 2643 Schaden verursachen, Gegner einfangen und sie in Richtung des Zaubernden ziehen."},
[53418] = {name = "Rüstung durchstechen", description = "[q]Verringert die Rüstung eines Feindes 8 sec lang um 50%."},
[53425] = {name = "Bärenpelz werfen", description = "[q]Verursacht 693 to 806 körperlichen Schaden."},
[53432] = {name = "Bärenfalle", description = "[q]Eine Bärenfalle platzieren, die zuschnappt, sobald sich der erste Feind nähert. Die Falle hält ihn an Ort und Stelle fest und verursacht 5 sec lang alle 2 Sek. 370 to 430 Blutungsschaden. Erlittener Schaden unterbricht den Effekt. Die Falle bleibt 1 min lang bestehen. Es kann immer nur jeweils eine Falle aktiv sein."},
[53437] = {name = "Rückenbrecher", description = "[q]Fügt dem Ziel Waffenschaden zu und betäubt es 4 sec lang."},
[53438] = {name = "Schrecken anstacheln", description = "[q]Verursacht alle 1 Sek. 450 Schattenschaden und das betroffene Ziel läuft 2 sec lang voller Entsetzen davon."},
[53454] = {name = "Durchbohren", description = "[q]Rammt einen Stachel durch den Boden, der alle Ziele im Umkreis von 4 Metern aufspießt, 2828 to 3172 Schaden verursacht und sie in die Luft schleudert. Dieser Angriff ignoriert Rüstung."},
[53463] = {name = "Fleisch regenerieren", description = "[q]Stellt das Fleisch des Ziels wieder her."},
[53467] = {name = "Egelschwarm", description = "[q]Erzeugt einen Heuschreckenschwarm, der den Zaubernden beschützt. Die Heuschrecken verursachen bei allen Feinden innerhalb von 60 Metern pro Sekunde 330 to 370 Naturschaden. Hält 15 sec lang an."},
[53472] = {name = "Hämmern", description = "[q]Bäumt sich auf um bricht auf alle Feinde innerhalb von 15 Metern vor dem Zaubernden hernieder, verursacht 9425 to 10575 Schaden und stampft alle Ziele 6 sec lang in den Boden."},
[53493] = {name = "Verbrennen", description = "[q]Fügt Eurem Ziel 1480 to 1720 Feuerschaden zu."},
[53496] = {name = "Hervorschießende Flammen", description = "[q]Aus den Händen des Zaubernden treten Feuerpfeile aus und fügen Gegnern in der Nähe Feuerschaden zu."},
[53498] = {name = "Prankenhieb", description = "[q]Ein grimmiger Hieb, der dem Ziel und bis zu drei weiteren Gegnern in der Nähe Schaden zufügt."},
[53499] = {name = "Krallenhieb", description = "[q]Attackiert das Ziel mit Krallen, fügt 880 to 1320 Schaden sowie 1005 zusätzlichen Schaden im Verlauf von 9 sec zu."},
[53504] = {name = "Flackernde Flammen", description = "[q]Verursacht 24 sec lang alle 3 Sekunden 200 Feuerschaden.\n Kühlender Nebel verringert diesen Effekt."},
[53520] = {name = "Aaskäfer", description = "[q]Beschwört für 4 sec pro Sekunde 2 Aaskäfer."},
[53536] = {name = "Heulende Böe", description = "[q]Weht dem Ziel eine eisige Böe entgegen, die allen Zielen innerhalb von 10 Metern 278 to 302 Frostschaden zufügt. Gefrorene Ziele erleiden dreifachen Schaden."},
[53549] = {name = "Eisige Berührung", description = "[q]Verursacht 86 bis 92 Frostschaden (modifiziert durch Angriffskraft) und verringert until cancelled lang das Nahkampfangriffs-, Distanzangriffs- und Zaubertempo des Ziels um 86 to 92%."},
[53550] = {name = "Gedankenfrost", description = "[q]Die Gedanken des Ziels werden von einer Kälte geschlagen, die 100 Frostschaden verursacht (modifiziert durch Angriffskraft). Das Wirken von Zaubern wird unterbrochen sowie 4 sec lang verhindert, dass Zauber dieser Art gewirkt werden können."},
[53570] = {name = "Zehrende Kälte", description = "[q]Entfesselt alle verfügbare Runenmacht, um der Erde unter dem Todesritter jegliche Wärme zu entziehen. Feinde innerhalb von 10 Metern werden in Eis eingeschlossen, das für 1 Sek. pro 5 Runenmacht jegliche ihrer Aktionen verhindert. Feinde zählen als eingefroren, jeglicher verursachte Schaden unterbricht den Effekt."},
[53602] = {name = "Hervorschießen", description = "[q]Schießt schnell auf einen Feind zu, verursacht 12 sec lang alle 3 Sek. 750 Schaden."},
[53605] = {name = "Degeneration", description = "[q]Greift das Ziel sofort an, verursacht 60% Waffenschaden und eine Krankheit, die im Verlauf von 21 sec 1400 Schaden zufügt. Alle auf das Ziel wirkenden, Heilung über Zeit verursachenden Effekte werden verderbt und erhöhen den verursachten Schaden der Krankheit dramatisch. Der Heileffekt wird dabei entfernt."},
[53607] = {name = "Verjüngung", description = "[q]Heilt einen Verbündeten 12 sec lang alle 3 Sek."},
[53608] = {name = "Blühendes Leben", description = "Blühendes Leben Magie\nHeilt pro Sekunde um 232 to 268 Gesundheit, plus 1573 bis 1827 wenn der Effekt endet oder gebannt wird."},
[53616] = {name = "Giftblitzsalve", description = "[q]Schießt Gift auf den Feind, fügt ihm Naturschaden und dann 9 sec lang alle 3 Sek. zusätzlichen Schaden zu."},
[53617] = {name = "Giftblitz", description = "[q]Schießt Gift auf den Feind, fügt ihm Naturschaden und dann 9 sec lang alle 3 Sek. zusätzlichen Schaden zu."},
[53618] = {name = "Rüstung zerreißen", description = "[q]Hackt nach der Rüstung des Gegners und verringert sie bei jedem Einsatz von 'Rüstung zerreißen'. Kann bis zu 20-mal angewendet werden. Hält 20 sec lang an."},
[53625] = {name = "Heldenhafter Sprung", description = "[q]Stürmt auf einen Gegner zu, verursacht normalen Schaden plus 0 und betäubt ihn 2 sec lang."},
[53629] = {name = "Aufwärtshaken", description = "[q]Fügt einem Feind normalen Schaden plus 150 zu und stößt ihn zurück."},
[53630] = {name = "Donner", description = "[q]Ihr ruft einen Blitzschlag hernieder, der bei allen Feinden im Umkreis von 10 Metern Schaden verursacht. Alle Feinde erleiden 14003 to 15997 Naturschaden und werden 1599.7 Meter zurückgestoßen."},
[53631] = {name = "Spalten", description = "[q]Fügt einem Feind und seinen nächsten Verbündeten Waffenschaden plus 30 zu und stößt sie zurück. Wirkt auf bis zu 5 Ziele."},
[53632] = {name = "Ghulplosion", description = "[q]KABUMM!"},
[53633] = {name = "Spalten", description = "[q]Fügt einem Feind und seinen nächsten Verbündeten Waffenschaden plus 30 zu und stößt sie zurück. Wirkt auf bis zu 5 Ziele."},
[53634] = {name = "Stampfen", description = "[q]Stampft auf den Boden auf, verursacht bei Feinden in einem großen Umkreis körperlichen Schaden und schleudert sie in die Luft."},
[53635] = {name = "Umarmung des Todes", description = "[q]Schlägt alle Gegner in der Nähe, verursacht dabei 13125 to 16875 Schaden und heilt alle Verbündeten in der Nähe um 13125 to 16875."},
[53638] = {name = "Heiliger Zorn", description = "[q]Schleudert Blitze heiliger Macht in alle Richtungen. Verursacht bei allen Zielen innerhalb von 0 Metern 765 to 935 Heiligschaden, mit jedem Sprung verdoppelt sich der Schaden."},
[53639] = {name = "Todesstoß", description = "[q]Greift das Ziel sofort an, verursacht 400% Waffenschaden und eine Krankheit, die im Verlauf von 21 sec 8400 Schaden zufügt. Alle auf das Ziel wirkenden, Heilung über Zeit verursachenden Effekte werden verderbt, und erhöhen den verursachten Schaden der Krankheit dramatisch. Der Heileffekt wird dabei entfernt."},
[53640] = {name = "Unheilige Verseuchung", description = "[q]Ein widerwärtiger Schwarm unheiliger Insekten umgibt den Zaubernden in einem Radius von 10 Metern. Alle Gegner, die sich im Schwarm aufhalten, erleiden pro Sek. 0 Schaden und werden mit einer Krankheit angesteckt, die bis zu 3-mal stapelbar ist. Hält pro 10 Runenmacht 1 Sek. lang an. Das Erzeugen von Runenmacht, während der Effekt andauert, kann ihn verlängern."},
[53642] = {name = "Die Macht von Mograine", description = "[q]Von der Macht des Hochlords Darion Mograine erfüllt. Euer Schaden, Eure Gesundheit und Eure Gesundheitsregeneration sind stark erhöht. Ihr seid eine unaufhaltbare Macht der Dunkelheit!"},
[53643] = {name = "Heiliger Stoß", description = "[q]Verursacht bei allen Geißeldienern vor dem Zaubernden 7438 to 9562 Heiligschaden."},
[53646] = {name = "Dämonischer Pakt", description = "[q]Erhöht Euren Zauberschaden um 2% und Euer beschworener Dämon gewährt allen nahen befreundeten Gruppen- oder Schlachtzugsmitgliedern den Effekt 'Dämonischer Pakt'.\n\n Der Effekt 'Dämonischer Pakt' erhöht die Zaubermacht um 10%."},
[53658] = {name = "Das Licht der Morgendämmerung", description = "[q]Und es wird sein eine Dämmerung vor der dunkelsten Stunde..."},
[53694] = {name = "Seuchenstoß", description = "[q]Ein bösartiger Schlag, der Waffenschaden verursacht, das Ziel verseucht und im Verlauf von 20 sec 2666.7 Schattenschaden hervorruft."},
[53717] = {name = "Leichenexplosion", description = "[q]Bringt einen Leichnam zum Bersten und verursacht bei allen Gegnern innerhalb von 20 Metern 60 Naturschaden (modifiziert durch Angriffskraft). Wird einen Leichnam in der Nähe nutzen, wenn das Ziel kein Leichnam ist. Der Zauber kann nicht auf die Überreste von mechanischen Einheiten oder Elementaren angewendet werden."},
[53721] = {name = "Tod und Verfall", description = "[q]Alle Ziele im betroffenen Gebiet erleiden 10 sec lang alle 1 Sek. 10 Schattenschaden (modifiziert durch Angriffskraft). Es besteht die Chance, dass sich betroffene Ziele vor Furcht zusammenkauern."},
[53765] = {name = "Schattenportal öffnen", description = "[q]Öffnet ein Portal ins Schattenreich, um von dort einen Diener herbeizurufen."},
[53766] = {name = "Antimagische Hülle", description = "[q]Umgibt den Todesritter mit einer antimagischen Hülle, die 75% Schaden des nächsten schädigenden Zaubers absorbiert. Von der antimagischen Hülle absorbierter Schaden erfüllt den Todesritter mit zusätzlicher Runenmacht. Hält 5 sec lang an."},
[53769] = {name = "Todesmantel", description = "[q]Entfesselt unheilige Energien, die bei einem feindlichen Ziel Schattenschaden verursachen (modifiziert durch Angriffskraft), oder bei einem freundlichen untoten Ziel Schaden heilen."},
[53778] = {name = "Handauflegung", description = "[q]Eine mächtige Heilung."},
[53788] = {name = "Lavaeruption", description = "[q]Ihr schleudert flüssige Lava auf das Ziel, die 1776 to 2264 Feuerschaden verursacht. Ist das Ziel von 'Flammenschock' betroffen, wird 'Lavaeruption' den Flammenschock aufzehren und einen kritischen Treffer erzielen."},
[53790] = {name = "Verteidigungshaltung", description = "[q]Eine defensive Haltung."},
[53791] = {name = "Berserkerhaltung", description = "[q]Eine offensive Haltung."},
[53792] = {name = "Kampfhaltung", description = "[q]Eine ausbalancierte Haltung."},
[53801] = {name = "Raserei", description = "[q]Erhöht 10 min lang das Angriffstempo des Zaubernden um 25% und den von ihm verursachten körperlichen Schaden um 50%."},
[53813] = {name = "Arkanschild", description = "[q]Schützt den Zaubernden vor Schaden."},
[53824] = {name = "Werfen", description = "[q]Wirft Eure Waffe auf das Ziel."},
[53850] = {name = "Schattenschlag", description = "[q]Entfesselt Energien, die alle nahen Feinde zurückstoßen und 960 to 1440 Schattenschaden verursachen."},
[54028] = {name = "Eindringling!", description = "[q]Eine Magierwache hat Euch aus der Silbernen Enklave teleportiert."},
[54029] = {name = "Eindringling!", description = "[q]Eine Magierwache hat Euch aus Sonnenhäschers Zuflucht teleportiert."},
[54049] = {name = "Schattenbiss", description = "[q]Beißt den Feind und verursacht so [ 166 + (1.228 * (Spell power * 0.5))] Schattenschaden plus zusätzlich 30 % Schaden für jeden Eurer auf das Ziel wirkenden regelmäßigen Effekte."},
[54074] = {name = "Wundgift", description = "[q]Vergiftet den Feind und verringert alle auf ihn angewandten Heileffekte 15 sec lang um 10%."},
[54087] = {name = "Seuchenkristall werfen", description = "[q]Wirft einen Seuchenkristall auf ein feindliches Ziel. Der Kristall zersplittert beim Aufschlag, verursacht 2043 to 2257 Naturschaden und giftige Rückstände rufen im Verlauf von 45 sec weiteren Schaden hervor."},
[54093] = {name = "Stille", description = "[q]Bringt nahe Feinde 6 sec lang zum Schweigen."},
[54095] = {name = "Feuerball", description = "[q]Fügt einem Gegner 2025 to 2475 Feuerschaden zu."},
[54102] = {name = "Astrale Kugel beschwören", description = "[q]Beschwört eine Sphäre des Astraleums, um den Zaubernden in der Schlacht zu unterstützen."},
[54105] = {name = "Seuchennebel", description = "[q]Verursacht bei nahen Feinden Naturschaden."},
[54113] = {name = "Schattenblitz", description = "[q]Schleudert ein Geschoss dunkler Magie und verursacht Schattenschaden."},
[54115] = {name = "Seuchenkristallexplosion", description = "[q]Wirft einen Seuchenkristall auf ein feindliches Ziel. Der Kristall zersplittert beim Aufschlag, verursacht 2043 to 2257 Naturschaden und giftige Rückstände rufen im Verlauf von 45 sec weiteren Schaden hervor."},
[54123] = {name = "Raserei", description = "[q]Erhöht 10 min lang das Angriffstempo des Zaubernden um 50% und den von ihm verursachten körperlichen Schaden um 50%."},
[54132] = {name = "Erschütternder Schlag", description = "[q]Ein brutaler Schlag, der 5000 Schaden verursacht und den Gegner 5 sec lang betäubt."},
[54135] = {name = "Machtvoller Stoß", description = "[q]Ein machtvoller Angriff, der 15300 to 20700 körperlichen Schaden verursacht."},
[54136] = {name = "Seuchenblutinfusion", description = "[q]Heilt den Kontrolleur des Seuchenbluttrolls 5 sec lang alle 1 Sek. um 2000."},
[54160] = {name = "Arkane Macht", description = "[q]Erhöht das Angriffs- und Zaubertempo des Zaubernden um 75% und den von ihm verursachten Schaden um 75%."},
[54164] = {name = "Spieler beschwören", description = nil},
[54166] = {name = "Heiligtum des Schöpfers", description = "[q]Entfernt den Etymidian aus dem Kampf und schützt ihn 12 sec lang vor allen Angriffen und Zaubern. In dieser Zeit kann der Etymidian jedoch ebenfalls nicht angreifen oder Zauber wirken."},
[54170] = {name = "Segeln", description = "[q]Flugtempo um 350% erhöht."},
[54177] = {name = "Zepiks Eifer", description = "[q]Beim Verursachen von Schaden besteht eine Chance, dass Zepiks verursachter Schaden für kurze Zeit erhöht wird. Bis zu 5-mal stapelbar."},
[54185] = {name = "Schlitzende Klauen", description = "[q]Verursacht 120 körperlichen Schaden."},
[54188] = {name = "Rüstung zerreißen", description = "[q]Hackt nach der Rüstung eines Feindes, wird pro 'Rüstung zerreißen' weiter verringert. Bis zu 5-mal stapelbar. Hält 30 sec lang an."},
[54193] = {name = "Erdfuror", description = "[q]Schleudert alle nahen Feinde in die Luft, verursacht Schaden und verlangsamt 6 sec lang ihr Bewegungstempo."},
[54194] = {name = "Heilen", description = "[q]Stellt bei einem nahen Verbündeten Gesundheit wieder her."},
[54195] = {name = "Erdrache", description = "[q]Wirft eine Kugel verzauberter Erde auf den Feind, verursacht erheblichen Schaden."},
[54202] = {name = "Arkanbeschusssalve", description = "[q]Schießt Raketen auf Gegner ab und verursacht 1758 to 2042 Arkanschaden."},
[54210] = {name = "Feuerregen", description = "[q]Lässt einen Schmelzregen niedergehen, der alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen 5 sec lang alle 3 Sek. 6 Feuerschaden zufügt."},
[54226] = {name = "Arkanpuffer", description = "[q]Verursacht bei einem Feind 879 to 1021 Arkanschaden und erhöht seinen erlittenen Arkanschaden um 150. Hält 20 sec lang an."},
[54237] = {name = "Wasserschlag", description = "[q]Schießt einen Wasserstoß auf den Gegner, fügt ihm 2775 to 3225 Frostschaden zu und stößt ihn zurück."},
[54241] = {name = "Wasserschusssalve", description = "[q]Schleudert ein wässriges Geschoss auf alle Feinde in einem Umkreis von 45 Metern und fügt 1850 to 2150 Frostschaden zu."},
[54261] = {name = "Eislanze", description = "[q]Fügt einem feindlichen Ziel 46 to 53 Frostschaden zu. Bei eingefrorenen Zielen wird dreifacher Schaden zugefügt."},
[54287] = {name = "Wutanfall", description = "[q]Erhöht 2 min lang das Angriffstempo der Zaubernden um 60% und seinen verursachten körperlichen Schaden."},
[54290] = {name = "Netz schleudern", description = "[q]Verursacht 1500 Naturschaden und verringert das Bewegungstempo um 10%. Hält 5 Sek. lang an. Bis zu 7-mal stapelbar."},
[54306] = {name = "Schützende Blase", description = "[q]Schützt den Zaubernden in einer Wasserblase und verringert den erlittenen Schaden um 99%. Jeder Angriffs- oder Zaubertreffer gegen den Zaubernden verbraucht eine Aufladung. Hält until cancelled lang an oder bis alle 35 Aufladungen verbraucht sind."},
[54309] = {name = "Mal der Dunkelheit", description = "[q]Überwältigt ein Ziel mit reiner Dunkelheit, die pro Sekunde 500 Schattenschaden verursacht. Zusätzlich erleiden Verbündete des Ziels, die sich in seiner Nähe befinden, jedes Mal 1885 to 2115 Schattenschaden, wenn das Ziel geheilt wird. Hält 10 sec lang an."},
[54311] = {name = "Gruftskarabäen", description = "[q]Beschwört mehrere Gruftskarabäen, die das Ziel des Zaubernden angreifen."},
[54312] = {name = "Raserei", description = "[q]Erhöht 10 min lang das Zaubertempo um 100% und den verursachten körperlichen Schaden um 25%."},
[54313] = {name = "Gruftskarabäenschwarm", description = "[q]Beschwört einen Gruftskarabäenschwarm, der alle nahen feindlichen Ziele angreift."},
[54314] = {name = "Kraft entziehen", description = "[q]Entzieht allen Gegnern in der Nähe die Macht, verringert ihren zugefügten Schaden um 1% und erhöht Euren zugefügten Schaden um 2%."},
[54318] = {name = "Ruhestein", description = "[q]Bringt Euch zurück nach: <Hearthstone Location>. Sprecht mit einem Gastwirt an einem anderen Ort, um Euren Heimatort zu ändern."},
[54322] = {name = "Gottesschild", description = "[q]Schützt den Zaubernden 12 sec lang vor allen Schäden und Zaubern, erhöht jedoch währenddessen die Zeit zwischen seinen Angriffen um 100%."},
[54323] = {name = "Segen des Lichts", description = "[q]Erfüllt das Ziel mit dem Segen und der Zustimmung des Heiligen Lichts."},
[54340] = {name = "Widerwärtiges Erbrochenes", description = "[q]Verursacht 602 to 698 Naturschaden und verringert 10 sec lang das Bewegungstempo des Gegners um 50%."},
[54345] = {name = "Welkstoß", description = "[q]Ein Schlag, der 120% normalen Waffenschaden verursacht. Lässt den Körper des Ziels welken und erhöht 4 sec lang die Zeit zwischen den Angriffen eines Gegners um 30%."},
[54356] = {name = "Unaufhaltsamer Wutanfall", description = "[q]Erhöht das Angriffstempo des Zaubernden um 50% und den durch ihn verursachten körperlichen Schaden um 25%. Hält until cancelled lang an. Der Zaubernde kann nicht aufgehalten werden."},
[54361] = {name = "Leerenverschiebung", description = "[q]Verschiebt das Ziel gewaltsam in die Leere, verursacht pro Sekunde 750 Schattenschaden. Wenn der Effekt endet wurde das Ziel vollständig in die Leere verschoben und kann versteckte Leerenkreaturen sehen."},
[54369] = {name = "Leerwachposten beschwören", description = "[q]Beschwört eine Leerenwache."},
[54378] = {name = "Tödliche Wunde", description = "[q]Ein kräftiger Schlag trifft das Ziel und verringert die Wirksamkeit von Heileffekten um 10%."},
[54386] = {name = "Darmuks Wachsamkeit", description = "[q]Erhöht 6 sec lang die Ausweichchance des Zaubernden um 75%."},
[54387] = {name = "Gefräßige Klaue", description = "[q]Entzieht einem Feind 278 to 322 Gesundheit und überträgt sie auf den Zaubernden."},
[54396] = {name = "Optische Verbindung", description = "[q]Kanalisiert einen Strahl dunkler Energie auf das Ziel und verursacht im Verlauf von 12 sec Schattenschaden. Zuerst wird der Schaden langsam zugefügt und nimmt dann zu, bis die Kanalisierung ihre Gesamtdauer erreicht hat."},
[54399] = {name = "Wasserblase", description = "[q]Ihr seid von einer Wasserblase umgeben, die 12 sec lang alle 3 Sek. 204 to 236 Frostschaden verursacht und das Bewegungstempo um 50% verringert."},
[54400] = {name = "Wasserwall", description = "[q]Ihr beschwört einen wilden Wasserwall, der an feindlichen Zielen bei Kontakt 602 to 698 Naturschaden verursacht."},
[54417] = {name = "Strahl des Leidens", description = "[q]Fügt einem einzelnen Ziel eine Zeit lang Schattenschaden zu."},
[54420] = {name = "Greifen übergeben", description = "[q]Bringt den Greifen des Ansturms zu Uzo Todesrufer auf der Todesanhöhe."},
[54424] = {name = "Teufelsintelligenz", description = "[q]Erhöht das maximale Mana aller Gruppen- und Schlachtzugsmitglieder um 2126 und lässt sie alle 5 Sek. 0 Mana regenerieren."},
[54429] = {name = "Blitzwirbel", description = "[q]Wirbelt um einen Feind herum und trifft ihn mit schmerzvollen Blitzschlägen."},
[54431] = {name = "Speer werfen", description = "[q]Wirft eine Waffe nach einem Feind und fügt körperlichen Schaden zu."},
[54451] = {name = "Welkberührung", description = "[q]Lässt einen Gegner 12 sec lang erkranken, verursacht alle 3 Sek. Schattenschaden und verringert sein Bewegungstempo um 50%."},
[54453] = {name = "Fangnetz", description = "[q]Kanalisiert einen Strahl von Netzen, macht damit das Ziel bewegungsunfähig und fügt ihm nach 3 907 to 1053 Schattenschaden zu."},
[54458] = {name = "Zerkrachen", description = "[q]Stößt Euren Feind von Euch weg und verursacht dabei 500 körperlichen Schaden."},
[54459] = {name = "Zermalmen", description = "[q]Einen Feind mit einem mächtigen Frontalangriff attackieren. Verursacht 1360 to 1840 Schaden."},
[54460] = {name = "Sturmangriff", description = "[q]Stürzt Euch auf Euer Ziel, welches dabei für 3 Sek. betäubt wird."},
[54462] = {name = "Heulendes Kreischen", description = "[q]Das Bewegungstempo naher Feinde wird 6 sec lang um 50% verringert.\n Verringert die Angriffskraft naher Feinde um 25."},
[54467] = {name = "Knochenrüstung", description = "[q]Der Knochengreif wird von 5 wirbelnden Knochen umgeben. So lang zumindest 1 Knochen verbleibt, wird sein erlittener Schaden um 40% verringert und er verursacht mit allen Angriffen, Zaubern und Fähigkeiten 20% mehr Schaden. Jeder erfolgreiche Zauber oder Angriff braucht einen Knochen auf. Hält 5 min lang an."},
[54469] = {name = "Seuchenstoß", description = "[q]Ein bösartiger Schlag, der Klauenschaden plus 2000 verursacht (modifiziert durch Angriffskraft) und das Ziel mit einer Krankheit ansteckt, die im Verlauf von 12 sec 2200 Schattenschaden hervorruft. Nur nützlich gegen Greifenreiter des Ansturms."},
[54470] = {name = "Giftiger Biss", description = "[q]Spuckt Gift auf den Feind, fügt ihm Naturschaden und dann 9 sec lang alle 3 Sek. zusätzlichen Schaden zu."},
[54475] = {name = "Toben", description = "[q]Erhöht 6 sec lang das Angriffstempo des Zaubernden um 30% und den von ihm zugefügten körperlichen Schaden um 20%. Führt nach 6 sec zu Erschöpfung."},
[54476] = {name = "Blutpräsenz", description = "[q]Stärkt den Knochengreif mit der Präsenz des Blutes, erhöht seinen Schaden um 5% und heilt den Knochengreif um 1% des verursachten Schadens."},
[54477] = {name = "Erschöpft", description = "[q]Das Toben hat das Ziel erschöpft und betäubt es 4 sec lang."},
[54479] = {name = "Erdschild", description = "[q]Schützt das Ziel mit einem Erdschild. Nahkampfangriffe heilen das geschützte Ziel, dieser Effekt tritt aber nur einmal alle paar Sekunden auf. 20 Aufladungen. Hält 2 min lang an."},
[54481] = {name = "Kettenheilung", description = "[q]Erfüllt einen verwundeten Verbündeten mit Heilenergie, die sich auf einen anderen in der Nähe befindlichen Verbündeten ausbreitet. Der Zauber wirkt sich auf bis zu 3 Ziele aus."},
[54482] = {name = "Blut von Sseratus", description = "[q]Das Blut von Sseratus erhöht 2 min lang das Angriffs- und Zaubertempo des Ziels um 10%."},
[54483] = {name = "Lebenskraft von Sseratus", description = "[q]Das Blut von Sseratus erhöht 2 min lang das Angriffs- und Zaubertempo des Ziels um 60% und verringert den erlittenen Schaden um 30%."},
[54484] = {name = "Schlange der Drakkari beschwören", description = "[q]Beschwört eine Schlange der Drakkari, um dem Zaubernden zu helfen."},
[54487] = {name = "Sprungangriff", description = "[q]Springt das Ziel an und verursacht 120% Waffenschaden."},
[54511] = {name = "Erdschock", description = "[q]Schockt einen Gegner mit erschütternder Kraft, verursacht Naturschaden und unterbricht 2 sec lang den gewirkten Zauber."},
[54512] = {name = "Seuchenschild", description = "[q]Absorbiert 1500 Schaden."},
[54516] = {name = "Kampfrausch", description = "[q]Erhöht Nahkampf-, Distanzangriffs- und Zaubertempo aller Gruppenmitglieder 20 sec lang um 35%."},
[54517] = {name = "Magnetische Anziehung", description = "[q]Zieht einen Spieler von der gegenüberliegenden Plattform zum Zaubernden."},
[54520] = {name = "Sühne", description = "[q]Schleudert eine Salve heiligen Lichts auf das Ziel und verursacht 3 sec lang bei einem Gegner alle 1 Sek. 296 to 344 Heiligschaden."},
[54524] = {name = "Umhüllung der Dunkelheit", description = "[q]Verringert bei erlittenem Schaden die erhaltene Heilung des Angreifers um 20%."},
[54526] = {name = "Qual", description = "[q]Quält den Geist eines Gegners, verursacht alle 1 Sek. 105 to 115 Schattenschaden und betäubt das Ziel 5 sec lang."},
[54527] = {name = "Korrosiver Speichel", description = "[q]Leckt den Gegner ab und bedeckt ihn mit Speichel, der pro Anwendung 5% seiner Rüstung wegätzt. Bis zu 20-mal stapelbar."},
[54532] = {name = "Eisstachel", description = "[q]Ein Eisstachel verursacht 629 to 731 Frostschaden."},
[54537] = {name = "Detonation", description = "[q]Verursacht 8313 to 10687 Feuerschaden an allen in der Nähe befindlichen Einheiten."},
[54559] = {name = "Energiewoge", description = nil},
[54565] = {name = "Whammy", description = "[q]Wirkt einen Whammy auf das Ziel, verursacht Naturschaden und verhext das Ziel 3 sec lang."},
[54580] = {name = "Gerinnen", description = "[q]Die Gerinnung erhöht den verursachten körperlichen Schaden um 150."},
[54588] = {name = "Windstoß", description = "[q]Ein Windstoß trägt das Ziel hoch in die Luft und verursacht dadurch 1249 to 1451 Naturschaden."},
[54589] = {name = "Wirbelnde Winde", description = "[q]Die wirbelnden Winde hüllen den Zaubernden ein, erhöhen 1.50 min lang sein Bewegungstempo um 25% sowie sein Angriffs- und Zaubertempo um 10%."},
[54593] = {name = "Schlangenstoß", description = "[q]Das Schlangengift verursacht 8 sec lang alle 2 Sek. 213 to 247 Naturschaden."},
[54601] = {name = "Schlangengestalt", description = "[q]Der Zaubernde nimmt die Gestalt einer Schlange an, wodurch 8 sec lang der verursachte Schaden um 200, das Angriffs- und Zaubertempo um 25% erhöht wird."},
[54603] = {name = "Beweglichkeit der Schlange", description = "[q]Verringert 8 sec lang Eure Chance, von Angriffen getroffen zu werden, um 25%."},
[54606] = {name = "Quetz'luns anhaltende Präsenz", description = "[q]Die Macht von Neltharion verringert den erlittenen Schaden um 20% und erhöht den verursachten Schaden um 20%. Hält 1.50 min lang an."},
[54609] = {name = "Frostbrandangriff", description = "[q]Erfüllt die Waffe des Schamanen mit Frostmagie, verursachter Schaden verringert 15 sec lang das Bewegungstempo des Ziels um 25%."},
[54615] = {name = "Gezielter Schuss", description = "[q]Ein gezielter Schuss, der den Distanzschaden um 600 Schaden erhöht und die erhaltene Heilung 6 sec lang um 50% verringert."},
[54617] = {name = "Speer werfen", description = "[q]Wirft eine Waffe nach einem Feind und fügt körperlichen Schaden zu."},
[54626] = {name = "Giftphiole", description = "[q]Verursacht 10 sec lang bei nahen Feinden Naturschaden."},
[54630] = {name = "Giftbenetzter Speer", description = "[q]Wirft einen Speer auf einen Gegner und verursacht 9 sec lang alle 3 Sek. Naturschaden."},
[54633] = {name = "Heiltrank trinken", description = "[q]Schluckt einen Heiltrank."},
[54651] = {name = "Seelenbrand", description = "[q]Gewährt bei einem Treffer die Chance, dass der Angreifer sich verbrennt, wodurch er Feuerschaden erleidet und alle Werte verringert werden."},
[54657] = {name = "Unkörperlich", description = "[q]Ihr seid 2 sec lang unkörperlich, wodurch sich die Chance auszuweichen um 50 erhöht."},
[54663] = {name = "Tödlicher Biss", description = "[q]Eintzieht einem Feind Gesundheit und heilt den Zaubernden um das bis zu Zweifache dieses Wertes."},
[54670] = {name = "Köpfen", description = "[q]Versucht alle Ziele in Nahkampfreichweite des Zaubernden zu köpfen und verursacht 300% Waffenschaden."},
[54673] = {name = "Eisbrocken", description = "[q]Verursacht 879 to 1021 Frostschaden und stößt das Ziel zurück."},
[54678] = {name = "Brutale Kraft", description = "[q]Die brutale Kraft des Zaubernden lässt Angriffe Rüstung ignorieren."},
[54682] = {name = "Flammenatem", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden 879 to 1021 Feuerschaden zu."},
[54684] = {name = "Feuerbombe", description = "[q]Verursacht Feuerschaden bei einem Feind und seinen nahen Verbündeten."},
[54701] = {name = "Raserei", description = "[q]Erhöht das Angriffstempo des Zaubernden um 50% und den verursachten körperlichen Schaden um 50. Hält 1.50 min lang an."},
[54703] = {name = "Verwunden", description = "[q]Fügt einem Gegner 15 sec lang alle 3 Sek. 2000 körperlichen Schaden zu."},
[54716] = {name = "Tödliche Stöße", description = "[q]Verletzt das Ziel und verringert die Wirksamkeit jeglicher Heilung 3 sec lang um 50%."},
[54719] = {name = "Mächtiges Zuschlagen", description = "[q]Fügt einem Gegner 175% Waffenschaden zu."},
[54761] = {name = "Giftstachel", description = "[q]Verursacht unmittelbar 744 to 956 Naturschaden und 10 sec lang alle 1 zusätzlich 75 Naturschaden."},
[54770] = {name = "Knochensäge", description = "[q]Wirft einen Knochenwirbel.\n Trefferchance um 50% verringert."},
[54780] = {name = "Seuchenspritzer", description = "[q]Wirf eine Seuchenbombe auf ein nahes Ziel, verursacht beim Ziel und nahen Feinden 3325 to 3675 Naturschaden und 15 sec lang alle 3 Sek. weitere 950 to 1050 Naturschaden."},
[54781] = {name = "Rasender Sturzflug", description = "[q]Der Zaubernde springt auf sein Ziel zu, erhöht das Angriffstempo um 200%."},
[54790] = {name = "Blutwandlung", description = "[q]Verursacht körperlichen Schaden und erhöht 15 sec lang den verursachten Schaden um 15%."},
[54792] = {name = "Eisige Adern", description = "[q]Beschleunigt Euer Wirken von Zaubern und erhöht dadurch das Zaubertempo um 20%. Hält 20 sec lang an."},
[54801] = {name = "Anstürmen", description = "[q]Stürmt 40 Meter durch in einer Reihe befindliche Gegner vor und durchnässt sie mit Mojo."},
[54819] = {name = "Mojogetränkt", description = "[q]Prescht durch Gegner in einer Reihe und tränkt sie in Mojo. Verursacht unmittelbar Naturschaden und 45 sec lang alle 3 Sek. zusätzlichen Schaden."},
[54840] = {name = "Metamorphose", description = "[q]Ihr nehmt Dämonengestalt an. Eure Rüstung wird um 360% erhöht und die Dauer auf Euch wirkender Betäubungs- und Verlangsamungseffekte um 50% verringert.\n\n In der Dämonengestalt könnt Ihr keine Zauber wirken, erhaltet jedoch einzigartige dämonische Fähigkeiten."},
[54850] = {name = "Auftauchen", description = "[q]Das Elementar der Drakkari taucht aus dem Steinkoloss auf!"},
[54878] = {name = "Verschmelzen", description = "[q]Zurück in den Koloss strömen."},
[54890] = {name = "Arkane Explosion", description = "[q]Lässt eine Druckwelle aus Magie entstehen und fügt in der Nähe befindlichen Feinden Arkanschaden zu."},
[54916] = {name = "Blitzexplosion", description = "[q]Ruft einen Blitzschlag hernieder, der allen Gegnern in der Nähe des Zaubernden Naturschaden zufügt."},
[54919] = {name = "Verformte Rüstung", description = "[q]Verringert die Rüstung 10 sec lang um 50%."},
[54921] = {name = "Blitzwolke", description = "[q]Umgibt den Zaubernden mit einer Gewitterwolke, die eine Chance hat, bei Feinden im Nah- oder Distanzkampfbereich Naturschaden zu verursachen."},
[54951] = {name = "Spießen", description = "[q]Fügt einem Feind 12 sec lang alle 3 Sek. körperlichen Schaden zu."},
[54954] = {name = "Explosion", description = "[q]Fügt nahen Feinden Feuerschaden zu."},
[54956] = {name = "Aufspießender Ansturm", description = "[q]Stürmt auf einen Gegner zu, verursacht normalen Schaden plus 750 und stößt ihn zurück."},
[54959] = {name = "Terrorstampfen", description = "[q]Stößt alle Feinde im Umkreis von 8 Metern um den Zaubernden zurück und verursacht 525 to 675 Schaden."},
[54962] = {name = "Tickende Bombe", description = "[q]Explodiert nach ein paar Sekunden und verursacht 231 to 268 Feuerschaden.\n Tick-tack."},
[54965] = {name = "Bolthorns Rune der Flammen", description = "[q]Verursacht bei nahen Feinden alle 2 Sek. Feuerschaden."},
[54970] = {name = "Giftblitz", description = "[q]Verursacht bei einem Feind Naturschaden."},
[54983] = {name = "Werfen", description = "[q]Wirft Eure Waffe auf ein Ziel, verursacht körperlichen Schaden."},
[54987] = {name = "Giftiger Biss", description = "[q]Fügt einem Feind 1018 to 1182 Naturschaden zu und dann 8 sec lang alle 2 Sek. zusätzlich 694 to 806 Schaden."},
[54996] = {name = "Eisscherbe", description = "[q]Der Schnee in einem angewählten Gebiet wird blitzgefroren, um sturmgeschmiedete Verteidiger zu verlangsamen. (20 Sek. Abklingzeit)"},
[54997] = {name = "Netz werfen", description = "[q]Macht einen sturmgeschmiedeten Verfolger 6 sec lang unbeweglich. (10 Sek. Abklingzeit)"},
[55007] = {name = "Hammer werfen", description = "[q]Fügt einem Gegner Schaden zu."},
[55030] = {name = "Runendetonation", description = "[q]Bewegungsunfähig.\n Verursacht nach Ablauf von 4 sec 6750 to 8250 Feuerschaden."},
[55040] = {name = "Eiskältefalle", description = "[q]Eine Eiskältefalle platzieren, die den ersten sich nähernden Feind einfriert und 1 min lang jegliche Aktion des Feindes verhindert. Jeglicher erlittene Schaden wird das Eis wieder brechen. Die Falle bleibt 1 min lang bestehen. Es kann immer nur jeweils eine Falle aktiv sein."},
[55041] = {name = "Freezing Trap Effect", description = "[q]Eine Eiskältefalle platzieren, die den ersten sich nähernden Feind einfriert und 1 min lang jegliche Aktion des Feindes verhindert. Jeglicher erlittene Schaden wird das Eis wieder brechen. Die Falle wird 1 min lang bestehen bleiben. Es kann immer nur eine Falle aktiv sein."},
[55046] = {name = "Eissplitter", description = "[q]Befreit eine gefangene Brunnhildar aus einem Gefängnis von Dun Niffelem."},
[55063] = {name = "Schiedsgericht", description = "[q]Richtet über den gefallenen Vrykul und lässt ihn entweder nach Ymirjar aufsteigen oder verwandelt ihn in einen Vargul."},
[55065] = {name = "Einstürzender Turm", description = "[q]Wirft den Gegner zurück."},
[55066] = {name = "Wildwuchs", description = "[q]Verursacht alle 3 Sek. 111 to 129 Naturschaden und verringert das Bewegungstempo 12 sec lang um 15%"},
[55071] = {name = "Flammen speien", description = "[q]Verursacht alle 1 Sek. 264 to 306 Feuerschaden."},
[55077] = {name = "Anspringen", description = "[q]Springt an den Standort des Gegners. Zudem wird 5 sec lang der körperliche Schaden um 200% erhöht."},
[55078] = {name = "Blutseuche", description = "[q]Eine Krankheit, die alle 3 Sek. Schattenschaden verursacht. Hält 21 sec lang an."},
[55079] = {name = "Sturzflug", description = "[q]Verursacht alle 1.5 Sek. 125% Waffenschaden."},
[55081] = {name = "Giftnova", description = "[q]Fügt Gegnern in der Nähe 2590 to 3010 Naturschaden zu und verursacht anschließend 16 sec lang alle 2 Sek. 1064 to 1236 zusätzlichen Schaden."},
[55087] = {name = "Taifun", description = "[q]Beschwört einen wilden Taifun, der an feindlichen Zielen 1064 to 1236 Naturschaden verursacht."},
[55093] = {name = "Slad'rans Umklammerung", description = "[q]Slad'rans Umklammerung würgt den Gegner. Sobald der Effekt 5-mal gestapelt wurde, wird das Ziel von Schlangen umschlungen."},
[55095] = {name = "Frostfieber", description = "[q]Eine Krankheit, die alle 3 Sek. Frostschaden verursacht und das Nahkampf- und Distanzangriffstempo des Ziels um 20% verringert. Hält 21 sec lang an."},
[55098] = {name = "Transformation", description = "[q]Der Zaubernde verwandelt sich in ein mächtiges Mammut, verursacht dabei um 25% erhöhten körperlichen Schaden und erhält Immunität gegen Betäubungseffekte."},
[55100] = {name = "Betäubendes Brüllen", description = "[q]Ein lautes Brüllen benebelt die Gedanken aller Gegner, die ihn hören, und verringert das Zaubertempo 5 sec lang um 50%."},
[55101] = {name = "Beben", description = "[q]Die Erde wird von einem gewaltigen Beben erschüttert und Gegnern in der Nähe körperlicher Schaden zugefügt."},
[55102] = {name = "Entschlossenes Aufspießen", description = "[q]Spießt einen Gegner auf, fügt ihm körperlichen Schaden und eine blutende Wunde zu, die 21 sec lang zusätzlichen Schaden verursacht. Dieser Angriff kann weder geblockt oder pariert werden, noch ist ein Ausweichen möglich."},
[55104] = {name = "Entschlossener Stich", description = "[q]Ersticht einen Gegner und verursacht körperlichen Schaden. Dieser Angriff kann weder geblockt oder pariert werden, noch ist ein Ausweichen möglich."},
[55106] = {name = "Betäubender Schrei", description = "[q]Ein lautes Brüllen benebelt die Gedanken aller Gegner in Hörweite und verringert ihr Zaubertempo 5 sec lang um 25%."},
[55128] = {name = "Schlangenumschlingung", description = nil},
[55142] = {name = "Bebende Erde", description = "[q]Der Boden erzittert und fügt Gegnern in der Nähe körperlichen Schaden zu."},
[55163] = {name = "Mojoraserei", description = "[q]Erhöht das Zaubertempo proportional zum erlittenen Schaden."},
[55193] = {name = "Rhinozerosansturm", description = "[q]Erhöht das Bewegungstempo des Zaubernden um 75% und lässt ihn bei seinem ersten Angriff zusätzlich 800 Schaden zufügen."},
[55196] = {name = "Stampfen", description = "[q]Verursacht 100 Schaden und verringert die Rüstung des Ziels um 50%."},
[55209] = {name = "Todesmantel", description = "[q]Verursacht bei einem zufälligen nahen Ziel 5000 Schattenschaden."},
[55212] = {name = "Blutpräsenz", description = "[q]Stärkt den Todesritter mit der Präsenz des Blutes und erhöht seinen Schaden um 15%."},
[55213] = {name = "Unheilige Raserei", description = "[q]Versetzt eine freundliche Einheit 30 sec lang in einen Blutrausch, erhöht ihren verursachten körperlichen Schaden um 40%, lässt sie jedoch pro Sekunde 1% ihrer maximalen Gesundheit verlieren."},
[55214] = {name = "Tempoexplosion", description = "[q]Erhöht das Flugtempo 8 sec lang um 100%."},
[55216] = {name = "Lawine", description = "[q]Verursacht 1110 to 1290 Frostschaden."},
[55217] = {name = "Speer werfen", description = "[q]Wirft eine Waffe nach einem Feind und fügt körperlichen Schaden zu."},
[55218] = {name = "Stampede", description = "[q]Beschwört einen Rhinozerosgeist, der auf Euer Ziel zustürmt, 4713 to 5287 Schaden verursacht und es in die Luft stößt."},
[55222] = {name = "Unheilige Präsenz", description = "[q]Erhöht das Nahkampfangriffstempo um 15%."},
[55240] = {name = "Überragende Ketten", description = "[q]Umschlingt das Ziel mit Ketten, die alle 1 Sek. 800 körperlichen Schaden verursachen und es 5 sec lang betäuben."},
[55249] = {name = "Wirbelndes Schlitzen", description = "[q]Schlägt in einem Wirbel aus Stahl um sich, fügt allen nahen Feinden 2828 to 3172 körperlichen Schaden zu und lässt sie 9 sec lang alle 3 Sek. für zusätzlich 1000 Schaden bluten."},
[55255] = {name = "Seuchenstoß", description = "[q]Ein bösartiger Schlag, der Waffenschaden verursacht, das Ziel verseucht und im Verlauf von 12 sec 8000 Schattenschaden hervorruft."},
[55264] = {name = "Blutseuche", description = "[q]Ein bösartiger Schlag, der Waffenschaden verursacht, das Ziel verseucht und im Verlauf von 12 sec 8000 Schattenschaden hervorruft."},
[55266] = {name = "Wirbelwind", description = "[q]Greift Gegner in der Nähe in einem Wirbelwind aus Stahl an, der 6 sec lang anhält und bei jeder Drehung 100% des normalen Schadens verursacht."},
[55269] = {name = "Tödliches Starren", description = "[q]Feuert einen Strahl Schattenenergie auf die Feinde des Oculars."},
[55276] = {name = "Durchstechen", description = "[q]Bohrt den Speer des Zaubernden in das Ziel, umgeht dabei die Rüstung und verursacht 4713 to 5287 körperlichen Schaden. Zusätzlich dazu erleidet das Ziel aufgrund seiner Blutungen 2 min lang alle 3 Sek. 500 zusätzlichen Schaden."},
[55285] = {name = "Wutanfall", description = "[q]Erhöht 8 sec lang das Angriffstempo um 100%."},
[55292] = {name = "Stampfen", description = "[q]Stampft auf den Boden, verursacht 4713 to 5287 körperlichen Schaden und stößt Gegner vom Zaubernden zurück."},
[55294] = {name = "Vom Todesstreitross absitzen", description = "[q]Beschwört ein Todesstreitross."},
[55313] = {name = "Eisige Berührung", description = "[q]Verursacht 3781 to 4219 Frostschaden und verringert 8 sec lang das Angriffs-, Distanzangriffs- und Zaubertempo des Ziels um 35%."},
[55314] = {name = "Strangulieren", description = "[q]Verursacht 4 sec lang pro Sekunde 1000 Schattenschaden. Bringt das Ziel zum Schweigen und hindert es 4 sec lang daran, Zauber zu wirken."},
[55315] = {name = "Knochenrüstung", description = "[q]Hüllt den Zaubernden in eine Knochenrüstung, welche 1 min lang 50000 Schaden absorbiert. Während die Rüstung vorhält, kann das Zauberwirken nicht durch körperliche Angriffe unterbrochen werden."},
[55317] = {name = "Sturmangriff", description = "[q]Stürmt auf einen Gegner zu und verursacht 35% des normalen Schadens."},
[55318] = {name = "Rüstung durchstechen", description = "[q]Verringert die Rüstung eines Feindes 10 sec lang um 50%."},
[55319] = {name = "Brutaler Prankenhieb", description = "[q]Verursacht 28500 to 31500 körperlichen Schaden, der unter allen betroffenen Zielen gleichmäßig aufgeteilt wird."},
[55348] = {name = "Werfen", description = "[q]Wirft Eure Waffe auf ein Ziel und verursacht 2357 to 2643 körperlichen Schaden."},
[55416] = {name = "Leicht wie eine Feder", description = "[q]Verringert 1 min lang das Falltempo."},
[55421] = {name = "Gymers Wurf", description = "[q]Wirft den Vargul, den Gymer in seiner Hand hält. Explodiert beim Aufschlag!"},
[55426] = {name = "Gymers Zerkrachen", description = "[q]Gymer schmettert seine Faust auf den Boden und verursacht bei allen Feinden im Umkreis von 12 Metern 5363 to 7637 Schaden."},
[55429] = {name = "Gymers Brüllen", description = "[q]Gymer stößt ein ohrenbetäubendes Brüllen aus, das alle Feinde vor ihm zurückstößt."},
[55463] = {name = "Wirbelwind", description = "[q]Greift in der Nähe befindliche Feinde mit einem Wirbel aus Stahl an, der 2 sec lang anhält und 100% Waffenschaden verursacht."},
[55470] = {name = "Schlag des Ungleichgewichts", description = "[q]Verursacht 350% Waffenschaden und bringt das Ziel aus dem Gleichgewicht. Reduziert die Verteidigungsfertigkeit des Ziels 6 sec lang um 100."},
[55512] = {name = "Ruf der Erde", description = "[q]Beschwört eine Gruppe Irdene, damit sie einen Eisenriesen oder Feuerriesen nahe Dun Niffelem umringen."},
[55516] = {name = "Gymers Griff", description = "[q]Gymer greift sich einen Vargul in Reichweite und benutzt ihn als lebende Waffe! Auch nützlich, um sich Sturmwolken zu greifen, mit denen Gymer seine Gesundheit und Energie wieder auffüllen kann."},
[55521] = {name = "Vergifteter Speer", description = "[q]Verursacht 2357 to 2643 Naturschaden und zusätzlich 21 sec lang alle 3 Sek. 943 to 1057 Naturschaden."},
[55530] = {name = "Sturmangriff", description = "[q]Stürmt auf einen Gegner zu und verursacht normalen Schaden plus einen Bonus."},
[55550] = {name = "Gezacktes Messer", description = "[q]Wirft einen Dolch auf einen Feind, verursacht 5088 to 5912 körperlichen Schaden und 5 sec lang alle 1 Sek. weitere 2000 Schaden."},
[55563] = {name = "Bodenschmettern", description = "[q]Fügt nahen Gegnern normalen Schaden plus 35 zu und stößt sie zurück."},
[55567] = {name = "Kraftvoller Schlag", description = "[q]Fügt einem Gegner 150% Waffenschaden zu."},
[55582] = {name = "Manaverbindung", description = "[q]Stellt eine Manaverbindung zum Ziel her, durch die von Euch gewirkte Zauber aus dem Manapool des verbundenen Ziels zehren, anstatt Eurem eigenen. Hält 20 sec lang an."},
[55593] = {name = "Nekrotische Aura", description = "[q]Eine Welle nekrotischer Energien erfüllt den Raum, verhindert 17 sec lang das Wirken von jeglichen Heileffekten."},
[55597] = {name = "Welle der Heilung", description = "[q]Heilt das befreundete Ziel und stellt 13875 to 16125 Gesundheit wieder her."},
[55598] = {name = "Magie läutern", description = "[q]Bannt Magie von einem Verbündeten und entfernt 1 schädlichen Zauber."},
[55599] = {name = "Erdschild", description = "[q]Schützt das Ziel mit einem Erdschild. Nahkampfangriffe heilen das geschützte Ziel, dieser Effekt tritt aber nur einmal alle paar Sekunden auf. 6 Aufladungen. Hält 10 min lang an."},
[55602] = {name = "Heimtückischer Biss", description = "[q]Beißt einen Gegner, fügt körperlichen Schaden zu."},
[55604] = {name = "Todesseuche", description = "[q]Fügt einem Feind 15 sec lang alle 3 Sek. Naturschaden zu."},
[55605] = {name = "Durchbohrender Stoß", description = "[q]Fügt einem Feind 12 sec lang alle 3 Sek. körperlichen Schaden zu."},
[55606] = {name = "Unheilige Aura", description = "[q]Lässt den Zaubernden alle 2 Sek. bei allen nahen Feinden automatisch 350 Schattenschaden verursachen. Die Aura hält bis Abbruch an."},
[55613] = {name = "Flammenschock", description = "[q]Verbrennt sofort einen Feind und fügt ihm dann 12 sec lang alle 3 Sek. zusätzlichen Feuerschaden zu."},
[55622] = {name = "Durchbohren", description = "[q]Verursacht bei einem Feind körperlichen Schaden und lässt ihn 8 sec lang alle 2 Sek. bluten."},
[55624] = {name = "Arkaner Schuss", description = "[q]Schießt auf einen Gegner, fügt Arkanschaden zu."},
[55625] = {name = "Einlullender Schuss", description = "[q]Versucht, 1 Wuteffekt vom Ziel zu entfernen."},
[55626] = {name = "Mojowelle", description = "[q]Schickt in einer Geraden eine Mojowelle durch Feinde, verursacht 950 to 1050 Naturschaden und 9 sec lang alle 3 Sek. weiteren Schaden."},
[55627] = {name = "Mojopfütze", description = "[q]Verursacht 10 sec lang in einem kleinen Gebiet 8080 to 8920 Naturschaden."},
[55633] = {name = "Steinerner Körper", description = "[q]Erhöht 12 sec lang den verursachten körperlichen Schaden des Zaubernden um 50%, macht ihn jedoch bewegungsunfähig und erhöht die Zeit zwischen seinen Angriffen um 50%."},
[55635] = {name = "Donnerknall", description = "[q]Verursacht Naturschaden bei nahen Gegnern, erhöht die Zeit zwischen ihren Angriffen um 25% und verlangsamt ihr Bewegungstempo. Hält 10 sec lang an."},
[55636] = {name = "Schockwelle", description = "[q]Schickt dem Golem eine Druckwelle voraus, die 2 sec lang 3150 to 3850 Schaden verursacht und in einem kegelförmigen Bereich von 10 Metern alle gegnerischen Ziele betäubt."},
[55643] = {name = "Erbrechen", description = "[q]Verursacht bei einem Feind alle 1 Sek. 2400 Naturschaden. Entzieht zudem alle 1 Sek. 240 Mana."},
[55644] = {name = "Stampede!", description = "[q]Das sind ziemlich große Mammuts!"},
[55652] = {name = "Federn", description = "[q]Springt auf ein entferntes Ziel zu."},
[55659] = {name = "Lavaeruption", description = "[q]Ihr schleudert flüssige Lava auf das Ziel, die 1332 to 1698 Feuerschaden verursacht. Ist das Ziel von 'Flammenschock' betroffen, wird 'Lavaeruption' den Flammenschock aufzehren und den doppelten Schaden verursachen."},
[55663] = {name = "Ohrenbetäubendes Gebrüll", description = "[q]Fügt 1425 to 1575 körperlichen Schaden zu und bringt den Feind 4 sec lang zum Schweigen."},
[55696] = {name = "Schwanzfeger", description = "[q]Fügt Feinden in einem kegelförmigen Bereich hinter dem Zaubernden 2188 to 2812 Schaden zu und stößt sie zurück."},
[55700] = {name = "Giftspucke", description = "[q]Spuckt Gift auf den Feind, fügt ihm Naturschaden und dann 10 sec lang alle 2 Sek. zusätzlichen Schaden zu."},
[55703] = {name = "Kobrastoß", description = "[q]Schlägt einen Feind und verursacht 4163 to 4837 körperlichen Schaden."},
[55704] = {name = "Lavaeruption", description = "[q]Ihr schleudert flüssige Lava auf das Ziel, die Feuerschaden verursacht."},
[55706] = {name = "Sinistrer Schild", description = "[q]Verringert jeglichen erlittenen Schaden um 20%."},
[55715] = {name = "Zerrissene Erde", description = "[q]Ein Erdstachel verursacht 1850 to 2150 Naturschaden und schleudert den Feind in die Luft."},
[55716] = {name = "Victorious Challenger Dummy", description = nil},
[55798] = {name = "Leuchtfeuer", description = "[q]Gibt 30 sec lang alle verborgenen und unsichtbaren Feinde innerhalb von 10 Metern im Zielgebiet preis."},
[55811] = {name = "Brocken werfen", description = "[q]Schleudert einen Brocken auf einen Feind und fügt körperlichen Schaden zu."},
[55812] = {name = "Flammenharpune", description = "[q]Zielt auf Heuhaufen neben den Gebäuden von Valkyrion, um Feuer auszulösen."},
[55813] = {name = "Eckbiss", description = "[q]Eck beißt fest zu, verursacht bei einem Feind 150% seines normalen Schadens."},
[55814] = {name = "Eckspucken", description = "[q]Eck spuckt giftige Galle auf Feinde in einem kegelförmigen Bereich vor ihm. Verursacht 2970 Naturschaden und entzieht 3 sec lang alle 1 Sek. 220 Mana."},
[55815] = {name = "Eckspringen", description = "[q]Eck springt auf ein entferntes Ziel zu."},
[55816] = {name = "Eckberserker", description = "[q]Eck wird zum Berserker, erhöht sein Angriffstempo um 150% und seinen verursachten Schaden um 500%."},
[55821] = {name = "Massives Stampfen", description = "[q]Stampft auf den Boden, verursacht bei allen nahen Feinden 6300 to 7700 körperlichen Schaden und stößt sie zurück."},
[55825] = {name = "Gewitter", description = "[q]Ihr ruft einen Blitzschlag hernieder, der bei allen Feinden im Umkreis von 10 Metern 1027 to 1173 Naturschaden verursacht."},
[55847] = {name = "Schattenleere", description = "[q]Erzeugt einen Schattensturm, der 10 sec lang alle 1 Sek. 1388 to 1612 Schattenschaden verursacht."},
[55848] = {name = "Unsichtbarkeit", description = "[q]Lässt den Zaubernden unsichtbar werden."},
[55850] = {name = "Schattenblitzsalve", description = "[q]Schleudert Geschosse von dunkler Magie auf in der Nähe befindliche Feinde und verursacht Schattenschaden."},
[55860] = {name = "Schaufelhaueransturm", description = "[q]Erhöht das Bewegungstempo 5 sec lang um 60%."},
[55867] = {name = "Stinkender Bart", description = "[q]Verringert 30 sec lang die Ausdauer um 1%.\n\n\"Von innen hat er noch widerwärtiger gerochen...\""},
[55872] = {name = "Kugel der Flamme", description = "[q]Schleudert einen massive Feuerkugel."},
[55876] = {name = "Seuchenkontamination", description = "Erleidet alle 1 Sek. 3325 to 3675 Naturschaden."},
[55881] = {name = "Hurrikan", description = "[q]Erzeugt einen wütenden Sturm im Zielgebiet, der allen in der Nähe befindlichen Feinden alle 5 Sek. Naturschaden zufügt und die Zeit zwischen Angriffen betroffener Feinde um 67% erhöht. Hält 10 sec lang an."},
[55882] = {name = "Kugel des Stürme", description = "[q]Schleudert einen massive Blitzschlagkugel."},
[55886] = {name = "Brocken", description = "[q]Schleudert einen massiven Brocken nach einem Feind."},
[55888] = {name = "Kugel des Wassers", description = "[q]Schleudert einen massive Wasserkugel."},
[55889] = {name = "Kugel des Frosts", description = "[q]Verursacht beim Ziel 800 Frostschaden und verringert sein Bewegungstempo um 20%."},
[55909] = {name = "Brechende Welle", description = "[q]Eine Wasserwelle spült in einem kegelförmigen Bereich von 65 Metern über Feinde hinweg und verursacht 2313 to 2687 Frostschaden."},
[55912] = {name = "Luftstoß", description = "[q]Ein mächtiger Windstoß weht alle Gegner davon."},
[55916] = {name = "Magmawelle", description = "[q]Eine Welle von Magma rollt in einem kegelförmigen Bereich von 65 Metern über Feinde hinweg und verursacht 2313 to 2687 Feuerschaden."},
[55918] = {name = "Schockwelle", description = "[q]Schickt dem Krieger eine Druckwelle voraus, die (25 / 100 * Attack power) Schaden verursacht und in einem kegelförmigen Bereich von 10 Metern alle gegnerischen Ziele 4 sec lang betäubt."},
[55929] = {name = "Durchbohren", description = "[q]Stürmt das Ziel an und spießt es auf."},
[55931] = {name = "Flammensphäre beschwören", description = "[q]Beschwört eine Sphäre reinen Feuers, die auf das Ziel zuschwebt und Sengstrahlen auf Feinde abgibt, die sich in einem Umkreis von 18 Metern befinden."},
[55936] = {name = "Sturzflug", description = "[q]Stürzt sich auf einen Sturmgipfelwyrm und verursacht 5850 to 7150 Schaden."},
[55937] = {name = "Fischiger Geruch", description = "[q]Ihr seid mit dicker Heilbuttsoße zugekleistert."},
[55946] = {name = "Schnauzer beschwören", description = "[q]Ruft seinen treuen Begleiter Schnauzer."},
[55948] = {name = "Wachsen", description = "[q]Wächst, erhöht den verursachten Schaden um 10%."},
[55958] = {name = "Sturmblitz", description = "[q]Schleudert einen Sturmhammer auf einen Sturmgipfelwyrm, verursacht 8750 to 11250 Schaden und betäubt das Ziel 4 sec lang, bevor der Hammer zur Hand des Werfers zurückkehrt."},
[55959] = {name = "Umarmung des Vampyrs", description = "[q]Verschlingt die Lebenskraft des Ziels, betäubt es und verursacht 20 sec lang alle 2 Sek. 1500 Schattenschaden, oder bis Prinz Taldaram 20.000 Schaden erlitten hat."},
[55960] = {name = "Kill Credit", description = nil},
[55964] = {name = "Verschwinden", description = "[q]Versetzt den Zaubernden 2.50 sec lang in den Verstohlenheitsmodus."},
[55968] = {name = "Blutdurst", description = "[q]Greift das Ziel sofort an, verursacht 120% des normalen Waffenschadens. Zusätzlich stellen die nächsten 3 erfolgreichen Nahkampfangriffe 1% Gesundheit wieder her. Der Effekt hält 10 sec lang an."},
[55973] = {name = "Blutseuche", description = "[q]Ein bösartiger Schlag, der Waffenschaden verursacht, das Ziel verseucht und im Verlauf von 12 sec 2000 Schattenschaden hervorruft."},
