[55982] = {name = "Mammutsturmangriff", description = "[q]Stürmt auf einen Gegner zu, verursacht normalen Schaden plus 2647 to 2953 und betäubt ihn 3 sec lang."},
[55984] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Gegner, verursacht Schattenschaden und erhöht 10 sec lang erlittenen Schattenschaden um 10%."},
[55987] = {name = "Verschlingender Feuerball", description = "[q]Feuert einen verschlingenden Feuerball auf feindliche Ziele, verursacht 3063 to 3937 Schaden an allen Ghulen und 10500 to 13500 Schaden an Riesen."},
[56036] = {name = "Rune der Zerstörung", description = "[q]Markiert einen Feind 5 sec lang mit einer feurigen Rune, welche nach Ablauf von 5 Sek. Schaden verursacht."},
[56038] = {name = "Seuchenblitz", description = "[q]Ein Seuchengeschoss direkt aus Varguls verkümmertem Herz. Genutzt, um Feinde zu foltern. Verursacht 833 to 967 Schattenschaden."},
[56061] = {name = "Auslöschen", description = nil},
[56062] = {name = "Donnerndes Stampfen", description = "[q]Entsendet eine Druckwelle, die 3150 to 3850 Schaden verursacht und alle feindlichen Ziele innerhalb von 10 Metern nach hintern schleudert."},
[56063] = {name = "Arkane Explosion", description = "[q]Lässt eine Druckwelle aus Magie entstehen und fügt in der Nähe befindlichen Feinden 1350 to 1650 Arkanschaden zu."},
[56064] = {name = "Schattenblitzsalve", description = "[q]Schleudert Geschosse aus dunkler Magie und verursacht bei in der Nähe befindlichen Gegnern 1575 to 1925 Schattenschaden."},
[56091] = {name = "Flammenstachel", description = "[q]Feuert einen schnellen Flammenstrahl auf Euer Ziel, verursacht 943 to 1057 Feuerschaden. Gewährt 1 Combopunkt."},
[56092] = {name = "Flammenhülle", description = "[q]Verursacht einen Flammenschlag, der beim Ziel alle 3 Sek. 1500 Feuerschaden verursacht. Dieser Effekt ist stapelbar. Hält pro Combopunkt länger an:\n 1 Punkt: 6 Sekunden\n 2 Punkte: 10 Sekunden\n 3 Punkte: 14 Sekunden\n 4 Punkte: 18 Sekunden\n 5 Punkte: 22 Sekunden"},
[56095] = {name = "Frostpfeil", description = "[q]Feuert einen Pfeil aus Eis, der massiven Schaden verursacht und 6 sec lang das Bewegungstempo verringert."},
[56105] = {name = "Vortex", description = "[q]Erzeugt einen wirbelnden Vortex arkaner Energie, der bei allen darin gefangenen Gegnern Arkanschaden verursacht."},
[56107] = {name = "Berserkeransturm", description = "[q]Attackiert einen Feind, stößt ihn zurück und fügt ihm normalen Schaden plus 5100 to 6900 zu."},
[56130] = {name = "Brutseuche", description = "[q]Steckt den Feind mit einer Krankheit an und verursacht im Verlauf von 30 sec 12750 Naturschaden."},
[56149] = {name = "Tödliches Gift", description = "[q]Fügt 12 sec lang alle 3 Sek. Naturschaden zu."},
[56152] = {name = "Energiefunke", description = "[q]Erhöht den verursachten Schaden des Ziels zum 50%, opfert den Zaubernden während des Vorgangs."},
[56153] = {name = "Wächteraura", description = "[q]Schützt Schwärmer in einem großen Radius um den Zaubernden und macht sie immun gegen Schaden."},
[56219] = {name = "Gabe des Herolds", description = "[q]Erhöht 20 sec lang den verursachten Schaden des Zaubernden um 200%."},
[56223] = {name = "Netzseil", description = nil},
[56272] = {name = "Arkanodem", description = "[q]Verursacht in einem kegelförmigen Bereich von 40 Metern vor dem Zaubernden 18850 to 21150 Arkanschaden. 5 sec nachdem sie Schaden erlitten haben, explodieren die betroffenen Ziele vor Energie und verursachen an nahen Verbündeten 9425 to 10575 Arkanschaden."},
[56310] = {name = "Gebrochener Schwur", description = "[q]Erhöht 10 sec lang das Angriffstempo des Zaubernden um 30% und den von ihm verursachten körperlichen Schaden um 10%."},
[56319] = {name = "Kugelblitz", description = "[q]Verursacht bei allen Feinden, die den Bereich betreten im Verlauf von 8 sec alle 1 Sek. 850 Naturschaden."},
[56322] = {name = "Funkenfuror", description = "[q]Funken sprühen vom Zaubernden in alle Richtungen, verursachen pro Funke 329 to 381 Naturschaden."},
[56326] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm Naturschaden zu."},
[56330] = {name = "Eisens Bann", description = "[q]Verringert 2 min lang die Kosten von Zaubern und Fähigkeiten um 50% und erhöht den verursachten Schaden um 25%."},
[56352] = {name = "Sturmschlag", description = "[q]Verursacht 120% Waffenschaden und 12 sec lang alle 3 Sek. weitere 300 Naturschaden."},
[56354] = {name = "Sprinten", description = "[q]Erhöht 15 sec lang das Bewegungstempo des Zaubernden um 200%."},
[56356] = {name = "Eisenwolleumhang", description = "[q]Verringert 6 sec lang den erlittenen Schaden um 10%."},
[56397] = {name = "Arkanbeschuss", description = "[q]Schleudert mehrere Geschosse auf das feindliche Ziel und verursacht 0 Arkanschaden."},
[56402] = {name = "Krachendes Schmettern", description = "[q]Springt durch die Luft, kracht auf alle Gegner in der Nähe nieder und verursacht 1018 to 1182 körperlichen Schaden."},
[56408] = {name = "Wirbelwind", description = "[q]Greift in der Nähe befindliche Feinde mit einem Wirbel aus Stahl an, der normalen Schaden verursacht."},
[56410] = {name = "Blinder Glaube", description = "[q]Erhöht 2 min lang die kritische Trefferchance um 20%."},
[56418] = {name = "Aus dem Schnee auftauchen", description = nil},
[56425] = {name = "Griff der Erde", description = "[q]Umgibt den Feind mit Stein und verursacht bis zu 10 sec lang Bewegungsunfähigkeit."},
[56426] = {name = "Hinrichten", description = "[q]Versucht, einen verwundeten Feind ganz zu erledigen, fügt 120% normalen Schaden zu. 'Hinrichten' kann nur bei Feinden angewendet werden, die über 20% oder weniger Gesundheit verfügen."},
[56451] = {name = "Erdschild", description = "[q]Schützt das Ziel mit einem Erdschild. Nahkampfangriffe heilen das geschützte Ziel, dieser Effekt tritt aber nur einmal alle paar Sekunden auf. 10 Aufladungen. Hält 2 min lang an."},
[56490] = {name = "Stampfen", description = "[q]Erschüttert den Boden, fügt in der Nähe befindlichen Feinden normalen Schaden zu und stößt sie zurück."},
[56491] = {name = "Lavaeruption", description = "[q]Ihr schleudert flüssige Lava auf das Ziel, die Feuerschaden verursacht."},
[56504] = {name = "Untertauchen", description = "[q]Verschwindet unter der Schneedecke und wird mobil."},
[56505] = {name = "Woge der Kraft", description = "[q]Feuert einen Strahl reiner Energie auf einen Gegner und verursacht 5 sec lang 5000 Arkanschaden pro Sekunde bei allen nahen Gegnern."},
[56506] = {name = "Erdschock", description = "[q]Schockt einen Gegner mit erschütternder Kraft, verursacht Naturschaden und unterbricht 2 sec lang den gewirkten Zauber."},
[56508] = {name = "Auftauchen", description = "[q]Steigt aus dem Erdboden empor. Euer Jormungar ist an Ort und Stelle verwurzelt, aber fähig anzugreifen."},
[56513] = {name = "Jormungarstoß", description = "[q]Ein heftiger Angriff, der 5775 to 7425 Schaden verursacht und ein hohes Maß an Bedrohung erzeugt. (2 Sek. Abklingzeit)"},
[56520] = {name = "Segen der Macht", description = "[q]Belegt ein befreundetes Ziel mit einem Segen und erhöht 30 min lang die Angriffskraft um 350. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein."},
[56524] = {name = "Säureatem", description = "[q]Speit Säure auf vor Euch befindliche Feinde, verursacht 1330 to 1470 Schaden sowie 15 sec lang alle 2 Sek. 855 to 945 Schaden. (6 Sek. Abklingzeit)"},
[56580] = {name = "Klebriges Gift", description = "[q]Vergiftet einen Feind, verursacht 1000 Naturschaden und erhöht 45 sec lang die Kosten von Zaubern und Fähigkeiten um 10%."},
[56581] = {name = "Tödlicher Stich", description = "[q]Sticht einen Feind und fügt ihm 12 sec lang alle 2 Sek. Naturschaden zu. Die Kreatur muss sehr schwer verwundet sein, um diese Fähigkeit anwenden zu können."},
[56584] = {name = "Kombinierte Toxine", description = "[q]Toxine im Blut des Tiefenkriechers bedecken Eure Waffen und vermischen sich mit Euren Giften, und gewähren so die Chance, zusätzlichen Naturschaden zu verursachen, wenn Eure Gifte einem Gegner Schaden zufügen."},
[56605] = {name = "Virulentes Gift", description = "[q]Verursacht alle 3 Sek. 2500 Naturschaden. Hält 24 sec lang an. Bis zu 3-mal stapelbar."},
[56620] = {name = "Lodernde Flammen", description = "[q]Verursacht 3 sec lang alle 2.79900002 Sek. 197 to 253 Feuerschaden."},
[56621] = {name = "Donnerkugel", description = "[q]Schleudert einen Kugelblitz auf ein Ziel und verursacht 1651 to 2349 Schaden."},
[56625] = {name = "Raserei", description = "[q]Erhöht 2 min lang das Angriffstempo des Zaubernden um 50% und den von ihm verursachten körperlichen Schaden um 50%."},
[56632] = {name = "Verworrene Netze", description = "[q]Schießt klebrige Spinnweben auf die Füße aller Gegner innerhalb von 25 Metern und wurzelt sie 8 sec lang fest."},
[56640] = {name = "Netz ergreifen", description = "[q]Schießt einen dicken Strang Spinnweben auf ein Ziel, der 1650 to 1850 Schaden zufügt, Gegner einfängt und sie in Richtung des Zaubernden zieht."},
[56643] = {name = "Dreifachhieb", description = "[q]Schlägt einen Feind drei mal, verursacht pro Schlag 50% des normalen Waffenschadens."},
[56644] = {name = "Eisblock", description = nil},
[56646] = {name = "Wutanfall", description = "[q]Erhöht 6 sec lang das Angriffstempo des Zaubernden um 30%."},
[56648] = {name = "Potenter Pilz", description = "[q]Erhöht den dem Gegner zugefügten Schaden um 100%."},
[56683] = {name = "Gefangenen Kreuzfahrer ergreifen", description = "[q]Eure Argentumhimmelsklaue wird versuchen, einen gefangenen Argentumkreuzfahrer zu ergreifen."},
[56684] = {name = "Gefangenen Kreuzfahrer absetzen", description = "[q]Setzt den gefangenen Argentumkreuzfahrer im Feldlazarett der Vorhut ab."},
[56690] = {name = "Speerstoß", description = "[q]Schlägt nach dem Wildwyrm und verursacht 1000 to 1300 körperlichen Schaden.\n Diese Fähigkeit lockert Euren Griff des Wyrms um 5."},
[56698] = {name = "Schattenschlag", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden, der 50% der maximalen Gesundheit des Zieles entspricht."},
[56702] = {name = "Schattensichel", description = "[q]Schleudert 6 sec lang gebogene Schattenklingen auf zufällige Feinde. Kanalisiert."},
[56704] = {name = "Klauen ausweichen", description = "[q]Zieht Euch nah an die Brust des Wyrms heran um 2 sec lang Klauenangriffe zu vermeiden."},
[56706] = {name = "Kiefer auseinanderdrücken", description = "[q]Öffnet gewaltsam das Maul des Wildwyrms und offenbart dessen Schlund. Steigert die Chance, mit 'Fataler Stoß' zu treffen, um 5% pro Anwendung. Hält 10 sec lang."},
[56707] = {name = "Verpestende Fäulnis", description = "[q]Erhöht die Zeit zwischen Angriffen eines Feindes um 33% und verlangsamt sein Zauberwirken um 25%, fügt außerdem eine Chance von 5% hinzu, die Verpestung bei einem Treffer auf einen Verbündeten zu verbreiten. Hält 2 min lang an."},
[56709] = {name = "Aura der verlorenen Hoffnung", description = "[q]Verringert die maximale Gesundheit von Gegnern in einem weiten Bereich um den Zaubernden alle paar Sekunden um 0%. Stapelbar."},
[56712] = {name = "Segeln", description = "[q]Erhöht das Flugtempo um 385%."},
[56715] = {name = "Gesundheit rauben", description = "[q]Stiehlt einem Gegner sofort 500 Gesundheit und überträgt sie auf den Zaubernden."},
[56716] = {name = "Eisige Winde", description = "[q]Eisige Winde umwehen 2 min lang den Zaubernden. Die Kälte verlangsamt das Zaubertempo um 50%, erhöht aber den Schaden von Frostzaubern um 75%."},
[56728] = {name = "Augen im Dunklen", description = "[q]Gestattet es dem Auge von Taldaram, die gewirkten Zauber des Ziels zu unterbrechen."},
[56729] = {name = "Rasender Sprung", description = "[q]Der Zaubernde springt auf das Ziel zu, die Angriffsgeschwindigkeit des Zaubernden ist um 200% erhöht."},
[56730] = {name = "Dunkler Gegenzauber", description = "[q]Verursacht 1000 Schattenschaden und unterbricht einen gewirkten Zauber. Verhindert, dass der Gegner 8 sec lang einen Zauber dieser Art wirken kann."},
[56733] = {name = "Schattenfuror", description = "[q]Der Schattenfuror wird entfesselt, verursacht 1369 to 1631 Schattenschaden und stößt until cancelled lang alle Gegner im Umkreis von 10 Metern zurück."},
[56736] = {name = "Trampeln", description = "[q]Verursacht körperlichen Schaden bei in der Nähe befindlichen Feinden."},
[56737] = {name = "Stampfen", description = "[q]Stampft auf den Boden und schleudert Feinde im Umkreis von 50 Metern in die Luft."},
[56746] = {name = "Fausthieb", description = "[q]Nergeld zwingen, auf den Boden zu schlagen, was Feinden innerhalb von 3 Metern 6660 to 7740 Schaden zufügt."},
[56747] = {name = "Stampfen", description = "[q]Lässt Nergeld auf den Boden stampfen und verursacht bei allen Feinden in einem Radius von 15 Metern 5941 to 8459 Schaden. Zusätzlich werden sie niedergestoßen und betäubt."},
[56748] = {name = "Brüllen", description = "[q]Nergeld benutzen, um einen mächtigen Schrei loszulassen, der 2971 to 4229 Schaden verursacht und alle Feinde innerhalb von 30 Metern vor ihm zurückstößt."},
[56750] = {name = "Schnee sammeln", description = "[q]Lässt Snorri Schnee von einer nahe gelegenen Schneewehe nehmen."},
[56753] = {name = "Schneeball werfen", description = "[q]Befiehlt Snorri einen Schneeball auf einen wutentbrannten Klagegeist zu werfen."},
[56778] = {name = "Manaschild", description = "[q]Hüllt den Zaubernden in einen Schutzschild, der 1 min anhält, bis zu 30000 körperlichen Schaden absorbiert und anstelle von Gesundheit Mana abzieht. Entzieht dem Ziel 1 Mana pro Punkt absorbiertem Schaden."},
[56785] = {name = "Flüchtige Infektion", description = "[q]Infiziert einen Gegner 30 sec lang. Das Ziel verursacht bei seinen nahen Verbündeten alle 3 Sek. Naturschaden."},
[56837] = {name = "Frostblitz", description = "[q]Fügt in der Nähe befindlichen Feinden Frostschaden zu und verringert 4 sec lang ihr Bewegungstempo."},
[56843] = {name = "Salve", description = "[q]Feuert kontinuierlich eine Salve Munition auf das Zielgebiet und verursacht 6 sec lang pro Sekunde 1157 to 1343 Arkanschaden bei feindlichen Zielen im Umkreis von 8 Metern."},
[56855] = {name = "Wirbelschlag", description = "[q]Führt einen wirbelnden Angriff durch, der allen in der Nähe befindlichen Feinden Waffenschaden plus 150% zufügt und sie zurückstößt."},
[56858] = {name = "Flammenstoß", description = "[q]Lässt eine Flammensäule niedergehen, die alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen 8 sec lang alle 2 Sek. zusätzlichen Schaden zufügt."},
[56861] = {name = "Windstoß", description = "[q]Ihr beschwört einen wilden Taifun, der feindlichen Zielen bei Kontakt 602 to 698 Naturschaden zufügt und sie 5 Meter zurückstößt."},
[56862] = {name = "Wasserblitz", description = "[q]Schleudert ein wässriges Geschoss auf einen Feind und fügt 124 to 145 Frostschaden zu."},
[56867] = {name = "Giftwolke", description = "[q]Fügt 10 sec lang in der Nähe befindlichen Feinden alle 2 Sek. 950 Naturschaden zu."},
[56891] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm Naturschaden zu."},
[56896] = {name = "Rhinozerosstoß", description = "[q]Ein starker Angriff."},
[56897] = {name = "Sturmruf", description = "[q]Verursacht am Zielort 5 sec lang alle 1 Sek. 2200 Naturschaden. (15 Sek. Abklingzeit)"},
[56898] = {name = "Verderbnis", description = "[q]Verdirbt in der Nähe befindliche Feinde und fügt ihnen 15 sec lang alle 3 Sek. Schattenschaden zu."},
[56908] = {name = "Flammenatem", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden 8750 to 11250 Feuerschaden zu."},
[56909] = {name = "Spalten", description = "[q]Verursacht bei einem Feind und seinen nächsten Verbündeten 35% Waffenschaden, wirkt auf bis zu 10 Ziele."},
[56910] = {name = "Schwanzpeitscher", description = "[q]Ein fegender Schwanzschlag trifft alle Gegner hinter dem Zaubernden, verursacht 3063 to 3937 Schaden und betäubt sie 2 sec lang."},
[56920] = {name = "Schattenwort: Tod", description = "[q]Fügt einem Gegner 4163 to 4837 Schattenschaden zu, jedoch erleidet der Zaubernde den gleichen Schaden."},
[56925] = {name = "Frostschlingen", description = "[q]Frostfeuertentakel umschlingen das Ziel und verursachen 2188 to 2812 Frostschaden."},
[56926] = {name = "Donnerschock", description = "[q]Lässt eine Blitzschlagwolke entstehen, die 10 sec anhält und allen Feinden im ausgewählten Gebiet 1575 to 2025 Naturschaden und alle 2 Sek. 1575 to 2025 zusätzlichen Schaden zufügt."},
[57055] = {name = "Mini", description = "[q]Verringert den Schaden in der Nähe befindlicher Gegner um 75% und verringert deren Verteidigung um 500."},
[57057] = {name = "Torvalds Abschreckung", description = "[q]Bei Aktivierung wird jeder erlittene Schaden 10 sec lang um 60% verringert."},
[57058] = {name = "Arkaner Schock", description = "[q]Überzieht einen Feind mit Arkanmagie und verursacht 9425 to 10575 Arkanschaden."},
[57060] = {name = "Hast", description = "[q]Erhöht die Angriffs- und Zaubergeschwindigkeit des Zaubernden um 100%."},
[57061] = {name = "Giftwolke", description = "[q]Fügt 8 sec lang in der Nähe befindlichen Feinden alle 2 Sek. 3238 to 3762 Naturschaden zu."},
[57063] = {name = "Arkanmagnetismus", description = "[q]Erhöht 10 sec lang den erlittenen Arkanschaden um 2000."},
[57066] = {name = "Trampeln", description = "[q]Fügt nahen Gegnern 3299 to 3701 körperlichen Schaden zu."},
[57076] = {name = "Fleisch korrodieren", description = "[q]Korrodiert das Fleisch des Gegners und verursacht danach 15 sec lang alle 3 Sek. 825 Naturschaden."},
[57088] = {name = "Giftblitzsalve", description = "[q]Amanitar verschießt Gift auf Spielercharaktere und verursacht Naturschaden."},
[57090] = {name = "Wiederbeleben", description = "[q]Heilt das Ziel um 500 pro Sekunde. Hält 10 sec lang an. Gewährt einen Combopunkt. Bis zu 5-mal stapelbar."},
[57092] = {name = "Blitztempo", description = "[q]Erhöht das Flugtempo 8 sec lang um 500%."},
[57094] = {name = "Hieb", description = "[q]Fügt einem Feind 4163 to 4837 körperlichen Schaden zu."},
[57095] = {name = "Wucherwurzeln", description = "[q]Umwickelt einen Feind mit Wurzeln, macht ihn bis zu 10 sec lang bewegungsunfähig und fügt ihm alle 2 Sek. Naturschaden zu."},
[57108] = {name = "Flammenschild", description = "[q]Umgibt den Zaubernden mit einem Flammenschild, verringert jeden erlittenen Schaden um 80%. Hält pro Combopunkt länger an:\n 1 Punkt: 2 Sekunden\n 2 Punkte: 3 Sekunden\n 3 Punkte: 4 Sekunden\n 4 Punkte: 5 Sekunden\n 5 Punkte: 6 Sekunden"},
[57143] = {name = "Explosion des Lebens", description = "[q]Entlädt einen Stoß verjüngender Energie, der befreundete Einheiten innerhalb von 60 Metern heilt und die vom Zaubernden hervorgerufene Heilung um 50% erhöht. Erhöht die Heilung und verlängert den Effekt pro Combopunkt:\n 1 Punkt: 5.000 Heilung / 5 Sek.\n 2 Punkte: 7.500 Heilung / 10 Sek.\n 3 Punkte: 10.000 Heilung / 15 Sek.\n 4 Punkte: 12.500 Heilung / 20 Sek.\n 5 Punkte: 15.000 Heilung / 25 Sek."},
[57322] = {name = "Blitzschlagnova", description = nil},
[57374] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[57376] = {name = "Heiliger Blitz", description = "[q]Schleudert einen Blitz aus Lichtmagie auf einen Gegner und fügt Heiligschaden zu."},
[57377] = {name = "Geißelung", description = "[q]Lichtmagie peitscht auf jeden Gegner ein und verursacht 5655 to 6345 Heiligschaden."},
[57381] = {name = "Unvergänglicher Schmerz", description = "[q]Lichtmagie peitscht auf jeden Gegner ein und verursacht 5655 to 6345 Schattenschaden."},
[57385] = {name = "Argentumkanone", description = "[q]Auf feindliche Geißelschergen abschießen und das Feuer betrachten."},
[57395] = {name = "Verzweifelter Schlag", description = "[q]Nutzt die letzte verbleibende Energie des Zaubernden, verursacht 200% Waffenschaden und betäubt 3 sec lang den Zaubernden."},
[57408] = {name = "Sturmwolke", description = "[q]Beschwört eine Sturmwolke um den Zaubernden, die 300 Naturschaden verursacht, wenn er angegriffen wird."},
[57412] = {name = "Bombe der Abrechnung", description = "[q]Lässt die Abrechnung über die Geißelschergen branden. Verursacht geradezu lächerliche Mengen an Schaden. Manche behaupten sogar, der Schaden wäre wahnsinnig."},
[57413] = {name = "Unruhiger Traum", description = "[q]Im Smaragdgrünen Alptraum versunken."},
[57428] = {name = "Statisches Feld", description = "[q]Beschwört ein Feld knisternder Energie an den Aufenthaltsort eines zufälligen Ziels. Das Feld verursacht bei allen Gegnern innerhalb von 30 Metern um das Ziel pro Sekunde 9425 to 10575 Arkanschaden."},
[57430] = {name = "Statisches Feld", description = "[q]Beschwört ein Feld knisternder Energie an den Aufenthaltsort eines zufälligen Ziels. Das Feld verursacht bei allen Gegnern innerhalb von 30 Metern um dieses Gebiet pro Sekunde 9425 to 10575 Arkanschaden."},
[57432] = {name = "Arkaner Puls", description = "[q]Lässt einen sofortigen Puls arkaner Energie hinauspeitschen, verursacht bei Feinden innerhalb von 30 Metern 28275 to 31725 Arkanschaden."},
[57454] = {name = "Eisstachel", description = "[q]Ein Eisstachel verursacht 1295 to 1505 Frostschaden und schleudert das Ziel in die Luft."},
[57456] = {name = "Erfrierung", description = "[q]Verursacht 6 sec lang alle 3 48 to 52 Frostschaden.\n Verringert 6 sec lang das Bewegungstempo um 10%."},
[57459] = {name = "Arkaner Sturm", description = "[q]Feuert eine Salve magischer Geschosse in die Luft. Die Geschosse fliegen auf ein zufälliges, nahes Ziel zu, schlagen ein und verursachen dabei 9425 to 10575 Arkanschaden."},
[57468] = {name = "Tollen", description = "[q]Tollt über einen Gegner hinweg und verursacht 200% des normalen Schadens."},
[57477] = {name = "Gefrierender Atem", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Frostschaden zu, stiehlt ihnen Mana und verlangsamt 10 sec lang ihr Bewegungstempo."},
[57480] = {name = "Kinetischer Strahl", description = "[q]Fügt 720 to 880 Naturschaden zu und zieht das Ziel zum Zaubernden."},
[57488] = {name = "Sturmböe", description = "[q]Stürmt auf einen Gegner zu und verursacht normalen Schaden plus 428 to 472."},
[57491] = {name = "Flammentsunami", description = "[q]Verursacht bei in der Nähe befindlichen Feinden Feuerschaden und überschüttet sie mit Lava, wodurch 10 sec lang zusätzlicher Feuerschaden verursacht wird."},
[57493] = {name = "Geschwindigkeitsschub", description = "[q]Erhöht das Flugtempo 8 sec lang um 100%."},
[57524] = {name = "Metanoia", description = "[q]Erhöht den erlittenen und verursachten kritischen Schaden um 50% und erhöht die kritische Trefferchance des Zaubernden um 25%. Hält 2 min. lang an."},
[57546] = {name = "Großes Böses vertreiben", description = "[q]Der anvisierte untote oder dämonische Feind wird bis zu 20 sec lang zur Flucht gezwungen. Erlittener Schaden kann den Effekt unterbrechen. Es kann immer nur jeweils ein Ziel zur Flucht gezwungen werden."},
[57547] = {name = "Berührung der Val'kyr", description = "[q]Verursacht 5 sec lang alle 1 Sek. 324 to 376 Schattenschaden."},
[57557] = {name = "Pyropuffer", description = "[q]Fügt einem Gegner Feuerschaden zu und erhöht 1 min lang den erlittenen Feuerschaden."},
[57570] = {name = "Schattenodem", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden 6938 to 8062 Feuerschaden zu."},
[57579] = {name = "Schattenspalt", description = "[q]Verursacht nach 5 Sek. bei allen Gegnern im Schattenriss 6188 to 8812 Schattenschaden."},
[57580] = {name = "Blitzinfusion", description = "[q]Flößt einem Ziel Blitze ein und verursacht dadurch nach 5 sec eine Überladung, die 1411 to 1589 Naturschaden in der Umgebung des Ziels verursacht."},
[57581] = {name = "Explosion der Leere", description = "[q]Verursacht nach Ablauf von 5 Sek. bei allen Feinden im Schattenriss 6188 to 8812 Schattenschaden."},
[57589] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[57592] = {name = "Gebäude reparieren", description = "[q]Repariert beschädigte Gebäude."},
[57599] = {name = "Seuchenstoß", description = "[q]Ein bösartiger Schlag, der 60% Waffenschaden plus 27 verursacht und das Ziel mit der Blutseuche ansteckt, einer Krankheit, die im Verlauf der Zeit Schattenschaden verursacht. Der Stoß entfernt einen Heilung über Zeit verursachenden Effekt."},
[57601] = {name = "Blutseuche", description = "[q]Eine Krankheit, die im Verlauf von 12 sec Schattenschaden verursacht. Hat eine Chance von 50%, jedes Mal, wenn sie Schaden verursacht, einen Effekt zu entfernen, der Heilung über Zeit hervorruft."},
[57609] = {name = "Kanone abfeuern", description = "[q]Feuert einen Kanonenschuss ab, der 5997 to 6003 Schaden verursacht sowie an nahen Strukturen 1300 Belagerungsschaden."},
[57616] = {name = "Kristallstachel", description = "[q]Spuckt einen Kristallstachel auf das Ziel und fügt 666 to 774 Frostschaden zu."},
[57617] = {name = "Eisspitzengift", description = "[q]Schießt Gift auf einen Gegner und fügt Naturschaden zu."},
[57628] = {name = "Feuerball", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[57629] = {name = "Frostnova", description = "[q]Verursacht Frostschaden bei in der Nähe befindlichen Feinden und macht sie bis zu 5 sec lang bewegungsunfähig."},
[57635] = {name = "Rückzug", description = "[q]Versucht, sich mit einem Rückwärtssprung vom Ziel zurückzuziehen. Muss dem Ziel zugewandt sein."},
[57640] = {name = "Finsterer Stoß", description = "[q]Fügt einem Feind 125% Waffenschaden zu."},
[57641] = {name = "Ausweiden", description = "[q]Finishing-Move, der Schaden verursacht."},
[57643] = {name = "Zauberreflexion", description = "[q]Erhebt Euren Schild und reflektiert den nächsten auf Euch gewirkten Zauber. Hält 5 sec lang an."},
[57644] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[57645] = {name = "Verderbnis", description = "[q]Verdirbt in der Nähe befindliche Feinde und fügt ihnen 24 sec lang alle 3 Sek. Schattenschaden zu."},
[57647] = {name = "Mondfeuer", description = "[q]Verbrennt den Feind und fügt ihm Arkanschaden sowie 12 sec lang zusätzlichen Schaden zu."},
[57648] = {name = "Zorn", description = "[q]Schleudert einen Blitzschlag auf einen Feind und verursacht Naturschaden."},
[57652] = {name = "Brechende Welle", description = "[q]Ihr verursacht eine brechende Welle, die 4163 to 4837 Naturschaden verursacht, wenn sie mit feindlichen Zielen in Kontakt kommt, die sich innerhalb von 20 Metern vor Euch befinden, und stößt sie zurück. Der Schaden wird verdoppelt, wenn die Ziele eingefroren sind."},
[57655] = {name = "Katzengestalt", description = "[q]Verwandelt Eure Gestalt in die einer Katze. Erhöht Nahkampfangriffskraft um 0 plus Beweglichkeit. Beschützt den Zaubernden auch vor Verwandlungseffekten und erlaubt den Einsatz verschiedener Katzenfähigkeiten.\n\n Durch die Gestaltwandlung wird der Zaubernde von Verwandlungs- und bewegungseinschränkenden Effekten verschont."},
[57657] = {name = "Zerfleischen", description = "[q]Zerfleischt das Ziel, verursacht 100% des normalen Schadens plus 25 und lässt es 12 sec lang 30% zusätzlichen Schaden durch 'Schreddern', 'Zermalmen' und Blutungseffekte erleiden. Gewährt 1 Combopunkt."},
[57661] = {name = "Zerfetzen", description = "[q]Finishing-Move, der im Lauf der Zeit Schaden verursacht."},
[57665] = {name = "Frostblitz", description = "[q]Fügt einem Feind Frostschaden zu und verringert 4 sec lang sein Bewegungstempo."},
[57668] = {name = "Frostnova", description = "[q]Verursacht Frostschaden bei in der Nähe befindlichen Feinden und macht sie bis zu 8 sec lang bewegungsunfähig."},
[57676] = {name = "Portal: Shattrath", description = "[q]Lässt ein Portal entstehen, das Gruppenmitglieder, die es benutzen, nach Shattrath teleportiert."},
[57688] = {name = "Fokussierte Wut", description = "[q]Versetzt den Zaubernden in Raserei, nachdem er Opfer eines Angriffs oder Zaubers wurde, und erhöht verursachten Schaden sowie Angriffstempo."},
[57723] = {name = "Erschöpfung", description = "[q]Kann nicht von 'Heldentum' oder ähnlichen Effekten profitieren."},
[57724] = {name = "Sättigung", description = "[q]Kann nicht von 'Kampfrausch' oder ähnlichen Effekten profitieren."},
[57725] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[57728] = {name = "Schockwelle", description = "[q]Sendet vom Zaubernden aus eine Schockwelle nach vorne, die 4050 to 4950 Schaden verursacht und alle feindlichen Ziele innerhalb von 15 Metern in einem kegelförmigen Bereich vor dem Zaubernden 2 sec lang betäubt."},
[57733] = {name = "Drakonisches Rasen", description = "[q]Erhöht Nahkampf-, Distanzangriffs- und Zaubertempo aller Gruppenmitglieder 30 sec lang um 35%."},
[57740] = {name = "Aura der Hingabe", description = "[q]Verleiht Gruppenmitgliedern in der Nähe 10 min lang zusätzliche Rüstung."},
[57741] = {name = "Schockwelle", description = "[q]Schickt dem Golem eine Druckwelle voraus, die 2 sec lang 3150 to 3850 Schaden verursacht und in einem kegelförmigen Bereich von 10 Metern alle gegnerischen Ziele betäubt."},
[57742] = {name = "Vergeltende Wut", description = "[q]Versetzt alle nahen verbündeten Einheiten in Raserei und verleiht Immunität gegen Gruppenkontrolleffekte."},
[57753] = {name = "Flammenkugel herbeizaubern", description = "[q]Zaubert eine Kugel aus reinem Feuer, die durch die Luft auf ein Ziel zuschwebt, während sie sengende Strahlen auf Feinde schleudert, die sich auf 18 Meter nähern."},
[57757] = {name = "Feuerregen", description = "[q]Lässt einen Schmelzregen niedergehen, der alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen 6 sec lang alle 2 Sek. 2775 to 3225 Feuerschaden zufügt."},
[57759] = {name = "Hammerschlag", description = "[q]Schlägt einem Feind einen Hammer über den Kopf und verringert dessen Größe, Werte und verursachten körperlichen Schaden um 5%. Stapelbar."},
[57762] = {name = "Blühendes Leben", description = "[q]Heilt 142 to 158 pro Sekunde und 463 to 537 nach Ablauf oder Bannen des Effektes."},
[57765] = {name = "Pflege", description = "[q]Heilt ein freundliches Ziel um 694 to 806. Ist ein durch Euch gewirkter Effekt von 'Verjüngung', 'Nachwachsen' oder 'Blühendes Leben' auf dem Ziel aktiv, erhöht sich die Heilung um zusätzliche 20%."},
[57767] = {name = "Reinigung des Glaubens", description = "[q]Reinigt ein befreundetes Ziel und hebt dabei 1 Gifteffekt, 1 Krankheitseffekt und 1 Magieeffekt auf."},
[57769] = {name = "Siegel des Befehls", description = "[q]Verleiht dem Paladin eine Chance, 70 zusätzlichen Heiligschaden zu verursachen. Es kann immer nur jeweils ein Siegel auf dem Paladin aktiv sein. Hält 2 min lang an."},
[57774] = {name = "Richturteil des Lichts", description = "[q]Entfesselt until cancelled lang die Energie eines Siegelzaubers um einen Feind zu richten, wodurch Nahkampfangriffen auf das gerichtete Ziel eine Chance gewährt wird, beim Angreifer Gesundheit wiederherzustellen."},
[57775] = {name = "Große Heilung", description = "[q]Ruft heilige Magie herbei, um einen Verbündeten zu heilen."},
[57777] = {name = "Erneuerung", description = "[q]Heilt das Ziel 15 sec lang um 2360 to 2640 Schaden."},
[57778] = {name = "Schattenwort: Schmerz", description = "[q]Murmelt ein Wort der Dunkelheit, das einem Feind 18 sec lang alle 3 Sek. Schattenschaden zufügt."},
[57779] = {name = "Gedankenschinden", description = "[q]Fügt einem Feind Schattenschaden zu und verringert 3 sec lang sein Bewegungstempo."},
[57780] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm Naturschaden zu."},
[57781] = {name = "Blitzschlag", description = "[q]Schleudert einen Blitz auf das Ziel und fügt ihm 234 to 266 Naturschaden zu."},
[57783] = {name = "Erdschock", description = "[q]Schockt einen Gegner mit erschütternder Kraft, verursacht Naturschaden und unterbricht 2 sec lang den gewirkten Zauber."},
[57784] = {name = "Gewitter", description = "[q]Ihr ruft einen Blitzschlag hernieder, der Gegnern im Umkreis von 15 Metern Schaden zufügt. Alle Gegner in der Nähe erleiden 185 to 215 Naturschaden und werden zurückgestoßen."},
[57785] = {name = "Welle der Heilung", description = "[q]Ruft Naturmagie herbei, um einen Verbündeten zu heilen."},
[57789] = {name = "Tödlicher Stoß", description = "[q]Verursacht 100% Waffenschaden und verwundet das Ziel, verringert außerdem die Wirksamkeit jeglicher Heilung 5 sec lang um 50%."},
[57790] = {name = "Blutdurst", description = "[q]Greift das Ziel sofort an und verursacht (Attack power * 142 / 100) Schaden. Zusätzlich werden die nächsten erfolgreichen 3 Nahkampfangriffe 1,5% der maximalen Gesundheit wiederherstellen. Dieser Effekt hält 8 sec lang an. Schaden basiert auf Angriffskraft."},
[57791] = {name = "Blutdurst", description = "[q]Greift das Ziel sofort an und verursacht 30 Schaden. Zusätzlich werden die nächsten erfolgreichen 3 Nahkampfangriffe 1,5% der maximalen Gesundheit wiederherstellen. Dieser Effekt hält 8 sec lang an."},
[57794] = {name = "Heldenhafter Sprung", description = "[q]Springt durch die Luft und schmettert auf alle Feinde im Umkreis von 8 Metern um das Zielgebiet hinab, verursacht 50% Waffenschaden und betäubt sie 2 sec lang."},
[57795] = {name = "Verwüsten", description = "[q]Zerreißt die Rüstung des Ziels und ruft den Effekt 'Rüstung zerreißen' hervor. Zusätzlich werden für jeden auf das Ziel wirkenden Effekt von 'Rüstung zerreißen' 20% Waffenschaden plus 50 verursacht. 'Rüstung zerreißen' ist bis zu 5-mal stapelbar."},
[57798] = {name = "Weihe", description = "[q]Weiht das Land unter dem Paladin, fügt 8 sec lang Feinden, die das Gebiet betreten, 240 Heiligschaden zu."},
[57799] = {name = "Schild des Rächers", description = "[q]Schleudert einen heiligen Schild auf den Gegner, der 180 to 220 Heiligschaden zufügt, ihn benommen macht, und dann auf weitere nahe Gegner überspringt. Wirkt auf insgesamt 2 Ziele."},
[57801] = {name = "Flammenatem", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden 3063 to 3937 Feuerschaden zu."},
[57802] = {name = "Erdschild", description = "[q]Schützt das Ziel mit einem Erdschild. Nahkampfangriffe heilen das geschützte Ziel, dieser Effekt tritt aber nur einmal alle paar Sekunden auf. 6 Aufladungen. Hält 2 min lang an."},
[57807] = {name = "Rüstung zerreißen", description = "[q]Hackt nach der Rüstung des Gegners und verringert sie bei jedem Einsatz von 'Rüstung zerreißen'. Kann bis zu 5-mal angewendet werden. Hält 20 sec lang an."},
[57815] = {name = "Durchdringendes Kreischen", description = "[q]Verursacht 24 to 29 Schattenschaden bei in der Nähe befindlichen Feinden."},
[57825] = {name = "Frostblitz", description = "[q]Fügt einem Feind Frostschaden zu und verringert 4 sec lang sein Bewegungstempo."},
[57832] = {name = "Donnerknall", description = "[q]Trifft nahe Feinde, erhöht die Zeit zwischen ihren Angriffen 30 sec lang um 20% und verursacht 200 Schaden. Schaden wird durch Angriffskraft erhöht. Diese Fähigkeit verursacht zusätzliche Bedrohung und wirkt auf maximal 4 Ziele."},
[57833] = {name = "Frost spucken", description = "[q]Verursacht alle 1 Sek. 264 to 306 Frostschaden."},
[57835] = {name = "Gabe des Zwielichts", description = "[q]Erhöht den Schattenschaden des Ziels um 50% und reduziert den erlittenen Schattenschaden um 100%."},
[57843] = {name = "Mojoverstärkter Feuerzauberschutz", description = "[q]Umgibt den Zaubernden mit einem Schild, der 1.50 min lang bestehen bleibt. Erhöht den verursachten Schaden um 30% und kann eine bestimmte Menge Feuerschaden absorbieren."},
[57846] = {name = "Heldenhafter Stoß", description = "[q]Ein starker Angriff, der den Nahkampfschaden um 10% erhöht."},
[57861] = {name = "Rhunoks Brüllen", description = "[q]Stößt ein Brüllen aus, das 30 sec lang den erlittenen Schaden um 20% erhöht."},
[57882] = {name = "Sprung", description = "[q]Springt zu einem angegebenen Zielort zwischen 5 und 45 Metern Entfernung. Bei dieser Aktion wird der geschmeidige Pirscher den Kampf verlassen."},
[57912] = {name = "Arkaner Blitzschlag", description = "[q]Die Verteidigungssysteme der Violetten Festung verursachen 13000 Arkanschaden bei nicht autorisierten Lebensformen."},
[57941] = {name = "Gedankenschinden", description = "[q]Fügt einem Feind Schattenschaden zu und verringert 3 sec lang sein Bewegungstempo."},
[57942] = {name = "Schattenblitzsalve", description = "[q]Schleudert Geschosse von dunkler Magie auf in der Nähe befindliche Feinde und verursacht Schattenschaden."},
[57949] = {name = "Schaudern", description = "[q]Verursacht bei erlittenem Schaden 1414 to 1586 Schattenschaden. Springt auf nahe Schlachtzugsmitglieder über."},
[57959] = {name = "Planare Anomalien", description = nil},
[57976] = {name = "Planarer Schlag", description = "[q]Lässt eine Druckwelle aus Magie entstehen und fügt in der Nähe befindlichen Feinden Arkanschaden zu."},
[57984] = {name = "Feuerschlag", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[58025] = {name = "Frostpuffer", description = "[q]Erhöht 5 sec lang den Frostschaden, den das Ziel erleidet."},
[58026] = {name = "Segen des Kreuzzugs", description = "Ihr seid Dienern der Geißel gegenüber um einiges stärker! Erhöhte Mana- und Gesundheitsregeneration, während Ihr am Banner steht und diesen verteidigt."},
[58040] = {name = "Türsiegel zerstören", description = "[q]Schwächt das Siegel an der Gefängnistür."},
[58053] = {name = "Heiliges Licht", description = "[q]Heilt ein befreundetes Ziel um 4936 to 5493."},
[58054] = {name = "Segen der Könige", description = "[q]Belegt ein befreundetes Ziel mit einem Segen und erhöht 30 min lang alle Attribute um 10%. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein."},
[58105] = {name = "Shadrons Macht", description = "[q]Shadrons Gegenwart erhöht den erlittenen Feuerschaden aller Gegner um 100%."},
[58127] = {name = "Göttlicher Sturm", description = "[q]Ein sofortiger Waffenangriff, der bei bis zu 4 Feinden innerhalb von 8 Metern Heiligschaden verursacht."},
[58130] = {name = "Eisige Gegenwehr", description = "[q]Der Todesritter bringt sein Blut zum Gefrieren, um 12 sec lang immun gegenüber Betäubungseffekten zu werden und jeglichen erlittenen Schaden um 50% zu verringern. Entfernt keine bereits wirkenden Betäubungseffekte."},
[58137] = {name = "Ghulplosion", description = "[q]Der Ghul sprengt sich selbst in die Luft und verursacht 1388 to 1612 Schattenschaden in der Umgebung des Zaubernden."},
[58153] = {name = "Unheiliges Licht", description = "[q]Heilt ein befreundetes Ziel um 4936 to 5493."},
[58154] = {name = "Hammer der Ungerechtigkeit", description = "[q]Betäubt das Ziel 6 sec lang."},
[58187] = {name = "Übertragene Macht", description = "[q]Erhöht 1 sec lang das Angriffstempo eines Verbündeten um 1%."},
[58203] = {name = "Eisenkette", description = "[q]Befestigt eine Kette an einer Geißelbombe, mit dem Ziel, sie an einer schwerfälligen Schreckensgestalt vorbeizuziehen. Während die Kette befestigt ist, kann keine andere Aktion ausgeführt werden."},
[58282] = {name = "Heben", description = "[q]Hebt das Ziel weg und verursacht 900 to 1100 Schaden. Dieser Angriff hat verheerende Auswirkungen auf Gargoyles in Steinform."},
[58283] = {name = "Fels werfen", description = "[q]Schleudert einen Felsen auf einen Feind, verursacht 405 to 495 Schaden und lässt das Ziel 2 sec lang die Orientierung verlieren. Es ist unwahrscheinlich, dass der Feind bemerkt, wer den Felsen geworfen hat."},
[58291] = {name = "Schildstörung", description = "[q]Verringert die Magie, die einen Gefangenen in Schach hält."},
[58438] = {name = "Entzünden", description = "[q]Setzt einen friedlichen Ent bösartig in Flammen."},
[58455] = {name = "Arkane Explosion", description = "[q]Lässt eine Druckwelle aus Magie entstehen und fügt in der Nähe befindlichen Feinden 648 to 752 Arkanschaden zu."},
[58456] = {name = "Arkanbeschuss", description = "[q]Schleudert mehrere Geschosse auf das feindliche Ziel und verursacht 720 to 880 Arkanschaden."},
[58457] = {name = "Frostblitz", description = "[q]Fügt einem Feind 723 to 977 Frostschaden zu und verringert 4 sec lang sein Bewegungstempo."},
[58459] = {name = "Durchbohren", description = "[q]Fügt einem Gegner 12 sec lang alle 3 Sek. 250 körperlichen Schaden zu."},
[58460] = {name = "Brutaler Stoß", description = "[q]Verursacht 150% Waffenschaden."},
[58461] = {name = "Rüstung zerreißen", description = "[q]Hackt auf die Rüstung eines Feindes ein und verringert sie um 10% pro Einsatz von 'Rüstung zerreißen'. Kann bis zu 5-mal angewendet werden. Hält 20 sec lang."},
[58462] = {name = "Arkanschlag", description = "[q]Überzieht einen Feind mit Arkanmagie und verursacht 833 to 967 Arkanschaden. Jedes Mal, wenn Ihr 'Arkanschlag' wirkt, verringert sich die Zauberzeit. Dieser Effekt ist bis zu 5-mal stapelbar und hält 8 sec lang an."},
[58463] = {name = "Kältekegel", description = "[q]Verursacht bei Gegnern in einem kegelförmigen Bereich vor dem Zaubernden 50 Frostschaden und verringert ihr Bewegungstempo 6 sec lang."},
[58464] = {name = "Eisketten", description = "[q]Schlingt eisige Ketten um das Ziel, die es 4 sec lang am Platz festhalten."},
[58469] = {name = "Arkane Ermächtigung", description = "[q]Nahkampf-, Bewegungs-, Distanz- und Zaubertempo 10 sec lang um 50% erhöht."},
[58470] = {name = "Taktisches Blinzeln", description = "[q]Teleportiert den Zaubernden hinter das Ziel. Befreit den Zaubernden auch von jeglichen Fesseln."},
[58471] = {name = "Meucheln", description = "[q]Fügt einem Gegner 175% Waffenschaden zu, aber nur, wenn von hinten angegriffen wird."},
[58504] = {name = "Peinigender Schlag", description = "[q]Verursacht beim Ziel 100% Waffenschaden und Schmerzen, und der verursachte Schaden erhöht sich 5 sec lang um 20%."},
[58506] = {name = "Verstohlenheit", description = "[q]Versetzt den Zaubernden in den Verstohlenheitsmodus. Hält an, bis die Aktion abgebrochen wird."},
[58508] = {name = "Seitlicher Prankenhieb", description = "[q]Verursacht 60% Waffenschaden an einem nahen Ziel."},
[58510] = {name = "Magieverlangsamung", description = "[q]Verringert 6 sec lang das Zaubertempo des Gegners um 50%."},
[58511] = {name = "Aroma des verfaulten Apfels", description = "[q]Ihr riecht ziemlich übel."},
[58514] = {name = "Aroma einer verfaulten Banane", description = "[q]Der Geruch ist ein Gemisch aus Ghulfüßen und Schweinehintern."},
[58516] = {name = "Überwältigen", description = "[q]Überwältigt einen Feind sofort und verursacht 75% Waffenschaden. Kann nur verwendet werden, nachdem das Ziel ausgewichen ist. Auf 'Überwältigen' kann nicht mit 'Blocken', 'Ausweichen' oder 'Parieren' reagiert werden."},
[58517] = {name = "Schreckliche Wunde", description = "[q]Ein hinterhältiger Angriff, der 100% Waffenschaden verursacht und das Ziel stark bluten lässt, bis es vollständig geheilt wird. Fügt bis zur vollständigen Heilung alle 3 Sek. 694 to 806 Schaden zu."},
[58519] = {name = "Spucken", description = "[q]Bespuckt. Niemand mag Euch."},
[58526] = {name = "Azurbindungen", description = "[q]Fesselt einen Gegner mit Magietentakeln und hindert ihn 4 sec lang daran, anzugreifen oder Zauber zu wirken."},
[58531] = {name = "Arkane Geschosse", description = "[q]Schleudert ein arkanes Geschoss auf den Feind und verursacht Arkanschaden."},
[58532] = {name = "Frostblitzsalve", description = "[q]Verursacht bei nahen Gegnern 1800 to 2200 Frostschaden und verringert 4 sec lang ihr Bewegungstempo."},
[58534] = {name = "Tieffrieren", description = "[q]Verursacht 1018 to 1182 Frostschaden und betäubt das Ziel 4 sec lang."},
[58535] = {name = "Frostblitz", description = "[q]Fügt einem Feind Frostschaden zu und verringert 4 sec lang sein Bewegungstempo."},
[58536] = {name = "Arkane Salve", description = "[q]Schleudert magische Geschosse auf in der Nähe befindliche Feinde und verursacht 2035 to 2365 Arkanschaden."},
[58537] = {name = "Verwandlung", description = "[q]Verwandelt den Feind in ein Schaf und zwingt ihn, 5 sec lang so herumzuwandern. Während das Schaf herumwandert, kann es weder angreifen noch Zauber wirken. Jeglicher erlittene Schaden verwandelt das Ziel in seine normale Gestalt zurück. Es kann immer nur jeweils ein Ziel verwandelt werden. Wirkt nur bei Wildtieren, Drachen, Riesen, Humanoiden und Tieren."},
[58540] = {name = "Eidolongefängnis", description = "Eidolongefängnis Magie\nEingesperrt."},
[58541] = {name = "Phasensprung", description = "[q]Phasenverschiebt den Schemen 30 Meter vorwärts, es sei denn, es gibt ein Hindernis. Befreit den Schemen zusätzlich von allen Fesseln."},
[58542] = {name = "Eier ausbrüten", description = "[q]Brütet Zwielichteier aus..."},
[58543] = {name = "Phasenwanderung", description = "[q]Lässt Euch vorübergehend aus der normalen Welt schwinden. Der Kampf wird verlassen und das Bewegungstempo 10 sec lang erhöht."},
[58544] = {name = "Bluttrinker", description = "[q]Saugt einem Gegner 6 sec lang Gesundheit ab und überträgt sie auf den Schemen. Dieser Effekt ist extrem schwach, da der Schemen nicht an den direkten Kampf gewöhnt ist."},
[58548] = {name = "Astral", description = "[q]Für Feinde sehr schwer aufzuspüren."},
[58567] = {name = "Rüstung zerreißen", description = "[q]Zerreißt die Rüstung des Ziels, verringert sie pro Einsatz von 'Rüstung zerreißen' um 4% und verursacht ein hohes Maß an Bedrohung. Die Bedrohung wird durch Angriffskraft erhöht. Bis zu 3-mal stapelbar. Hält 30 sec lang an."},
[58579] = {name = "Brennende Lohe", description = "[q]Das Flammenfeld verursacht 30 sec lang alle 1 Sek. 150 Feuerschaden."},
[58619] = {name = "Sturmangriff", description = "[q]Stürmt auf einen Gegner zu, verursacht normalen Schaden plus 120 und stößt ihn zurück."},
[58624] = {name = "Verderbte Verjüngung", description = "[q]Belegt einen Feind mit verderbter Leyenergie und verursacht 12 sec lang alle 3 Sekunden 360 to 440 Arkanschaden."},
[58658] = {name = "Seuchenkristall verbannen", description = "[q]Verbannt den Kristall der Geißel zurück in das Reich, aus dem er beschworen wurde."},
[58663] = {name = "Stampfen", description = "[q]Stampft auf einen Feind, verursacht 3770 to 4230 körperlichen Schaden und stößt ihn 2 sec lang zu Boden."},
[58667] = {name = "Leyfluch", description = "[q]Kanalisiert die umgebenden Leyenergien, um einen Gegner zu verfluchen. Verursacht alle 3 Sekunden 250 Schaden und erhöht den erlittenen Zauberschaden 30 sec lang um 20%."},
[58678] = {name = "Felssplitter", description = "[q]Entfesselt einen Hagel gezackter Steine, der bei einem feindlichen Ziel 3 sec lang alle 0.1 Sek. Schaden verursacht sowie bei feindlichen Zielen innerhalb von 5 Metern um das Ziel."},
[58683] = {name = "Grausamer Kampf", description = "Erlittener körperlicher Schaden um 4% erhöht."},
[58688] = {name = "Unkontrollierbare Energie", description = "[q]Entlädt einen Strom arkaner Energie, der Feinden, die sich vor dem Zaubernden befinden, 3800 to 4200 Arkanschaden zufügt. Gegnerische Ziele erleiden auch 15 sec lang alle 3 Sek. 1000 Arkanschaden."},
[58690] = {name = "Schwanzfeger", description = "[q]Verursacht bei Feinden in einem kegelförmigen Bereich hinter dem Zaubernden 1575 to 1925 Schaden und unterbricht 2 sec lang Zauberwirken."},
[58693] = {name = "Blizzard", description = "[q]Ruft einen Blizzard herbei, der 8 sec lang anhält, Feinden im ausgewählten Gebiet alle 2 Sek. 1500 Frostschaden zufügt und ihr Bewegungstempo um 40% verringert."},
[58694] = {name = "Arkanes Vakuum", description = "[q]Beschwört alle in der Nähe befindlichen Feinde zum Aufenthaltsort des Zaubernden."},
[58729] = {name = "Geistige Immunität", description = "[q]Eure kürzliche Rückkehr aus dem Reich der Toten gewährt Euch temporär Schutz."},
[58743] = {name = "Abfangen", description = "[q]Stürmt auf einen Gegner zu, verursacht 65 Schaden und betäubt ihn 3 sec lang."},
[58747] = {name = "Abfangen", description = "[q]Stürmt auf einen Gegner zu, verursacht 65 Schaden und betäubt ihn 3 sec lang."},
[58769] = {name = "Abfangen", description = "[q]Stürmt auf einen Gegner zu, verursacht 65 Schaden und betäubt ihn 3 sec lang."},
[58770] = {name = "Mana entziehen", description = "[q]Entzieht einem Feind im Verlauf von 5 sec bis zu 5000 Mana und überträgt sie auf den Zaubernden."},
[58839] = {name = "Seuchenstoß", description = "[q]Ein bösartiger Schlag, der 60% Waffenschaden plus 9 verursacht und das Ziel mit der Blutseuche ansteckt, einer Krankheit, die im Verlauf der Zeit Schattenschaden verursacht. Der Stoß entfernt einen Heilung über Zeit verursachenden Effekt."},
[58840] = {name = "Blutseuche", description = "[q]Eine Krankheit, die im Verlauf von 12 sec Schattenschaden verursacht. Hat eine Chance von 50%, jedes Mal, wenn sie Schaden verursacht, einen Effekt zu entfernen, der Heilung über Zeit hervorruft."},
[58841] = {name = "Raserei", description = "[q]Erhöht den verursachten körperlichen Schaden des Zaubernden 30 sec lang um 10%."},
[58843] = {name = "Seuchenstoß", description = "[q]Ein bösartiger Schlag, der 60% Waffenschaden plus 9 verursacht und das Ziel mit der Blutseuche ansteckt, einer Krankheit, die im Verlauf der Zeit Schattenschaden verursacht. Der Stoß entfernt einen Heilung über Zeit verursachenden Effekt."},
[58844] = {name = "Blutseuche", description = "[q]Eine Krankheit, die im Verlauf von 12 sec Schattenschaden verursacht. Hat eine Chance von 50%, jedes Mal, wenn sie Schaden verursacht, einen Effekt zu entfernen, der Heilung über Zeit hervorruft."},
[58845] = {name = "Fluch des verdorbenen Fleisches", description = "[q]Reduziert die maximale Gesundheit des Ziels um 25% und verursacht 30 sec lang alle 3 Sek. 1500 Schattenschaden."},
[58848] = {name = "Zeit anhalten", description = "[q]Hält innerhalb einer Sphäre von 50 Metern 2 sec lang die Zeit an."},
[58857] = {name = "Zwillingsgeheul", description = "[q]Eure Geisterwölfe heulen und zwingen alle Gegner, sie 6 sec lang anzugreifen."},
[58861] = {name = "Hieb", description = "[q]Eure Geisterwölfe betäuben das Ziel 2 sec lang."},
[58867] = {name = "Geisterwolfsprung", description = "[q]Springt hinter das Ziel."},
[58875] = {name = "Geisterpfoten", description = "[q]Entfernt alle bewegungseinschränkenden Effekte und erhöht 15 sec lang Euer Bewegungstempo und das Eurer Geisterwölfe um 60%."},
[58905] = {name = "Angreiferschmettern", description = nil},
[58912] = {name = "Todessturm", description = "[q]Trifft einen Feind mit einem Todesblitz, der auf einen weiteren in der Nähe befindlichen Feind überspringt. Der Zauber trifft bis zu 12 Ziele und verursacht an jedem Ziel Schattenschaden."},
[58913] = {name = "Seelenspalter", description = "[q]Fügt einem Gegner und seinen nächsten Verbündeten Waffenschaden zu, wobei bis zu 3 Ziele betroffen sind. Stellt 5% Eures Manas wieder her."},
[58916] = {name = "Geschenk des Lichkönigs", description = "[q]Erweckt tote Fußsoldaten von Lordaeron im Zielgebiet als Ghule wieder."},
[58917] = {name = "Diener aufzehren", description = "[q]Verschlingt bis zu 5 nahe Ghuldiener. Pro aufgezehrtem Diener werden 10% Gesundheit und Mana wiederhergestellt."},
[58921] = {name = "Machtwort: Seelenstärke", description = "[q]Erhöht die Ausdauer eines Verbündeten 1 hour lang um 104."},
[58953] = {name = "Zuschlagen", description = "[q]Schlägt auf einen Feind ein, fügt ihm 10% Waffenschaden zu und unterbricht 4 sec lang den gewirkten Zauber."},
