[61990] = {name = "Blitzeis", description = "[q]Schließt das Ziel in einem Eisblock ein."},
[61998] = {name = "Schmelzstrahl", description = "[q]Fügt einem Gegner 5 sec lang Naturschaden zu."},
[62014] = {name = "Truthahnzähler", description = "[q]Verfolgt die Anzahl der von Euch getöteten Truthähne."},
[62016] = {name = "Kugel aufladen", description = "[q]Lädt eine nahe Kugel mit Elektrizität auf."},
[62030] = {name = "Versteinernder Atem", description = "[q]Verursacht alle 1 Sek. 14063 to 15937 Naturschaden und erhöht 8 sec lang den erlittenen Schaden um 20%."},
[62038] = {name = "Beißende Kälte", description = "[q]Verursacht bei unbeweglichen Feinden regelmäßigen, stapelbaren Schaden. Bewegung verringert diesen Effekt."},
[62042] = {name = "Sturmhammer", description = "[q]Wirft einen Sturmhammer, der einem Feind 2451 to 2551 Schaden zufügt, ihn zu Boden wirft und ihn 2 sec lang betäubt. Alle nahen Gegner beim Ziel erleiden einen betäubenden Donnerknall."},
[62055] = {name = "Brüchige Haut", description = "[q]Erhöht den erlittenen Schaden 8 sec lang um 20%."},
[62102] = {name = "Demoralisierender Ruf", description = "[q]Verringert 30 sec lang die Nahkampfangriffskraft von Vrykul und Worgs der Winterskorn in der Nähe."},
[62129] = {name = "Klage der Seelen", description = "[q]Verschießt eine Salve von Antimanageschossen auf einen Feind und stößt ihn zurück."},
[62130] = {name = "Schlag des Ungleichgewichts", description = "[q]Verursacht 200% Waffenschaden und bringt das Ziel aus dem Gleichgewicht. Reduziert die Verteidigungsfertigkeit des Ziels 15 sec lang um 200."},
[62131] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der 3700 to 4300 Naturschaden verursacht. Der Blitz wird auf in der Nähe befindliche Feinde überspringen und mit jedem weiteren Ziel 100% mehr Schaden verursachen."},
[62166] = {name = "Steinerner Griff", description = "[q]Quetscht das Leben aus einem Ziel, betäubt es und verursacht alle 5 Sek. 0 körperlichen Schaden bis Kologarns Arm 80000 Schaden erlitten hat."},
[62209] = {name = "Photosynthese", description = "[q]Die Sonnenstrahlen heilen Pflanzen alle 1 Sek. um 10000."},
[62211] = {name = "Instabiler Sonnenstrahl", description = "[q]Von der Macht der Sonne mit Energie aufgeladen."},
[62217] = {name = "Instabile Energie", description = "[q]Verursacht 12950 to 15050 Naturschaden zu und beendet die Effekte von 'Instabiler Sonnenstrahl'."},
[62218] = {name = "Raserei", description = "[q]Erhöht den Rasereiwert des Ghuls."},
[62238] = {name = "Instabiles Blitzgeschoss", description = "[q]Schleudert einen Blitzschlag, der allen Gegnern im gewählen Gebiet 8 sec lang alle 2 Sek. Naturschaden zufügt."},
[62239] = {name = "Hellblatts Fluxus", description = "[q]Erhöht den verursachten Schaden um 10%."},
[62240] = {name = "Solarflimmern", description = "[q]Fügt einer Anzahl von Zielen basierend auf 'Hellblatts Fluxus' 7863 to 9137 Feuerschaden zu."},
[62245] = {name = "Brocken werfen", description = "[q]Wirft einen Brocken, verursacht 126 to 154 Schaden und stößt alle Einheite im Zielbereich zurück."},
[62246] = {name = "Schockwelle", description = "[q]Schickt dem Urtum eine Kraftwelle voraus, die 1110 to 1290 Schaden verursacht und alle gegnerischen Ziele innerhalb von 30 Metern zurückstößt."},
[62269] = {name = "Rune des Todes", description = "[q]Beschwört eine Rune des Todes am Ort eines zufälligen feindlichen Ziels. Diese Rune verursacht bei allen innerhalb von 13 Metern um diesen Ort 2750 Schattenschaden alle 0,5 Sekunden."},
[62273] = {name = "Rune der Beschwörung", description = "[q]Erschafft eine Rune der Beschwörung in der Nähe eines zufälligen Feindes. Diese Rune wird regelmäßig Blitzelementare beschwören, die auf zufällige Feinde zustürmen und explodieren."},
[62274] = {name = "Runenschild", description = "[q]Bedeckt den Zaubernden mit magischen Runen, die bis zu 20000 Schaden absorbieren. Sobald 20000 Schaden absorbiert wurde, wird dieser in eine mächtige Energieexplosion umgewandet, die den Schaden des Zaubernden 15 sec lang um 50% erhöht."},
[62276] = {name = "Ummantelung des Blitzes", description = "[q]Umgibt den Zaubernden mit einem Mantel aus purer elektrischer Energie und verringert jeden erlittenen Schaden um 99%."},
[62279] = {name = "Blitzladung", description = "[q]Absorbiert einen Strom elektrischer Energie, fügt Feinden in einem kegelförmigen Bereich zwischen dem Zaubernden und der Energiequelle Naturschaden zu und erhöht Nahkampfschaden und Angriffstempo des Zaubernden um 15% sowie den zugefügten Naturschaden um 10%."},
[62286] = {name = "Teer", description = "[q]Lässt 45 sec lang eine Teerlache entstehen, die das Bewegungstempo von Feinden im Umkreis von 10 Metern verlangsamt. Teer ist entzündbar. Hält 45 sec lang an."},
[62287] = {name = "Teer", description = "[q]Verlangsamt das Bewegungstempo aller in der Nähe befindlichen Feinde um 75%."},
[62292] = {name = "Lohe", description = "[q]Der glühend heiße Teer verbrennt alles in seiner Nähe."},
[62294] = {name = "Heilung", description = "[q]Heilt Euer Ziel um 295 to 341."},
[62297] = {name = "Hodirs Furor", description = "[q]Fügt allen nahen Feinden 150000 Frostschaden zu und schließt sie in Eis ein."},
[62299] = {name = "Geschwindigkeitsschub", description = "[q]Feuert die Booster des Motorrads ab, was seine Geschwindigkeit 5 sec lang um 100% erhöht."},
[62301] = {name = "Kosmischer Schlag", description = "[q]Ruft einen abyssischen Brocken vom Himmel herbei, der Feuerschaden verursacht."},
[62306] = {name = "Brocken schleudern", description = "[q]Schleudert einen massiven Brocken in die Ferne, fügt allen Feinden innerhalb von 8 Metern um den Einschlagsort 27000 to 33000 Schaden zu."},
[62308] = {name = "Rammen", description = "[q]Rammt alle Feinde, die sich vor dem Verwüster befinden, verursacht 19000 to 21000 Schaden und stößt sie zurück. Verursacht zudem bei Gebäuden 2160 to 2640 Belagerungsschaden."},
[62310] = {name = "Durchbohren", description = "[q]Betäubt und fügt 5 sec lang alle 1 Sek. 16650 to 19350 körperlichen Schaden zu."},
[62312] = {name = "Netz", description = "[q]Macht einen Feind 2 sec lang bewegungsunfähig."},
[62315] = {name = "Säureatem", description = "[q]Spuckt ätzende Galle auf ein Ziel und nahe Feinde, verursacht dabei 6000 Naturschaden und 18 sec lang alle 2 Sek. zusätzliche 1500 Schaden."},
[62316] = {name = "Feger", description = "[q]Fegt die Beine eines Gegners weg, verursacht dabei 3500 Schaden und schlägt ihn 3 sec lang nieder."},
[62317] = {name = "Verwüsten", description = "[q]Zerreißt die Rüstung des Ziels und ruft den Effekt 'Rüstung zerreißen' hervor. Verursacht zusätzlich 90% Waffenschaden plus 50 pro Stapel dieses Effektes auf dem Ziel. 'Rüstung zerreißen' ist bis zu 5-mal stapelbar."},
[62318] = {name = "Stachelpfeil", description = "[q]Schießt einen Pfeil ab, der sich Fleisch des Gegners verhakt. Verursacht 4513 to 4987 körperlichen Schaden und 12 sec lang alle 3 Sek. 500 Schaden."},
[62320] = {name = "Aura der Schnelligkeit", description = "[q]Erhöht das Nahkampf-, Distanzangriffs- und Zaubertempo von nahen Verbündeten um 20%."},
[62321] = {name = "Runenschild", description = "[q]Umgibt den Zaubernden mit einer Runenbarriere, die 40000 magischen Schaden absorbiert und den erlittenen körperlichen Schaden um 50% verringert."},
[62322] = {name = "Runischer Schlag", description = "[q]Schlägt mit einer elektrifizierten Waffe nach einem Gegner, und fügt dabei Naturschaden zu, der Waffenschaden plus 1000 entspricht."},
[62324] = {name = "Passagier werfen", description = "[q]Schleudert einen Passagier in die Ferne.\n\n\"Früher haben wir für so was Goblins benutzt.\""},
[62325] = {name = "Bebende Erde", description = "[q]Fügt allen Feinden innerhalb eines Umkreises von 100 Metern 8550 to 9450 körperlichen Schaden zu und unterbricht 8 sec lang jegliches Wirken von Zaubern."},
[62326] = {name = "Unter die Gürtellinie", description = "[q]Trifft den Gegner mit einem fiesen Trick, fügt körperlichen Schaden zu und verringert den vom Ziel verursachten Schaden 20 sec lang um 3%."},
[62327] = {name = "Runenblitz", description = "[q]Trifft einen Feind mit einem Runenblitz und fügt 6563 to 8437 Naturschaden zu."},
[62328] = {name = "Runische Besserung", description = "[q]Heilt einen Verbündeten um 6938 to 8062, heilt dann 15 sec lang alle 3 Sek. um zusätzliche 3000."},
[62331] = {name = "Durchbohren", description = "[q]Ein hinterhältiger Angriff, der das Ziel bis zu einer Heilung von über 90% der maximalen Gesundheit stark bluten lässt. Verursacht 1 min lang oder bis zur Heilung alle 2 Sek. 1125 Schaden."},
[62332] = {name = "Schildschmettern", description = "[q]Fügt einem Feind 5088 to 5912 körperlichen Schaden zu und betäubt ihn 3 sec lang."},
[62333] = {name = "Erneuerung", description = "[q]Heilt ein befreundetes Ziel 15 sec lang alle 3 Sek. um 9263 to 9737."},
[62334] = {name = "Große Heilung", description = "[q]Ruft heilige Magie herbei, um einen Verbündeten zu heilen."},
[62335] = {name = "Heilige Pein", description = "[q]Zerschmettert einen Feind und fügt ihm 4950 to 6050 Heiligschaden zu."},
[62337] = {name = "Versteinerte Rinde", description = "[q]Nahkampfangriffe und Fähigkeiten werden 30 sec lang reflektiert.\n 60 Aufladungen."},
[62338] = {name = "Runenbarriere", description = "[q]Ein Schild aus Runenmacht umgibt den Zaubernden, welcher den erlittenen Schaden um 50% verringert und Angreifern 2000 Arkanschaden zufügt."},
[62339] = {name = "Zerkrachen", description = "[q]Fügt allen Feinden in einem kegelförmigen Bereich von 10 Metern vor dem Zaubernden 37000 to 43000 körperlichen Schaden zu."},
[62340] = {name = "Passagier geladen", description = "[q]Der Katapultarm wurde mit einem Passagier geladen."},
[62344] = {name = "Fäuste aus Stein", description = "[q]Die Steinfäuste verlangsamen das Bewegungstempo des Zaubernden um 20% und erhöhen den verursachten Schaden um 250%. Zusätzlich haben die vernichtenden Angriffe die Chance, dem Ziel die Knochen zu brechen, was dessen Chance zu blockieren, auszuweichen oder zu parieren 20 sec lang um 100% senkt."},
[62345] = {name = "Rammen", description = "[q]Rammt alle Feinde, die sich vor dem Verwüster befinden, verursacht 22501 to 27501 Schaden und stößt sie zurück. Verursacht zudem bei Gebäuden 2850 to 3150 Belagerungsschaden."},
[62346] = {name = "Volldampf voraus!", description = "[q]Schaltet die Dampfmaschine eine kurze Zeit lang in den Overdrive und lässt sie nach vorne schnellen. Verursacht dabei Belagerungsschaden und schleudert Gegner zurück."},
[62354] = {name = "Gebrochene Knochen", description = "[q]Ein massiver Angriff, der die Knochen des Ziels bricht, wodurch es nicht mehr Blocken, Ausweichen oder Parieren kann."},
[62358] = {name = "Kanone abfeuern", description = "[q]Feuert die Kanone ab."},
[62359] = {name = "Luftabwehrrakete", description = "[q]Feuert ein explosives Geschoss ab, das beim Kontakt mit Luftzielen explodiert."},
[62373] = {name = "Geschmolzen", description = "[q]Der Zaubernde wird geschmolzen, sein Tempo um 100% erhöht. Pro Sekunde schlagen Flammen aus dem Zaubernden und verursachen an allen Feinden innerhalb von 7 Metern 1885 to 2115 Feuerschaden."},
[62374] = {name = "Verfolgt", description = "[q]Veranlasst, dass die Maschine ihr Primärziel neuberechnet."},
[62376] = {name = "Rammbock", description = "[q]Rammt das Ziel, verursacht Schaden und stößt es nach vorn."},
[62382] = {name = "Spröde", description = "[q]Das kalte Wasser lässt die geschmolzenen Eisenkonstrukte schnell aushärten, wodurch sie handlungsunfähig werden. Zusätzlich werden sie zerspringen, wenn sie in diesem Zustand mehr als 5000 Schaden erleiden. Dabei wird allen Feinden innerhalb von 10 Metern 18850 to 21150 Schaden zugefügt."},
[62383] = {name = "Zertrümmern", description = "[q]Verursacht an allen Feinden innerhalb von 18850 to 21150 Metern 18850 to 21150 körperlichen Schaden. Der Schaden durch diesen Effekt ignoriert Rüstung."},
[62385] = {name = "Hellblatts Essenz", description = "[q]Die Essenz des Ältesten Hellblatt erhöht den von Freya und ihren Verbündeten der Natur verursachten Zauberschaden um 50%."},
[62386] = {name = "Steinrindes Essenz", description = "[q]Die Essenz des Ältesten Steinrinde erhöht den von Freya verursachten körperlichen Schaden um 50%."},
[62391] = {name = "Blitzkugel beschwören", description = "[q]Beschwört einen überladenen Energieball, der alles in seinem Weg zerstört."},
[62395] = {name = "Flammenkanone", description = "[q]Feuert die Kanone auf ein zufälliges feindliches Ziel ab."},
[62396] = {name = "Flammendüsen", description = "[q]Verursacht bei allen Feinden um den Zaubernden herum 3000 Feuerschaden."},
[62399] = {name = "Kern überladen", description = "[q]Überlädt den Kraftkern des Flammenleviathans."},
[62400] = {name = "Geschosssalve", description = "[q]Feuert eine Geschosssalve in die Luft. Die Geschosse schlagen ein zufälliges nahes Ziel ein und verursachen 3770 to 4230 Schaden."},
[62402] = {name = "Sengende Flamme", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[62411] = {name = "Stampfen", description = "[q]Verursacht bei nahen Gegnern 8313 to 10687 körperlichen Schaden zu und stößt sie zurück."},
[62437] = {name = "Bebende Erde", description = "[q]Fügt allen Feinden in einem kegelförmigen Bereich von 50000 Metern vor dem Zaubernden 5250 to 6750 körperlichen Schaden zu und unterbricht 8 sec lang das Wirken von Zaubern."},
[62441] = {name = "Erneuerung", description = "[q]Heilt ein befreundetes Ziel 15 sec lang alle 3 Sek. um 13650 to 14350."},
[62442] = {name = "Große Heilung", description = "[q]Ruft heilige Magie herbei, um einen Verbündeten zu heilen."},
[62443] = {name = "Heilige Pein", description = "[q]Zerschmettert einen Feind und fügt ihm 7650 to 9350 Heiligschaden zu."},
[62444] = {name = "Heldenhafter Stoß", description = "[q]Ein starker Angriff, der 3938 to 5062 zusätzlichen körperlichen Schaden verursacht."},
[62451] = {name = "Instabile Energie", description = "[q]Verursacht bei Feinden innerhalb von 4 Metern 10 sec lang alle 1 Sek. 4875 to 5125 Naturschaden."},
[62467] = {name = "Ausgelaugt", description = "[q]Der Älteste ist vom Verleihen seiner Kräfte an Freya erschöpft."},
[62469] = {name = "Eiskälte", description = "[q]Verursacht Frostschaden bei in der Nähe befindlichen Feinden und macht sie bis zu 10 sec lang bewegungsunfähig."},
[62471] = {name = "Erhöhte Geschwindigkeit", description = "[q]Injiziert flüssiges Pyrit in das Getriebe und erhöht das Bewegungstempo um 100%. Hält 20 sec lang an."},
[62475] = {name = "Systemabschaltung", description = "[q]Systeme überlastet, erlittener Schaden um 50% erhöht."},
[62478] = {name = "Gefrorene Schläge", description = "[q]Körperlicher Schaden um 70% verringert, Angriffe verursachen jedoch zusätzlich 31062 Frostschaden, zudem erleiden Feinde alle 2 Sek. 4000 Frostschaden. Hält 20 sec lang an."},
[62488] = {name = "Konstrukt aktivieren", description = "[q]Schleudert Magma nach einem zufälligen, deaktivierten Eisenkonstrukt, aktiviert es und lässt es den Zaubernden verteidigen."},
[62490] = {name = "Pyritfass werfen", description = "[q]Schleudert eine Kugel blaues Pyrit in die Ferne und verursacht Schaden, verbraucht jedoch Munition."},
[62494] = {name = "Flüssiges Pyrit", description = "[q]Enthält flüssiges Pyrit.\n Kann von Verwüstermechanikern geborgen werden."},
[62507] = {name = "Berührung der Vorherrschaft", description = "[q]Unterwirft das Ziel dem Willen Yogg-Sarons, schwächt ihn und stellt jedoch eine gewisse Art von Kontrolle her."},
[62519] = {name = "Einstimmung auf die Natur", description = "[q]Erhöht die erhaltene Heilung um 8%."},
[62522] = {name = "Elektroschock", description = "[q]Schockt alle Ziele in einem kegelförmigen Bereich von 25 Metern vor dem Zaubernden. Unterbricht außerdem das Wirken von Zaubern und verhindert 4 sec lang, dass diese Art Zauber gewirkt werden können."},
[62526] = {name = "Runendetonation", description = "[q]Macht ein Ziel durch einen Runenzauberschutz unbeweglich. Nach Ablauf von 4 sec detoniert er und verursacht 11250 to 13750 Feuerschaden um das Ziel."},
[62528] = {name = "Berührung von Eonar", description = "[q]Die Anwesenheit der Lebensbinderin heilt Freya alle 1 Sek. um 6000 Gesundheit."},
[62532] = {name = "Griff des Konservators", description = "[q]Der Griff des Beschützers verhindert Angriffe und das Wirken von Zaubern."},
[62541] = {name = "Potente Pheromone", description = "[q]Die potenten Pheromone wehren den Griff des Konservators ab.\n Erhöht den verursachten Schaden um 0%"},
[62546] = {name = "Versengen", description = "[q]Feuert einen Flammenstrahl ab, der alle Gegner innerhalb von 30 Metern vor dem Zaubernden verbrennt und 3 sec lang alle 0,5 Sek. 2357 to 2643 Feuerschaden verursacht. Zusätzlich erhitzt sich der Boden und fängt Feuer, wodurch alle Feinde innerhalb von 13 Metern pro Sekunde 1885 to 2115 Feuerschaden erleiden. Eisenkonstrukte innerhalb dieses Bereichs werden sich aufheizen und letztendlich schmelzen."},
[62548] = {name = "Versengen", description = "[q]Feuert einen Flammenstrahl ab, der alle Gegner innerhalb von 30 Metern vor dem Zaubernden verbrennt und 3 sec lang alle 0,5 Sek. 2357 to 2643 Feuerschaden verursacht. Zusätzlich erhitzt sich der Boden und fängt Feuer, wodurch alle Feinde innerhalb von 13 Metern pro Sekunde 1885 to 2115 Feuerschaden erleiden. Eisenkonstrukte innerhalb dieses Bereichs werden sich aufheizen und letztendlich schmelzen."},
[62563] = {name = "Sturmangriff", description = "[q]Ein Sturmangriff, der 45000 Schaden verursacht und eine Aufladung von 'Verteidigen' vom Gegner entfernt."},
[62575] = {name = "Schildbrecher", description = "[q]Ein Distanzangriff, der 2000 Schaden verursacht und eine Aufladung von 'Verteidigen' vom Gegner entfernt."},
[62580] = {name = "Frostblitzsalve", description = "[q]Fügt in der Nähe befindlichen Feinden Frostschaden zu und verringert 4 sec lang ihr Bewegungstempo."},
[62581] = {name = "Warzen!", description = "[q]Durch Eure sorglosen Froschküss-Eskapaden habt Ihr Euch Warzen eingehandelt."},
[62583] = {name = "Frostblitz", description = "[q]Fügt einem Feind Frostschaden zu und verringert 4 sec lang sein Bewegungstempo."},
[62584] = {name = "Geschenk der Lebensbinderin", description = "[q]Eonars Geschenk heilt Freya und alle ihre Diener um 30% ihrer Gesundheit."},
[62589] = {name = "Furor der Natur", description = "[q]Infiziert einen Feind 10 sec lang, sodass er seinen nahen Verbündeten alle 2 Sek. Naturschaden zufügt."},
[62597] = {name = "Frostnova", description = "[q]Verursacht 7069 to 7931 Frostschaden bei in der Nähe befindlichen Feinden und macht sie 6 sec lang bewegungsunfähig."},
[62598] = {name = "Detonieren", description = "[q]Die Detonation verursacht 4163 to 4837 Feuerschaden an Feinden innerhalb von 15 Metern."},
[62608] = {name = "Flammenpeitsche", description = "[q]Der Peitscher verursacht Feuerschaden entsprechend 150% seines Nahkampfschadens."},
[62613] = {name = "Sturmangriff", description = "[q]Stürmt auf einen Gegner zu, verursacht 4625 to 5375 körperlichen Schaden und stößt ihn zurück."},
[62619] = {name = "Pheromone", description = "[q]Die potenten Pheromone wehren den Griff des Beschützers ab."},
[62623] = {name = "Sonnenstrahl", description = "[q]Fügt Feinden innerhalb von 8 Metern um das Ziel 5088 to 5912 Naturschaden zu."},
[62625] = {name = "Erde erschüttern", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden 65801 to 74199 Naturschaden zu."},
[62632] = {name = "Wirbelsturm", description = "[q]Greift in der Nähe befindliche Feinde mit einem Wirbel aus Stahl an, der 15 sec lang anhält und normalen Schaden plus 110 verursacht."},
[62634] = {name = "Mörser", description = "[q]Lässt ein winziges explosives Geschoss einschlagen, dass allen Feinden im Zielgebiet Feuerschaden zufügt."},
[62649] = {name = "Sturmblitz", description = "[q]Fügt einem Feind 6013 to 6987 Schaden zu."},
[62650] = {name = "Härte des Frosts", description = "[q]Erhöht den verursachten Schaden um 10% und verringert den von allen Quellen erlittenen Schaden um 20%."},
[62653] = {name = "Flutwelle", description = "[q]Stürmt 40 Meter durch in einer Reihe befindliche Gegner."},
[62660] = {name = "Schattengeschoss", description = "[q]Feuert ein Geschoss auf ein zufälliges Ziel. Landet das Geschoss, verursacht es bei allen Feinden innerhalb eines Radius von 10 Metern 11310 to 12690 Schattenschaden."},
[62661] = {name = "Sengende Flammen", description = "[q]Fügt allen Feinden innerhalb von 100 Metern 13875 to 16125 Feuerschaden zu, überhitzt 10 sec lang ihre Rüstung und verringert so ihren Schutz um 75%."},
[62662] = {name = "Woge der Nacht", description = "[q]Erfüllt den Zaubernden mit dunkler Macht, die den körperlichen Schaden um 100% erhöht, das Bewegungstempo jedoch um 55% verringert. Hält 10 sec lang an."},
[62663] = {name = "Gehärtete Rinde", description = "[q]Gegen den Peitscher geführte Angriffe lassen die schützende Borke wachsen und erhöhen seinen Schaden, verlangsamen jedoch sein Bewegungstempo. Stapelbar."},
[62666] = {name = "Flügelstoß", description = "[q]Schleudert Feinde in einem Radius um den Zaubernden weg."},
[62670] = {name = "Widerstandskraft der Natur", description = "[q]Erhöht den verursachten Schaden um 10% die erhaltene Heilung aus allen Quellen um 20%."},
[62671] = {name = "Schnelligkeit des Gedankens", description = "[q]Erhöht allen verursachten Schaden um 10% und das Bewegungstempo um 20%."},
[62678] = {name = "Verbündete der Natur beschwören", description = "[q]Beschwört Verbündete von Freya, um in der Schlacht um die Sicherheit Ulduars Hilfe zu leisten."},
[62680] = {name = "Flammenstrahlen", description = "[q]Stampft auf den Boden, wodurch sich Flammengeysire unter allen Feinden öffnen. Diese Geysire verursachen 5655 to 6345 Feuerschaden, schleudern die Ziele in die Luft und unterbrechen 8 sec lang alle gewirkten Zauber. Zusätzlich erleiden die Ziele 8 sec lang 1000 Feuerschaden pro Sekunde."},
[62692] = {name = "Aura der Verzweiflung", description = "[q]Verhindert die Manaregeneration aus beinahe allen natürlichen Quellen. Die Fähigkeiten 'Aspekt der Viper', 'Richturteil des Weisen', 'Schamanistische Wut' und 'Einklang des Geistes' bleiben zumindest teilweise effektiv. Verringert auch das Nahkampfangriffstempo um 20%."},
[62702] = {name = "Furor des Sturms", description = "[q]Erhöht den verursachten Schaden 10% und die gesamte Gesundheit um 20%."},
[62709] = {name = "Gegenangriff!", description = "[q]Ein Gegenangriff, der 35000 Schaden verursacht und eine Aufladung von 'Verteidigen' vom Gegner entfernt."},
[62710] = {name = "Strong Guy Sundering Thrust Aura", description = nil},
[62756] = {name = "Stag Fire Wall", description = nil},
[62775] = {name = "Betäubender Wutanfall", description = "[q]Fügt allen Feinden in einem Bereich von 500 Metern Schaden zu und verringert das Bewegungstempo 4 sec lang um 10%."},
[62793] = {name = "Zorn", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm Naturschaden zu."},
[62796] = {name = "Feuerball", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[62804] = {name = "Verjüngung", description = "[q]Heilt einen Verbündeten 12 sec lang alle 3 Sek."},
[62809] = {name = "Große Heilung", description = "[q]Ruft heilige Magie herbei, um alle nahen Verbündeten zu heilen."},
[62818] = {name = "Schießen", description = "[q]Schießt auf einen Zerstörer oder Eindringling und verursacht körperlichen Schaden."},
[62821] = {name = "Wärmendes Feuer", description = "[q]Wärmt nahe befreundete Kreaturen, verhindert Einfrierungseffekte und beißende Kälte und erhöht die Willenskraft um 4. Zusätzlich haben Zauber und Distanzangriffe eine Chance, Gegner anzusengen. Das wärmende Feuer kann von fallendem Eis oder mächtiger Frostmagie gelöscht werden."},
[62822] = {name = "Heiltrank trinken", description = "[q]Benutzt ein heiliges Elixier, um den Wächter zu heilen."},
[62823] = {name = "Wärmendes Feuer herbeizaubern", description = "[q]Zaubert ein Feuer herbei, das nahe befreundete Einheiten wärmt. Es kann von fallendem Eis oder Blitzeis gelöscht werden."},
[62831] = {name = "Roboter wiederaufladen", description = "[q]Lädt einen Roboter auf dem Schrotthaufen wieder auf und erweckt ihn zum Leben."},
[62834] = {name = "Bumm", description = "[q]Explodiert beim Tod, fügt allen nahen Einheiten Feuerschaden zu."},
[62845] = {name = "Kniesehne", description = "[q]Verursacht an einem Feind 20% Waffenschaden und verringert sein Bewegungstempo 10 sec lang um 60%."},
[62856] = {name = "Unsichtbarkeit entdecken", description = "[q]Ermöglicht es Orux, dem dreimal Verdammten, geringe Unsichtbarkeit zu entdecken."},
[62863] = {name = "Duell", description = "[q]Fordert einen anderen Kämpfer zum berittenen Duell heraus!"},
[62906] = {name = "Freyas Wall", description = "[q]Fügt allen nahen Feinden 13500 to 16500 Naturschaden zu und beschwört Wächter der Natur, um den Feind zu umstellen."},
[62909] = {name = "Mimirons Inferno", description = "[q]Ferursacht an allen nahen Feinden 13500 to 16500 Feuerschaden und hinterlässt eine Lohe am Boden."},
[62911] = {name = "Thorims Hammer", description = "[q]Fügt allen nahen Feinden 13500 to 16500 Sturmschlagschaden zu."},
[62939] = {name = "Blitzpeitsche", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 5 Ziele aus und verursacht bei jedem folgenden Ziel immer größeren Naturschaden."},
[62942] = {name = "Runenverstärkung", description = "[q]Stärkt nahe Verbündete."},
[62944] = {name = "Kodostampfen", description = nil},
[62960] = {name = "Sturmangriff", description = "[q]Ein Sturmangriff, der 8500 Schaden verursacht und eine Aufladung von 'Verteidigen' vom Gegner entfernt."},
[62974] = {name = "Überschallhupe", description = "[q]Schickt dem Motorrad eine Kraftwelle voraus. Verursacht in einem frontalen kegelförmigen Bereich von 35 Metern an feindlichen Zielen 6300 to 7700 Schaden."},
[62975] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[62997] = {name = "Plasmaeruption", description = "[q]Ein Strom aus Plasma schießt aus der Kanone und verursacht 6 sec lang 17000 Zauberfeuerschaden pro Sekunde. Diesem Effekt kann nicht widerstanden werden."},
[63003] = {name = "Sturmangriff", description = "[q]Ein Sturmangriff, der 17000 Schaden verursacht und eine Aufladung von 'Verteidigen' vom Gegner entfernt."},
[63005] = {name = "Herausforderer", description = "Während ihr Champions herausfordert könnt Ihr nicht durch die Argentumheilbrunnen geheilt werden."},
[63007] = {name = "Wächterpheromone", description = "[q]Emittiert in einem Umkreis um den Zaubernden Aggregationspheromone."},
[63009] = {name = "Explosion", description = "[q]Verursacht bei allen Feinden innerhalb von 3 Metern 12000 Feuerschaden."},
[63010] = {name = "Sturmangriff", description = "[q]Ein Sturmangriff, der 8500 Schaden verursacht und eine Aufladung von 'Verteidigen' vom Gegner entfernt."},
[63018] = {name = "Sengendes Licht", description = "[q]Lädt das Ziel mit göttlicher Energie auf und versengt 9 sec lang nahe Verbündete."},
[63024] = {name = "Gravitationsbombe", description = "[q]Lädt das Ziel mit dunkler Energie auf und lässt es explodieren. Nach Ablauf von 10 sec wird das Ziel nahe Verbündete zu sich hin ziehen."},
[63027] = {name = "Annäherungsminen", description = "[q]Wirft Annäherungsminen in zufällige Richtungen, die in 15 Metern Entfernung landen. Diese Minen zünden, wenn sich ihnen Feinde bis auf 3 Meter nähern, und verursachen bei allen Feinden innerhalb von 3 Metern 12000 Zauberfeuerschaden."},
[63028] = {name = "Force Cast", description = nil},
[63032] = {name = "Sternenfeuer", description = "[q]Fügt dem Ziel 694 to 806 Arkanschaden zu."},
[63038] = {name = "Dunkle Salve", description = "[q]Schleudert Geschosse aus dunkler Magie, verursacht Schattenschaden und verwundet nahe Gegner. Die Wirksamkeit jeglicher Heilung wird 10 sec lang um 25% verringert."},
[63042] = {name = "Gedanken beherrschen", description = "[q]Beherrscht die Gedanken des Ziels und bringt es eine Zeit lang unter Yogg-Sarons Kontrolle. Verringert zusätzlich die geistige Gesundheit um 4%."},
[63044] = {name = "Wurzeln versetzen", description = nil},
[63047] = {name = "Wächterpeitsche", description = "[q]Verursacht 11100 to 12900 Naturschaden und 6 sec lang zusätzlich alle 1 Sek. 2313 to 2687 Naturschaden."},
[63059] = {name = "Bestäuben", description = "[q]Bestäubt einen Wächterpeitscher, heilt ihm um 15% und erhöht 30 sec lang seinen verursachten Schaden um 25%."},
[63082] = {name = "Leben binden", description = "[q]Heilt 6 sec lang alle 1 Sek. um 9250 to 10750."},
[63111] = {name = "Frostspeer", description = "[q]Der Frostspeer verursacht 7863 to 9137 Frostschaden sowie zusätzlich alle 5 100 Frostschaden. Verlangsamt das Angriffs- und Zaubertempo um 100%. Hält until cancelled lang an."},
[63134] = {name = "Saras Segen", description = "[q]Heilt ein befreundetes Ziel um 27000 to 33000, verursacht jedoch im Verlauf von 20 sec 60000 Schattenschaden."},
[63136] = {name = "Umarmung des Winters", description = "[q]Ein gefrorener Schild, der Schaden absorbiert und den verursachten körperlichen Schaden um 100% erhöht. Hält 1 min lang an."},
[63138] = {name = "Saras Eifer", description = "[q]Erhöht den Schaden eines freundlichen Ziels um 20%, erhöht jedoch auch seinen erlittenen Schaden um 100%. Hält 15 sec lang an."},
[63147] = {name = "Saras Wut", description = "[q]Fügt alle 3 Sek. 12500 Schattenschaden zu, erhöht jedoch den verursachten körperlichen Schaden um 12000. Hält 12 sec lang an."},
[63148] = {name = "Gottesschild", description = "[q]Schützt den Paladin 12 sec lang vor jeglichem Schaden und jeglichen Zaubern, erhöht jedoch die Zeit zwischen seinen Angriffen um 100%. Einmal geschützt, kann der Paladin 1 min lang nicht erneut durch 'Gottesschild', 'Göttlicher Schutz' oder 'Hand des Schutzes' unverwundbar gemacht werden."},
[63149] = {name = "Gewalttätige Erde", description = "[q]Die gewalttätige Erde reißt unter den Füßen der Ziele auf und schleudert sie nach oben. Fügt allen innerhalb von 4 Metern um das Ziel 9713 to 11287 Naturschaden zu."},
[63169] = {name = "Gelenke versteinern", description = "[q]Verursacht Naturschaden und verringert 20 sec lang das Bewegungstempo um 25%."},
[63208] = {name = "Gorka Ritt", description = nil},
[63226] = {name = "Giftatem", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden 7863 to 9137 Naturschaden zu sowie 12 sec lang alle 3 Sek. zusätzlich 3700 to 4300 Naturschaden."},
[63227] = {name = "Wutanfall", description = "[q]Erhöht das Angriffstempo um 100% und den verursachten Schaden um 50%. Hält 8 sec lang an."},
[63233] = {name = "Nekroschock", description = "[q]Ein Distanzangriff, der 814 Schaden verursacht und eine Aufladung von 'Verteidigen' vom Gegner entfernt."},
[63236] = {name = "Verschlingende Flamme", description = "[q]Speit eine Lavabombe auf einen Feind und verursacht Feuerschaden."},
[63240] = {name = "Eisenwurzeldornen", description = "[q]Aus dem befreundeten Ziel sprießen Dornen, die jedem Angreifer 650 Naturschaden zufügen. Hält 10 sec lang an."},
[63241] = {name = "Gelassenheit", description = "[q]Regeneriert 10 sec lang alle 2 Sek. 13875 to 16125 Gesundheit aller Gruppenmitglieder. Kanalisiert."},
[63242] = {name = "Pflege", description = "[q]Heilt ein freundliches Ziel um 23125 to 26875. Ist ein durch Euch gewirkter Effekt von 'Verjüngung', 'Nachwachsen' oder 'Blühendes Leben' auf dem Ziel aktiv, erhöht sich die Heilung um zusätzliche 20%."},
[63247] = {name = "Lebendiger Tsunami", description = "[q]Ihr beschwört einen lebendigen Tsunami, der bei Kontakt mit feindlichen Zielen 6013 to 6987 Naturschaden verursacht sowie 10 sec lang alle 2 Sek. 1850 to 2150 Naturschaden."},
[63272] = {name = "Hurrikan", description = "[q]Erzeugt einen wütenden Sturm im Zielgebiet, der allen in der Nähe befindlichen Feinden alle 2 Sek. 7863 to 9137 Naturschaden zufügt und die Zeit zwischen Angriffen betroffener Feinde um 67% erhöht. Hält 10 sec lang an. Muss kanalisiert werden, um den Zauber aufrechtzuerhalten."},
[63275] = {name = "Angeln", description = "[q]Nehmt eine Angelrute zur Hand und sucht Euch ein Gewässer zum Angeln. Klickt mit der rechten Maustaste auf den Köder im Wasser, wenn sich dieser bewegt, um den Fisch zu fangen. Bei höherer Fertigkeit verbessern sich Eure Aussichten, in Bereichen mit höherer Stufe erfolgreich zu angeln."},
[63276] = {name = "Mal der Gesichtslosen", description = "[q]Belegt ein Ziel mit einem uralten Fluch. Feinden in der Nähe des Ziels werden 10 sec lang alle 1 Sek. 5000 Gesundheit entzogen."},
[63317] = {name = "Flammenatem", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden 13125 to 16875 Feuerschaden zu."},
[63341] = {name = "Tragbarer Düsenantrieb", description = "[q]Es ist ein Vogel... es ist ein Flugzeug... es ist Mimiron!"},
[63356] = {name = "Überkopfkracher", description = "[q]Der massive Angriff verursacht körperlichen Schaden und verringert die Rüstung 20 sec lang um 20%."},
[63364] = {name = "Saronitbarriere", description = "[q]Erschafft eine Saronitbarriere, die jeglichen erlittenen Schaden um 99% verringert."},
[63414] = {name = "Hinaufdrehen", description = "[q]Lässt die Zwillingskanonen 4 sec lang warmlaufen, bevor eine Lasersalve in eine zufällige Richtung gefeuert wird. Feinde im Sperrfeuer erleiden alle 0.3 Sek. 20000 Zauberfeuerschaden. Hält 10 sec lang an."},
[63420] = {name = "Wahre Dunkelheit", description = "[q]Verursacht bei allen Feinden 750 Schaden und erhöht den erlittenen Schattenschaden um 10% pro Anwendung."},
[63499] = {name = "Magiebannung", description = "[q]Bannt auf dem Verbündeten liegende Magie, entfernt 1 schädlichen Zauber."},
[63507] = {name = "Ruin der Seuchenbestien", description = "[q]Ein schneller Schlag, der Seuchenbestien der Anub'ar 4050 to 4950 Schaden zufügt. Dieser Angriff kann in sehr schneller Folge ausgeführt werden."},
[63520] = {name = "Schredderklinge des Kriegshymnenklans", description = "[q]Visiert ein Sprengstofffass der Horde an, um es zu zerstören. Die Klinge kann auch auf ein Kriegshymnenziel geschleudert werden, um 158 to 192 körperlichen Schaden zu verursachen. Verhindert Plündern und Erfahrung beim betroffenen Ziel. Kann nicht bei einem Hordenziel des Kriegshymnenklans angewendet werden, das bereits von einem anderen Spieler beansprucht wurde."},
[63527] = {name = "Aufgeladene Sphäre beschwören", description = "[q]Beschwört eine elektrische Sphäre, die einem Verbündeten als Kraftfokus dient."},
[63528] = {name = "Überladen", description = "[q]Erhöht den verursachten Schaden des Ziels um 60% und lässt die Angriffe des Ziels mächtige, elektrische Ladungen verursachen. Der Zaubernde wird dabei geopfert."},
[63539] = {name = "Trennungsangst", description = "[q]Trennung von einem Gefährten verursacht einen Kraftschub."},
[63541] = {name = "Gabelblitzschlag", description = "[q]Fügt Gegnern in einem kegelförmigen Bereich vor dem Zaubernden Naturschaden zu."},
[63546] = {name = "Stampfen", description = "[q]Stampft auf den Boden und verursacht an nahen Gegnern normalen Schaden."},
[63573] = {name = "Einarmiger Überkopfkracher", description = "[q]Der nicht so massive Angriff verursacht körperlichen Schaden und verringert die Rüstung 20 sec lang um 20%."},
[63575] = {name = "Rauchfahne", description = "[q]Die Nachwirkung der Explosion lässt den Passagier eine kurze Zeit lang fliegen."},
[63601] = {name = "Verstärkte Eiserne Wurzeln", description = nil},
[63603] = {name = "Dengeln!", description = "[q]Wenn Ihr den Schredder an genau der richtigen Stelle schlagt -- oder einfach lange genug -- repariert Ihr ihn um 225 to 275 Gesundheit. Kann nicht im Kampf oder während einer Bewegung benutzt werden."},
[63612] = {name = "Blitzschlagmal", description = "[q]Schockt das Ziel für 2500 Naturschaden und erhöht den erlittenen Naturschaden 20 sec lang um 40%."},
[63615] = {name = "Rüstung verheeren", description = "[q]Verringert die Rüstung des Ziels um 5%."},
[63618] = {name = "Überladung", description = "[q]Feuert einen Orbitalschlag, der sofort alle nahen Feinde tötet, sobald er das Gestaltungsgelände verlässt."},
[63619] = {name = "Kriechende Schatten", description = "[q]Kriecht durch die Schatten zum feindlichen Ziel und erhöht 5 sec lang den vom Schattengeist verursachten Schaden um 15%."},
[63630] = {name = "Rachsüchtige Woge", description = "[q]Der Tod eines Gefährten zieht einen Energieanstieg nach sich, erhöht den verursachten Schaden um 100% und das Angriffstempo 50%."},
[63631] = {name = "Schockschlag", description = "[q]Verursacht bei allen Feinden innerhalb von 15 Metern 100000 Naturschaden."},
[63666] = {name = "Brandbombe", description = "[q]Feuert eine Brandbombe auf ein zufälliges feindliches Ziel. Die Bombe explodiert beim Aufprall und verursacht sofort 7540 to 8460 Feuerschaden sowie 8 sec lang 32000 zusätzlichen Feuerschaden. Dieser Effekt versucht, wenn möglich, Ziele anzuvisieren, die weiter als 15 Meter entfernt sind."},
[63677] = {name = "Hitzewelle", description = "[q]Feuert eine Hitzewelle ab, die sofort 1885 to 2115 Feuerschaden verursacht und 5 sec lang zusätzlich 10000 Feuerschaden an allen Feinden."},
[63689] = {name = "Plasmakugel", description = "[q]Baut eine Plasmakugel auf und feuert sie auf das Ziel ab, verursacht 9425 to 10575 Zauberfeuerschaden."},
[63703] = {name = "Leerenwelle", description = "[q]Entfesselt alle 5 Sek. eine Welle negativer Energie, die das Mana aller in der Nähe befindlicher Feinde verbrennt."},
[63710] = {name = "Leerenbarriere", description = "[q]Umgibt den Zaubernden mit einer undurchdringlichen Leerenbarriere, die allen Schaden fernhält."},
[63713] = {name = "Gedankenkontrolle", description = "[q]Kontrolliert 20 sec lang einen humanoiden Feind."},
[63716] = {name = "Steingebrüll", description = "[q]Entfesselt einen Hagel gezackter Steine, die 2 sec lang alle 0.2 Sek. feindlichen Ziele treffen."},
[63722] = {name = "Schattengeschoss", description = "[q]Feuert ein Geschoss auf ein zufälliges Ziel ab. Wenn das Geschoss einschlägt, verursacht es an Feinden im Umkreis von 0 Metern 11310 to 12690 Schattenschaden."},
[63723] = {name = "Schattennova", description = "[q]Verursacht an allen Feinden innerhalb von 15 Metern 6938 to 8062 Schattenschaden und stößt sie zurück."},
[63753] = {name = "Dolchfächer", description = "[q]Wirft sofort beide Waffen auf alle Ziele innerhalb von 10 Metern und verursacht 100% Waffenschaden."},
[63754] = {name = "Meucheln", description = "[q]Fügt einem Feind 200% normalen Schaden zu, allerdings nur, wenn er von hinten angegriffen wird."},
[63755] = {name = "Tödliches Gift", description = "[q]Fügt 12 sec lang alle 3 Sek. Naturschaden zu."},
[63757] = {name = "Donnerknall", description = "[q]Fügt allen nahen Feinden 4250 to 5750 Naturschaden zu und verringert das Angriffstempo um 20%."},
[63758] = {name = "Frostblitzsalve", description = "[q]Schleudert Eisgeschosse, verursacht an nahen Feinden 3150 to 3850 Frostschaden."},
[63760] = {name = "Große Heilung", description = "[q]Ruft heilige Magie herbei, um einen Verbündeten zu heilen."},
[63761] = {name = "Target Search A", description = "[q]Sucht nach einem zufälligen Ziel A."},
[63762] = {name = "Target Search B", description = "[q]Sucht nach einem zufälligen Ziel B."},
[63763] = {name = "Target Search C", description = "[q]Sucht nach einem zufälligen Ziel C."},
[63764] = {name = "Target Search D", description = "[q]Sucht nach einem zufälligen Ziel D."},
[63774] = {name = "Feuerelementar beschwören", description = "[q]Beschwört 0 Feuerelementare als Begleitung für den Zaubernden bis zur Freigabe."},
[63775] = {name = "Flammenstoß", description = "[q]Lässt eine Flammensäule niedergehen, die alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen 8 sec lang alle 2 Sek. zusätzlichen Schaden zufügt."},
[63784] = {name = "Klingensturm", description = "[q]Greift sofort alle nahen Ziele an. Für die nächsten 8 sec werdet Ihr alle 1 Sek. einen Wirbelwindangriff durchführen. Während 'Klingensturm' aktiv ist, könnt Ihr Euch bewegen, aber während dieser Zeit keine anderen Fähigkeiten einsetzen. Ihr fühlt weder Mitleid, noch Bedauern oder Furcht und es gibt keine Möglichkeit, Euch aufzuhalten, es sei denn, Ihr werdet getötet."},
[63789] = {name = "Feuerball", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[63795] = {name = "Psychose", description = "[q]Verursacht 7500 Schattenschaden. Verringert zusätzlich die geistige Gesundheit um 9%."},
[63802] = {name = "Gehirnverbindung", description = "[q]Verbindet das Gehirn zweier feindlicher Ziele. Betroffene Ziele erleiden 3000 Schattenschaden und verlieren alle 1 Sek. 2% geistiger Gesundheit, wenn sie weiter als 20 Meter auseinander stehen."},
[63806] = {name = "Wirbelwind", description = "[q]Greift Gegner in der Nähe in einem Wirbelwind aus Stahl an, der 4 sec lang anhält und normalen Schaden plus 300 verursacht."},
[63818] = {name = "Kullern", description = "[q]Das donnernde Geröll verursacht 3238 to 3762 körperlichen Schaden an Zielen innerhalb von 7 Metern um den Zaubernden."},
[63830] = {name = "Geisteskrankheit", description = "[q]Verursacht 5000 Schattenschaden und lässt das Ziel 4 sec lang vor Angst davonrennen. Danach versucht der Effekt, auf einen Feind innerhalb von 10 Metern überzuspringen. Verringert zusätzlich die geistige Gesundheit um 3%."},
[63849] = {name = "Freigelegtes Herz", description = "[q]Das Herz des XT-002 liegt frei, wodurch es verwundbar wird."},
[63861] = {name = "Ketten des Gesetzes", description = "[q]Macht einen Feind 5 sec lang bewegungsunfähig."},
[63891] = {name = "Todesstrahl", description = "[q]Entlädt vier Ströme tödlicher Energie, die alle Feinde zerstören, die mit ihnen in Berührung kommen."},
[63894] = {name = "Schattenhafte Barriere", description = "[q]Schützt den Zaubernden vor allen Angriffen und Zaubern."},
[63912] = {name = "Frostnova", description = "[q]Verursacht Frostschaden bei in der Nähe befindlichen Feinden und macht sie bis zu 8 sec lang bewegungsunfähig."},
[63913] = {name = "Frostblitz", description = "[q]Fügt einem Feind Frostschaden zu und verringert 4 sec lang sein Bewegungstempo."},
[63939] = {name = "Verderbnis der Dunkelküste", description = nil},
[63978] = {name = "Steinnova", description = "[q]Die Steinnova verursacht bei allen Zielen innerhalb von 10 Metern um den Zaubernden 5550 to 6450 körperlichen Schaden und erhöht den erlittenen Schaden 10 sec lang um 5%\n Dieser Effekt ist stapelbar."},
[64010] = {name = "Unbeschreiblich", description = "[q]Irgendetwas an dieser Kreatur ist einfach... falsch."},
[64013] = {name = "Unbeschreiblich", description = "[q]Irgendetwas an diesem Gegenstand ist einfach... falsch."},
[64016] = {name = "Flammenpuffer", description = "[q]Erhöht den von einem Gegner erlittenen Feuerschaden 1 min lang um 1000."},
[64041] = {name = "Speer werfen", description = "[q]Feuert eine Harpune auf das Ziel und verursacht 53 to 77 Schaden."},
[64058] = {name = "Psychisches Entsetzen", description = "[q]Ihr versetzt das Ziel 3 sec lang in Angst und Schrecken, sodass es vor Furcht erzittert und für 10 sec all seine Waffen fallen lässt."},
[64059] = {name = "Wahnsinn hervorrufen", description = "[q]Treibt alle nahen Ziele in den Wahnsinn, die geistige Gesundheit wird um 100% verringert."},
[64102] = {name = "Schwarzer Pfeil", description = "[q]Verflucht einen Feind, verursacht 1 min lang regelmäßig Schattenschaden. Falls das Ziel stirbt, während es von 'Schwarzer Pfeil' betroffen ist, wird aus dem Leichnam des Ziels ein Skelett beschworen."},
[64122] = {name = "Explosion des Schwarzen Lochs", description = "[q]Fügt Feinden 16088 to 16912 Schattenschaden zu."},
[64140] = {name = "Schallexplosion", description = "[q]Verursacht an nahen Feinden normalen Schaden plus 3 und verhindert 3 sec lang das Wirken von Zaubern."},
[64141] = {name = "Blitzeis", description = "[q]Fügt in der Nähe befindlichen Feinden Frostschaden zu und friert sie 5 sec lang ein."},
[64144] = {name = "Hervorbrechen", description = "[q]Ein Tentakel schießt aus dem Boden, verursacht an allen Feinden in einem kleinen Gebiet 1488 to 2012 körperlichen Schaden und schleudert sie zurück."},
[64145] = {name = "Kraft schwächen", description = "[q]Schwächt alle Feinde in einem großen Bereich um den Zaubernden, ihr verursachter Schaden wird um 20% verringert. Kanalisiert."},
[64152] = {name = "Trocknendes Gift", description = "[q]Verursacht 21000 Naturschaden an einem Feind und entzieht 18 sec lang 21000 Mana."},
[64153] = {name = "Schwarze Seuche", description = "[q]Steckt einen Feind mit der Seuche an, wodurch dieser sich in regelmäßigen Abständen unkontrollierbar übergibt."},
[64156] = {name = "Apathie", description = "[q]Verringert das Angriffs- und Zaubertempo eines Feindes um 60% und verlangsamt Bewegungen um 60%. Hält 20 sec lang."},
[64157] = {name = "Fluch der Verdammnis", description = "[q]Verdammt das Ziel, wodurch es nach Ablauf von 12 sec 20000 Schattenschaden erleidet."},
[64159] = {name = "Blutsauger", description = "[q]Überträgt alle 1 Sek. 2000 Gesundheit vom Ziel auf den Zaubernden. Hält 5 sec lang an."},
[64162] = {name = "Geschwächt", description = "[q]Unvergängliche Wachen sind anfällig gegen 'Titanischer Sturm', wenn ihre Gesundheit niedrig ist."},
[64163] = {name = "Wahnsinniger Blick", description = "[q]In den Schlund des Wahnsinns zu blicken bringt Euren Geist aus dem Gleichgewicht. Solange Ihr den Zaubernden anseht, erleidet Ihr alle 1 Sek. 5700 to 6300 Schattenschaden und Eure geistige Gesundheit wird um 4% verringert."},
[64167] = {name = "Wahnsinniger Blick", description = "[q]In den lachenden Schädel zu blicken bringt Euren Geist aus dem Gleichgewicht, verursacht 1750 Schattenschaden und verringert Eure geistige Gesundheit alle 1 Sek. um 2%, solange Ihr den Zaubernden anblickt."},
[64169] = {name = "Brunnen der geistigen Gesundheit", description = "[q]Im Brunnen zu stehen bringt Euch wieder zur Besinnung, Ihr regeneriert alle 2 Sek. 20% geistige Gesundheit. Während Ihr im Brunnen steht, wird Euer erlittener Schaden um 50% verringert."},
[64170] = {name = "Brunnen der geistigen Gesundheit", description = "[q]Zaubert einen Brunnen der geistigen Gesundheit herbei, der die geistige Gesundheit von jedem, der sich in ihm befindet, regeneriert."},
[64171] = {name = "Titanischer Sturm", description = "[q]Kanalisiert einen mächtigen Sturm titanischer Energien, der regelmäßig Feindliche Ziele mit Blitzschlägen trifft, die stark genug sind, um geschwächte unsterbliche Kreaturen zu zerstören."},
[64173] = {name = "Zerschmetterte Illusion", description = "[q]Yogg-Saron ist durch das Zerbrechen der Illusion für einen Moment betäubt."},
[64174] = {name = "Hodirs schützender Blick", description = "[q]Ermöglicht Hodir, ein befreundetes Ziel, das tödlichen Schaden erlitten hat, zu retten, indem er es bis zu until cancelled lang in einem fast undurchdringlichen Eisblock einschließt. Hodir muss sich nach dieser Aktion ausruhen."},
[64187] = {name = "Sturmschild", description = "[q]Schildet den Zaubernden durch Elektrizität ab, die Angreifern bei einem Treffer 250 Naturschaden zufügt. Zusätzlich wird der Zaubernde immun gegenüber Betäubungseffekten."},
[64189] = {name = "Ohrenbetäubendes Gebrüll", description = "[q]Fügt 7600 to 8400 körperlichen Schaden zu und bringt den Feind 4 sec lang zum Schweigen."},
[64193] = {name = "Gebrochenes Herz", description = "[q]Das Herz des XT-002 Dekonstruktor wurde abgeschnitten, wodurch die Kampfeinschränkungen des XT-002 Dekonstruktors entfernt wurden. Schaden um 15% erhöht. Gesundheit um 60% erhöht."},
[64213] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der 4625 to 5375 Naturschaden verursacht. Der Blitz wird auf in der Nähe befindliche Feinde überspringen und mit jedem weiteren Ziel 50% mehr Schaden verursachen."},
[64216] = {name = "Blitzschlagnova", description = "[q]Emittiert einen Energiepuls, der Feinden innerhalb von 20 Metern 20813 to 24187 Naturschaden zufügt."},
[64217] = {name = "Überladen", description = "[q]Mit unkontrollierter Kraft überladen. Erhöht den verursachten Schaden pro Stapel um 20%."},
[64218] = {name = "Überladen", description = "[q]Überlädt einen nahen Sturmdiener mit einem heftigen Sog Sturmenergien."},
[64227] = {name = "Statisch Aufgeladen", description = "[q]Verursacht an allen Feinden in einem Radius von 500 Metern alle 3 Sek. Naturschaden."},
[64230] = {name = "Schock", description = "[q]Fügt dem Feind und bis zu 0 in der Nähe befindlichen Feinden Naturschaden zu."},
[64238] = {name = "Berserker", description = "[q]Erhöht 30 min lang sowohl das Angriffstempo des Zaubernden um 150% als auch seinen verursachten Schaden um 900%."},
[64319] = {name = "Zurückstoßen", description = "[q]Stößt alle Ziele vor dem Zaubernden zurück."},
[64348] = {name = "Handpuls", description = "[q]Feuert einen Energieimpuls auf einen zufälligen Feind und verursacht 4242 to 4758 Zauberfeuerschaden an allen Feinden in einem kegelförmigen Bereich vor dem Ziel."},
[64359] = {name = "Rituelle Bindung", description = "[q]Wird gebunden."},
[64362] = {name = "Ritual Bond Cloud", description = nil},
[64363] = {name = "Schock", description = "[q]Schleudert sofort einen Blitz auf einen Feind und verursacht Naturschaden."},
[64374] = {name = "Wildes Anspringen", description = "[q]Ein wilder Sprung, der 5550 to 6450 körperlichen Schaden verursacht. Zusätzlich dazu werden 5 sec lang alle 1 Sek. 3700 to 4300 körperlicher Schaden verursacht."},
[64375] = {name = "Fleisch reißen", description = "[q]Ein tödlicher Angriff, der 20 sec lang alle 2 Sek. 2313 to 2687 körperlichen Schaden verursacht. Bis zu 100-mal stapelbar."},
[64379] = {name = "Überladen", description = "[q]Mit Sturmenergie überladen."},
[64381] = {name = "Stärke des Rudels", description = "[q]Die Präsenz der anderen Mitglieder des Rudels erhöht den verursachten Schaden aller Mitglieder des Rudels innerhalb von 10 Metern um 30%."},
[64383] = {name = "Selbstreparatur", description = "[q]Heilt den Zaubernden um 50% seiner gesamten Gesundheit. Dies ist ein automatischer Vorgang, der ausgelöst wird, wenn der Zaubernde wenig Gesundheit erreicht. Wenn alle drei Teile zerstört werden, bevor sie sich reparieren können, wird der Reparaturvorgang unterbrochen und der Zaubernde wird dauerhaft vernichtet."},
[64386] = {name = "Schreckliches Kreischen", description = "[q]Das Kreischen erfüllt die Herzen aller nahen Feinde mit Angst und lässt sie 5 sec vor Entsetzen fliehen."},
[64389] = {name = "Schildwachenschlag", description = "[q]Ein massiver Schlag, der 3500 Schattenschaden verursacht und den erlittenen Schattenschaden 5 sec lang um 100% erhöht."},
[64395] = {name = "Quantenstoß", description = "[q]Schlägt einen Feind und verursacht 15675 to 17325 körperlichen Schaden."},
[64396] = {name = "Wächterschwarm", description = "[q]Visiert einen Spieler an, der vom Wächterschwarm angegriffen werden soll."},
[64412] = {name = "Phasenschlag", description = "[q]Ein massiver Schlag, der 8788 to 10212 Schaden verursacht und das Ziel langsam auf eine andere Existenzebene schwinden lässt."},
[64420] = {name = "Scharfschützentraining", description = "[q]Erhöht die kritische Trefferchance Eurer Fähigkeit 'Tödlicher Schuss' um 15%. Wenn Ihr 6 Sek. lang stillsteht, wird der verursachte Schaden Eurer Fähigkeiten 'Zuverlässiger Schuss' und 'Kobraschuss' 15 sec lang um 6% erhöht."},
[64422] = {name = "Überschallkreischen", description = "[q]Eine Überschallschockwelle, die allen Gegnern in ihrem Weg 60125 to 69875 körperlichen Schaden zufügt. Der Schaden der Schockwelle wird zwischen allen Zielen in ihrem Weg aufgeteilt."},
[64429] = {name = "Todesgriff", description = "[q]Macht sich die unheilige Energie, die alle Materie umgibt und verbindet, zunutze und zieht das Ziel zum Zaubernden hin."},
[64431] = {name = "Todesgriff", description = "[q]Zieht Euer Ziel durch einen Strahl unheiliger Kraft zu Euch."},
[64437] = {name = "Flame Arrow Aura", description = "[q]Feuert einen Pfeil aus Eis, der massiven Schaden verursacht und das Bewegungstempo verringert."},
[64439] = {name = "Flammenpfeil", description = "[q]Feuert einen Feuerpfeil ab, der 8 sec lang schwerste Verbrennungen verursacht."},
[64443] = {name = "Urknall", description = "[q]Fügt nahen Feinden 76313 to 88687 körperlichen Schaden zu."},
[64455] = {name = "Wilde Essenz", description = "[q]Jede Essenz erhöht den vom wilden Verteidiger verursachten Schaden um 50%. Der wilde Verteidiger kann sich auf Kosten einer seiner wilden Essenzen selbst wiederbeleben."},
[64465] = {name = "Schattenleuchtfeuer", description = "[q]Das Ziel wird zu einem Leuchtfeuer für Yogg-Sarons machtvolle Schatten."},
[64468] = {name = "Machtvolle Schatten", description = "[q]Verleiht befreundeten Einheiten innerhalb von 20 Metern des Ziels Macht, wodurch sie 20 sec lang 225000 Gesundheit regenerieren."},
[64478] = {name = "Wildes Anspringen", description = "[q]Der wilde Verteidiger springt das Ziel an, betäubt es 4 sec lang und verursacht alle 1 Sek. Schattenschaden."},
[64482] = {name = "Turm des Lebens", description = "[q]Die geladenen orbitalen Verteidigungssysteme versorgen den Leviathan mit zusätzlicher Kraft. Gesundheit erhöht. Erlittener Feuerschaden verringert."},
[64487] = {name = "Aufstieg in die Himmel", description = "[q]Verursacht bei Feinden 655500 to 724500 Arkanschaden."},
[64501] = {name = "Force Cast Signal to Argent Charger", description = nil},
[64505] = {name = "Dunkler Schild", description = "[q]Schützt den Zaubernden 8 sec lang vor allen Angriffen und Zaubern, die Zeit zwischen Angiffen wird während dieser Zeit jedoch um 100% erhöht."},
[64528] = {name = "Eis schmelzen", description = "[q]Versucht ein in der Nähe befindliches Blitzeis zu schmelzen, verursacht im Verlauf von 10 sec Feuerschaden entsprechend 100% der gesamten Gesundheit."},
[64550] = {name = "Schildspalterpfeil", description = "[q]Feuert einen Feuerpfeil ab, der until cancelled lang schwerste Verbrennungen verursacht."},
[64570] = {name = "Löschschaum", description = "[q]Sprüht Löschschaum in die Luft, der sich im Raum verteilt und Flammen erstickt. Zusätzlich wird die Zauberzeit von betroffenen Feinden 8 sec lang um 50% verringert."},
[64582] = {name = "Notfallmodus", description = "[q]Aktiviert in bedrohlichen Situationen einen Notfallmodus, der den Schaden und die Gesundheit um 25% erhöht."},
[64587] = {name = "Naturbombe", description = "[q]Fügt Feinden innerhalb von 10 Metern um den Zaubernden 5850 to 6150 Naturschaden zu und stößt sie zurück."},
[64595] = {name = "Schildbrecher", description = "[q]Ein Distanzangriff, der 10000 Schaden verursacht und eine Aufladung von 'Verteidigen' vom Gegner entfernt."},
[64599] = {name = "Arkanbeschuss", description = "[q]Verursacht bei einem Feind Arkanschaden."},
[64616] = {name = "Ohrenbetäubende Sirene", description = "[q]Bringt alle Feinde innerhalb von 10 Metern um den Zaubernden zum Schweigen."},
[64619] = {name = "Wassersprühen", description = "[q]Sprüht einen kräftigen Wasserstrahl, der alle Flammen innerhalb von 15 Metern erstickt, 18850 to 21150 Frostschaden verursacht und Gegner im Einflussbereich zurückstößt."},
[64623] = {name = "Frostbombe", description = nil},
[64638] = {name = "Säurebiss", description = "[q]Verursacht Naturschaden an einem Feind und verringert dessen Rüstung 30 sec lang um 1000. Bis zu 5-mal stapelbar."},
[64639] = {name = "Stampfen", description = "[q]Stampft auf den Boden, schleudert Feinde im Umkreis von 50 Metern in die Luft und verursacht 8000 Frostschaden."},
[64642] = {name = "Blizzard", description = "[q]Ruft einen Blizzard herbei, der 6 sec lang anhält und Feinden im ausgewählten Gebiet alle 2 Sek. 5088 to 5912 Frostschaden zufügt."},
[64643] = {name = "Wirbelstoß", description = "[q]Verursacht an in der Nähe befindlichen Feinden 50% Waffenschaden und schleudert sie zurück."},
[64644] = {name = "Schild des Winterklagegeists", description = "[q]Schirmt befreundete Kreaturen in der Nähe des Winterklagegeistes ab, wodurch der Schaden von Angriffen mit Flächenschaden um 90% verringert wird."},
[64645] = {name = "Kältekegel", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Frostschaden zu und verlangsamt ihr Bewegungstempo 8 sec lang."},
[64646] = {name = "Korrumpierte Weisheit", description = "[q]Verschleiert das Wissen der Feinde um die heiligen Künste, um ihre Heilungsfähigkeiten zu unterdrücken.\n[q]Modifies Damage/Healing Done -90%:\nHeiliges Licht , Lichtblitz"},
[64647] = {name = "Schneeblindheit", description = "[q]Blendet ein feindliches Ziel 2 sec lang mit Schnee und verursacht 4250 to 5750 Schaden."},
[64649] = {name = "Gefrierender Atem", description = "[q]Ein eisiger Atem gefriert alle Ziele in einem kegelförmigen Bereich vor dem Zaubernden und verursacht 5 sec lang 14000 Frostschaden."},
[64660] = {name = "Erste-Hilfe-Kasten", description = "[q]Stellt im Verlauf von 4 sec die gesamte Gesundheit Eures Passagiers wieder her."},
[64662] = {name = "Blinzeln", description = "[q]Teleportiert in die Nähe eines unerreichbaren Feindes."},
[64663] = {name = "Arkanschlag", description = "[q]Verursacht 9000 to 11000 Arkanschaden an allen Feinden im Umkreis von 15 Metern, und bringt sie 10 sec lang zum Schweigen."},
[64668] = {name = "Magnetfeld", description = "[q]Projiziert ein Magnetfeld auf das Ziel und macht es unbeweglich. Zusätzlich erleidet das Ziel 30% mehr Schaden von allen Quellen. Hält 6 sec lang an."},
[64677] = {name = "Schildgenerator", description = "[q]Aktiviert den Schildgenerator der Belagerungsmaschine, wodurch 5 sec lang der erlittene körperliche, Feuer-, Frost- und Arkanschaden verringert wird."},
[64692] = {name = "Flammenkanone", description = "[q]Feuert die Kanone auf ein zufälliges feindliches Ziel ab."},
[64695] = {name = "Erdengriff", description = "[q]Euer Totem der Erdbindung hat eine Chance, Ziele 5 sec lang festzuwurzeln, wenn es aufgestellt wird."},
[64696] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm Naturschaden zu."},
[64697] = {name = "Erdbeben", description = "[q]Fügt Feinden innerhalb von 55 Metern 6013 to 6987 Schaden zu, stößt sie zu Boden und bringt sie 4 sec lang zum Schweigen."},
[64698] = {name = "Pyroschlag", description = "[q]Verursacht 8325 to 9675 Feuerschaden an einem Feind und verbrennt das Ziel 12 sec lang alle 3 Sek. für 3000 zusätzlichen Schaden."},
[64699] = {name = "Magmaspritzer", description = "[q]Bespritzt das Ziel mit Magma, schmilzt seine Rüstung und verursacht Feuerschaden sowie 21 sec lang zusätzlichen Feuerschaden."},
[64705] = {name = "Unerstickbare Flammen", description = "[q]Das Ziel wird von unerstickbaren Flammen verschlungen, wodurch ihm 5 sec lang alle 1 Sek. Schaden in Höhe von 8% seiner maximalen Gesundheit zugefügt wird. Anschließend springen die Flammen auf ein anderes Ziel über. Nur die kühlenden Wasser von Ignis' Schmiede können diese Flammen löschen."},
[64717] = {name = "Flammensprühen", description = "[q]Verursacht 7069 to 7931 Feuerschaden an Feinden, die sich innerhalb von 15 Metern vor dem Zaubernden befinden."},
[64719] = {name = "Sturmangriff", description = "[q]Stürmt auf einen Gegner zu und verursacht 6750 to 8250 körperlichen Schaden."},
[64720] = {name = "Flammenemission", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden 5400 to 6600 Feuerschaden zu."},
[64732] = {name = "Wassereimer werfen", description = "[q]Löscht Feuer."},
[64740] = {name = "Energieraub", description = "[q]Fügt pro Sekunde 2000 Arkanschaden zu."},
[64746] = {name = "Supererhitzte Winde", description = "[q]Beschwört einen Zyklon, der sich zufällig herumbewegt. Feinde in der Nähe des Zyklons werden in die Luft geschleudert."},
[64757] = {name = "Sturmschlag", description = "[q]Greift das Ziel sofort mit beiden Waffen an. Zusätzlich wird in den nächsten 2 Fällen, in denen das Ziel Naturschaden erleidet, dieser Schaden um 20% erhöht. Hält 12 sec lang an."},
[64758] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 5 Ziele aus und verursacht bei jedem steigend mehr Naturschaden."},
[64766] = {name = "Lasersalve", description = "[q]Feuert eine Salve Arkanangriffe ab, die allen Feinden in der Nähe Schaden zufügt."},
[64771] = {name = "Rüstung fusionieren", description = "[q]Verringert die Rüstung sowie Angriffs- und Bewegungstempo eines Feinde 20 sec lang um 20%."},
[64773] = {name = "Feuerschlag", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[64775] = {name = "Schattenhafte Barriere", description = "[q]Schützt den Zaubernden vor allen Angriffen und Zaubern."},
[64776] = {name = "Maschinengewehr", description = "[q]Ein kanalisierter Angriff, der dem Ziel 3 sec lang alle 0,25 Sek. 1885 to 2115 Schaden zufügt. Dieser Effekt ignoriert Rüstung."},
[64779] = {name = "Kräftiger Sprung", description = "[q]Ein kräftiger Sprung hin zum Zielort."},
[64783] = {name = "Deplatzierungsvorrichtung", description = nil},
[64819] = {name = "Verheerender Sprung", description = "[q]Springt einen Feind an, verursacht 6500 körperlichen Schaden und betäubt 4 sec lang alle Feinde innerhalb von 10 Metern."},
[64825] = {name = "Unglaubliches Brüllen", description = "[q]Fügt allen Feinden in einem kegelförmigen Bereich vor dem Zaubernden 8775 to 9225 körperlichen Schaden zu."},
[64847] = {name = "Runenflammenstrahlen", description = "[q]Die Flammenstrahlen verursachen 5 sec lang alle 1 Sek. 6825 to 7175 Feuerschaden in einem kegelförmigen Bereich von 15 Metern vor dem Zaubernden."},
[64852] = {name = "Brennende Rune", description = "[q]Fügt Feinden innerhalb von 3 Metern 10 sec lang alle 1 Sek. 2438 to 2562 Feuerschaden zu."},
[64870] = {name = "Lavaeruption", description = "[q]Ihr schleudert flüssige Lava auf das Ziel, die 8788 to 10212 Feuerschaden verursacht. Ist das Ziel von 'Flammenschock' betroffen, wird 'Lavaeruption' den Flammenschock aufzehren und einen kritischen Treffer erzielen."},
[64874] = {name = "Runenschlag", description = "[q]Ein massiver Schlag, der 11100 to 12900 Schaden verursacht und der Haut des Ziels eine Rune einprägt."},
[64877] = {name = "Fäuste härten", description = "[q]Gewährt dem Zaubernden 10 sec lang zusätzlich 100% Schaden pro Treffer, macht ihn aber unbeweglich."},
[64889] = {name = "Blitzgeladen", description = "[q]Angriffe haben eine Chance, Kugelblitzschlag zu wirken."},
[64897] = {name = "Metall fusionieren", description = "[q]Heilt ein befreundetes Ziel 5 sec lang alle 1 Sek. um 19500 to 20500."},
[64903] = {name = "Blitzverschmelzung", description = "[q]Infiziert einen Feind 10 sec lang, wodurch an allen in der Nähe befindlichen Verbündeten alle 2 Sek. Naturschaden verursacht wird."},
[64918] = {name = "Elektroschock", description = "[q]Ein massiver, elektrischer Schock, der 8775 to 9225 Naturschaden verursacht und alle Ziele auf den Boden schleudert."},
[64919] = {name = "Eisnova", description = "[q]Fügt Feinden innerhalb von 10 Metern 1885 to 2115 Frostschaden zu, erhöht 10 sec lang den erlittenen Frostschaden um 25% und verlangsamt das Bewegungstempo um 10%."},
[64953] = {name = "Sprungangriff", description = "[q]Springt das Ziel an und verursacht 120% Waffenschaden."},
[64954] = {name = "Wasserblitz", description = "[q]Schleudert ein wässriges Geschoss auf einen Feind und fügt 33 to 38 Frostschaden zu."},
[64966] = {name = "Eisgeschütz", description = "[q]Beschwört in der Nähe ein Eisgeschütz."},
[64979] = {name = "Luftabwehrrakete", description = "[q]Feuert ein explosives Geschoss ab, das beim Kontakt mit Luftzielen explodiert."},
[65033] = {name = "Fesselndes Verwunden", description = "[q]Verursacht 15 sec lang alle 3 Sek. körperlichen Schaden und verlangsamt das Bewegungstempo um 3%."},
[65035] = {name = "Ätzgift", description = "[q]Verursacht bei einem Feind Naturschaden."},
[65038] = {name = "Zermalmen", description = "[q]Zerquetscht das Ziel und verursacht 1388 to 1612 Schaden."},
[65060] = {name = "Zwielichtblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[65062] = {name = "Peitsche", description = "[q]Peitscht das Ziel, verursacht Naturschaden."},
[65063] = {name = "Smaragdbombardement", description = "[q]Fügt Zwielichtreitern und dem Zwielichtportal Naturschaden zu."},
[65070] = {name = "Verteidigungsmatrix", description = "[q]Die schützende Matrix beschützt alle Verbündeten innerhalb von 7 Metern. Verringert den erlittenen Schaden 15 sec lang um 90%."},
[65072] = {name = "An Stall gebunden", description = "[q]Eine Leine aus Seil."},
[65073] = {name = "Müllpresse", description = nil},
[65075] = {name = "Turm der Flammen", description = "[q]Die geladenen orbitalen Verteidigungssysteme versorgen den Leviathan mit zusätzlicher Kraft. Gesundheit und Feuerschaden erhöht."},
[65076] = {name = "Turm der Stürme", description = "[q]Die geladenen orbitalen Verteidigungssysteme versorgen den Leviathan mit zusätzlicher Kraft. Gesundheit und Schaden erhöht."},
[65077] = {name = "Turm des Frosts", description = "[q]Die geladenen orbitalen Verteidigungssysteme versorgen den Leviathan mit zusätzlicher Kraft. Gesundheit erhöht. Bewegung aller nahen Feinde verlangsamt."},
[65078] = {name = "Komprimiert.", description = "[q]Verursacht bei Feinden innerhalb von 8 Metern um den Zaubernden 6338 to 6662 Naturschaden."},
[65080] = {name = "Metallschrott schneiden", description = "[q]Verursacht 4 sec lang alle 1 Sek. 2438 to 2562 Feuerschaden."},
[65084] = {name = "Selbstzerstörung", description = "[q]Sprengt sich selbst, fügt nahen Feinden Feuerschaden zu."},
[65089] = {name = "Sägeklingen", description = "[q]Verursacht 488 to 512 körperlichen Schaden und zusätzlich 6 sec lang alle 1 Sek. 147 to 153 körperlichen Schaden. Stapelbar."},
[65099] = {name = "Recyclebotsäge aufstellen", description = "[q]Beschwört eine Recyclebotsäge."},
[65123] = {name = "Sturmwolke", description = "[q]Beschwört eine blitzgeladene Sturmwolke auf einen zufälligen befreundeten Spieler. Während er in diese Wolke eingehüllt ist, werden bis zu 4 befreundete Einheiten, die er berührt, von Blitz getroffen und mit Sturmkraft aufgeladen, die ihren kritischen Schaden 30 sec lang um 135% erhöht."},
[65127] = {name = "Funkeln eines Dunkelküstenirrwischs", description = nil},
[65128] = {name = "Dynamit werfen", description = "[q]Fügt allen Feinden innerhalb von 5 Metern um das Ziel 1110 to 1290 Feuerschaden zu."},
[65129] = {name = "Explosion", description = "[q]Verursacht 4163 to 4837 Feuerschaden bei in der Nähe befindlichen Feinden."},
[65130] = {name = "Zeitbombe", description = "[q]Explodiert nach ein paar Sekunden und verursacht 7400 to 8600 Feuerschaden. Tick-tack."},
[65133] = {name = "Sturmwolke", description = "[q]Beschwört eine blitzgeladene Sturmwolke auf einen zufälligen befreundeten Spieler. Während er in diese Wolke eingehüllt ist, werden bis zu 6 befreundete Einheiten, die er berührt, von Blitz getroffen und mit Sturmkraft aufgeladen, die ihren kritischen Schaden 30 sec lang um 135% erhöht."},
[65147] = {name = "Schildbrecher", description = "[q]Ein Distanzangriff, der 2000 Schaden verursacht und eine Aufladung von 'Verteidigen' vom Gegner entfernt."},
[65162] = {name = "Auf Keristrasza reiten", description = nil},
[65172] = {name = "Frostblitz", description = "[q]Fügt einem Feind Frostschaden zu und verringert 4 sec lang sein Bewegungstempo."},
[65173] = {name = "Eisketten", description = "[q]Schlingt eisige Ketten um den Feind, die ihn festhalten und verbrennt 10 sec lang alle 2 Sek. 350 to 450 Mana."},
[65191] = {name = "Aufgezehrt", description = "Etwas verzehrt diese Kreatur, in Leib und Seele."},
[65192] = {name = "Löschschaum", description = "[q]Sprüht Löschschaum in die Luft, der Flammen innerhalb von 10 Metern erstickt. Zusätzlich wird die Zauberzeit von betroffenen Feinden 8 sec lang um 50% verringert."},
[65204] = {name = "Die entfesselte Bestie", description = nil},
[65210] = {name = "Destabilisierungsmatrix", description = "[q]Kanalisiert einen Strahl Titanenenergie, der die Molekularstruktur gewisser saronitbasierter Kreaturen destabilisiert, wodurch 30 sec lang ihr Angriffstempo um 100% und Ihr Zaubertempo um 300% verringert wird."},
[65214] = {name = "Fürstlicher Feuerbrand", description = "[q]Lässt jeden in der Nähe befindlichen wirbelnden Vortex für 24 to 29 Feuerschaden in Flammen aufgehen und verursacht dann 12 sec lang alle 3 Sek. zusätzlich 4 Feuerschaden."},
[65215] = {name = "Höllischer Großbrand", description = "[q]Fügt einem wirbelnden Vortex und allen, die sich in der Nähe befinden, 302 to 338 Feuerschaden zu."},
[65216] = {name = "Vortexrache", description = "[q]Umgibt Lord Magmathar mit der Macht des Vortexes und verursacht 278 to 322 Naturschaden, sowie zusätzlich 9 sec lang alle 3 Sek. 225 Naturschaden."},
[65231] = {name = "Himmelsblitz", description = "[q]Überzieht Lord Magmathar mit Blitzen und fügt ihm 24 to 29 Naturschaden zu."},
[65232] = {name = "Flammen löschen", description = "[q]Löscht den Fürstlichen Feuerbrand von Lord Magmathar. Kann alle 15 Sek. angewandt werden."},
[65233] = {name = "Zum Vortex zurückkehren", description = "[q]Bringt Euch und den wirbelnden Vortex zurück zum Vortex am südlichen Rand des Donnergipfels. Kann nur benutzt werden, wenn Ihr Lord Magmathar schon umkreist."},
[65248] = {name = "Frostatem", description = "[q]Verursacht in einem kegelförmigen Bereich von 30 Metern vor dem Zaubernden 5363 to 5637 Frostschaden. Zusätzlich wird das Angriffstempo der Ziele 6 sec lang um 100% verringert."},
[65260] = {name = "Flügelstoß", description = "[q]Fügt allen Feinden innerhalb von 25 Metern 750 to 1250 Schaden zu und stößt sie zurück."},
[65297] = {name = "Instabile Explosion", description = "[q]Der arme Orc, der den Flammenwerfer trägt, erleidet 2625 to 3375 Feuerschaden."},
[65305] = {name = "Artifact Pings Back", description = nil},
[65306] = {name = "Summon Buried Treasure Pile", description = nil},
[65310] = {name = "Titan's Terminal Quest Complete", description = nil},
[65351] = {name = "Papierzeppelin werfen", description = "[q]Wirft den Zeppelin einem Freund aus Nordend zu."},
[65386] = {name = "Ehrt die Toten", description = "Ehrt während des Tages der Toten die Verstorbenen."},
[65388] = {name = "Flammenwerfer", description = "[q]Verbrennt alle Gegner in einem kegelförmigen Bereich vor dem Verwüster und verursacht 10 sec lang alle 1 Sek. zusätzlichen Feuerschaden."},
[65402] = {name = "Prankenhieb", description = "[q]Schlägt in der Nähe befindliche Feinde und verursacht 241 to 279 Schaden."},
[65406] = {name = "Krallenhieb", description = "[q]Attackiert das Ziel mit Krallen, fügt 240 to 360 Schaden sowie 360 zusätzlichen Schaden im Verlauf von 9 sec zu."},
[65407] = {name = "Anspringen", description = "[q]Springt bis zu 5 to 45 Meter weit, betäubt das Ziel 3 sec lang und verursacht 319 to 431 Schaden. Minimale Reichweite ist 5 Meter."},
[65417] = {name = "Verwüster des Kriegshymnenklans explodiert", description = "[q]Der Verwüster explodiert, verursacht 263 to 337 Feuerschaden und wirft alle Hordenmitglieder innerhalb von 10 Metern um."},
[65481] = {name = "Zur Basis zurückkehren!", description = "[q]Befiehlt dem Windreiter, zu Höllschreis Wacht zurückzukehren."},
[65488] = {name = "Gedankenschinden", description = "[q]Fügt einem Feind Schattenschaden zu und verringert 3 sec lang sein Bewegungstempo."},
[65489] = {name = "Sprengkörper werfen", description = "[q]Vernichtet in Astranaar Elfen und ihren Besitz."},
[65490] = {name = "Vampirberührung", description = "[q]Fügt dem Ziel im Verlauf von 15 sec 33750 to 41250 Schattenschaden zu."},
[65492] = {name = "Gedankenschlag", description = "[q]Überzieht das Ziel mit 7088 to 7912 Schattenschaden."},
[65494] = {name = "Lastkodo rufen", description = nil},
[65516] = {name = "Schneeball", description = "[q]Wirf mich, bevor ich schmelze!"},
[65541] = {name = "Schattenwort: Schmerz", description = "[q]Murmelt ein Wort der Dunkelheit, das einem Feind 15 sec lang alle 3 Sek. Schattenschaden zufügt."},
[65542] = {name = "Stille", description = "[q]Bringt das Ziel zum Schweigen, sodass es 5 sec lang keine Zauber wirken kann."},
[65543] = {name = "Psychischer Schrei", description = "[q]Stößt einen psychischen Schrei aus, der 8 sec lang in der Nähe befindliche Feinde in die Flucht schlägt."},
[65544] = {name = "Dispersion", description = "[q]Löst Euch zu reiner Schattenenergie auf und verringert jeglichen erlittenen Schaden um 90%. Ihr könnt weder angreifen noch Zauber wirken, regeneriert jedoch 6 sec lang pro Sekunde 6% Mana. 'Dispersion' kann gewirkt werden, während Betäubungs-, Furcht- oder Stilleeffekte auf Euch wirken. Bei Anwendung werden alle wirkenden Verlangsamungs- und bewegungseinschränkenden Effekte entfernt und Ihr werdet für die Dauer des Effektes von 'Dispersion' gegen sie immun."},
[65546] = {name = "Magiebannung", description = "[q]Bannt auf dem Ziel liegende Magie und entfernt 2 schädliche Zauber von einem Freund oder 2 nützliche Zauber von einem Feind."},
[65547] = {name = "PvP-Schmuckstück", description = "[q]Entfernt alle bewegungseinschränkenden Effekte sowie alle Effekte, die den Verlust der Kontrolle über Euren Charakter verursachen."},
[65571] = {name = "Kristallene Opfergabe", description = "[q]Beschwört eine kristallene Opfergabe für die Großen."},
[65579] = {name = "Thessera landen", description = "[q]Befiehlt Thessera, zu landen."},
[65605] = {name = "Keimende Saat", description = "[q]Pflanzt ein explosiven Samen in einen Feind ein. Der Samen explodiert nach 4 Sek. und verursacht beim Ziel und all seinen in der Nähe befindlichen Verbündeten 3200 Naturschaden."},
[65611] = {name = "Stoffpuppe werfen", description = "[q]Wirft die Stoffpuppe zu einem Wolvarlümmel."},
[65628] = {name = "Gütig", description = "[q]Ihr seid eine nette Person."},
[65629] = {name = "Unnett", description = "[q]Ihr seid keine nette Person."},
[65667] = {name = "Hitze", description = "[q]Überzieht alle nahen Eisenkonstrukte mit intensiver Hitze, was ihr Bewegungstempo um 5 und ihr Tempo um 5 erhöht. Dieser Effekt ist bis zu 10-mal stapelbar. Wenn der Effekt 10 Stapel erreicht hat, wird das betroffene Eisenkonstrukt schmelzen."},
[65719] = {name = "Schattennova", description = "[q]Fügt Kreaturen innerhalb von 15 Metern wahllosen Schattenschaden zu."},
[65735] = {name = "Verjüngung", description = "[q]Heilt einen Verbündeten 12 sec lang alle 3 Sek."},
[65753] = {name = "Tongas Augen", description = "[q]Tonga Runentotem wacht aus der Luft über Euch."},
[65762] = {name = "Extrascharfe Klauen", description = "[q]Schattumbra ist wegen des Angriffs auf den Rajenbaum äußerst erzürnt über den Vorposten des Kriegshymnenklans, und fügt seinen Kämpfern daher zusätzlich 110 to 140 körperlichen Schaden zu."},
[65766] = {name = "Woge des Lichts", description = "[q]Das Pulsieren der Woge des Lichts fügt dunklen Gegnern alle 2 Sek. 4500 Schaden zu."},
[65768] = {name = "Woge der Nacht", description = "[q]Das Pulsieren der Woge der Nacht fügt hellen Gegnern alle 2 Sek. 4500 Schaden zu."},
[65775] = {name = "Säuregetränkte Mandibeln", description = "[q]Säure von den Mandibeln des Skarabäus verursacht alle 3 Sek. 1800 Naturschaden. Hält 1 min lang an."},
[65790] = {name = "Gegenzauber", description = "[q]Kontert das Zauberwirken des Feindes und verhindert 8 sec lang, dass ein Zauber dieser Art der Magie gewirkt werden kann. Führt zu einem hohen Maß an Bedrohung."},
[65791] = {name = "Arkanschlag", description = "[q]Überzieht einen Feind mit Energie und verursacht 12488 to 14512 Arkanschaden. Mit jedem Wirken von 'Arkanschlag' werden der verursachte Schaden aller Arkanzauber um 10% und die Manakosten von 'Arkanschlag' um 150% erhöht. Dieser Effekt ist bis zu 4-mal stapelbar und hält 6 sec lang an oder bis ein Arkanzauber außer 'Arkanschlag' gewirkt wurde."},
[65792] = {name = "Frostnova", description = "[q]Fügt in der Nähe des Zaubernden befindlichen Feinden 365 to 415 Frostschaden zu und friert sie bis zu 8 sec lang ein. Erlittener Schaden kann den Effekt unterbrechen."},
[65793] = {name = "Blinzeln", description = "[q]Teleportiert den Zaubernden 20 Meter nach vorn, es sei denn, etwas steht im Weg. Befreit den Zaubernden auch von Betäubungen und Fesseln."},
[65795] = {name = "Entfesseltes Licht", description = "[q]Das konzentrierte Licht entfesselt seine Energien und fügt Gegnern innerhalb von 6 Metern 14625 to 15375 Lichtschaden zu."},
[65799] = {name = "Arkanbeschuss", description = "[q]Schleudert mehrere Geschosse auf das feindliche Ziel und verursacht 10450 to 11550 Arkanschaden."},
[65800] = {name = "Arkane Explosion", description = "[q]Verursacht eine Explosion arkaner Magie um den Zaubernden herum und fügt allen Zielen in einem Umkreis von 10 Metern 5280 to 5720 Arkanschaden zu."},
[65801] = {name = "Verwandlung", description = "[q]Verwandelt den Feind in ein Schaf und zwingt ihn, 10 sec lang so herumzuwandern. Während das Schaf herumwandert, kann es weder angreifen noch Zauber wirken. Jeglicher erlittene Schaden verwandelt das Ziel in seine normale Gestalt zurück. Es kann immer nur jeweils ein Ziel verwandelt werden. Wirkt nur bei Wildtieren, Humanoiden und Tieren."},
[65802] = {name = "Eisblock", description = "[q]Ihr werdet in einen Eisblock eingehüllt, der Euch 5 sec lang vor allen körperlichen Angriffen und Zaubern schützt, allerdings könnt Ihr während dieser Zeit weder angreifen noch Euch bewegen und auch keine Zauber wirken. Verursacht ebenfalls 'Unterkühlung' und verhindert 30 sec lang, dass 'Eisblock' gewirkt werden kann."},
[65804] = {name = "Banner der Horde platzieren", description = "[q]Für die Horde!"},
[65807] = {name = "Frostblitz", description = "[q]Schleudert einen Frostblitz auf den Feind, der 11563 to 13437 Frostschaden verursacht und 9 sec lang das Bewegungstempo um 40% reduziert."},
[65808] = {name = "Entfesselte Nacht", description = "[q]Die konzentrierte Nacht entfesselt ihre Energien und fügt Gegnern innerhalb von 6 Metern 14625 to 15375 Nachtschaden zu."},
[65809] = {name = "Furcht", description = "[q]Erfüllt den Feind mit Furcht, woraufhin er bis zu 10 sec lang voller Furcht flüchtet. Erlittener Schaden kann den Effekt unterbrechen. Es kann immer nur jeweils ein einzelnes Ziel mit Furcht erfüllt werden."},
[65810] = {name = "Verderbnis", description = "[q]Verdirbt das Ziel und verursacht 18 sec lang insgesamt 30000 Schattenschaden."},
[65812] = {name = "Instabiles Gebrechen", description = "[q]Schattenenergie zerstört langsam das Ziel und verursacht im Verlauf von 15 sec 23125 to 26875 Schaden. Sollte 'Instabiles Gebrechen' gebannt werden, erleidet der Bannende 13875 to 16125 Schattenschaden und wird 4 sec lang zum Schweigen gebracht."},
[65814] = {name = "Fluch der Pein", description = "[q]Verflucht das Ziel mit Pein und fügt 24 sec lang insgesamt 42000 Schattenschaden zu. Zuerst wird der Schaden langsam zugefügt und nimmt dann zu, bis der Fluch seine Gesamtdauer erreicht hat. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein."},
[65815] = {name = "Fluch der Erschöpfung", description = "[q]Verringert das Bewegungstempo des Ziels 12 sec lang um 50%. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein."},
[65816] = {name = "Höllenfeuer", description = "[q]Entzündet den Bereich um den Zaubernden herum, fügt ihm selbst alle 1 Sek. 1500 Feuerschaden zu sowie im gleichen Rhythmus allen in der Nähe befindlichen Feinden 9000 Feuerschaden. Hält 15 sec lang an."},
[65819] = {name = "Sengender Schmerz", description = "[q]Fügt dem Ziel sengenden Schmerz zu und verursacht 5490 to 6510 Feuerschaden."},
[65821] = {name = "Schattenblitz", description = "[q]Schleudert einen Schattenblitz auf den Feind und verursacht 11813 to 13187 Schattenschaden."},
[65837] = {name = "Rankenpeitsche", description = "[q]Peitscht nach bis zu 5 Zielen und verursacht 43 to 57 Naturschaden pro Sekunde."},
[65843] = {name = "Schockwelle", description = "[q]Schickt dem Urtum eine Kraftwelle voraus, die 324 to 376 Schaden verursacht und alle gegnerischen Ziele innerhalb von 30 Metern zurückstößt."},
[65854] = {name = "Sternenfeuer", description = "[q]Fügt dem Ziel 12387 to 14613 Arkanschaden zu."},
[65855] = {name = "Insektenschwarm", description = "[q]Das feindliche Ziel wird von Insekten umschwärmt, die dessen Trefferchance mit Nahkampf- und Distanzangriffen um 3% verringern und beim Betroffenen über 12 sec 22200 to 25800 Naturschaden verursachen."},
[65856] = {name = "Mondfeuer", description = "[q]Verbrennt den Feind und fügt ihm 4140 to 4860 Arkanschaden sowie 12 sec lang insgesamt 7000 zusätzlichen Arkanschaden zu."},
[65857] = {name = "Wucherwurzeln", description = "[q]Lässt das Ziel an Ort und Stelle festwurzeln und verursacht im Verlauf von 10 sec insgesamt 1576.7 to 1756.7 Naturschaden. Erlittener Schaden kann den Effekt unterbrechen."},
[65858] = {name = "Schild des Lichts", description = "[q]Ein heller Schild schützt die Schwester, absorbiert 1200000 Schaden und verhindert 16 sec lang das Unterbrechen von Zaubern."},
[65859] = {name = "Wirbelsturm", description = "[q]Wirbelt das Ziel bis zu 6 sec lang in die Luft. Während dieser Zeit ist es handlungsunfähig, aber auch unverwundbar. Es kann immer nur ein Ziel gleichzeitig von Eurem Zauber 'Wirbelsturm' betroffen sein."},
[65860] = {name = "Baumrinde", description = "[q]Die Haut des Druiden wird hart wie Rinde. Jeglicher erlittene Schaden wird um 40% verringert. Kann auch gewirkt werden, wenn man betäubt, eingefroren, handlungsunfähig oder in Schlaf versetzt worden ist oder sich fürchtet. In allen Gestalten nutzbar. Hält 12 sec lang an."},
[65861] = {name = "Naturgewalt", description = "[q]Beschwört 3 Treants, die das Ziel 30 sec lang angreifen."},
[65862] = {name = "Zorn", description = "[q]Fügt dem Ziel 8460 to 9540 Naturschaden zu."},
[65863] = {name = "Feenfeuer", description = "[q]Verringert die Rüstung des Ziels 40 sec lang um 15%. Während der Wirkungsdauer kann das Ziel weder Verstohlenheit anwenden noch unsichtbar werden."},
[65866] = {name = "Explosivschuss", description = "[q]Feuert ein Explosivgeschoss in das Ziel, das 4533 to 5467 Feuerschaden verursacht. Die Sprengladung verursacht 2 sec lang pro Sekunde weiteren Schaden."},
[65867] = {name = "Zuverlässiger Schuss", description = "[q]Ein zuverlässiger Schuss, der einem Ziel Waffenschaden zufügt."},
[65868] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[65869] = {name = "Rückzug", description = "[q]Ihr versucht, Euch mit einem Rückwärtssprung vom Ziel zurückzuziehen."},
[65871] = {name = "Abschreckung", description = "[q]Erhöht bei Aktivierung Eure Parierchance um 100% und gewährt Euch eine Chance von 100%, Zauber von vor Euch befindlichen Zielen abzuwehren. Hält 5 sec lang an."},
[65874] = {name = "Schild der Nacht", description = "[q]Ein dunkler Schild schützt die Schwester, absorbiert 1200000 Schaden und verhindert 16 sec lang das Unterbrechen von Zaubern."},
[65875] = {name = "Zwillingspakt", description = "[q]Der Zwillingspakt heilt die Zwillinge um 50% ihrer gesamten Gesundheit."},
[65876] = {name = "Zwillingspakt", description = "[q]Der Zwillingspakt heilt die Zwillinge um 50% ihrer gesamten Gesundheit."},
[65877] = {name = "Wyverngift", description = "[q]Ein Gift, welches das Ziel 8 sec lang einschlafen lässt. Jeglicher erlittene Schaden hebt den Effekt wieder auf. Sobald das Ziel erwacht, verursacht der Giftpfeil im Verlauf von 8 sec insgesamt 2.6 Naturschaden. Auf dem Ziel kann gleichzeitig nur ein Gift pro Jäger aktiv sein."},
[65879] = {name = "Macht der Zwillinge", description = "[q]Die geteilte Macht der Zwillinge erhöht ihren verursachten körperlichen Schaden um 20% und gestattet es ihnen 15 sec lang, Waffen beidhändig zu nutzen."},
[65880] = {name = "Frostfalle", description = "[q]Eine Frostfalle platzieren, die 30 sec lang um sich herum eine Eisfläche bildet, sobald der erste Feind sich ihr nähert. Das Bewegungstempo aller Feinde in einem Umkreis von 10 Metern wird um 50% verlangsamt, solange sie sich im Wirkungsbereich befinden. Die Falle bleibt 1 min lang bestehen. Es kann immer nur jeweils eine Falle aktiv sein."},
[65883] = {name = "Gezielter Schuss", description = "[q]Ein gezielter Schuss, der den Distanzschaden um 6000 erhöht und auf das Ziel gewirkte Heileffekte um 50% verringert. Hält 10 sec lang an."},
[65916] = {name = "Macht der Zwillinge", description = "[q]Die geteilte Macht der Zwillinge erhöht ihren verursachten körperlichen Schaden um 20% und gestattet es ihnen 15 sec lang, Waffen beidhändig zu nutzen."},
[65918] = {name = "Betäubt", description = "[q]Vom Reittier gestoßen zu werden, betäubt das Ziel 10 sec lang."},
[65924] = {name = "Überwältigen", description = "[q]Sofortige Überwältigung des Feindes, wodurch Waffenschaden plus 35 entsteht. Kann nur verwendet werden, nachdem das Ziel ausgewichen ist. Auf 'Überwältigen' kann nicht mit Blocken, Ausweichen oder Parieren reagiert werden."},
[65925] = {name = "Unerbittliche Gewalt", description = "[q]Wenn Ihr einen Spieler mit 'Überwältigen' trefft, während er einen Zauber wirkt, wird sein Zauberschaden und seine Heilung 6 sec lang um 50% verringert."},
[65926] = {name = "Tödlicher Stoß", description = "[q]Ein heimtückischer Stoß, der Waffenschaden plus 1000 verursacht und das Ziel verwundet, sodass die Wirksamkeit jeglicher Heilung 10 sec lang um 50% verringert wird."},
[65927] = {name = "Sturmangriff", description = "[q]Stürmt auf einen Gegner zu und betäubt ihn 1.50 sec lang."},
[65929] = {name = "Sturmangriffsbetäubung", description = "[q]Stürmt einen Feind an und betäubt ihn 1.50 sec lang."},
[65930] = {name = "Drohruf", description = "[q]Der Krieger schreit, woraufhin Feinde in einem Umkreis von 8 Metern 8 sec lang vor Angst erstarren."},
[65932] = {name = "Gegenschlag", description = "[q]Ein sofortiger Gegenangriff gegen jeden Feind, der Euch im Nahkampf schlägt. Hält 12 sec lang an. Bei Nahkampfangriffen, die von hinten geführt werden, ist ein Gegenangriff nicht möglich. Maximal 20 Angriffe können 'Gegenschlag' auslösen."},
[65935] = {name = "Entwaffnen", description = "[q]Entwaffnet 10 sec lang die Waffenhand- und Distanzwaffe des Gegners."},
[65936] = {name = "Rüstung zerreißen", description = "[q]Zerreißt die Rüstung des Ziels, verringert sie pro Einsatz von 'Rüstung zerreißen' um 4% und verursacht ein hohes Maß an Bedrohung. Die Bedrohung wird durch Angriffskraft erhöht. Kann bis zu 5-mal angewendet werden. Hält 20 sec lang an."},
[65940] = {name = "Zerschmetternder Wurf", description = "[q]Wirft Eure Waffe nach dem Feind, verursacht Waffenschaden, verringert die Rüstung des Ziels 10 sec lang um 20% oder entfernt jegliche Unverwundbarkeitseffekte."},
[65946] = {name = "Klingensturm", description = "[q]In einem stählernen Wirbel greift Ihr Feinde in einem Umkreis von 8 Metern an und verursacht 50% Waffenschaden."},
[65949] = {name = "Schnelligkeit der Val'kyr", description = "[q]Die Macht der Zwillinge erhöht 15 sec lang das Angriffstempo um 10%. Stapelbar."},
[65950] = {name = "Berührung des Lichts", description = "[q]Die Val'kyr hat Euch mit ihrer Berührung des Lichts belegt, die Spielern, die unter dem Effekt der Essenz der Nacht stehen, alle 2 Sek. 5850 to 6150 Lichtschaden zufügt. Die Berührung des Lichts wird helle Ziele nicht verletzen."},
[65954] = {name = "Blutsturz", description = "[q]Ein sofortiger Stoß, der 110% Waffenschaden sowie einen Blutsturz beim Ziel verursacht und so den dem Ziel zugefügten körperlichen Schaden um bis zu 75 erhöht. Hält 10 Aufladungen oder 15 sec lang an. Gewährt 1 Combopunkt."},
[65955] = {name = "Dolchfächer", description = "[q]Wirft sofort beide Waffen auf alle Ziele innerhalb von 10 Metern und verursacht 90% Waffenschaden."},
[65956] = {name = "Klingenwirbel", description = "[q]Erhöht Euer Angriffstempo um 20%. Zusätzlich treffen Angriffe einen weiteren in der Nähe befindlichen Gegner. Hält 15 sec an."},
[65957] = {name = "Ausweiden", description = "[q]Finishing-Move, der 8750 to 11250 körperlichen Schaden verursacht."},
[65960] = {name = "Blenden", description = "[q]Blendet das Ziel und lässt es bis zu 1 min lang desorientiert umherlaufen. Jeglicher erlittene Schaden wird den Effekt aufheben."},
[65961] = {name = "Mantel der Schatten", description = "[q]Bannt sofort alle schädlichen Zaubereffekte und erhöht Eure Chance, jeder Art von Zauber zu widerstehen, 5 sec lang um 90%. Effekte, welche die Benutzung von 'Mantel der Schatten' verhindern, werden nicht entfernt."},
[65962] = {name = "Wundgift", description = "[q]Vergiftet den Feind und verringert alle auf ihn angewandten Heileffekte 15 sec lang um 50%."},
[65970] = {name = "Sturmschlag", description = "[q]Greift das Ziel sofort mit beiden Waffen an. Zusätzlich werden die nächsten 4 Quellen des Naturschadens, der dem Ziel zugefügt wird, um 20% erhöht. Hält 12 sec lang an."},
[65973] = {name = "Erdschock", description = "[q]Schockt das Ziel unmittelbar mit erschütternder Kraft und verursacht 3213 to 3387 Naturschaden. Unterbricht außerdem das Wirken von Zaubern und verhindert 2 sec lang, dass diese Art Zauber gewirkt werden kann."},
[65974] = {name = "Lavapeitsche", description = "[q]Lädt Eure Schildhandwaffe mit Lava auf, verursacht sofort 100% Waffenschaden der Schildhandwaffe. Der Schaden ist um 25% erhöht, wenn Eure Schildhandwaffe mit 'Flammenzunge' verzaubert ist."},
[65976] = {name = "Windzorn", description = "[q]Mit der Macht des Windes erfüllt, schlägt schnell 2 zusätzliche Male zu."},
[65980] = {name = "Kampfrausch", description = "[q]Erhöht Nahkampf-, Distanzangriffs- und Zaubertempo aller Gruppenmitglieder um 30%. Hält 40 sec lang an."},
[65983] = {name = "Heldentum", description = "[q]Erhöht Nahkampf-, Distanzangriffs- und Zaubertempo aller Gruppen- und Schlachtzugsmitglieder um 30%. Hält 40 sec lang an."},
[65986] = {name = "Waffe des Mahlstroms", description = "[q]Verringert die Zauberzeit Eures nächsten Wirkens von 'Blitzschlag', 'Kettenblitzschlag', 'Geringe Welle der Heilung', 'Welle der Heilung' oder 'Kettenheilung' um 20%."},
[65998] = {name = "Sengen", description = "[q]Versengt das Ziel mit 4950 to 6050 Feuerschaden."},
[66001] = {name = "Berührung der Nacht", description = "[q]Die Val'kyr hat Euch mit ihrer Berührung der Nacht belegt, die Spielern, die unter dem Effekt der Essenz des Lichts stehen, alle 2 Sek. 5850 to 6150 Nachtschaden zufügt. Die Berührung der Nacht wird dunkle Ziele nicht verletzen."},
[66003] = {name = "Kreuzfahrerstoß", description = "[q]Ein sofortiger Stoß, der 110% Waffenschaden verursacht."},
[66004] = {name = "Siegel des Befehls", description = "[q]Alle Nahkampfangriffe haben eine Chance, zusätzlichen Heiligschaden zu verursachen."},
[66005] = {name = "Richturteil des Befehls", description = "[q]Richtet den Gegner für 8550 to 9450 Heiligschaden."},
[66006] = {name = "Göttlicher Sturm", description = "[q]Ein sofortiger Waffenangriff, der bei bis zu 4 Feinden innerhalb von 8 Metern 110% Waffenschaden verursacht."},
[66007] = {name = "Hammer der Gerechtigkeit", description = "[q]Betäubt das Ziel 6 sec lang."},
[66008] = {name = "Buße", description = "[q]Versetzt das feindliche Ziel in einen meditativen Zustand, macht es so bis zu 10 sec lang handlungsunfähig. Jeglicher erlittene Schaden wird das Ziel wecken. Wirkt auf Dämonen, Drachkin, Riesen, Humanoide und Untote."},
[66009] = {name = "Hand des Schutzes", description = "[q]Ein anvisiertes Bündnismitglied ist 10 sec lang vor allen körperlichen Angriffen geschützt."},
[66010] = {name = "Gottesschild", description = "[q]Schützt den Paladin 12 sec lang vor jeglichem Schaden und Zaubern, verringert jedoch seinen verursachten Schaden um 50%."},
[66011] = {name = "Zornige Vergeltung", description = "[q]Erhöht jeglichen verursachten Schaden und Heilung 20 sec lang um 20%."},
[66012] = {name = "Gefrierender Streich", description = "[q]Fügt einem Feind 25% Frostschlagschaden zu und hüllt ihn 3 sec lang in einen Eisblock."},
[66013] = {name = "Durchdringende Kälte", description = "[q]Durchdringende Kälte peinigt mehrere feindliche Ziele und verursacht alle 3 Sek. 6000 Frostschaden. Hält 18 sec lang an."},
[66017] = {name = "Todesgriff", description = "[q]Nutzt die Mächte unheiliger Energien, die alle Materie umgibt und bindet, um das Ziel zum Todesritter hin zu ziehen und es zu zwingen, ihn 3 sec lang anzugreifen."},
[66018] = {name = "Strangulieren", description = "[q]Würgt einen Feind und bringt ihn bis zu 5 sec lang zum Schweigen. Endet der Effekt, erleidet das Ziel 180 Schattenschaden."},
[66019] = {name = "Todesmantel", description = "[q]Entfesselt unheilige Energien, die einem feindlichen Ziel 6475 to 7525 Schattenschaden zufügen oder ein befreundetes untotes Ziel um 9713 Gesundheit heilen."},
[66020] = {name = "Eisketten", description = "[q]Umschlingt das Ziel mit eisigen Ketten und verringert sein Bewegungstempo auf 5% des Normalwerts. Das Ziel erhält im Verlauf von 10 sec pro Sekunde 10% seines Bewegungstempos zurück."},
[66021] = {name = "Eisige Berührung", description = "[q]Unterkühlt das Ziel für 5272 bis 5728 Frostschaden und steckt es mit Frostfieber an, einer Krankheit, die regelmäßig Schaden verursacht und 21 sec lang das Nahkampf- und Distanzangriffstempo des Ziels um 20% verringert."},
[66023] = {name = "Eisige Gegenwehr", description = "[q]Der Todesritter bringt sein Blut zum Gefrieren, um immun gegenüber Betäubungseffekten zu werden und jeglichen erlittenen Schaden um 45% zu verringern. Hält 12 sec lang an."},
[66032] = {name = "Netz", description = "[q]Macht einen Feind 10 sec lang bewegungsunfähig."},
[66042] = {name = "Feuerball", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[66043] = {name = "Verwandlung", description = "[q]Verwandelt den Feind in ein Schaf und zwingt ihn, 4 sec lang so herumzuwandern. Während das Schaf herumwandert, kann es weder angreifen noch Zauber wirken. Jeglicher erlittene Schaden verwandelt das Ziel in seine normale Gestalt zurück. Es kann immer nur jeweils ein Ziel verwandelt werden. Wirkt nur bei Wildtieren, Drachen, Riesen, Humanoiden und Tieren."},
[66044] = {name = "Druckwelle", description = "[q]Entfesselt eine Flammenwelle, die nahen Feinden Feuerschaden zufügt und ihr Bewegungstempo 4 sec lang verringert."},
[66045] = {name = "Hast", description = "[q]Erhöht das Angriffs- und Zaubertempo des Zaubernden um 100%."},
[66046] = {name = "Lichtvortex", description = "[q]Eydis Lichtbann beginnt, einen machtvollen Lichtvortex zu wirken. Kanalisiert 5 Sek. lang. Verursacht 5 sec lang alle 1 Sek. 15600 to 16400 Schaden."},
[66047] = {name = "Froststoß", description = "[q]Schlägt den Feind sofort, verursacht 100% Waffenschaden plus 250 als Frostschaden. Dieser Stoß kann nicht pariert, geblockt und ihm kann nicht ausgewichen werden."},
[66053] = {name = "Springflut", description = "[q]Heilt ein befreundetes Ziel um 57600 to 62400 und über 15 sec um weitere 138750 to 161250."},
[66054] = {name = "Verhexen", description = "[q]Verwandelt den Gegner in einen Frosch. Während es verhext ist, kann das Ziel nicht angreifen oder Zauber wirken. Erlittener Schaden kann den Effekt unterbrechen. Hält 8 sec lang an. Es kann jeweils nur ein Ziel verhext sein. Kann nur auf Humanoide und Wildtiere angewandt werden."},
[66055] = {name = "Geringe Welle der Heilung", description = "[q]Heilt ein befreundetes Ziel um 84015 to 95985."},
[66056] = {name = "Geistläuterung", description = "[q]Läutert den Geist eines freundlichen Ziels, entfernt einen Gift-, einen Krankheits- und einen Flucheffekt."},
[66058] = {name = "Nachtvortex", description = "[q]Eydis Nachtbann beginnt, einen machtvollen Nachtvortex zu wirken. Kanalisiert 5 sec lang. Verursacht 5 sec lang alle 1 Sek. 15600 to 16400 Schaden."},
[66060] = {name = "Sprinten", description = "[q]Erhöht das Bewegungstempo 3 sec lang um 120%."},
[66063] = {name = "Erdschild", description = "[q]Schützt das Ziel mit einem Erdschild, durch den Angriffe das geschützte Ziel heilen. Dieser Effekt kann jedoch nur einmal innerhalb einiger Sekunden ausgelöst werden. 6 Aufladungen. Hält 2 min lang an."},
[66065] = {name = "Verjüngung", description = "[q]Heilt das Ziel im Verlauf von 15 sec um 416250 to 483750."},
[66066] = {name = "Pflege", description = "[q]Heilt ein befreundetes Ziel um 138750 to 161250."},
[66067] = {name = "Nachwachsen", description = "[q]Heilt ein befreundetes Ziel um 85050 to 94950 und über 21 sec um weitere 194250 to 225750."},
[66068] = {name = "Dornen", description = "[q]Aus dem befreundeten Ziel sprießen Dornen, die jedem Angreifer 463 to 537 Naturschaden zufügen. Hält 10 min lang an."},
[66069] = {name = "Zwillingsstachel", description = "[q]Der Zwillingsstachel verursacht 125% Waffenschaden und erhöht den erlittenen Schaden um 20%. Hält 15 sec lang oder für 5 Aufladungen an."},
[66070] = {name = "Wucherwurzeln", description = "[q]Lässt das Ziel an Ort und Stelle festwurzeln und verursacht 10 sec lang insgesamt 1500 Naturschaden. Erlittener Schaden kann den Effekt unterbrechen. Kann nur draußen verwendet werden."},
[66071] = {name = "Griff der Natur", description = "[q]Bei Aktivierung besteht eine Chance von 100%, dass Nahkampfangriffe auf den Zaubernden den Angreifer mit dem Effekt 'Wucherwurzeln' belegen. 1 Aufladung. Hält 45 sec lang an."},
[66075] = {name = "Zwillingsstachel", description = "[q]Der Zwillingsstachel verursacht 125% Waffenschaden und erhöht den erlittenen Schaden um 20%. Hält 15 sec lang oder für 5 Aufladungen an."},
[66076] = {name = "Werfen", description = "[q]Wirft Eure Waffe, verursacht 0% Waffenschaden."},
[66081] = {name = "Mehrfachschuss", description = "[q]Feuert eine Salve von Geschossen auf einen Feind und seine nächsten Verbündeten ab, fügt normalen Schaden plus 100 zu, wirkt auf bis zu 3 Ziele."},
[66083] = {name = "Blitzschlagpfeile", description = "[q]Beschwört einen Blitzschlag, der die Pfeile des Zaubernden trifft und sie verzaubert. Automatische Distanzangriffe verursachen zusätzlich 4713 to 5287 Naturschaden."},
[66086] = {name = "Gelassenheit", description = "[q]Regeneriert 10 sec lang alle 2 Sek. die Gesundheit naher Gruppenmitglieder um 231250 to 268750. Kanalisiert."},
[66092] = {name = "Entschlossenheit", description = "[q]Der Skarabäus gewinnt seine Gesundheit zurück und wird immun gegen Betäubungs- und bewegungseinschränkende Effekte. Erhöht zudem das Bewegungstempo um 100%."},
[66093] = {name = "Blühendes Leben", description = "[q]Heilt pro Sekunde um 11310 to 12690 und nach Ablauf oder Bannen des Effektes um weitere 55500 to 64500."},
[66098] = {name = "Sühne", description = "[q]Schleudert eine Salve heiligen Lichts auf das Ziel, wodurch es sofort um 42255 to 47745 geheilt wird sowie 2 sec lang alle 1 Sek. um einen zusätzlichen Betrag."},
[66099] = {name = "Machtwort: Schild", description = "[q]Nutzt die Seelenkraft des Gruppenmitglieds zum Abschirmen und absorbiert dabei 67063 to 77937 Schaden. Hält 30 sec lang an. Solange der Schild hält, wird das Zauberwirken durch erlittenen Schaden nicht unterbrochen. Nachdem das Ziel einmal abgeschirmt wurde, kann es 15 sec lang nicht wieder abgeschirmt werden."},
[66100] = {name = "Manabrand", description = "[q]Zerstört 4374 to 4626 Mana des Ziels. Für jeden Punkt auf diese Weise zerstörten Manas erleidet das Ziel 3 Punkte Schattenschaden."},
[66104] = {name = "Blitzheilung", description = "[q]Heilt ein befreundetes Ziel um 83250 to 96750."},
[66112] = {name = "Heiliges Licht", description = "[q]Heilt ein befreundetes Ziel um 127710 to 142290."},
[66113] = {name = "Lichtblitz", description = "[q]Heilt ein befreundetes Ziel um 84825 to 95175."},
[66114] = {name = "Heiliger Schock", description = "[q]Überzieht das Ziel mit heiliger Energie, die Feinden 72000 to 78000 Heiligschaden zufügt oder Verbündete um 72000 to 78000 Gesundheit heilt."},
[66115] = {name = "Hand der Freiheit", description = "[q]Legt eine Hand auf ein freundliches Ziel und gewährt ihm so 10 sec lang Immunität gegen jegliche bewegungseinschränkenden Effekte. Spieler können pro Paladin immer nur von einer Hand berührt werden."},
[66116] = {name = "Reinigung des Glaubens", description = "[q]Reinigt ein befreundetes Ziel und hebt dabei 1 Gifteffekt, 1 Krankheitseffekt und 1 Magieeffekt auf."},
[66117] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[66118] = {name = "Egelschwarm", description = "[q]Entfesselt einen Insektenschwarm, der die Feinde des Zaubernden angreift und ihnen alle 1 Sek. 30% der aktuellen Gesundheit des jeweiligen Ziels entzieht. Es werden minimal 250 Gesundheit entzogen."},
[66129] = {name = "Spinnenraserei", description = "[q]Erhöht das Angriffs- und Zauber- und Bewegungstempo jedes nerubischen Gräbers innerhalb von 12 Metern um 100%."},
[66134] = {name = "Schattenhieb", description = "[q]Schreitet durch die Schatten, um hinter einem feindlichen Ziel aufzutauchen und 40000 Schattenschaden zu verursachen."},
[66177] = {name = "Erneuerung", description = "[q]Heilt das Ziel im Verlauf von 15 sec um 346875 to 403125."},
[66178] = {name = "Schattenschritt", description = "[q]Versucht, in die Schatten zu tauchen, um hinter dem Gegner zu erscheinen."},
[66180] = {name = "Schwerer Lederball", description = "[q]Werft den Ball einem freundlich gesinnten Spieler zu. Falls er Platz in seinem Rucksack hat, wird er ihn fangen!"},
[66187] = {name = "Konstante Rune", description = nil},
[66193] = {name = "Permafrost", description = "[q]Gefriert den Boden in einem kleinen Bereich, verringert das Bewegungstempo in diesem Gebiet um 80% und hindert die meisten Kreaturen daran, sich durch den Boden zu graben."},
[66195] = {name = "Brandbombe", description = "[q]Schleudert einen Brandsatz, der allen Feinden innerhalb von 5 Metern um den Einschlagsort Feuerschaden zufügt. Zusätzlich dazu werden die Ziele mit einer brennenden Flüssigkeit bedeckt, die 5 sec lang zusätzlichen Feuerschaden anrichtet."},
[66197] = {name = "Legionsflamme", description = "[q]Verbrennt das Ziel und ruft am Ort des Ziels 6 sec lang alle 1 Sek. eine Legionsflamme hervor."},
[66203] = {name = "Dampfschlag", description = "[q]Fügt allen Gegnern im Umkreis von 15 Metern Feuerschaden zu und stößt sie zurück. Sobald diese Fähigkeit ausgelöst wird, wird der Belagerungsturm 4 sec lang keine Dampfkraft erzeugen können."},
[66207] = {name = "Zurechtstutzen", description = "[q]Verstümmelt den Feind und verringert 10 sec lang das Bewegungstempo des feindlichen Ziels um 50%."},
[66218] = {name = "Abschuss", description = "[q]Schleudert den Fahrer an den gewünschten Ort."},
[66228] = {name = "Macht des Nether", description = "[q]Die Macht des Nethers erhöht 30 sec lang den verursachten Magieschaden um 20%."},
[66237] = {name = "Fleisch einäschern", description = "[q]Brennt Euch das Fleisch von den Knochen! Absorbiert die nächsten 85000 Punkte erhaltene Heilung und verringert den verursachten Schaden 15 sec lang um 50%. Wenn der Zauber 'Fleisch einäschern' nicht entfernt wird, bevor seine Effektdauer abläuft, wird er ein brennendes Inferno verursachen."},
[66251] = {name = "Abschuss", description = "[q]Schleudert den Fahrer in Richtung des festgelegten Ortes."},
[66254] = {name = "Feuerzyklon beschwören", description = "[q]Beschwört 1 Feuerelementar als Begleitung für den Zaubernden bis zur Freigabe."},
[66258] = {name = "Höllische Eruption", description = "[q]Lord Jaraxxus beschwört einen höllischen Vulkan, durch den Höllenbestien erscheinen."},
[66269] = {name = "Netherportal", description = "[q]Lord Jaraxxus beschwört ein Netherportal."},
[66282] = {name = "Waptorfallenquietschen", description = "[q]Die Falle erzeugt ein ohrenbetäubendes Geröusch, als würden einen eine Million Raptoren anschreien."},
[66283] = {name = "Windender Schmerzstachel", description = "[q]Springt auf den Feind zu, ergreift ihn und verursacht erheblichen körperlichen Schaden."},
[66290] = {name = "Schlaf", description = "[q]Versetzt einen Feind bis zu 6 sec lang in Schlaf. Jeglicher erlittene Schaden führt dazu, dass das Ziel erwacht. Es kann immer nur jeweils ein Ziel in Schlaf versetzt werden."},
[66296] = {name = "Schattenschleier", description = "[q]Hüllt das Gefährt 20 sec lang in Schatten, erhöht die Verstohlenheit und das Bewegungstempo."},
[66300] = {name = "Auf die Tube drücken", description = "[q]Drückt das Pedal voll durch! Erhöht Tempo um 50%."},
[66313] = {name = "Feuerbombe", description = "[q]Fügt allen Feinden in einem Bereich von 8 Metern um das Ziel 4813 to 6187 Feuerschaden zu."},
[66320] = {name = "Feuerbombe", description = "[q]Fügt Feinden alle 1 Sek. 7800 to 8200 Feuerschaden zu."},
[66330] = {name = "Erschütterndes Stampfen", description = "[q]Ein erschütterndes Stampfen fügt allen Feinden in einem Bereich von 15 Metern 11700 to 12300 körperlichen Schaden zu und unterbricht 8 Sek. lang das Wirken von Zaubern."},
[66331] = {name = "Pfählen", description = "[q]Fügt einem Feind 150% Waffenschaden zu und lässt ihn pro Anwendung alle 2 Sek. für 3938 to 5062 Schaden bluten. Hält 40 sec lang an."},
[66334] = {name = "Kuss der Herrin", description = "[q]Der nächste gewirkte Zauber mit einer Zauberzeit unterbricht 8 sec lang das Zauberwirken in dieser Magieart und verursacht 13650 to 14350 Schattenschaden."},
[66378] = {name = "Shivanschlitzen", description = "[q]Ein wirbelnder Angriff, der 75% Waffenschaden verursacht und Rüstung durchschlägt."},
[66407] = {name = "Kopfkracher", description = "[q]Betäubt einen Feind 2 sec lang."},
[66408] = {name = "Einprügeln", description = "[q]Trifft einen Feind für 75% des normalen Nahkampfschadens und unterbricht 5 sec lang das Wirken von Zaubern."},
[66456] = {name = "Gleve werfen", description = "[q]Feuert eine große Gleve auf das Ziel und verursacht in einem Radius von 5 Metern körperlichen Schaden und 4242 to 4758 Belagerungsschaden."},
[66489] = {name = "Speerwurf", description = "[q]Fügt einem Feind Frostschaden zu."},
[66494] = {name = "Teufelshitze", description = "[q]Die teuflische Höllenbestie stürmt ein Ziel an, verursacht 11700 to 12300 Feuerschaden und löst in diesem Gebiet ein Teufelsinferno aus."},
[66495] = {name = "Teufelsinferno", description = "[q]Fügt Feinden innerhalb von 15 Metern 6 sec lang regelmäßig Feuerschaden zu."},
[66514] = {name = "Frostatem", description = "[q]Fügt Feinden in einem kegelförmigen Bereich von 70 Metern vor dem Zaubernden 0 Frostschaden zu. Darüber hinaus sind die Ziele 2 sec lang festgefroren."},
[66515] = {name = "Reflektierender Schild", description = "[q]Hüllt den Zaubernden in einen Schild, der Schaden absorbiert und 25% allen absorbierten Schadens auf den Angreifer reflektiert."},
[66518] = {name = "Luftschiffkanone", description = "[q]Schleudert eine Feuerkugel, die innerhalb von 10 Metern um den Einschlagsort Feuerschaden und 943 to 1057 Belagerungsschaden verursacht."},
[66528] = {name = "Teufelsblitzschlag", description = "[q]Trifft einen Feind mit einem Legionsblitzschlag, der auf andere nahe Feinde überspringt. Der Zauber trifft bis zu 3 Ziele."},
[66529] = {name = "Luftschiffkanone", description = "[q]Schleudert eine Feuerkugel, die innerhalb von 10 Metern um den Einschlagsort Feuerschaden und 943 to 1057 Belagerungsschaden verursacht."},
[66532] = {name = "Teufelsfeuerball", description = "[q]Verursacht 34125 to 35875 Feuerschaden sowie zusätzlich 5 sec lang alle 1 Sek. 10725 to 11275 Feuerschaden."},
[66536] = {name = "Heilige Pein", description = "[q]Peinigt einen Gegner und verursacht 5625 to 6875 Heiligschaden."},
[66537] = {name = "Erneuerung", description = "[q]Heilt ein befreundetes Ziel alle 3 Sek. um 22000."},
[66538] = {name = "Heiliges Feuer", description = "[q]Verzehrt einen Feind mit Flammen, verbrennt ihn und fügt ihm im Verlauf von 8 sec alle 2 Sek. zusätzlichen Feuerschaden zu."},
[66542] = {name = "Brandstoffrakete", description = "[q]Feuert eine große, mit Brandsätzen gefüllte Rakete in die Ferne. Die Brandsätze verbrennen 15 sec lang alle Gegner innerhalb von 5 Metern um den Einschlagort."},
[66544] = {name = "Yellow Aura", description = nil},
[66546] = {name = "Heilige Nova", description = "[q]Verursacht eine Explosion heiligen Lichts, die nahen Feinden 7000 to 9000 Heiligschaden zufügt und diese zurückstößt."},
[66547] = {name = "Beichten", description = "[q]Zwingt das Ziel, seine finstersten Erinnerungen zu beichten."},
[66552] = {name = "Wandelnder Alptraum", description = "[q]Erfüllt nahe Feinde mit Furcht, verursacht regelmäßigen Schattenschaden und lässt sie 4 sec lang voller Schrecken flüchten."},
[66601] = {name = "Blue Aura", description = nil},
[66602] = {name = "Red Aura", description = nil},
[66613] = {name = "Hammer der Gerechtigkeit", description = "[q]Betäubt das Ziel 6 sec lang."},
[66619] = {name = "Schatten der Vergangenheit", description = "[q]Schatten der Vergangenheit umschlingen das Ziel, verursachen Schattenschaden und verringern das Angriffs- und Zaubertempo 5 sec lang um 90%."},
[66620] = {name = "Alte Wunden", description = "[q]Fügt einem Gegner 12 sec lang alle 1 Sek. 1875 körperlichen Schaden zu."},
[66636] = {name = "Aufkochende Wut", description = "[q]Die aufkochende Wut erhöht den verursachten Schaden um 15%."},
[66665] = {name = "Brennender Atem", description = "[q]Der brennende Atem fügt allen Zielen 3 sec lang alle 1 Sek. 5363 to 5637 Feuerschaden zu."},
[66683] = {name = "Massives Schmettern", description = "[q]Springt in die Luft und kommt mit massiver Kraft auf dem Boden auf, fügt allen Feinden 16188 to 18812 körperlichen Schaden zu, betäubt sie und stößt sie zurück."},
[66684] = {name = "Flammender Zunder", description = "[q]Fügt allen Feinden innerhalb von 4 Metern um den flammenden Zunder 4388 to 4612 Feuerschaden zu."},
[66689] = {name = "Arktischer Atem", description = "[q]Ein eisiger Atem gefriert alle Ziele in einem kegelförmigen Bereich vor dem Zaubernden und verursacht 5 sec lang 30000 Frostschaden."},
[66721] = {name = "Brennende Wut", description = "[q]Erhöht den verursachten Schaden um 5%."},
[66725] = {name = "Meteorfäuste", description = "[q]Der von den Angriffen zugefügte Schaden wird gleichwertig mit einem Ziel geteilt, das sich in einem Bereich von 10 Metern um das Hauptziel befindet."},
[66734] = {name = "Trampeln", description = "[q]Trampelt Feinde innerhalb von 12 Metern nieder und verursacht 50000 körperlichen Schaden."},
[66757] = {name = "Gekühlter Boden", description = "[q]Der Boden unter dem Auge des Frosts ist so kalt, dass Eure Füße beinahe am Boden festfrieren."},
[66758] = {name = "Betäubte Benommenheit", description = "[q]Von einem massiven Einschlag betäubt, kann nicht agieren und erleidet 15 sec lang 100% mehr Schaden."},
[66759] = {name = "Schäumende Wut", description = "[q]Erhöht den körperlichen Schaden und das Angriffstempo um 100%."},
[66770] = {name = "Heftiger Kopfstoß", description = "[q]Versetzt einem Feind einen heftigen Kopfstoß, der 83250 to 96750 körperlichen Schaden verursacht und ihn 2.50 sec lang betäubt."},
[66776] = {name = "Wut", description = "[q]Der General hat seinen Kommandostand verlassen und ist wütend. Erhöht seinen verursachten Schaden um 300%, sein Bewegungstempo um 100% und verringert seinen erlittenen Schaden um 95%, bis er sich wieder in seinen Kommandostand zurückbegibt."},
[66794] = {name = "Feger", description = "[q]Wirbelt herum, fügt Feinden innerhalb von 15 Metern 10175 to 11825 körperlichen Schaden zu und stößt sie zurück."},
[66795] = {name = "Holz sammeln", description = "[q]Sammelt einen in der Nähe befindlichen Stapel azsharischen Holzes ein."},
[66796] = {name = "Feuer speien", description = "[q]Fügt einem Feind 10175 to 11825 Feuerschaden zu."},
[66798] = {name = "Todesfrist", description = "[q]Die 'Todesfrist' betäubt das Ziel, verursacht Schattenschaden und stößt das Ziel zurück."},
[66808] = {name = "Meteorfäuste", description = "[q]Der von den Angriffen zugefügte Schaden wird gleichwertig mit einem Ziel geteilt, das sich in einem Bereich von 10 Metern um das Hauptziel befindet."},
[66811] = {name = "Motorsäge", description = "[q]Sägt innerhalb von 5 Metern vor Euch Bäume ab oder fügt Feinden Schaden zu."},
[66813] = {name = "Lavaeruption", description = "[q]Der Flammenwächter schleudert geschmolzene Lava nach dem Ziel und verursacht so 19425 to 22575 Feuerschaden."},
[66818] = {name = "Ätzender Auswurf", description = nil},
[66821] = {name = "Geschmolzener Auswurf", description = "[q]Fügt allen Feinden in einem kegelförmigen Bereich vor dem Zaubernden 2.50 sec lang alle 0.3 Sek. 4625 to 5375 Feuerschaden zu."},
[66822] = {name = "Summon Sniper", description = nil},
[66823] = {name = "Paralysierendes Toxin", description = "[q]Vergiftet das Ziel mit einem lähmenden Toxin, das alle 2 Sek. ansteigenden Naturschaden verursacht und das Bewegungstempo im Verlauf der Zeit bis zum völligen Stillstand weiter verlangsamt. Der Kontakt mit brennender Galle neutralisiert paralysierendes Toxin."},
[66824] = {name = "Paralysierender Biss", description = "[q]Fügt einem Feind 18500 to 21500 Naturschaden zu und injiziert ihm paralysierendes Toxin."},
[66862] = {name = "Strahlen", description = "[q]Licht durchdringt Eadric. Alle in der Nähe befindlichen Feinde, die ihn ansehen, erleiden 5938 to 6562 Heiligschaden und wandern 4 sec lang geblendet herum."},
[66863] = {name = "Hammer der Gerechtigkeit", description = "[q]Betäubt einen Feind, macht ihn gegen 'Hammer der Rechtschaffenen' verwundbar. Kann sich 6 sec lang weder bewegen noch angreifen."},
[66865] = {name = "Rache", description = "[q]Eadrics Nahkampfangriffe haben eine Chance, seinen nächsten Nahkampfangriff zu einem kritischen Treffer werden zu lassen."},
[66867] = {name = "Hammer der Rechtschaffenen", description = "[q]Schleudert einen Hammer gegen einen Feind und fügt 16000 Heiligschaden zu. Wenn das Ziel nicht von 'Hammer der Gerechtigkeit' betroffen ist, kann der Hammer gefangen und wieder gegen den Zaubernden geschleudert werden."},
[66868] = {name = "Klinge schleudern", description = "[q]Wirft Sägeblätter auf feindliche Nachtelfen im Umkreis von 35 Metern."},
[66869] = {name = "Brennende Galle", description = "[q]Bedeckt Feinde mit brennender Galle, die ihnen und ihren befreundeten Einheiten regelmäßigen Feuerschaden zufügt. Es ist bekannt, dass die brennende Galle von Jormungar paralysierende Toxine neutralisiert."},
[66879] = {name = "Brennender Biss", description = "[q]Fügt einem Feind 18500 to 21500 Feuerschaden zu und bedeckt ihn mit brennender Galle."},
[66880] = {name = "Säurespucke", description = "[q]Fügt einem Feind 10175 to 11825 Naturschaden zu."},
[66883] = {name = "Schleimpfütze", description = "[q]Eine Giftwolke erscheint und wächst 30 sec lang im Zielgebiet."},
[66893] = {name = "Granatenwerfer", description = "[q]Wirft wahllos Granaten wie Reis bei einer Hochzeit."},
[66901] = {name = "Paralysierendes Sprühen", description = "[q]Sprüht Säure auf einen Gegner und nahe Ziele, verursacht 12950 to 15050 Naturschaden und vergiftet sie mit einem paralysierenden Toxin."},
[66902] = {name = "Brennendes Sprühen", description = "[q]Sprüht Flüssigkeit auf einen Gegner und nahe Ziele, verursacht 12950 to 15050 Feuerschaden und bedeckt sie mit brennender Galle."},
[66937] = {name = "Aufladen", description = "[q]Greift auf die Notfallenergiereserven zu und heilt den Schredder für 70% seiner maximalen Gesundheit."},
[66954] = {name = "Ätzstoß", description = "[q]Fügt einem Feind Naturschaden zu und reduziert seine Rüstung 6 sec lang um 50%."},
[66966] = {name = "Zerkrachen", description = "[q]Zerschmettert das Ziel, fügt ihm 68 to 82 Schaden zu und stößt ihn zurück."},
[66967] = {name = "Wucherwurzeln", description = "[q]Umwickelt einen Feind mit Wurzeln, macht ihn bis zu 5 sec lang bewegungsunfähig und fügt ihm alle 3 Sek. Naturschaden zu."},
[66968] = {name = "Brocken", description = "[q]Schleudert einen Brocken auf einen Feind und fügt körperlichen Schaden zu."},
[67002] = {name = "Crowley Summon Initialize", description = nil},
[67005] = {name = "Schatten beschwören", description = "[q]Beschwört einen Todesschatten, der unter dem Befehl von Todessprecher Kharos steht."},
[67022] = {name = "Strangulierender Nebel", description = "[q]Umhüllt das Ziel mit Nebel, fügt alle 1 Sek. 700 Schattenschaden zu."},
[67034] = {name = "Gleve werfen", description = "[q]Feuert eine große Gleve auf das Ziel und verursacht in einem Radius von 5 Metern körperlichen Schaden und 4242 to 4758 Belagerungsschaden."},
[67037] = {name = "Wirbelwind", description = "[q]Greift in der Nähe befindliche Feinde mit einem Wirbel aus Stahl an, der until cancelled lang anhält und normalen Schaden plus 50 verursacht."},
[67038] = {name = "Zerkrachen", description = "[q]Fügt allen Feinden in einem kegelförmigen Bereich von 30 Metern vor dem Zaubernden 4375 to 5625 körperlichen Schaden zu."},
[67194] = {name = "Springbrunnen des Lichts", description = "[q]Erschafft einen Springbrunnen des Lichts, der nahe freundliche Einheiten 45 sec lang oder bis zu seiner Zerstörung heilt."},
[67195] = {name = "Klingensalve", description = "[q]Feuert eine Salve kleinerer Klingen auf das Zielgebiet. Jede Klinge verursacht in einem Umkreis von 5 Metern körperlichen Schaden. Hält 10 sec lang an."},
[67229] = {name = "Gedankenkontrolle", description = "[q]Übernimmt 8 sec lang die Kontrolle über die Gedanken eines Humanoiden."},
[67233] = {name = "Schlagwirbel", description = "[q]Erhöht die Angriffsgeschwindigkeit des Zaubernden um 100%, verringert jedoch verursachten Schaden pro Treffer um 30%. Hält 8 sec lang an."},
[67235] = {name = "Zuschlagen", description = "[q]Schlägt auf einen Feind ein, fügt ihm 30 Schaden zu und unterbricht 4 sec lang den gewirkten Zauber."},
[67237] = {name = "Schlag des Ungleichgewichts", description = "[q]Verursacht 120% Waffenschaden und bringt das Ziel aus dem Gleichgewicht. Reduziert die Verteidigungsfertigkeit des Ziels 6 sec lang um 100."},
[67247] = {name = "Sengendes Licht", description = "[q]Ein Verbündeter wird von blendendem Licht umschlungen und um 5655 to 6345 geheilt, während alle Feinde im Umkreis von 8 Metern des Ziels 5655 to 6345 Heiligschaden erleiden."},
[67251] = {name = "Gottesschild", description = "[q]Schützt den Zaubernden 9 sec lang vor allen Angriffen und Zaubern."},
[67255] = {name = "Finale Meditation", description = "[q]Meditiert, verlangsamt das Bewegungstempo und das Angriffstempo von Gegnern innerhalb von 15 Metern. Hält 8 sec lang an."},
[67279] = {name = "Rebellenkanone", description = "[q]Schießt eine mächtige Kanonenkugel auf den Feind."},
[67280] = {name = "Dolchwurf", description = "[q]Wirft einen Dolch auf einen zufälligen Feind, verursacht sofort Schaden und alle 3 Sek. weiteren Schaden. Hält 12 sec lang an."},
[67294] = {name = "Shoot Me Cue", description = nil},
[67301] = {name = "Feuerschuss", description = "[q]Feuert einen Flammenschuss auf den Gegner ab, der zusätzlich 20 Schaden verursacht und den Schaden feuerbasiert macht."},
[67322] = {name = "Untertauchen", description = "[q]Versucht, sich im Sand zu vergraben. Kann nicht auf Boden gewirkt werden, der mit 'Permafrost' gefroren wurde."},
[67345] = {name = "Wirbeln", description = "[q]Wirbelt herum, fügt allen nahen Feinden 12950 to 15050 körperlichen Schaden zu und stößt sie zurück."},
[67367] = {name = "Neuverfleischlicht", description = "[q]Neuverfleischlicht... und sieht etwas mitgenommen aus."},
[67393] = {name = "Passagier auswerfen", description = "[q]Schleudert Euren Passagier vom Motorrad."},
[67411] = {name = "Geistersiphon", description = "[q]Zehrt die Geisterenergie gefallender Diener von Elune auf."},
[67440] = {name = "Brocken schleudern", description = "[q]Schleudert einen massiven Brocken in die Ferne. Der Brocken fügt allen Feinden innerhalb von 10 Metern Feuerschaden zu und verursacht 1414 to 1586 Belagerungsschaden."},
[67441] = {name = "Rammen", description = "[q]Rammt alle Feinde innerhalb von 7 Metern vor dem Verwüster, verursacht Schaden und stößt sie zurück. Fügt zudem Gebäuden 1885 to 2115 Belagerungsschaden zu."},
[67452] = {name = "Raketenschlag", description = "[q]Feuert eine große Rakete in die Ferne, die allen Gegnern innerhalb von 8 Metern um die Explosion Schaden zufügt."},
[67461] = {name = "Kanone abfeuern", description = "[q]Feuert einen Kanonenschuss ab, der Feuerschaden verursacht sowie an nahen Strukturen 4242 to 4758 Belagerungsschaden."},
[67503] = {name = "Rebellenmut", description = "[q]Schneller, besser, stärker."},
[67514] = {name = "Teufelsjäger beschwören", description = "[q]Beschwört eine Teufelsjäger, der den Zaubernden bis zur Freigabe begleitet."},
[67523] = {name = "Feuergarbe", description = "[q]Zündet einen Feind an... zu TODE!"},
[67528] = {name = "Welle der Heilung", description = "[q]Ruft Naturmagie herbei, um einen Verbündeten zu heilen."},
[67529] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 3 Ziele aus und verursacht bei jedem folgenden Ziel immer größeren Naturschaden."},
[67530] = {name = "Erdschild", description = "[q]Schützt das Ziel mit einem Erdschild. Nahkampfangriffe heilen das geschützte Ziel, dieser Effekt tritt aber nur einmal alle paar Sekunden auf. 10 Aufladungen. Hält 2 min lang an."},
[67534] = {name = "Verhexung der Besserung", description = "[q]Verhext das Ziel, sodass alle von ihm erhaltenen direkten Heilungen alle seine in der Nähe befindlichen Feinde heilen."},
[67540] = {name = "Abfangen", description = "[q]Stürm einen Feind an und verursacht 0% Waffenschaden."},
[67541] = {name = "Klingensturm", description = "[q]Greift sofort bis zu 1 nahe Ziele an. Für die nächsten 6 sec werdet Ihr alle 1 Sek. einen Wirbelwindangriff durchführen. Während 'Klingensturm' aktiv ist, könnt Ihr Euch bewegen, aber während dieser Zeit keine anderen Fähigkeiten einsetzen. Ihr fühlt weder Mitleid, noch Bedauern oder Furcht und es gibt keine Möglichkeit, Euch aufzuhalten, es sei denn, Ihr werdet getötet."},
[67542] = {name = "Tödlicher Stoß", description = "[q]Verursacht 100% Waffenschaden und verwundet das Ziel, verringert außerdem die Wirksamkeit jeglicher Heilung 5 sec lang um 50%."},
[67546] = {name = "Überrollwurf", description = "[q]Ergreift Euer Ziel und schleudert es rückwärts."},
[67570] = {name = "Feldmesser eskortieren", description = "Führt den Feldmesser zu den drei Markierungen am Mennarsee."},
[67574] = {name = "Von Anub'arak verfolgt", description = "[q]Anub'arak verfolgt Euch."},
[67588] = {name = "Untersuche", description = "[q]Spielt mit der Untersuchungsausrüstung."},
[67594] = {name = "Giftflasche", description = "[q]Verursacht pro Sekunde 1750 Naturschaden."},
[67597] = {name = "Quest Complete", description = nil},
[67689] = {name = "Geisterhafte Fehlzündung", description = "[q]Etwas widerliches kommt aus der Raupe!"},
[67692] = {name = "Den Sturm besänftigen", description = "[q]Bringt den Spieler zurück zu Furiens Posten."},
[67706] = {name = "Dolchfächer", description = "[q]Wirft sofort beide Waffen auf alle Ziele innerhalb von 8 Metern und verursacht 150% Waffenschaden durch Dolche und 100% Waffenschaden durch alle anderen Waffenarten."},
[67709] = {name = "Ausweiden", description = "[q]Finishing-Move, der Schaden verursacht."},
[67710] = {name = "Tödliches Gift", description = "[q]Die Waffe des Zaubernden wird mit einem Gift überzogen, das 1 Stunde lang vorhält. Jeder Treffer vergiftet den Feind alle 3 für 1250 Naturschaden. Dieser Effekt kann nicht öfter als ein Mal alle 5 Sek. auftreten. Auf einem einzelnen Ziel bis zu 5-mal stapelbar."},
[67716] = {name = "Wirbelwind", description = "[q]Greift in einem Wirbel aus Stahl alle Feinde in einem Bereich von 5 Metern an, fügt jedem Feind mit beiden Waffen Waffenschaden zu."},
[67718] = {name = "Eisige Berührung", description = "[q]Verursacht 4875 to 5125 Frostschaden und ruft Frostfieber hervor."},
[67719] = {name = "Frostfieber", description = "[q]Eine Krankheit, die alle 3 Sek. Frostschaden verursacht und das Nahkampf- und Distanzangriffstempo des Ziels 15 sec lang um 25% verringert."},
[67721] = {name = "Schwäche aufdecken", description = "[q]Offenbart die Schwächen des Ziels. Der aus körperlichen Angriffen erlittene Schaden ist 10 sec lang um 25% erhöht. Bis zu 9-mal stapelbar."},
[67722] = {name = "Blutseuche", description = "[q]Die Blutseuche verursacht 12 sec lang alle 2 Sek. 3510 to 3690 Schattenschaden."},
[67724] = {name = "Seuchenstoß", description = "[q]Ein bösartiger Schlag, der Waffenschaden verursacht und das Ziel mit der Blutseuche ansteckt, einer Krankheit, die im Verlauf der Zeit Schattenschaden verursacht."},
[67725] = {name = "Auslöschen", description = "[q]Ein brutaler, sofortiger Angriff, der 125% Waffenschaden plus 1500 verursacht. Für jede Eurer auf das Ziel wirkenden Krankheiten wird der gesamte Schaden um 30% erhöht, wobei die Krankheiten vom Ziel entfernt werden."},
[67729] = {name = "Explodieren", description = "[q]Der Ghul explodiert, wird dabei zerstört und verursacht bei allen Feinden innerhalb von 8 Metern 8775 to 9225 Schattenschaden."},
[67749] = {name = "Sprung", description = "[q]Springt hinter den angewählten Gegner oder Freund."},
[67751] = {name = "Ghulexplosion", description = "[q]Der Schwarze Ritter bringt einen seiner Ghuldiener zum Explodieren!"},
[67761] = {name = "Armee der Toten", description = "[q]Beschwört eine ganze Legion Ghule, die für den Todesritter kämpfen. Die Ghule werden ausschwärmen und alle möglichen Ziele verspotten und bekämpfen. Der Schwarze Ritter wird hin und wieder auch noch 'Ghulexplosion' auf einen von ihnen wirken!"},
[67767] = {name = "Frostfieber", description = "[q]Eine Krankheit, die alle 3 Sek. Frostschaden verursacht und das Nahkampf- und Distanzangriffstempo des Ziels 15 sec lang um 14% verringert."},
[67774] = {name = "Klaue", description = "[q]Beharkt einen Gegner mit Klauen, verursacht 150 Schaden und wählt ein neues Ziel."},
[67777] = {name = "Tier rufen", description = "[q]Beschwört Euer Tier zu Euch."},
[67778] = {name = "Entweihung", description = "[q]Lässt am Aufenthaltsort des Ziels entweihten Boden entstehen. Das Bewegungstempo von Zielen in diesem Bereich wird von den gierig klammernden Armen der Toten um 50% verringert, während sie sich auf dem unheiligen Boden befinden. Hält 2 sec lang an."},
[67794] = {name = "Insektenverwandlung", description = "[q]Verwandelt den Feind in ein Insekt und erhöht die Wahrscheinlichkeit, zertreten zu werden."},
[67796] = {name = "Rammen", description = "[q]Rammt alle Feinde innerhalb von 13 Metern vor der Belagerungsmaschine, verursacht Schaden und stößt sie zurück. Fügt zudem Gebäuden 1500 Belagerungsschaden zu."},
[67797] = {name = "Volldampf voraus!", description = "[q]Schaltet die Dampfmaschine eine kurze Zeit lang in den Overdrive und lässt sie nach vorne schnellen, fügt Spielercharakteren Schaden zu und stößt sie zurück. Fügt zudem Gebäuden 1885 to 2115 Belagerungsschaden zu."},
[67805] = {name = "Lauerer angreifen", description = "[q]Findet den nächsten Blutfanglauerer und greift ihn an."},
[67808] = {name = "Biss des Todes", description = "[q]Fügt allen Gegnern 2925 to 3075 Schattenfrostschaden zu und erhöht den erlittenen Magieschaden 15 sec lang um 5%."},
[67822] = {name = "Kakerlake beschwören", description = nil},
[67823] = {name = "Zum Tode verurteilt", description = "[q]Der Schwarze Ritter hat Euch zum Tode verurteilt. Euer erlittener Magieschaden wird 10 sec lang um 200% erhöht."},
[67825] = {name = "Linker Haken", description = "[q]Fügt einem Feind und seinem nächsten Verbündeten 80% des normalen Nahkampfschadens zu."},
[67827] = {name = "Schnapptritt", description = "[q]Fügt einem Gegner Schaden zu und betäubt ihn 2 sec lang."},
[67834] = {name = "Boreanische Tundra", description = "[q]Transportiert Euch irgendwohin in die Boreanische Tundra - hoffentlich."},
[67835] = {name = "Sholazarbecken", description = "[q]Transportiert Euch irgendwohin ins Sholazarbecken - hoffentlich."},
[67836] = {name = "Eiskrone", description = "[q]Transportiert Euch irgendwohin in Eiskrone - hoffentlich."},
[67837] = {name = "Sturmgipfel", description = "[q]Transportiert Euch irgendwohin in die Sturmgipel - hoffentlich."},
[67838] = {name = "Heulender Fjord", description = "[q]Transportiert Euch irgendwohin in den Heulenden Fjord - hoffentlich."},
[67868] = {name = "Niedergetrampelt", description = "Betäubt."},
[68054] = {name = "Dringende Angelegenheiten", description = "Dieser extrem gutaussehende Roboter hat einige dringende Angelegenheiten, um die er sich kümmern muss. Profitiert von seinen Diensten, so lang Ihr könnt!"},
[68081] = {name = "Unterirdisch", description = "[q]Teleportiert Euch an einen stinkenden unterirdischen Ort."},
[68129] = {name = "Schiffeversenker beschwören", description = "[q]Ruft den Furor des Meeres herbei, um einen Wasserelementar zu beschwören."},
[68174] = {name = "Neptulons Segen", description = "[q]Erhöht Schwimmtempo um 250%."},
[68186] = {name = "Anub'arak Scarab Achievement 10", description = nil},
[68197] = {name = "Eadric Achievement", description = nil},
[68207] = {name = "Gift spucken", description = "[q]Fügt einem Gegner 8 sec lang alle 2 Sek. 15 Naturschaden zu."},
[68208] = {name = "Dornen verschießen", description = "[q]Schleudert Dornen nach einem Gegner und verursacht so 13 to 17 Naturschaden."},
[68209] = {name = "Frostblitz", description = "[q]Fügt einem Feind 22 to 28 Frostschaden zu und verringert 4 sec lang sein Bewegungstempo."},
[68235] = {name = "Kanonenfeuer", description = "[q]Schießt eine mächtige Kanonenkugel auf den Feind."},
[68246] = {name = "Erfrierung", description = "[q]Fügt einem Feind Frostschaden zu und reduziert 15 sec lang sein Zaubertempo."},
[68249] = {name = "Flutwelle", description = "[q]Ein Wasserwelle wogt in einem kegelförmigen Bereich von 100 Metern vor dem Zaubernden hinweg und verursacht 139 to 161 Naturschaden."},
[68250] = {name = "Wassergeschoss", description = "[q]Schleudert ein Wassergeschoss auf einen Feind und fügt ihm Frostschaden zu."},
[68252] = {name = "Eisblock", description = "[q]Ihr werdet in einen Eisblock eingehüllt, der Euch 6 sec lang vor allen körperlichen Angriffen und Zaubern schützt sowie Eure Gesundheit und Euer Mana regeneriert."},
[68263] = {name = "Frostblitz", description = "[q]Fügt einem Feind Frostschaden zu und verringert 4 sec lang sein Bewegungstempo."},
[68272] = {name = "Bitterbräu", description = "[q]Fügt dem Ziel 834 to 934 Naturschaden zu."},
[68282] = {name = "Sturmangriff", description = "[q]Ein Sturmangriff, der 20000 Schaden verursacht und eine Aufladung von 'Verteidigen' vom Gegner entfernt."},
[68301] = {name = "Sturmangriff", description = "[q]Ein Sturmangriff, der 0 Schaden verursacht und eine Aufladung von 'Verteidigen' vom Gegner entfernt."},
[68333] = {name = "Gnomeningenieurskunst verlernen", description = "[q]Verlernt die Spezialisierung auf die Gnomeningenieurskunst, wodurch Ihr eine andere Spezialisierung erlernen könnt."},
[68334] = {name = "Gobliningenieurskunst verlernen", description = "[q]Verlernt die Spezialisierung auf die Gobliningenieurskunst, wodurch Ihr eine andere Spezialisierung erlernen könnt."},
[68335] = {name = "Wutanfall", description = "[q]Erhöht den verursachten Schaden um 50%."},
[68340] = {name = "Rückzug", description = "[q]Versucht, sich mit einem Rückwärtssprung vom Ziel zurückzuziehen. Muss dem Ziel zugewandt sein."},
