[69579] = {name = "Bogenschlitzer", description = "[q]Hackt nach der Rüstung des Feindes und verringert sie um 10% pro Einsatz von 'Bogenschlitzer'. Kann bis zu 5-mal angewendet werden. Hält 30 sec lang an."},
[69580] = {name = "Schildblock", description = "[q]Erhöht 20 sec lang die Chance zu blocken um 100%, blockt jedoch nur 1 Angriff."},
