[69603] = {name = "Verseuchung", description = "[q]Ein unheiliges Gebrechen verseucht das Ziel und fügt ihm alle 3 Sek. Naturschaden zu. Springt nach Ablauf von 21 sec auf ein anderes Ziel über, wenn der Effekt nicht innerhalb dieses Zeitraums aufgehoben wird."},
[69633] = {name = "Schattenschleier", description = "[q]Verringert 15 sec lang die Heileffekte eines Feindes um 75%."},
