[69649] = {name = "Frostatem", description = "[q]Verursacht in einem kegelförmigen Bereich von 60 Metern vor dem Zaubernden 55500 to 64500 Frostschaden, zusätzlich wird 1.50 min lang das Angriffstempo der Ziele um 50% und ihr Bewegungstempo um 15% verringert."},
[69651] = {name = "Verwundender Schlag", description = "[q]Verursacht 200% Waffenschaden und verwundet das Ziel, verringert außerdem die Wirksamkeit jeglicher Heilung 10 sec lang um 40%."},
[69652] = {name = "Klingensturm", description = "[q]Greift sofort alle nahen Ziele an. Während der nächsten 6 sec werdet Ihr alle 1 Sek. einen Wirbelwindangriff durchführen. Während 'Klingensturm' aktiv ist, könnt Ihr Euch bewegen, aber während dieser Zeit keine anderen Fähigkeiten einsetzen. Ihr fühlt weder Mitleid, noch Bedauern oder Furcht und es gibt keine Möglichkeit, Euch aufzuhalten, es sei denn, Ihr werdet getötet."},
[69674] = {name = "Mutierte Infektion", description = "[q]Die mutierte Infektion fügt alle 1 Sek. 4875 to 5125 Schaden zu und verringert die erhaltene Heilung 12 sec lang um 75%. Wenn die Infektion entfernt wird, entsteht am Standpunkt des Ziels ein kleiner Schlamm."},
[69679] = {name = "Raketenartillerie", description = "[q]Schleudert eine Rakete, die beim Aufprall explodiert und in der Nähe befindlichen Feinden Feuerschaden sowie dem feindlichen Kanonenboot Belagerungsschaden zufügt."},
[69705] = {name = "Unter Null", description = "[q]Friert das Ziel in einem Eisblock ein, indem die Feuchtigkeit in der Luft konzentriert und gefroren wird. Der Zaubernde muss seine Kräfte kanalisieren, um den Eisblock aufrechtzuerhalten."},
[69708] = {name = "Eisgefängnis", description = "[q]Betäubt das Ziel und verhindert jegliche seiner Aktionen, macht es jedoch auch unverletzbar."},
[69745] = {name = "General Trigger 2 to Vehicle", description = nil},
[69750] = {name = "Schwach strahlender Schlamm", description = "[q]Der strahlende Schlamm fügt Feinden innerhalb von 10 Metern 4875 to 5125 Schaden zu und verschmilzt den kleinen Schlamm mit anderem in der Nähe befindlichen Schlamm."},
[69760] = {name = "Strahlender Schlamm", description = "[q]Der strahlende Schlamm fügt Feinden innerhalb von 10 Metern 6338 to 6662 Schaden zu und verschmilzt den großen Schlamm mit anderem in der Nähe befindlichen Schlamm."},
[69762] = {name = "Entfesselte Magie", description = "[q]Verursacht beim Ziel ein arkanes Verhängnis, das nach 5 sec zu einem Gegenschlag arkaner Macht aller gewirkter Zauber führt. Mehrfaches Zauberwirken des betroffenen Ziels verstärkt den Gegenschlag."},
[69774] = {name = "Klebriger Schlamm", description = "[q]Der Schlamm sprüht eine Pfütze grünen Glibbers in die Nähe des Ziels. Der grüne Glibber verursacht alle 1 Sek. 3900 to 4100 Schaden und verringert das Bewegungstempo um 50%."},
[69776] = {name = "Klebriger Schlamm", description = "[q]Der grüne Glibber verursacht alle 1 Sek. 3900 to 4100 Schaden und verringert das Bewegungstempo um 50%."},
[69780] = {name = "Unbarmherziger Winter", description = "[q]Verringert das Bewegungstempo um 75%."},
[69787] = {name = "Eisbarriere", description = "[q]Schirmt den Zaubernden sofort ab und absorbiert 100000000 Schaden. Hält until cancelled lang an. Solange der Schild hält, werden Zauber nicht unterbrochen."},
[69808] = {name = "Schlachtstandarte der Himmelsbrecher", description = "[q]Erhöht 2 min lang die maximale Gesundheit aller befreundeten Ziele im Umkreis von 80 Metern um 15% sowie ihr Nahkampfangriffs-, Distanzangriffs- und Zaubertempo um 25%."},
[69809] = {name = "Schlachtstandarte der Kor'kron", description = "[q]Erhöht 2 min lang die maximale Gesundheit aller befreundeten Ziele im Umkreis von 80 Metern um 15% sowie ihr Nahkampfangriffs-, Distanzangriffs- und Zaubertempo um 25%."},
[69810] = {name = "Schlachtstandarte der Himmelsbrecher beschwören", description = "[q]Beschwört 0 Sukkuben, die den Zaubernden bis zur Freigabe begleiten."},
