[70299] = {name = "Essenz entziehen", description = nil},
[70302] = {name = "Blendender Schmutz", description = "[q]Blendet das Ziel und lässt es bis zu 3 sec lang verwirrt umherlaufen. Jeglicher erlittene Schaden wird den Effekt aufheben."},
