[70309] = {name = "Verwundender Wurf", description = "[q]Wirft Eure Waffe nach dem Ziel. Verursacht 3010 to 3990 Schaden sowie zusätzlich 18 sec lang alle 3 Sek. 2000 körperlichen Schaden."},
[70316] = {name = "Spalten", description = "[q]Fügt einem Feind und seinen nächsten Verbündeten 110% des normalen Nahkampfschadens zu, wirkt auf bis zu 3 Ziele."},
[70337] = {name = "Nekrotische Seuche", description = "[q]Infiziert das Ziel mit einer tödlichen Seuche, die 15 sec lang alle 5 Sek. 150000 Schattenschaden verursacht. Endet der Effekt oder stirbt das Ziel, während es betroffen ist, wird dieser Effekt einen weiteren Stapel gewinnen und auf eine in der Nähe befindliche Einheit überspringen. Wird der Effekt gebannt, wird er einen Stapel verlieren und auf eine in der Nähe befindliche Einheit überspringen. Jedes Mal, wenn dieser Effekt weiterspringt, erhöht sich die Macht des Lichkönigs."},
[70340] = {name = "Furor des Löwen", description = "[q]Die Stärke des Löwen verstärkt Eure Angriffskraft."},
[70341] = {name = "Schleimpfütze", description = "[q]Wirft eine Phiole mit mutiertem Schleim, wodurch im Zielgebiet eine Schleimpfütze entsteht."},
[70343] = {name = "Schleimpfütze", description = "[q]Der mutierte Schleim wächst immer weiter und verursacht alle 1 Sek. 6825 to 7175 Schaden, bis er von einer anderen mutierten Kreatur verzehrt wird!"},
[70351] = {name = "Instabiles Experiment", description = "[q]Professor Seuchenmord beginnt an Euch allen ein tückisches Experiment!"},
[70358] = {name = "Ghulsklave beschwören", description = "[q]Beschwört im Verlauf von 3 sec 3 Ghulsklaven."},
[70360] = {name = "Schlamm essen", description = "[q]Frisst in der Nähe befindlichen mutierten Schleim, wodurch die Größe der Pfütze verringert wird und Ihr 4 Schlammenergie erhaltet!"},
[70361] = {name = "Spalten", description = "[q]Fügt einem Feind und seinen nächsten Verbündeten 120% Waffenschaden zu, wirkt auf bis zu 3 Ziele."},
[70362] = {name = "Blizzard", description = "[q]Ruft einen Blizzard herbei, der 8 sec lang anhält, Feinden im ausgewählten Gebiet alle 2 Sek. 8000 Frostschaden zufügt und ihr Bewegungstempo um 30% verringert."},
[70363] = {name = "Kannibalismus", description = "[q]Regeneriert Gesundheit. Hält 12 sec lang an."},
[70371] = {name = "Wutanfall", description = "[q]Verringert die Zeit zwischen Euren Angriffen 20 sec lang um 100%."},
[70372] = {name = "Torkelnden Schrecken beschwören", description = "[q]Beschwört einen torkelnden Schrecken."},
[70381] = {name = "Tieffrieren", description = "[q]Unterkühlt ein zufälliges Ziel, verringert damit sein Bewegungstempo um 50% und fügt ihm nach 14 Sek. erheblichen Frostschaden zu."},
[70386] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[70388] = {name = "Saat der Verderbnis", description = "[q]Pflanzt dem feindlichen Ziel eine Saat der Verderbnis ein, die im Verlauf von 18 sec 1500 Schattenschaden verursacht. Wenn das Ziel insgesamt 2500 Schaden erleidet, detoniert die Saat und fügt allen Feinden im Umkreis von 10 Metern um das Ziel Schattenschaden zu."},
[70391] = {name = "Fluch der Pein", description = "[q]Verflucht das Ziel mit Pein und fügt 24 sec lang insgesamt 7200 Schattenschaden zu. Zuerst wird der Schaden langsam zugefügt und nimmt dann zu, bis der Fluch seine Gesamtdauer erreicht hat. Es kann immer nur jeweils ein Fluch pro Hexenmeister auf einem beliebigen Ziel aktiv sein."},
[70392] = {name = "Schwarzes Brandmal", description = "[q]Fügt einem Feind 139 to 161 Schattenschaden zu und erhöht seinen erlittenen Schattenschaden 10 sec lang um 50%."},
[70394] = {name = "Krankheitswolke", description = "[q]Eine faulige Krankheitswolke umgibt den Zaubernden, die nahen Gegnern Naturschaden zufügt."},
[70396] = {name = "Ghoulschlitzen", description = "[q]Ein starker Angriff, der 3938 to 5062 zusätzlichen körperlichen Schaden verursacht."},
[70404] = {name = "Schatz' Schleife", description = "[q]Eine Schleife der Zuneigung."},
[70405] = {name = "Mutierte Transformation", description = "[q]Die Krankheiten der mutierten Monstrosität fügen in der Nähe befindlichen Spielern Schaden zu!"},
[70407] = {name = "Druckwelle", description = "[q]Entfesselt eine Flammenwelle, die nahen Feinden Feuerschaden zufügt und ihr Bewegungstempo 4 sec lang verringert."},
[70408] = {name = "Magie verstärken", description = "[q]Verstärkt gegen den Feind gerichtete Magie, wobei der von Zaubern zugefügte Schaden um bis zu 2400 und Heilzauber um bis zu 3600 erhöht werden. Hält 30 sec lang an."},
[70409] = {name = "Feuerball", description = "[q]Fügt dem Ziel 11100 to 12900 Feuerschaden zu."},
[70410] = {name = "Verwandlung: Spinne", description = "[q]Verwandelt einen Feind in eine Spinne und zwingt ihn, 12 sec lang ziellos herumzuwandern. Während die Spinne herumwandert, kann sie weder angreifen noch Zauber wirken. Jeglicher erlittene Schaden verwandelt das Ziel in seine normale Gestalt zurück. Es kann immer nur ein Ziel verwandelt werden. Wirkt nur bei Wildtieren, Drachen, Risen, Humanoiden und Tieren."},
[70421] = {name = "Blizzard", description = "[q]Ruft einen Blizzard herbei, der 10 sec lang anhält und Feinden im ausgewählten Gebiet alle 2 Sek. 43 Frostschaden zufügt."},
[70423] = {name = "Vampirfluch", description = "[q]Das Erhalten direkter Heilungen verursacht den Effekt 'Verdorbenes Blut'. Hält 20 sec lang an."},
[70425] = {name = "Kettenheilung", description = "[q]Erfüllt einen verwundeten Verbündeten mit Heilenergie, die sich auf einen anderen in der Nähe befindlichen Verbündeten ausbreitet. Der Zauber wirkt sich auf bis zu 3 Ziele aus."},
[70431] = {name = "Schattenschritt", description = "[q]Versucht, in die Schatten zu tauchen, um hinter dem Gegner zu erscheinen."},
[70432] = {name = "Blutentzug", description = "[q]Betäubt das Ziel und verursacht alle 2 Sek. 3250 Blutungsschaden."},
[70435] = {name = "Fleisch zerreißen", description = "[q]Fügt einem Feind 100% Waffenschaden zu und lässt ihn pro Anwendung alle 2 Sek. für 4375 to 5625 Schaden bluten. Hält 30 sec lang an."},
[70437] = {name = "Unheiliger Stoß", description = "[q]Ein brutaler Angriff, der den Feind sofort schlägt und ihm 150% Waffenschaden zufügt."},
[70447] = {name = "Flüchtiger Schlammkleber", description = "[q]Der flüchtige Schlammkleber bindet das Ziel an den flüchtigen Schlamm. Verursacht alle 1 Sek. 7313 to 7687 Schaden und macht das Ziel unbeweglich. Der flüchtige Schlamm wird sich auf das Ziel zubewegen, explodieren und 243750 to 256250 Schaden verursachen. Dieser Schaden wird unter allen nahen Zielen aufgeteilt."},
[70449] = {name = "Vampiransturm", description = "[q]Stürmt auf einen Gegner zu, fügt ihm sowie seinen nahen Verbündeten 5550 to 6450 Schaden zu und stößt alle betroffenen Ziele zurück."},
[70450] = {name = "Blutspiegel", description = "[q]0% des von einer verbundenen Quelle erlittenen Schadens wird in Form von Schattenschaden auf Euch reflektiert."},
[70460] = {name = "Eisflammenstrahl", description = "[q]Fügt jedem in Reichweite Frostschaden zu."},
[70464] = {name = "Eislanzensalve", description = "[q]Fügt Feinden im Umkreis von 80 Metern 13200 to 19800 Schaden zu. Fügt eingefrorenen Zielen dreifachen Schaden zu."},
[70475] = {name = "Riesiger Insektenschwarm", description = "[q]Ein massiver Insektenschwarm erfüllt den Bereich und verursacht alle 1 Sek. Schaden in Höhe von 5% der maximalen Gesundheit."},
[70480] = {name = "Geist freilassen", description = nil},
[70481] = {name = "Geist freilassen", description = nil},
[70482] = {name = "Geist freilassen", description = nil},
[70492] = {name = "Schlammeruption", description = "[q]Verursacht 243750 to 256250 Schaden, der mit in der Nähe befindlichen Zielen geteilt wird und sie in alle Richtungen wegstößt!"},
[70498] = {name = "Widerwärtige Geister", description = "[q]Erschafft 10 widerwärtige Geister, die nach 30 Sekunden Feinde angreifen."},
[70503] = {name = "Geistexplosion", description = "[q]Opfert das Leben des Zaubernden, um allen Feinden im Umkreis von 5 Metern 35100 to 36900 Schattenschaden zuzufügen."},
[70512] = {name = "Kreischen der Hochgeborenen", description = "[q]Kreischt vor Qualen und fügt allen Gegnern in einem kegelförmigen Bereich vor dem Zaubernden 7200 to 8800 Schattenschaden zu."},
[70513] = {name = "Mehrfachschuss", description = "[q]Feuert eine Salve von Geschossen auf einen Feind und seine nächsten Verbündeten ab und fügt bis zu 3 Zielen normalen Schaden plus 75 zu."},
[70516] = {name = "Pyroschlag", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[70517] = {name = "Totem des heilenden Flusses", description = "[q]Beschwört für 1 min ein Totem des heilenden Flusses mit 5 Gesundheit zu Füßen des Zaubernden, das Gruppenmitglieder im Umkreis von 20 Metern alle 2 Sek. um 28 heilt."},
[70521] = {name = "Frostbombe", description = "[q]Fügt allen in der Nähe befindlichen Zielen 0 Frostschaden zu."},
[70539] = {name = "Erbrochener Schlamm", description = "[q]Der erbrochene Schlamm hat eine überraschende Wirkung auf das Ziel, die sein Bewegungstempo um 50% verringert und ihm 20 sec lang alle 2 Sek. 9263 to 9737 Schaden zufügt."},
[70541] = {name = "Befallen", description = "[q]Fügt allen feindlichen Spielern im Umkreis von 60 Metern 12723 to 14276 Schattenschaden zu. Zusätzlich erleiden die Ziele pro Sekunde ansteigenden Schattenschaden. Dieser Effekt wird entfernt, wenn das Ziel über mehr als 90% Gesundheit verfügt."},
[70542] = {name = "Mutiertes Schlitzen", description = "[q]Verursacht sofort 100% Waffenschaden und verringert den Widerstand des Ziels gegen körperlichen Schaden 20 sec lang um 4%. Bis zu 5-mal stapelbar."},
[70588] = {name = "Unterdrückung", description = "[q]Verringert die vom Ziel erhaltene Heilung until cancelled lang um 10%."},
[70589] = {name = "Eisige Berührung", description = "[q]Verursacht 0 Frostschaden und verringert until cancelled lang das Angriffs-, Distanzangriffs- und Zaubertempo des Ziels um 35%."},
[70594] = {name = "Todeskälteblitz", description = "[q]Fügt einem feindlichen Ziel 11563 to 13437 Schattenfrostschaden zu."},
[70602] = {name = "Verderbnis", description = "[q]Lässt allmählich Verderbnis in die Gedanken des Ziels einsickern, um es langsam dem Willen des Lichkönigs zu unterwerfen."},
[70633] = {name = "Magensäure", description = "[q]Verursacht pro Sekunde 3750 Naturschaden und erhöht den erlittenen körperlichen Schaden 12 sec lang um 25%."},
[70645] = {name = "Ketten des Schattens", description = "[q]Verbannt einen Feind und verhindert damit jegliche Aktion, macht es aber bis zu 10 sec lang unverwundbar. Es kann immer nur jeweils ein Ziel verbannt werden."},
[70659] = {name = "Nekrotischer Stoß", description = "[q]Greift einen Feind mit einer verfluchten Klinge an, die ihm 70% Waffenschaden zufügt und eine anhaltende Krankheit hervorruft. Diese negiert die nächsten 20000 Heilung die das Ziel erhält."},
[70670] = {name = "Schattenspalten", description = "[q]Fügt Feinden vor dem Angreifer 19000 to 21000 Schattenschaden zu."},
[70671] = {name = "Blutsaugendes Verrotten", description = "[q]Belegt das Ziel mit 'Eitriges Verrotten', verursacht alle 3 Sek. 4242 to 4758 Naturschaden und verringert seine erhaltene Heilung um 25%. Hält 9 sec lang an."},
[70674] = {name = "Vampirmacht", description = "[q]Erfüllt den Zaubernden mit dunkler Macht, die den verursachten Schaden um 25% erhöht und den Zaubernden um 300% seines verursachten Schadens heilt."},
[70705] = {name = "Banner der Brennenden Klinge", description = "[q]Die Standarte eines Klingenmeisters."},
[70714] = {name = "Eisige Rüstung", description = "[q]Hüllt den Zaubernden in einen schützenden Eisblock."},
[70716] = {name = "Zerkrachen", description = nil},
[70732] = {name = "Glevensperrfeuer", description = "[q]Feuert eine Salve aus Gleven auf das Ziel ab, fügt Feinden im Gebiet körperlichen Schaden zu und stößt sie zurück."},
[70735] = {name = "Gleve schleudern", description = "[q]Feuert eine große Gleve auf das Zielgebiet, fügt Feinden körperlichen Schaden zu und stößt sie zurück."},
[70744] = {name = "Ätzschlag", description = "[q]Der Zaubernde explodiert und fügt allen Feinden im Umkreis von 15 Metern sofort 17672 to 19828 Naturschaden und pro Sekunde 1250 Naturschaden zu. Hält 20 sec lang an."},
[70746] = {name = "Teleport in den Sonnenbrunnen", description = nil},
[70750] = {name = "Schlachtruf", description = "[q]Erhöht 30 sec lang den Schaden naher Gruppenmitglieder um 50% und ihr Angriffstempo um 20%."},
[70751] = {name = "Korrosion", description = "[q]Jedes Mal, wenn der Zaubernde einen Nahkampftreffer landet, erleidet das Ziel alle 3 Sek. 3125 Naturschaden und seine Rüstung wird um 10% verringert. Dieser Effekt ist bis zu 5-mal stapelbar. Hält 6 sec lang an."},
[70754] = {name = "Feuerball", description = "[q]Fügt einem Gegner 5891 to 6609 Feuerschaden zu."},
[70759] = {name = "Frostblitzsalve", description = "[q]Fügt Feinden 4713 to 5287 Frostschaden zu und verringert 8 sec lang ihr Bewegungstempo um 50%."},
[70768] = {name = "Umhüllung des Okkulten", description = "[q]Umhüllt den Zaubernden mit einer mächtigen Barriere, die die gesamte schädliche Magie abwehrt, Zauberunterbrechung verhindert und bis zu 50000 Schaden absorbiert, bevor sie zerbricht."},
[70782] = {name = "Ghulberührung", description = "[q]Verringert das Bewegungstempo des Ziels um 20%. Bis zu 4-mal stapelbar."},
[70794] = {name = "Freezing Trap Effect", description = "[q]Eine Eiskältefalle platzieren, die den ersten sich nähernden Feind einfriert und 1 min lang jegliche Aktion des Feindes verhindert. Jeglicher erlittene Schaden wird das Eis wieder brechen. Die Falle wird 1 min lang bestehen bleiben. Es kann immer nur eine Falle aktiv sein."},
[70796] = {name = "Gezielter Schuss", description = "[q]Ein gezielter Schuss, der hohen Schaden verursacht."},
[70842] = {name = "Manabarriere", description = "[q]Umhüllt den Zaubernden mit einer mächtigen Barriere, die kontinuierlich jede verlorene Gesundheit auf Kosten des Manas des Zaubernden wieder auffrischt."},
[70853] = {name = "Formbarer Glibber", description = "[q]Verursacht in einem Radius von 5 Metern 23400 to 24600 Schaden und verringert 15 sec lang das Zauber- und Angriffstempo um 250%."},
[70864] = {name = "Vegards Rache", description = nil},
[70866] = {name = "Schattenschlag", description = "[q]Fügt allen Feinden im Umkreis von 5 Metern 4500 to 5500 Schattenschaden zu, verringert ihr Bewegungstempo um 70% und stößt sie weg."},
[70867] = {name = "Essenz der Blutkönigin", description = "[q]Ihr seid mit dem Blut der Vampyrkönigin erfüllt. Der verursachte Schaden ist um 100% erhöht.\n Angriffe heilen den Zaubernden."},
[70873] = {name = "Smaragdgrüne Erquickung", description = "[q]Erquickt das Ziel mit Träumen, wodurch es alle 3 Sek. 200 Mana regeneriert. Der Schaden und die Heilung des Ziels wird ebenfalls um 10% erhöht. Bis zu 100-mal stapelbar. Hält 40 sec lang an."},
[70886] = {name = "Vegards Durst", description = "[q]Entzieht einem Feind im Verlauf von 4 sec insgesamt 7400 to 8600 Gesundheit und überträgt sie auf den Zaubernden."},
[70900] = {name = "Dunkle Transformation", description = "[q]Verwandelt das Ziel in ein untotes Ungetüm, was Größe und verursachten Schaden erheblich erhöht."},
[70901] = {name = "Dunkle Machterfüllung", description = "[q]Erfüllt die Zauber des Ziels mit Macht, sodass sie Flächenschaden verursachen und nicht unterbrochen werden können."},
[70903] = {name = "Dunkles Martyrium", description = "[q]Der Zaubernde unterwirft sich dunklen Energien und explodiert, wobei allen Feinden in einem Umkreis von 15 Metern 25000 Seuchenschaden zugefügt wird. Nur Knochenreste bleiben übrig."},
[70904] = {name = "Verderbnis", description = "Betäubt.\nWird entfernt, wenn vollständig geheilt."},
[70906] = {name = "Todeskälteschlag", description = "[q]Fügt allen Feinden im Umkreis von 10 Metern um das Ziel 14800 to 17200 Schattenfrostschaden zu."},
[70910] = {name = "Harte Landung", description = "[q]Kracht in den Dreck. Volles Rohr."},
[70911] = {name = "Entfesselte Seuche", description = "[q]Verursacht alle 1 Sek. Schaden. Der Schaden wird mit jeder Sekunde, die die Seuche auf Euch lastet, erhöht! Kommt Ihr einem befreundeten Ziel zu nahe, wird die Seuche von Euch auf das Ziel übertragen."},
[70923] = {name = "Unkontrollierbare Raserei", description = "[q]Bezaubert.\n Erhöht den verursachten Schaden um 9999%.\n Gesundheit um 900% erhöht.\n Erhöht die erhaltene Heilung um 1000%."},
[70926] = {name = "Forcecast Ride Named Raptor", description = nil},
[70952] = {name = "Herbeirufung des Blutes", description = "[q]Die Kugel der Sinistren erfüllt Valanar mit Macht und gewährt ihm mächtigere Fähigkeiten."},
[70960] = {name = "Knochenwirbel", description = "[q]Erfüllt die Angriffe des Zaubernden mit Zorn und erhöht das Angriffstempo um 100%."},
[70961] = {name = "Zerschmetterte Knochen", description = "[q]Fügt in der Nähe befindlichen Feinden beim Tod 10000 Schaden zu."},
[70964] = {name = "Schildhieb", description = "[q]Schlägt mit Eurem Schild auf das Ziel ein, fügt ihm 8000 Schaden zu und erhöht seine Verwundbarkeit gegen magische Angriffe um 25%."},
[70965] = {name = "Gruftskarabäen", description = "[q]Beschwört mehrere Gruftskarabäen, die das Ziel des Zaubernden angreifen."},
[70980] = {name = "Fangnetz", description = "[q]Kanalisiert einen Netzstrahl, der das Ziel 3 sec lang bewegungsunfähig macht. Wenn der Effekt endet, ist das Ziel in Spinnennetze eingewoben, die es 10 sec lang betäuben. Wenn die Spinnennetze um das Ziel entfernt werden, wird das Ziel befreit."},
[70986] = {name = "Umhüllung der Trauer", description = "[q]Vom Zaubernden geht eine Aura des Kummers und der Verzweiflung aus, die in der Nähe befindlichen Feinden alle 2 Sek. 4500 Schattenschaden zufügt."},
[71001] = {name = "Tod und Verfall", description = "[q]Fügt allen Zielen im betroffenen Gebiet 10 sec lang alle 1 Sek. 6000 Schattenschaden zu."},
[71003] = {name = "Vegards Berührung", description = "[q]Verursacht alle 2 Sek. 450 to 550 Schaden."},
[71010] = {name = "Fangnetz", description = "[q]Kanalisiert einen Netzstrahl, der das Ziel 3 sec lang bewegungsunfähig macht. Wenn der Effekt endet, ist das Ziel in Spinnennetze eingewoben, die es 10 sec lang betäuben. Wenn die Spinnennetze um das Ziel entfernt werden, wird das Ziel befreit."},
[71019] = {name = "Kriegsdonner", description = "[q]Fügt in der Nähe befindlichen Feinden normalen Schaden plus 0 zu und betäubt sie 5 sec lang."},
[71020] = {name = "Dunkle Besserung", description = "[q]Heilt einen Verbündeten mit dunkler Magie."},
[71021] = {name = "Säbelhieb", description = "[q]Teilt 200% des normalen Nahkampfschadens zwischen einem Gegner und seinen zwei nächsten Verbündeten auf."},
[71022] = {name = "Unterbrechender Schrei", description = "[q]Unterbricht das Zaubern aller in der Nähe befindlichen Feinde."},
[71029] = {name = "Gletscherexplosion", description = "[q]Feuert eine massive Gletscherexplosion ab, die alle Feinde vor dem Zaubernden trifft."},
[71088] = {name = "Seuchenbombe", description = "[q]Opfert das Leben des Zaubernden, um in der Nähe befindlichen Feinden 28125 to 31875 Feuerschaden zuzufügen."},
[71089] = {name = "Sprudelnder Eiter", description = "[q]Fügt einem beliebigen in der Nähe befindlichen Feind Naturschaden zu. Hält 10 sec lang an."},
[71103] = {name = "Mutationsauslösendes Spray", description = "[q]Injiziert dem Ziel einen transmogrifizierenden Schub Strahlung."},
[71114] = {name = "Massives Stampfen", description = "[q]Stampft auf den Boden, verursacht bei allen nahen Feinden 10800 to 13200 körperlichen Schaden und stößt sie zurück."},
[71123] = {name = "Dezimieren", description = "[q]Verringert die Gesundheit aller nahen Einheiten auf 15% ihrer maximalen Gesundheit."},
[71127] = {name = "Tödliche Wunde", description = "[q]Ein kräftiger Schlag trifft das Ziel und verringert die Wirksamkeit von Heileffekten um 10%."},
[71140] = {name = "Geißelhaken", description = "[q]Verursacht 4950 to 6050 körperlichen Schaden und zieht das Ziel zum Zaubernden."},
[71150] = {name = "Seuchenwolke", description = "[q]Stößt eine Seuchenwolke auf den Boden aus, die 20 sec lang alle 3 Sek. 7000 Naturschaden verursacht."},
[71157] = {name = "Infizierte Wunde", description = "[q]Erhöht den erlittenen körperlichen Schaden eines Feindes 1 min lang um 100."},
[71163] = {name = "Humanoiden verschlingen", description = "[q]Verursacht alle 1.5 Sek. 15% Schaden."},
[71164] = {name = "Eingesprungener Gesichtszermalmer", description = "[q]Sprungangriff ins Gesicht. Äußerst schmerzhaft!"},
[71179] = {name = "Manaleere", description = "[q]Beschwört an der Position eines Gegners eine Manaleere. Die Manaleere verbrennt pro Sekunde 1250 Mana aller Gegner im Umkreis von 6 Metern. Hält 30 Sek. lang an."},
[71188] = {name = "Erfahren", description = "[q]Verursachter Schaden um 30% erhöht.\n Angriffs- und Zaubertempo um 20% erhöht."},
[71189] = {name = "Traumwandlers Zorn", description = "[q]Fügt allen umgebenden Feinden 10000000 Naturschaden zu."},
[71193] = {name = "Veteran", description = "[q]Verursachter Schaden um 60% erhöht.\n Angriffs- und Zaubertempo um 40% erhöht."},
[71195] = {name = "Elite", description = "[q]Verursachter Schaden um 120% erhöht.\n Angriffs- und Zaubertempo um 80% erhöht."},
[71196] = {name = "Traumriss", description = "[q]Teleportiert den Zaubernden in den smaragdgrünen Traum."},
[71203] = {name = "Seelenspeise", description = nil},
[71204] = {name = "Berührung der Unbedeutsamkeit", description = "[q]Verringert die verursachte Bedrohung des Ziels um 20%."},
[71236] = {name = "Dunkles Martyrium", description = "[q]Der Zaubernde unterwirft sich dunklen Energien und explodiert, wobei allen Feinden in einem Umkreis von 15 Metern 25000 Seuchenschaden zugefügt wird. Nur Knochenreste bleiben übrig."},
[71237] = {name = "Fluch der Starre", description = "[q]Infiziert ein feindliches Ziel mit einem Fluch, der die Abklingzeit der Fähigkeiten um 15 Sek. erhöht."},
[71249] = {name = "Frostfalle", description = "[q]Eine Eiskältefalle platzieren, die den ersten sich nähernden Feind einfriert und 5 sec lang jegliche Aktion des Feindes verhindert. Jeglicher erlittene Schaden wird das Eis wieder brechen. Die Falle bleibt 30 sec lang bestehen. Es kann immer nur jeweils eine Falle aktiv sein."},
[71251] = {name = "Flinkschuss", description = "[q]Lässt den Zaubernden sich 8 sec lang fokussieren und in schneller Folge auf nahe Gegner schießen."},
[71252] = {name = "Salve", description = "[q]Schießt eine Pfeilsalve auf ein Zielgebiet."},
[71253] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[71254] = {name = "Schattenblitz", description = "[q]Verursacht 11375 to 14625 Schattenschaden bei einem Feind."},
[71255] = {name = "Erstickende Gasbombe", description = "[q]Professor Seuchenmord wirft mit Gasbomben um sich. Eine Gasbombe verursacht alle 1 Sek. 6825 to 7175 Schaden und explodiert nach Ablauf von 20 sec, wodurch zusätzlich 29250 to 30750 Schaden verursacht wird."},
[71257] = {name = "Barbarischer Stoß", description = "[q]Ein brutaler Schlag, der das Ziel bluten lässt und ihm 4 sec lang zusätzlichen Schaden zufügt."},
[71258] = {name = "Adrenalinrausch", description = "[q]Erfüllt die Angriffe des Zaubernden mit Zorn und erhöht das Angriffstempo um 100%."},
[71270] = {name = "Arktische Kühle", description = "[q]Der Zaubernde kanalisiert die eisige Kälte, wodurch er pro Sekunde Frostschaden verursacht."},
[71274] = {name = "Gefrorene Kugel", description = "[q]Der Zaubernde kanalisiert eine massive gefrorene Kugel, die auf einen in der Nähe befindlichen Feind zielt."},
[71289] = {name = "Gedankenkontrolle", description = "[q]Unterwirft den Willen des anvisierten Feindes, wodurch er 12 sec lang bezaubert wird. In dieser Zeit wird der von ihnen verursachte Schaden um 200% und ihre Heilung um 500% erhöht."},
[71296] = {name = "Schattenblitz", description = "[q]Fügt dem Ziel 12000 Schattenschaden zu."},
[71298] = {name = "Verbannen", description = "[q]Verbannt das feindliche Ziel und verhindert damit jegliche Aktion, macht es aber bis zu 6 sec lang unverwundbar. Es kann immer nur jeweils ein Ziel verbannt werden."},
[71299] = {name = "Umarmung des Todes", description = "[q]Ein Schild aus Schattenmacht umgibt den Zaubernden und macht ihn gegen Unterbrechungen immun. Die Barriere hält 10 sec lang und absorbiert 250000 Schaden."},
[71303] = {name = "Ymirjar beschwören", description = "[q]Beschwört 71302 Zombies, um den Zaubernden zu unterstützen."},
[71306] = {name = "Verdrehte Winde", description = "[q]Tauscht die Plätze und Bedrohungslisten mit dem Ziel."},
[71316] = {name = "Gletscherstoß", description = "[q]Ein hinterhältiger, eiskalter Angriff, der beim Ziel bis zur vollständigen Heilung starkes Bluten hervorruft. Verursacht bis zur vollständigen Heilung alle 3 Sek. 20% Frostschaden."},
[71318] = {name = "Frostblitz", description = "[q]Fügt einem Feind Frostschaden zu und verringert 6 sec lang sein Bewegungstempo."},
[71320] = {name = "Frostnova", description = "[q]Verursacht 4625 to 5375 Frostschaden bei in der Nähe befindlichen Feinden und macht sie 4 sec lang bewegungsunfähig."},
[71323] = {name = "Frostklinge", description = "[q]Greift in der Nähe befindliche Feinde mit einem Wirbel aus Stahl an, der 6 sec. lang anhält und doppelten Schaden verursacht."},
[71325] = {name = "Frostklinge", description = "[q]Erfüllt Eure Waffe mit der Macht der Eiskrone, wodurch sie 14 sec lang Frostschaden ausstößt."},
[71326] = {name = "Gruftskarabäen", description = "[q]Beschwört mehrere Gruftskarabäen, die das Ziel des Zaubernden angreifen."},
[71327] = {name = "Gespinst", description = "[q]Zaubert ein Netz über einen Feind und macht ihn so 5 sec lang bewegungsunfähig."},
[71330] = {name = "Eisgrab", description = "[q]Betäubt einen Feind, sodass er sich 5 sec lang weder bewegen noch angreifen kann."},
[71335] = {name = "Brennendes Pech", description = "[q]Wirft einen Klumpen Brennendes Pech, der das Zielgebiet in Brand setzt und 3 sec lang 17250 Belagerungsschaden und zusätzlich 7500 Feuerschaden verursacht."},
[71336] = {name = "Pakt der Sinistren", description = "[q]Fügt Euch und in der Nähe befindlichen unverbundenen Verbündeten wachsenden Schattenschaden zu. Dieser Effekt hält an, bis sich alle verbundenen Ziele in einem Umkreis von 5 Metern voneinander befinden."},
[71337] = {name = "Erschütternder Schock", description = "[q]Springt in die Luft und stößt in der Nähe befindliche Feinde weg."},
[71339] = {name = "Brennendes Pech", description = "[q]Wirft einen Klumpen Brennendes Pech, der das Zielgebiet in Brand setzt und 3 sec lang 17250 Belagerungsschaden und zusätzlich 7500 Feuerschaden verursacht."},
[71350] = {name = "Feuer konzentrieren", description = "[q]Der Welpe fokussiert seine Angriffe auf das Ziel."},
[71357] = {name = "Welpen befehligen", description = "[q]Veranlasst den Tierführer der Frostwächter, einen Welpen zum Angriff eines bestimmten Feindes zu zwingen."},
[71361] = {name = "Frostschlag", description = "[q]Fügt einem Feind Frostschaden zu und verringert 4 sec lang sein Bewegungstempo."},
[71369] = {name = "Schwanzfeger", description = "[q]Fügt Feinden in einem kegelförmigen Bereich hinter dem Zaubernden 5250 to 8750 Schaden zu und stößt sie zurück."},
[71376] = {name = "Eisige Luft", description = "[q]Fügt Feinden in der Nähe des Einschlagsortes 7875 to 10125 Frostschaden zu."},
[71380] = {name = "Eisige Luft", description = "[q]Verringert das Bewegungstempo um 75% und fügt allen, die vom nachwirkenden eisigen Hauch getroffen werden, alle 2 Sek. 7400 to 8600 Frostschaden zu."},
[71386] = {name = "Frostatem", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Frostschaden zu und verlangsamt ihr Angriffstempo 4 sec lang um 75%."},
[71387] = {name = "Frostaura", description = "[q]Verursacht alle 2 Sek. 1200 Frostschaden."},
[71393] = {name = "Flammen", description = "[q]Explodiert heftig und fügt nahen Feinden mindestens 12000 Feuerschaden zu. Größere Flammenkugeln verursachen basierend auf ihrer Größe zusätzlichen Schaden."},
[71400] = {name = "Verschwinden", description = "[q]Teleportiert den Zaubernden hinter das Ziel. Befreit den Zaubernden auch von jeglichen Fesseln."},
[71405] = {name = "Schattenlanze", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht 19013 to 19987 Schattenschaden."},
[71410] = {name = "Meucheln", description = "[q]Fügt einem Feind normalen Schaden plus 40 zu, aber nur, wenn von hinten angegriffen wird."},
[71420] = {name = "Frostblitz", description = "[q]Fügt einem Feind 58500 to 61500 Frostschaden zu und verringert 4 sec lang sein Bewegungstempo um 50%."},
[71463] = {name = "Ätherschild", description = "[q]Schützt den Zaubernden vor den meisten Angriffen und Zaubern. Zusätzlich regeneriert der Zaubernde Gesundheit."},
[71465] = {name = "Heilige Woge", description = "[q]Pulsierender Zauberlichtschaden fügt allen Gegnern alle 2 Sek. 1500 Schaden zu."},
[71468] = {name = "Ätherschlag", description = "[q]Verursacht 15000 Zauberlichtschaden."},
[71488] = {name = "Geißelstoß", description = "[q]Ein unheiliger Stoß, der 200% Waffenschaden in Form von Schattenschaden verursacht."},
[71489] = {name = "Todesstoß", description = "[q]Ein tödlicher Angriff, der 75% Waffenschaden plus 84 verursacht und den Todesritter um 20% seiner maximalen Gesundheit heilt."},
[71490] = {name = "Todesmantel", description = "[q]Fügt einem beliebigen in der Nähe befindlichen Ziel 5000 Schattenschaden zu."},
[71507] = {name = "Schwer parfümiert", description = "[q]Holla! Der Geruch ist überwältigend..."},
[71516] = {name = "Schattenmacht", description = "[q]Entzieht bösartigen Schlamm, um Schattenschneide mit unheiliger Macht zu erfüllen."},
[71521] = {name = "Hand von Gul'dan", description = "[q]Beschwört einen Meteor auf das gegnerische Ziel herab, der 1793 Schattenflammenschaden verursacht und in einem Umkreis von 4 Metern eine magische Aura hervorruft, welche die kritische Trefferchance der Fähigkeiten von Dämonendienern von Hexenmeistern gegen alle Ziele innerhalb dieser Aura um 10% erhöht. Die Aura hält 15 sec lang an."},
[71546] = {name = "Heilige Pein", description = "[q]Zerschmettert einen Feind und fügt ihm 7200 to 8800 Heiligschaden zu."},
[71548] = {name = "Machtwort: Schild", description = "[q]Nutzt die Seelenkraft des Gruppenmitglieds zum Abschirmen und absorbiert dabei 14550 Schaden. Hält 30 sec lang an. Solange der Schild hält, wird das Zauberwirken durch erlittenen Schaden nicht unterbrochen. Nachdem das Ziel einmal abgeschirmt wurde, kann es 15 sec lang nicht wieder abgeschirmt werden."},
[71549] = {name = "Kreuzfahrerstoß", description = "[q]Ein sofortiger Schlag, der 110% Waffenschaden verursacht."},
[71550] = {name = "Gottesschild", description = "[q]Schützt den Paladin 12 sec lang vor allen Schäden und Zaubern, verringert jedoch seinen verursachten Schaden um 50%. Einmal geschützt, kann der Paladin 1 min lang nicht erneut durch 'Gottesschild', 'Göttlicher Schutz' oder 'Hand des Schutzes' geschützt werden. Kann innerhalb von 12 sec nach 'Zornige Vergeltung' nicht genutzt werden."},
[71551] = {name = "Richturteil des Befehls", description = "[q]Richtet den Gegner für 3325 to 3675 Heiligschaden."},
[71552] = {name = "Tödlicher Stoß", description = "[q]Ein heimtückischer Stoß, der Waffenschaden plus 380 verursacht und das Ziel verwundet, sodass die Wirksamkeit jeglicher Heilung 10 sec lang um 50% verringert wird."},
[71553] = {name = "Sturmangriff", description = "[q]Stürmt auf einen Gegner zu und betäubt ihn 1.50 sec lang."},
[71554] = {name = "Rüstung zerreißen", description = "[q]Zerreißt die Rüstung des Ziels, verringert sie pro Einsatz von 'Rüstung zerreißen' um 4% und verursacht ein hohes Maß an Bedrohung. Die Bedrohung wird durch Angriffskraft erhöht. Kann bis zu 5-mal angewendet werden. Hält 30 sec lang an."},
[71590] = {name = "Raketenstart", description = "[q]Feuert eine mächtige Rakete auf den Gegner, die 960 to 1440 Schaden verursacht und ihn 3 Sek. lang betäubt. Das Ding hat aber einen ordentlichen Rückstoß..."},
[71592] = {name = "Teufelseisenbombe", description = "[q]Fügt 330 to 770 Feuerschaden zu und betäubt Ziele 2 sec lang in einem Radius von 5 Metern. Jeglicher Schaden wird den Effekt abbrechen. Unzuverlässig gegen Ziele über Stufe 76."},
[71594] = {name = "Maschinengewehr", description = "[q]Feuert das Maschinengewehr auf den Feind ab. Das Maschinengwehr wird den Feind 6 sec lang weiter verfolgen, selbst wenn er sich bewegt."},
[71595] = {name = "Blitzheilung", description = "[q]Heilt ein befreundetes Ziel um 8325 to 9675."},
[71603] = {name = "Mutierte Stärke", description = "[q]Erhöht den verursachten Schaden um 50% und das Angriffstempo um 50%. Außerdem führen Angriffe dazu, dass der Zaubernde weiter mutiert, was alle 3 Sek. zunehmenden Schaden verursacht."},
[71612] = {name = "Summon Vision of Charlga", description = nil},
[71614] = {name = "Eisiges Schloss", description = "[q]Hüllt das Ziel in Eis ein, wodurch es nicht mehr agieren kann aber immun gegen alle Angriffe und Zauber wird."},
[71615] = {name = "Tränengas", description = "[q]Ihr seid vom Tränengas benebelt und könnt Euch nicht bewegen!"},
[71621] = {name = "Gebräu herstellen", description = "[q]Professor Seuchenmord durchsucht den Labortisch nach einem mächtigen Trank!"},
[71623] = {name = "Heißblütiges Schlitzen", description = "[q]Fügt einem Feind 75% Waffenschaden zu und lässt ihn pro Anwendung alle 3 Sek. für 7000 to 9000 Schaden bluten. Hält 15 sec lang an."},
[71656] = {name = "Talked to Mankrik", description = nil},
[71657] = {name = "Tödlicher Eibeschuss", description = "[q]Wirft einen tödlichen Schauer aus explosiven Eiern auf die Feinde des Mechahuhns und fügt ihnen 14 to 21 Feuerschaden zu. Trifft alle Feinde im Umkreis von 5 Metern um das Hauptziel des Mechahuhns."},
[71658] = {name = "Ei-senhartes Bombardement", description = "[q]Wirft einen tödlichen Schauer aus explosiven Eiern auf den Hammer und fügt ihm 285 to 365 Feuerschaden zu. Waffensystem vom Mechahuhn zur Verfügung gestellt."},
[71659] = {name = "Verfluchter Laserstrahl", description = "[q]Fügt dem Hammer 190 to 210 Feuerschaden zu. Jeder Hai sollte einen gottverdammten Laser haben!"},
[71660] = {name = "Reparieren", description = "[q]Repariert den Dampfbetriebenen Mechahai-X um 200. Das werdet Ihr ganz dringend benötigen!"},
[71701] = {name = "Nightmare Mass Aura Cancel", description = nil},
[71705] = {name = "Kriegsfalken beschwören", description = "[q]Beschwört einen Falken als Begleitung für den Zaubernden bis zur Freigabe."},
[71711] = {name = "Summon Gray's Abomination", description = nil},
[71718] = {name = "Flamme herbeizaubern", description = "[q]Beschwört einen Flammenball, der auf das Ziel zuschwebt und beim Aufprall explodiert."},
[71736] = {name = "Vampiraura", description = "[q]Nahkampfangriffe heilen den Angreifer um 9000 to 11000."},
[71769] = {name = "Totenerweckung", description = "[q]Erweckt die Toten wieder zum Leben, um als untote Sklaven des Lichkönigs zu dienen."},
[71772] = {name = "Blutgeschosswirbel", description = "[q]Beschwört einen Mahlstrom aus wirbelnden Blutgeschossen."},
[71774] = {name = "Zerkrachen", description = nil},
[71784] = {name = "Hammer des Verrats", description = "[q]Betäubt das Ziel 6 sec lang."},
[71785] = {name = "Großbrand", description = "[q]Setzt einen Feind in Brand, fügt ihm im Verlauf von 10 sec insgesamt 3000 Feuerschaden zu und versetzt ihn in Panik. Solange das Ziel betroffen ist, versengen die Flammen in regelmäßigen Abständen auch seine in der Nähe befindlichen Verbündeten und fügen ihnen 150 Schaden zu."},
[71792] = {name = "Gift spucken", description = "[q]Fügt einem Gegner 47 to 53 Naturschaden sowie 15 sec lang zusätzlich alle 3 Sek. 20 Naturschaden zu."},
[71801] = {name = "Rausch", description = "[q]Erhöht den Schaden des nächsten Nahkampfangriffs des Zaubernden um 50%."},
[71805] = {name = "Seuchengestank", description = "[q]Der faulige Geruch fügt allen in der Nähe befindlichen Feinden 1500 Naturschaden zu."},
[71807] = {name = "Gleißende Funken", description = "[q]Aus den Händen des Zaubernden schießen kegelförmig gleißende Funken, die Feinde mit 16720 to 18480 Feuerschaden 8 sec lang verbrennen und ihr Bewegungstempo um 40% verringern."},
[71812] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht 13 to 16 Schattenschaden."},
[71815] = {name = "Machterfüllte Schattenlanze", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht 95063 to 99937 Schattenschaden."},
[71822] = {name = "Schattenresonanz", description = "[q]Das nächste Ziel hallt mit Schattenenergie wider, wodurch es 1000 Schaden erleidet und sein erlittener Schattenschaden aus allen Quellen um 35% verringert wird. Hält until cancelled lang an."},
[71841] = {name = "Heilige Pein", description = "[q]Zerschmettert einen Feind, fügt ihm 1591 to 1785 Heiligschaden zu und heilt den Zaubernden um 25% seiner maximalen Gesundheit."},
[71842] = {name = "Heilige Pein", description = "[q]Zerschmettert einen Feind, fügt ihm 1804 to 2022 Heiligschaden zu und heilt den Zaubernden um 25% seiner maximalen Gesundheit."},
[71861] = {name = "Schwärmende Schatten", description = "[q]Ein schwärmendes Nichts verzehrt das Ziel, wodurch unter dem Ziel alle 0.5 Sek. eine Schattenmasse erscheint."},
[71893] = {name = "Tränke schlucken", description = "[q]Professor Seuchenmord beginnt, jeden einzelnen Trank auf dem Tisch zu trinken!"},
[71906] = {name = "Abgetrennte Essenz", description = "[q]Reißt dem Ziel die Seele aus dem Körper. Die dunkle Seite der Seele bildet ein geisterhaftes Wesen, das dem Willen des Zaubernden unterworfen ist. Hält until cancelled lang an."},
[71921] = {name = "Wilder Sprung", description = nil},
[71923] = {name = "Blutseuche", description = "[q]Eine Krankheit, die 12 sec lang Schattenschaden verursacht."},
[71924] = {name = "Seuchenstoß", description = "[q]Ein bösartiger Schlag, der 60% Waffenschaden plus 27 verursacht und das Ziel mit Blutseuche infiziert, einer Krankheit die im Verlauf der Zeit Schattenschaden verursacht. Der Schlag entfernt einen Effekt, der Heilung über Zeit hervorruft."},
[71925] = {name = "Zerfleischen", description = "[q]Zerfleischt das Ziel, verursacht 100% des normalen Schadens plus 25 und lässt es 12 sec lang 30% zusätzlichen Schaden durch 'Zermalmen', 'Schreddern' und Blutungseffekte erleiden. Gewährt 1 Combopunkt."},
[71926] = {name = "Zerfetzen", description = "[q]Finishing-Move, der Schaden über Zeit verursacht."},
[71927] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[71928] = {name = "Feuerball", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[71930] = {name = "Lichtblitz", description = "[q]Ruft heilige Magie herbei, um einen Verbündeten zu heilen."},
[71931] = {name = "Große Heilung", description = "[q]Ruft heilige Magie herbei, um einen Verbündeten zu heilen."},
[71932] = {name = "Erneuerung", description = "[q]Heilt das Ziel im Verlauf von 15 sec um 23565 to 26435."},
[71933] = {name = "Ausweiden", description = "[q]Finishing-Move, der Schaden verursacht."},
[71934] = {name = "Blitzschlag", description = "[q]Schleudert einen Blitz auf das Ziel und fügt ihm 2334 to 2666 Naturschaden zu."},
[71936] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[71937] = {name = "Verderbnis", description = "[q]Verderbt in der Nähe befindliche Feinde und fügt ihnen so 24 sec lang alle 3 Sek. Schattenschaden zu."},
[71940] = {name = "Schreckliche Alpträume", description = "[q]Lässt das Ziel Alpträume erleiden, die pro Sekunde 200 Naturschaden verursachen. Zusätzlich regeneriert das Ziel alle 3 Sek. 200 Mana. Der Schaden und die Heilung des Ziels werden ebenfalls um 10% erhöht. Bis zu 100-mal stapelbar. Hält 40 sec lang an."},
[71943] = {name = "Schattenresonanz", description = "[q]Beschwört einen dunklen Kern. Dunkle Kerne nähren sich von ihrer eignen Energie, wodurch sie sich nach gewisser Zeit selbst zerstören."},
[71951] = {name = "Nekrotischer Stoß", description = "[q]Trifft den Feind mit einer verfluchten Klinge, fügt dem Ziel 4625 to 5375 körperlichen Schaden und eine andauernde Krankheit zu, die die nächste erhaltene Heilung in Höhe von 15000 negiert."},
[71953] = {name = "Leuchtende Aura", description = "[q]Der Paladin erglänzt in strahlendem Licht und heilt nahe Verbündete alle 3 Sek. um 3% ihrer maximalen Gesundheit."},
[71955] = {name = "Fokussierte Angriffe", description = "[q]Springt 30 sec lang auf den Rücken des Ziels, was die Angriffsgeschwindigkeit erhöht, es aber unmöglich macht, ein anderes Ziel anzugreifen."},
[71956] = {name = "Erfrischender Regen", description = "[q]Regeneriert 10 sec lang alle nahen Gruppenmitglieder alle 2 Sek. um 13875 to 16125. Kanalisiert."},
[71960] = {name = "Heldenhafter Sprung", description = "[q]Springt durch die Luft und schmettert auf alle Feinde im Umkreis von 8 Metern um das Zielgebiet hinab, verursacht Schaden und betäubt sie 2 sec lang."},
[71965] = {name = "Regen des Chaos", description = "[q]Lässt einen Schmelzregen niedergehen, der alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen 9 sec lang alle 3 Sek. 142 to 157 Chaosschaden zufügt."},
[71973] = {name = "Schlangenspucken", description = "[q]Verursacht beim Ziel Feuerschaden."},
[71974] = {name = "Schlangenschutz", description = "[q]Beschwört für 1 min ein Totem der Verbrennung mit 5 Gesundheit zu Euren Füßen, das wiederholt einen Feind im Umkreis von 0 Metern angreift und 5 Feuerschaden zufügt."},
[71976] = {name = "Heilender Fluss", description = "[q]Heilt alle 2 Sek. um 50."},
[71984] = {name = "Totem des heilenden Flusses", description = "[q]Beschwört ein Totem des heilenden Flusses mit 5 Gesundheit zu Füßen des Zaubernden. Gruppenmitglieder im Umkreis von 60 Metern erhalten alle 2 Sek. 50 Gesundheit. Hält 10 min lang an."},
[71993] = {name = "Gefrorener Schlaghammer", description = "[q]Angriffe haben eine Chance, Erfrierungen zu verursachen! Erfrierungen fügen 20 sec lang alle 2 Sek. 2438 to 2562 Schaden zu."},
[72004] = {name = "Erfrierung", description = "[q]Die Erfrierung verursacht alle 2 Sek. 2438 to 2562 Frostschaden und verringert das Bewegungstempo um 5%. Hält 20 sec lang an."},
[72013] = {name = "Glutsteinkanone", description = "[q]Schießt eine mächtige Kanonenkugel auf den Feind."},
[72014] = {name = "Kettenheilung", description = "[q]Erfüllt einen verwundeten Verbündeten mit Heilenergie, die sich auf einen anderen in der Nähe befindlichen Verbündeten ausbreitet. Der Zauber wirkt sich auf bis zu 3 Ziele aus, aber die Heilenergie wird bei jedem folgenden Ziel um 50% reduziert."},
[72034] = {name = "Schneesturm", description = "[q]Fügt allen nahen Feinden 13163 to 13837 Frostschaden zu und erhöht den erlittenen Frostschaden um 15%."},
[72037] = {name = "Schockvortex", description = "[q]Erschafft in der Nähe eines feindlichen Ziels einen wirbelnden Machtvortex, der Schaden verursacht und Feinde in seiner Nähe zurückstößt."},
[72039] = {name = "Machterfüllter Schockvortex", description = "[q]Erschafft bei allen nahen Feinden Machtvortexe, die 5000 körperlichen Schaden verursachen und Feinde nahe dem Ziel zurückstoßen."},
[72040] = {name = "Machtvolle Flamme herbeizaubern", description = "[q]Beschwört einen machtvollen Flammenball, der auf das Ziel zuschwebt und beim Aufprall explodiert."},
[72043] = {name = "Segen der Könige", description = "[q]Belegt ein befreundetes Ziel mit einem Segen und erhöht 10 min lang alle Attribute um 10%. Spieler können pro Paladin immer nur mit einem einzigen Segen belegt sein."},
[72050] = {name = "Feuriger Brocken", description = "[q]Fügt Gegnern im Umkreis von 5 Metern um das Zielgebiet Schaden zu."},
[72052] = {name = "Kinetische Bombe", description = "[q]Explodiert beim Aufschlag auf dem Boden, verursacht 18000 to 22000 körperlichen Schaden und stößt Feinde in einem großen Gebiet zurück."},
[72055] = {name = "Absorptionsschild", description = "[q]Nutzt die Kraft der Netherregionen, um den Zaubernden abzuschirmen. Absorbiert 50 Schaden. Hält 5 min lang an. Solange der Schild hält wird das Wirken von Zaubern nicht durch Schaden unterbrochen."},
[72057] = {name = "Lichklatscher", description = "[q]Schlägt das Ziel mit einer kalten Hand, verursacht 10500 to 13500 Schattenschaden und stößt es zurück."},
[72065] = {name = "Umhüllung des Schutzes", description = "[q]Beschützt ein anvisiertes Gruppenmitglied 6 sec lang vor allen körperlichen Angriffen."},
[72066] = {name = "Umhüllung des Zauberschutzes", description = "[q]Beschützt ein anvisiertes Gruppenmitglied 6 sec lang vor allen magischen Angriffen."},
[72069] = {name = "Soldat der Schlacht um Gilneas", description = "[q]Schneller, besser, stärker."},
[72090] = {name = "Gefrierender Boden", description = "[q]Verursacht Frostschaden bei in der Nähe befindlichen Feinden und macht sie bis zu 5 sec lang bewegungsunfähig."},
[72091] = {name = "Gefrorene Kugel", description = "[q]Erschafft an einem nahen Ort eine gefrorene Kugel."},
[72114] = {name = "Gezielter Schuss", description = "[q]Ein gezielter Schuss, der den Distanzschaden um 0 Schaden erhöht."},
[72120] = {name = "Erfrierung", description = "[q]Die Erfrierung verursacht alle 2 Sek. 1950 to 2050 Frostschaden und verringert das Bewegungstempo um 5%. Hält 10 sec lang an."},
[72122] = {name = "Gefrorener Schlaghammer", description = "[q]Angriffe haben eine Chance, Erfrierungen zu verursachen! Erfrierungen fügen 10 sec lang alle 2 Sek. 1950 to 2050 Schaden zu."},
[72123] = {name = "Frostschlag", description = "[q]Der Frostwächter schickt dem Ziel einen Frostschlag entgegen, der 20813 to 24187 Frostschaden verursacht."},
[72133] = {name = "Schmerz und Leid", description = "[q]Verursacht in einem kegelförmigen Bereich vor dem Zaubernden 7540 to 8460 Schattenschaden. Zusätzlich dazu erleiden die Ziele 3 sec lang pro Sekunde 1200 Schattenschaden."},
[72143] = {name = "Wutanfall", description = "[q]Erhöht den körperlichen Schaden um 200%."},
[72149] = {name = "Schockwelle", description = "[q]Sendet vom Zaubernden aus eine Schockwelle nach vorne, die in einem kegelfömigen Bereich von 20 Metern 150% Waffenschaden verursacht."},
[72155] = {name = "Pesthauchprobe sammeln", description = nil},
[72163] = {name = "Feuerball", description = "[q]Schleudert einen Feuerball, der 6175 to 6825 Feuerschaden verursacht."},
[72166] = {name = "Frostblitz", description = "[q]Schleudert einen Frostblitz auf den Feind, der 5225 to 5775 Frostschaden verursacht und sein Bewegungstempo 4 sec lang um 50% verringert."},
[72169] = {name = "Flammenstoß", description = "[q]Lässt eine Feuersäule niedergehen, die alle Feinde im Gebiet verbrennt und ihnen zunächst 3932 to 4568 Feuerschaden und zusätzlich 6 sec lang insgesamt 5400 Feuerschaden zufügt."},
[72171] = {name = "Eisketten", description = "[q]Schlingt eisige Ketten um das Ziel, die es 6 sec lang am Platz festhalten."},
[72172] = {name = "Blutbestie rufen", description = "[q]Saurfang ruft ein Paar Blutbestien herbei."},
[72176] = {name = "Blutverbindung", description = "[q]Durch diese Kreatur verursachter Schaden erfüllt Saurfang mit Blutmacht."},
[72194] = {name = "Schildhieb", description = "[q]Schlägt mit dem Schild des Zaubernden auf einen Feind ein, verursacht körperlichen Schaden und unterbricht einen gewirkten Zauber."},
[72198] = {name = "Spektralstoß", description = "[q]Ein spektraler Angriff, der 100% Waffenschaden verursacht. Dieser Stoß ignoriert jegliche Rüstung."},
[72203] = {name = "Gequälter Wutanfall", description = "[q]Erhöht das Nahkampfangriffstempo um 100%."},
[72206] = {name = "Kanone abfeuern", description = "[q]Feuert eine Kanone auf die Winzlinge von Oomlot mit einer Reichweite von bis zu 100 Metern."},
[72208] = {name = "Schießen", description = "[q]Schießt auf einen Feind und verursacht 4163 to 4837 körperlichen Schaden."},
[72215] = {name = "Frostfalle", description = "[q]Eine Frostfalle platzieren, die 30 sec lang um sich herum eine Eisfläche bildet, sobald der erste Feind sich ihr nähert. Das Bewegungstempo aller Feinde in einem Umkreis von 10 Metern wird um 50% verlangsamt, solange sie sich im Wirkungsbereich befinden. Die Falle bleibt 1 min lang bestehen. Es kann immer nur jeweils eine Falle aktiv sein."},
[72219] = {name = "Magenblähung", description = "[q]Fügt dem Ziel 14625 to 15375 Schaden zu und verursacht eine Magenblähung, die seinen verursachten Schaden 1.67 min lang um 10% erhöht. Nach Erreichen von 10 Stapeln wird eine Magenexplosion ausgelöst, die nahen Verbündeten massiven Schaden zufügt."},
[72222] = {name = "Verfluchter Pfeil", description = "[q]Schießt einen verfluchten Pfeil, der den erlittenen Magieschaden des Ziels um 50% erhöht."},
[72232] = {name = "Geschwächte Seele", description = "[q]Die Seele des Ziels ist durch die Einwirkung von 'Machtwort: Schild' geschwächt und kann 1 min lang nicht in ein Schild gehüllt werden."},
[72244] = {name = "Ithis' Wirbelsturm", description = "[q]Teleportiert das Ziel zur Spitze der nächsten Klippe."},
[72262] = {name = "Beben", description = "[q]Strahlt unterirdische Schockwellen aus, die den Rand der Plattform zum Bröckeln bringen."},
[72268] = {name = "Eisschuss", description = "[q]Schießt auf einen Feind, verursacht 5700 to 6300 Frostschaden und betäubt das Ziel 2 sec lang."},
[72272] = {name = "Ekelhaftes Gas", description = "[q]Verursacht im Zielbereich eine ekelhafte Seuche, die 6 sec lang alle 2 Sek. 4875 to 5125 Schaden verursacht. Die mit der Seuche infizierten Ziele müssen sich unwillkürlich übergeben, was in der Nähe befindlichen Verbündeten 5850 to 6150 Schaden zufügt."},
[72293] = {name = "Mal des gefallenen Champions", description = "[q]Die Nahkampfangriffe von Todesbringer Saurfang werden auf das Ziel übertragen und verursachen zusätzlichen körperlichen Schaden. Stirbt das Ziel, während es unter dem Einfluss dieses Effekts steht, wird Saurfang um 20% seiner gesamten Gesundheit geheilt."},
[72294] = {name = "Flüchtlinge fressen", description = "[q]Verschling die Leichen gefallener Reisender."},
[72318] = {name = "Schattenwort: Schmerz", description = "[q]Murmelt ein Wort der Dunkelheit, das einem Gegner 8 sec lang alle 2 Sek. Schattenschaden zufügt."},
[72320] = {name = "Kreis der Zerstörung", description = "[q]Ein Kreis der Zerstörung wird entfesselt, der Feinden im Umkreis von 10 Metern um das Ziel 3194 to 3806 Schattenschaden zufügt und sie zurückstößt."},
[72321] = {name = "Vor Furcht zusammenkauern", description = "[q]Erfüllt den Feind mit Furcht, woraufhin er 4 sec lang vor Angst erstarrt."},
[72322] = {name = "Dunkle Besserung", description = "[q]Heilt bei einem Verbündeten 50875 to 59125 Schaden unter Verwendung von dunkler Magie."},
[72326] = {name = "Schattenschritt", description = "[q]Tritt durch die Schatten, verursacht 7000 Schattenschaden und teleportiert sich hinter das Ziel."},
[72328] = {name = "BFGC - Attempt to Start", description = nil},
[72329] = {name = "Tödliches Gift", description = "[q]Fügt 12 sec lang jede 3 Sekunde 1065 Naturschaden zu. Bis zu 5-mal auf einem Ziel stapelbar."},
[72333] = {name = "Giftbenutzten Dolch werfen", description = "[q]Verursacht 12 sec lang alle 3 Sek. Naturschaden und verringert das Bewegungstempo um 15%."},
[72335] = {name = "Nierenhieb", description = "[q]Ein gemeiner Schlag auf lebenswichtige Organe des Gegners, der das aktuelle Ziel 3 sec lang betäubt."},
[72342] = {name = "Halluzination", description = "[q]Erschafft in der Nähe eine Kopie des Zaubernden, die die Feinde des Magiers angreift und Zauber wirkt. Hält 30 sec lang an. Die Kopie wird bei ihrem Tod explodieren und nahen Feinden 10000 Arkanschaden zufügen."},
[72343] = {name = "Halluzination", description = "[q]Macht den Zaubernden unsichtbar und betäubt ihn 6 sec lang."},
[72344] = {name = "Halluzination", description = "[q]Stirbt die Halluzination, wird allen Feinden in ihrer Nähe 10000 Arkanschaden zugefügt."},
[72350] = {name = "Frostgrams Furor", description = "[q]Fügt allen Feinden 1000000 Schattenschaden zu und macht es ihnen unmöglich, ihren Geist freizulassen oder wiederbelebt zu werden."},
[72360] = {name = "Auslöschen", description = "[q]Ein brutaler sofortiger Angriff, der 26000 Schaden verursacht."},
[72362] = {name = "Brunnen der Verderbnis", description = "[q]Verderbt alle Ziele innerhalb des Kreises und erhöht ihren erlittenen Schattenschaden."},
[72363] = {name = "Verderbtes Fleisch", description = "[q]Verdirbt das Fleisch in der Nähe befindlicher Feinde und verringert ihre maximale Gesundheit 8 sec lang um 50%."},
[72368] = {name = "Geteiltes Leid", description = "[q]Fügt einem feindlichen Ziel im Verlauf von 12 sec 2.4e+07 Schattenschaden zu. Wird der Effekt gebannt, wird der verbleibende Schaden zwischem dem Ziel und seinen Verbündeten aufgeteilt."},
[72371] = {name = "Blutmacht", description = "[q]Für jeden Punkt Blutmacht, den er besitzt, wächst Saurfang um 1% und verursacht 1% mehr Schaden."},
[72378] = {name = "Blutnova", description = "[q]Blut explodiert aus einem zufälligen feindlichen Ziel und fügt einem nahen Feind 1 körperlichen Schaden zu."},
[72385] = {name = "Kochendes Blut", description = "[q]Bringt das Blut eines Feindes zum Kochen und verursacht 15 sec lang alle 3 Sek. 5000 körperlichen Schaden."},
[72390] = {name = "Hoffnungslosigkeit", description = "[q]Verringert den verursachten Schaden und die Heilung aller nahen Feinde um 25%."},
[72410] = {name = "Rune des Blutes", description = "[q]Die Nahkampfangriffe von Todesbringer Saurfang entziehen dem Ziel Gesundheit. Saurfang wird um ein Vielfaches des entzogenen Betrags geheilt."},
[72422] = {name = "Zuckender Stoß", description = "[q]Ein zuckender Stoß trifft den Gegner, der 100% des Schadens eines normalen Nahkampfangriffs verursacht und die Ausweichschance des Ziels 5 sec lang um 20% verringert. Dieser Stoß kann nicht vermieden verden."},
[72426] = {name = "Drohende Verzweiflung", description = "[q]Erfüllt einen Feind mit einem bedrohlichen Gefühl der Verzweiflung. Wird der Effekt nicht vor Ablauf von 6 sec gebannt, wird das Ziel betäubt und kauert sich 6 sec lang ängstlich zusammen."},
[72429] = {name = "Massenwiederbelebung", description = "[q]Wiedererweckt alle nahen gefallenen Verbündeten."},
[72435] = {name = "Entweihender Schrecken", description = "[q]Ein markerschütternder Schrei erfüllt nahe feindliche Ziele mit Entsetzen und verursacht 4 sec lang alle 1 Sek. 4000 Schattenschaden."},
[72451] = {name = "Mutierte Seuche", description = "[q]Fügt alle 3 Sek. Schaden zu."},
[72466] = {name = "Seelenbewahrung", description = "[q]Erweckt den Lebensfunken im Ziel."},
[72518] = {name = "Einhüllende Winde", description = "[q]Setzt das Ziel in einem unentrinnbaren Widwirbel fest."},
[72525] = {name = "Berserker", description = "[q]Verursachter Schaden um 240% erhöht.\n Angriffs- und Zaubertempo um 160% erhöht."},
[72591] = {name = "Stampfen", description = "[q]Betäubt 2.50 sec lang Dinosaurier in einem Umkreis von 10 Metern. Kann Steine hochwerfen."},
[72595] = {name = "Seele wiederherstellen", description = "[q]Lässt die Seele des Ziels wieder in ihren Körper zurückkehren, wodurch sie ins Leben zurückgebracht wird."},
[72681] = {name = "Silithidenbändigung", description = "Silithidenbändigung\nIhr habt einen Silithiden gefangen! Führt ihn zurück ins Lager, bevor er das Seil durchbeißt."},
[72723] = {name = "Widerstandsfähige Haut", description = "[q]Die Haut dieser Kreatur ist sehr widerstandsfähig. Der erlittene Flächenschaden wird um 95% und der Schaden durch Krankheiten um 70% verringert."},
[72728] = {name = "Fleisch korrodieren", description = "[q]Korrodiert das Fleisch des Gegners und verursacht danach 15 sec lang alle 3 Sek. Naturschaden."},
[72737] = {name = "Raserei", description = "[q]Saurfang verfällt in Raserei, die sein Angriffstempo um 30% erhöht."},
[72741] = {name = "Batterie abfeuern", description = "[q]Feuert auf die Stoßtrupps."},
[72754] = {name = "Entweihen", description = "[q]Fügt allen Feinden im Bereich pro Sekunde Schattenschaden zu. Immer, wenn ein Feind durch diesen Effekt Schaden nimmt, erhöhen sich der Schaden und die Größe des Gebiets. Hält 60 Sek. lang an."},
[72762] = {name = "Entweihen", description = "[q]Entweiht den Boden unter einem zufälligen Ziel. Jegliche in diesem Bereich gefangene Feinde erleiden Schattenschaden und lassen den Bereich anwachsen. Hält 30 Sek. lang an."},
[72769] = {name = "Blutgeruch", description = "[q]Saurfangs Blutbestien strömen einen Blutgeruch aus, der das Bewegungstempo aller nahen Feinde um 80% verringert und ihren erlittenen Schaden 10 sec lang um 300% erhöht."},
[72839] = {name = "Brennen", description = "Das Katapult ist beschädigt und brennt. Ihr könnt es nur für kurze Zeit benutzen. Nutzt die Chance!"},
[72840] = {name = "Unbeständiges Experiment", description = "[q]Professor Seuchenmord beginnt an Euch allen ein tückisches Experiment!"},
[72844] = {name = "B.C. Eliminator: Fire Cannon - Cover", description = nil},
[72849] = {name = "Zurückfliegen", description = "[q]Kehrt nach Gilneas zurück und lasst die Fledermaus landen."},
[72898] = {name = "Wasserblitz", description = "[q]Fügt dem Ziel 50 Frostschaden zu."},
[72905] = {name = "Frostblitzsalve", description = "[q]Verursacht 14400 to 17600 Frostschaden bei in der Nähe befindlichen Feinden und verringert 4 sec lang ihr Bewegungstempo."},
[72922] = {name = "Nachwachsen", description = "[q]Heilt einen Verbündeten für einen festen Wert, heilt until cancelled lang weiteren Schaden."},
[72935] = {name = "Zombieverwandlung", description = "[q]Fängt das Ziel in einem ausbruchsicheren Windvortex ein und zieht die Seele aus seinem Körper, sodass es zu einem hohlen, hirnlosen Zombie wird!"},
[72960] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[72963] = {name = "Verwesendes Fleisch", description = "[q]Jedes Mal, wenn der Zaubernde Nahkampfschaden verursacht, wird das Ziel mit Säure bespritzt, die 8 sec lang pro Sekunde 250 Naturschaden verursacht."},
[72975] = {name = "Schattenbarriere", description = "Bastoons dunkle Energien umgeben und schützen Donnerschrei."},
[72991] = {name = "Feuerball", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[73001] = {name = "Schattengefängnis", description = "[q]Bindet einen Feind. Bewegt sich der gebundene Feind, erleidet er zunächst 350 Schattenschaden, der mit jeder weiteren Sekunde Bewegung um 500 erhöht wird. Bleibt das Ziel 10 sec lang bewegungslos, werden die Stapel zurückgesetzt."},
[73004] = {name = "Gefrierende Berührung", description = "[q]Betäubt das Ziel und verhindert jegliche seiner Aktionen, macht es jedoch auch unverletzbar."},
[73013] = {name = "Schattenwoge", description = nil},
[73014] = {name = "Frostwoge", description = nil},
[73070] = {name = "Schrecken entfachen", description = "[q]Erfüllt Feinde mit Furcht, woraufhin diese bis zu 4 sec lang voller Schrecken flüchten."},
[73079] = {name = "Seuchenschlag", description = "[q]Schießt Gift auf den Feind und fügt Naturschaden zu."},
[73087] = {name = "Schattenschock", description = "[q]Peitscht einen Feind sofort mit dunkler Magie und verursacht Schattenschaden."},
[73097] = {name = "Vulkanischer Atem", description = "[q]Volcanoth atmet mit einer Reichweite von 100 Metern geschmolzene Lava, die 150 Feuerschaden verursacht."},
[73100] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[73136] = {name = "Summon Vine with Ground Burst", description = nil},
[73151] = {name = "Grüne Runen", description = nil},
[73155] = {name = "Betäuben", description = "Betäuben Magie\nBetäubt durch das plötzliche Aufreißen der Erde und das schreckliche entfesselte Wachstum."},
[73212] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm Naturschaden zu."},
[73253] = {name = "Welle der Heilung", description = "[q]Ruft Naturmagie herbei, um einen Verbündeten zu heilen."},
[73254] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm Naturschaden zu."},
[73255] = {name = "Erdschock", description = "[q]Schockt einen Gegner mit erschütternder Kraft, verursacht Naturschaden und unterbricht 2 sec lang den gewirkten Zauber."},
[73257] = {name = "Piratenzerstörende Bombe", description = "[q]Werft eine große Bombe, um Blockierer der Südmeerfreibeuter am Ort des Einschlags in die Luft zu sprengen."},
[73297] = {name = "Eisige Explosion", description = "[q]Stößt in der Nähe befindliche Ziele zurück und hat eine Chance, sie anschließen in einem Eisblock einzufrieren."},
[73312] = {name = "Summon 4 Horde", description = nil},
[73392] = {name = "Verstohlenheit", description = "[q]Versetzt den Zaubernden in den Verstohlenheitsmodus. Hält an, bis die Aktion abgebrochen wird."},
[73393] = {name = "Totem des Manastroms", description = "[q]Beschwört ein Totem des Manastroms mit 5 Gesundheit zu Füßen des Zaubernden. Gruppenmitglieder in einem Umkreis von 60 Metern erhalten alle 2 Sek. 50 Mana. Hält 10 min lang an."},
[73394] = {name = "Manastrom", description = "[q]Erzeugt alle 2 Sek. 50 Mana."},
[73424] = {name = "Schrumpfen", description = "[q]Verringert 30 sec lang die Stärke und Ausdauer eines Gegners."},
[73432] = {name = "Seelennarbe", description = "[q]Verursacht im Verlauf von 2.50 sec 5% der gesamten Gesundheit als Schattenschaden."},
[73441] = {name = "Summon Captured Bughunter", description = nil},
[73456] = {name = "Maschinengewehr", description = "[q]Feuert ein Maschinengewehr auf feindliche Tarnkappenflieger und verursacht 438 to 562 Schaden in einer Reichweite von bis zu 100 Metern."},
[73477] = {name = "Wilde Wieselraketen", description = "[q]Fügt dem nächsten Tarnkappenflugzeug von Gnomeregan im Umkreis von 100 Metern 985 to 1265 Feuerschaden zu."},
[73485] = {name = "Maschinengewehr", description = nil},
[73488] = {name = "Lebensentzug", description = "[q]Fügt einem feindlichen Ziel 4625 to 5375 Schattenschaden zu und heilt den Zaubernden um das Zehnfache des verursachten Schadens."},
[73510] = {name = "Gedankenstachel", description = "[q]Überzieht das Ziel mit 1213 Schattenfrostschaden, zehrt jedoch Eure auf das Ziel wirkenden regelmäßigen Schattenschadenseffekte auf.\n\n Euer Zauber 'Gedankenstachel' erhöht zudem die kritische Trefferchance Eures nächsten Wirkens von 'Gedankenschlag' auf das Ziel um 30 %. Bis zu 3 -mal stapelbar."},
[73529] = {name = "Schattenfalle", description = "[q]Fügt allen Gegnern innerhalb von 10 Metern 23563 to 26437 Schattenschaden zu und stößt sie zurück."},
[73539] = {name = "Schattenfalle beschwören", description = "[q]Beschwört eine Schattenfalle, die allen Gegnern im Umkreis von 10 Metern 23563 to 26437 Schattenschaden zufügt und sie zurückstößt."},
[73562] = {name = "Fluch des Fleisches", description = "[q]Feegly hat etwas Furchtbares mit diesem Zwerg angestellt."},
[73564] = {name = "Sturmblitz", description = "[q]Fügt einem Gegner 7068 to 7931 Naturschaden zu."},
[73576] = {name = "Explosion", description = "[q]Fügt allen Feinden innerhalb von 10 Metern 23563 to 26437 Schattenschaden zu."},
[73599] = {name = "Kaja'Cola minus eins", description = "[q]Neue, verbesserte Kaja'Cola minus eins! Nur die Hälfte der Kalorien, aber doppelt soviele IDEEN! (TM)"},
[73605] = {name = "Kaja'Cola minus eins", description = "[q]Neue, verbesserte Kaja'Cola minus eins! Nur die Hälfte der Kalorien, aber doppelt soviele IDEEN! (TM)"},
[73650] = {name = "Seele wiederherstellen", description = nil},
[73654] = {name = "Seelen ernten", description = "[q]Versucht, die Seelen aller in der Nähe befindlichen Feinde zu ernten und verursacht dabei until cancelled lang pro Sekunde 4000 Schattenschaden. Sollte ein Ziel noch leben, nachdem das Kanalisieren abgeschlossen ist, wird die Seele des Ziels in Frostgram gezogen, um verschlungen zu werden."},
[73740] = {name = "Rammen", description = "[q]Rammt alle Feinde, die sich vor dem Verwüster befinden, fügt ihnen 6000 Schaden zu und stößt sie zurück."},
[73757] = {name = "Wasserwandeln", description = "[q]Gewährt dem Ziel die Fähigkeit, auf dem Wasser zu wandeln."},
[73841] = {name = "Jagen", description = "[q]Verfolgt die Spur Eures Abendessens."},
[73851] = {name = "Spurt", description = "[q]Erhöht das Bewegungstempo 5 sec lang um 60%."},
[73854] = {name = "Summon the Dogs of War", description = "[q]Beschwört 2 gezähmte Hyänen als Begleitung für den Zaubernden bis zur Freigabe."},
[73855] = {name = "Schädelstüber", description = "[q]Betäubt einen Feind 2 sec lang."},
[73858] = {name = "Verlangen nach Blut", description = "[q]Stiehlt einem zufälligen Feind Gesundheit und verringert den Schaden des Zaubernden um 15%."},
[73859] = {name = "Kopfstoß", description = "[q]Fügt nahen Gegnern normalen Schaden plus 30 zu und stößt sie zurück."},
[73860] = {name = "Pulverisieren", description = "[q]Fügt nahen Gegnern normalen Schaden plus 0 zu und stößt sie zurück."},
[73861] = {name = "Feuersturm", description = "[q]Lässt einen Schmelzregen niedergehen, der alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen 5 sec lang alle 2 Sek. 13875 to 16125 Feuerschaden zufügt."},
[73863] = {name = "Weiter Streich", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden normalen Schaden plus 50 zu."},
[73867] = {name = "Schneidlaser", description = "[q]Verursacht bei einem Gegner 3 sec lang pro Sekunde 20 Feuerschaden."},
[73868] = {name = "Donnernde Hufe", description = "[q]Trampelt den Wolf nieder."},
[73874] = {name = "Sonnenstoß", description = "[q]Ein wütender Stoß der Sonne verbrennt das feindliche Ziel und verursacht 15 sec lang alle 3 Sek. 7500 to 8500 Feuerschaden."},
