[76205] = {name = "Drachenatem des smaragdgrünen Flammenwirkers", description = nil},
[76207] = {name = "Versklaven", description = "[q]Springt auf den Kopf eines zufälligen feindlichen Spielers und bazubert ihn. So lang die Bezauberung vorhält, werden Gesundheit, Schaden und Heilung des Opfers erhöht. Der Effekt hält 30 sec lang an oder bis das Opfer 50% Gesundheit erreicht."},
[76218] = {name = "Geisterhafte Klaue", description = "[q]Der Furor des Wolfsahnen trifft feinde für 2399 bis 3601 Schaden."},
[76232] = {name = "Sturmfuror", description = "[q]Erhöht den vom Ziel erlittenen Zauberschaden um 2000%."},
[76233] = {name = "Irdener Zorn", description = "[q]Erhöht den vom Ziel erlittenen körperlichen Schaden um 2000%."},
[76234] = {name = "Gedankennebel", description = "[q]Beschwört eine 10 Meter große Zone aus Nebel um den Zaubernden. Jeder Gegner innerhalb des Nebels kann keine Angriffe oder Zauber ausführen. Zusätzlich erleidet er alle 0,5 Sek. 471 to 528 Schattenschaden, solange er sich darin befindet. Hält 20s lang an."},
[76246] = {name = "Sengende Flamme", description = "[q]Die intensive Hitze von Ragnaros verursacht bei allen nahen Feinden 875 to 1125 Feuerschaden."},
[76303] = {name = "Zwielichtschutz", description = "[q]Solange er von der Aura des Zwielichts beschützt wird, erleidet Drahga 90% weniger Schaden!"},
[76307] = {name = "Magie absorbieren", description = "[q]Hüllt den Zaubernden in einen Schild, der jeglichen Zauberschaden absorbiert. Zusätzlich wird der Zaubernde jedes Mal, wenn er Zauberschaden erleiden würde, stattdessen um das dreifache des absorbierten Werts geheilt. Hält 5 sec lang an."},
[76312] = {name = "Erdschmettern", description = "[q]Aschenmaul schlägt auf den Boden und betäubt seine Feinde!"},
[76314] = {name = "Lodernder Zwielichtschild", description = "[q]Legt einen Lodernden Zwielichtschild auf ein in der Nähe befindliches freundliches Ziel. Angriffe auf das Ziel können eine Lohe verursachen. Heilt das Ziel 6 sec lang alle 1 Sek. um 34125 to 35875."},
[76325] = {name = "Flammen rufen", description = "[q]Flammenstrahlen schießen aus dem Zaubernden, landen auf dem Schlachtfeld und verursachen Schaden in einem Bereich."},
[76332] = {name = "Verderbte Flamme", description = "[q]Die verderbte Flamme verursacht alle 1 Sek. 5850 to 6150 Feuerschaden und löst 'Brennende Verderbnis' aus."},
[76335] = {name = "Brennende Verderbnis", description = "[q]Erhöht den erlittenen Feuerschaden 10 sec lang um 10%. 10-mal stapelbar."},
[76339] = {name = "Unnachgiebiger Schmerz", description = "[q]Fügt allen umgebenden Feinden entsetzliche Schmerzen zu, verursacht 10 sec lang pro Sekunde 4750 to 5250 Schattenschaden."},
[76340] = {name = "Verdrehtes Arkanes", description = "[q]Das verdrehte Arkane erhöht Zaubertempo und verursachten Zauberschaden um 3%."},
[76355] = {name = "Segen der Sonne", description = "[q]Die brennende Hitze der Sonne verursacht pro Sekunde 4162 to 4837 Schaden und erhöht den verursachten Schaden um 100%."},
[76363] = {name = "Welle der Verderbnis", description = "[q]Fügt allen Gegnern im Umkreis von 4 Metern 8 sec lang pro Sekunde 1000 Naturschaden zu."},
[76369] = {name = "Zwielichtblitz", description = "[q]Verursacht 24375 to 25625 Schattenschaden."},
[76370] = {name = "Verzerrtes Zwielicht", description = "[q]Fügt dem Ziel Schattenschaden in Höhe von 50% seiner maximalen Gesundheit zu."},
[76378] = {name = "Arkane Infusion", description = "[q]Angriffe verursachen 100% Arkanschaden und 'Spalten'."},
[76380] = {name = "Magmaschlag", description = "[q]Fügt einem Feind massiven Feuerschaden zu."},
[76394] = {name = "Fluch der Azurgeborenen", description = "[q]Erhöht den erlittenen Magieschaden 10 sec lang um 50%."},
[76395] = {name = "Kälte von Eiskrone", description = "[q]Kanalisiert die Macht von Eiskrone, was erlittenen Feuerschaden um 50% verringert, erhaltene Heilung um 1000% erhöht und Ragnaros jede Sek. Frostschaden zufügt."},
[76404] = {name = "Blutroter Ansturm", description = nil},
[76409] = {name = "Blutrote Schockwelle", description = "[q]Fügt Gegnern im Umkreis von 5 Metern 24862 to 26137 Feuerschaden zu und stößt sie zurück."},
[76411] = {name = "Fleischmühle", description = "[q]Der Zwielichtvollstrecker dreht sich in einem Wirbelwind und verursacht 4 sec lang bei Feinden in einem Umkreis von 5 Metern alle 0.5 Sek. 9750 to 10250 körperlichen Schaden."},
[76415] = {name = "Schwindelig", description = "[q]Durch das Herumwirbeln ist dem Zwielichtvollstrecker schwindelig geworden, wodurch er betäubt wird und sich sein erlittener Schaden 4 sec lang um 50% erhöht."},
[76416] = {name = "Schattenblitz", description = "[q]Verursacht 15600 to 16400 Schattenschaden."},
[76418] = {name = "Teufelsjäger beschwören", description = "[q]Beschwört einen Teufelsjäger, der den Zaubernden bis zur Freigabe begleitet."},
[76423] = {name = "Taunkawut", description = "[q]Gerät in Rage, erhöht Schaden und Größe aber verringert das Tempo."},
[76429] = {name = "Statischer Schlag", description = "[q]Lässt eine Welle aus Elektrizität entstehen, die 20 Naturschaden verursacht. Verursacht dreifachen Schaden, wenn sich das Ziel unter Wasser befindet."},
[76430] = {name = "Flammentsunami", description = "[q]Verursacht bei in der Nähe befindlichen Feinden Feuerschaden und überschüttet sie mit Lava, wodurch 10 sec lang zusätzlicher Feuerschaden verursacht wird."},
[76460] = {name = "Tempoexplosion", description = "[q]Gewährt 5 Sek. lang einen Flugtemposchub."},
[76473] = {name = "Druckwelle", description = "[q]Entfesselt eine Flammenwelle, die nahen Feinden Feuerschaden zufügt und ihr Bewegungstempo 8 sec lang verringert."},
[76478] = {name = "Rotglühendes Schüreisen", description = "[q]Fügt einem Gegner 6000 to 10000 Feuerschaden zu und erhöht 12 sec lang seinen erlittenen Feuerschaden um 2250."},
[76484] = {name = "Fesseln", description = "[q]Fesselt einen Feind 8 sec. lang und hindert ihn daran, Zauber zu wirken, anzugreifen, oder sich zu bewegen. Jeder verursachte Schaden wird das Ziel befreien. Es kann nur je ein Ziel auf einmal gefesselt sein."},
[76487] = {name = "Wildes Geprügel", description = "[q]Erhöht das Angriffstempo um 200% und verringert die Trefferchance um 50%. Hält 6 sec lang an."},
[76490] = {name = "Klauenwirbel", description = "[q]Entfesselt Griswolds Zorn auf in der Nähe befindliche Feinde, wodurch er sie 6 sec lang wütend angreift."},
[76500] = {name = "Finsterer Stoß", description = "[q]Verursacht normalen Schaden plus 7500 bei einem Feind."},
[76502] = {name = "Herzsucherklinge", description = "[q]Wirft einen Dolch nach dem Herzen eines Gegners und verursacht so 4875 to 5125 körperlichen Schaden. Wenn das Ziel über mehr als 75% Gesundheit verfügt, wird der Schaden verdreifacht."},
[76507] = {name = "Durchstechende Klauen", description = "[q]Verursacht 6 sec lang alle 3 Sek. 975 to 1025 körperlichen Schaden. Stapelbar."},
[76508] = {name = "Frostblitz", description = "[q]Fügt einem Feind Frostschaden zu und verringert 3 sec lang sein Bewegungstempo."},
[76509] = {name = "Frostnova", description = "[q]Verursacht 1850 to 2150 Frostschaden bei in der Nähe befindlichen Feinden und macht sie 6 sec lang bewegungsunfähig."},
[76512] = {name = "Infizierte Wunde", description = "[q]Erhöht 30 sec lang den durch einen Gegner erlittenen körperlichen Schaden."},
[76514] = {name = "Verwirrende Flammen", description = "[q]Die verwirrenden Flammen verursachen alle 1 Sek. 8775 to 9225 Feuerschaden und betäuben das Ziel 5 sec lang."},
[76516] = {name = "Vergifteter Speer", description = "[q]Schleudert einen in Gift getauchten speer nach dem Ziel. Verursacht 19000 to 21000 Naturschaden sowie zusätzlich 10 sec lang pro Sekunde 2000 Naturschaden."},
[76517] = {name = "Hervorbrechendes Feuer", description = "[q]Flammen brechen unter dem Ziel hervor, verursachen 16087 to 16912 Feuerschaden und stoßen das Ziel zurück."},
[76520] = {name = "Meucheln", description = "[q]Fügt einem Gegner 150% Waffenschaden zu, aber nur, wenn von hinten angegriffen wird."},
[76522] = {name = "Machtschlag", description = "[q]Fügt einen Feind körperlichen Schaden zu und stößt ihn zu Boden."},
[76524] = {name = "Schreckliches Wirbeln", description = "[q]Ein hinterhältiger Angriff, der das Ziel stark bluten lässt, bis es vollständig geheilt wird. Fügt bis zur vollständigen Heilung alle 3 Sek. 1500 Schaden zu."},
[76535] = {name = "Wasserblitz", description = "[q]Verursacht 18850 to 21150 Frostschaden und verlangsamt das Tempo des Ziels um 5%."},
[76557] = {name = "Windwandeln", description = "[q]Der aufgestiegene Windspäher beginnt sich so schnell zu bewegen, dass er verschwimmt. Er fügt in der Nähe befindlichen Feinden 24375 to 25625 Schaden zu."},
[76560] = {name = "Tsunami", description = "[q]Fügt Gegnern in einem kegelförmigen Bereich von 30 Metern vor dem Zaubernden 28275 to 31725 Frostschaden zu und stößt sie zurück."},
[76561] = {name = "Gravitationsstoß", description = "[q]Fügt einem Gegner Schaden in Höhe von 60% seiner aktuellen Gesundheit zu und verringert kurzzeitig seine Angriffskraft. Dieser Angriff kann weder geblockt oder pariert werden, noch ist ein Ausweichen möglich."},
[76565] = {name = "Eisenerz werfen", description = "[q]Wirft einen Brocken azurblaues Eisenerz."},
[76572] = {name = "Kurzer Wurf", description = "[q]Wirft eine Waffe nach einem Feind und fügt körperlichen Schaden zu."},
[76575] = {name = "Blut der Weiterentwickelten", description = "[q]Das Blut des evolvierten Zwielichtzeloten bedeckt die Waffen und bieten eine Chance, zusätzlichen körperlichen Schaden zu verursachen, wenn die Bluteffekte einen Gegner verletzen."},
[76576] = {name = "Clear Summoned Ore", description = nil},
[76578] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 3 Ziele aus und verursacht bei jedem folgenden Ziel immer größeren Naturschaden."},
[76579] = {name = "Überladen", description = "[q]Überzieht einen Feind mit Blitzen und verursacht Naturschaden. Verursacht alle 3 Sek. 3900 to 4100 Naturschaden und lässt Angriffe einen Elektrischen Schlag verursachen. 'Elektrischer Schlag' verursacht je nach Anzahl der Stapel von 'Überladen' mehr Schaden."},
[76582] = {name = "Solarplexus", description = "[q]Fügt einem Feind 10 Schaden zu und macht ihn bis zu 4 sec lang handlungsunfähig. Ihr stoppt alle Angriffe automatisch. Das Ziel muss Euch zugewandt sein. Jeder Schaden, der dem betäubten Ziel zugefügt wird, lässt es wieder aufwachen."},
[76583] = {name = "Tritt", description = "[q]Tritt einen Gegner, fügt ihm Schaden zu und unterbricht 6 sec lang den gewirkten Zauber."},
[76584] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[76588] = {name = "Schattennova", description = "[q]Fügt allen nahen Gegnern 7400 to 8600 Schattenschaden zu."},
[76589] = {name = "Arkanbeschuss", description = "[q]Schleudert arkane Energien auf das feindliche Ziel, die 5400 to 6600 Arkanschaden verursachen."},
[76590] = {name = "Bodenhämmern", description = "[q]Schlägt auf den Boden und verursacht Beben, die 6 sec lang alle 0.5 Sek. 24000 Schaden bei allen Feinden innerhalb eines Bereichs von 5 Metern verursachen."},
[76591] = {name = "Aura der arkanen Hast", description = "[q]Erhöht 2 min lang das Zaubertempo des Zaubernden um 20% sowie den Zauberschaden um 1500."},
[76592] = {name = "Arkane Hast", description = "[q]Erhöht 5 sec lang das Zaubertempo von nahen Freunden um 20% sowie den Zauberschaden um 1500."},
[76594] = {name = "Verwunden", description = "[q]Fügt einem Feind 15 sec lang alle 3 Sek. körperlichen Schaden zu."},
[76596] = {name = "Steinhaut", description = "[q]Verhärtet die Haut des Ziels und verringert so seinen erlittenen Schaden um 75%."},
[76603] = {name = "Erdstachel", description = "[q]Fügt Gegnern im Umkreis von 3 Metern 29250 to 30750 Naturschaden zu."},
[76604] = {name = "Leerenreißen", description = "[q]Reißt ein Portal auf, das direkt in die Leere führt. Dieses Portal zieht alle Feinde hinein und hält sie dort 3 sec lang fest."},
[76610] = {name = "Erdschock", description = "[q]Schockt das Ziel sofort mit traumatisierender Wucht und verursacht 7800 to 8200 Naturschaden."},
[76612] = {name = "Arkane Geschosse", description = "[q]Schleudert ein arkanes Geschoss auf den Feind und verursacht Arkanschaden."},
[76617] = {name = "Hitzeausstoß", description = "[q]Die extreme Hitze erschöpft Ziele in einem großen Bereich, verringert die maximale Gesundheit um 10% und das Bewegungstempo um 10%. Stapelbar."},
[76618] = {name = "Feuerbrand", description = "[q]Verbrennt sofort einen Feind und fügt ihm dann 15 sec lang alle 3 Sek. zusätzlichen Feuerschaden zu."},
[76620] = {name = "Azurschlag", description = "[q]Der Azurgeborene Kriegsfürst kanalisiert seine Energie in einen Azurschlag, der Feinden in einem Umkreis von 8 Metern 43875 to 46125 Arkanschaden zufügt."},
[76622] = {name = "Rüstung zerreißen", description = "[q]Hackt auf die Rüstung eines Feindes ein und verringert sie um 10% pro Einsatz von 'Rüstung zerreißen'. Kann bis zu 5-mal angewendet werden. Hält 20 sec lang."},
[76626] = {name = "Verzerrtes Gesicht beschwören", description = "[q]Beschwört regelmäßig ein verzerrtes Gesicht aus der Nähe."},
[76628] = {name = "Lavasabber", description = "[q]Brennender Lavaspeichel fügt Feinden im Gebiet pro Sekunden 7000 Feuerschaden zu. Hält 20 sec lang an."},
[76634] = {name = "Seegang", description = "[q]Fügt Feinden im Umkreis von 60 Metern alle 3 Sek. 18850 to 21150 Schattenschaden zu. Hält 9 sec lang an."},
[76664] = {name = "Heimtückisches Anstürmen", description = "[q]Stürmt auf ein Ziel zu und fügt allen Feinden im Weg der Kriegsmaid 7160 to 8840 körperlichen Schaden zu."},
[76665] = {name = "Kleiner großer Flammenatem", description = "[q]Verursacht im Verlauf von 5 sec 49900 Feuerschaden."},
[76679] = {name = "Entwaffnende Flamme", description = "[q]Die sengende Hitze entwaffnet das Ziel und erhitzt seine Angriffe, sodass Nahkampfangriffe zusätzlichen Feuerschaden verursachen."},
[76685] = {name = "Machterfülltes Zwielicht", description = "[q]Beschwört einen Kreis aus machterfülltem Zwielicht unter dem blutrotgeborenen Kriegsfürsten, der den von ihm verursachten Schaden darin um 50% erhöht."},
[76686] = {name = "Schattengefängnis", description = "[q]Schließt das Ziel in ein Schattengefängnis ein, das ihm bei Bewegung Schaden zufügt."},
[76688] = {name = "Bohren", description = "[q]Verursacht Feuerschaden und verringert die Rüstung des feindlichen Ziels. Kanalisiert."},
[76693] = {name = "Machterfüllendes Zwielicht", description = "[q]Erhöht beim Stehen im machterfüllten Zwielicht den verursachten Schaden um 50%."},
[76703] = {name = "Rüstung zermalmen", description = "[q]Der heftige Angriff fügt einen Schlag zu, der 100% Waffenschaden verursacht und den Rüstungswert 15 sec lang um 20% reduziert. Bis zu 10-mal stapelbar."},
[76711] = {name = "Angekettete Gedanken", description = "[q]Der Zaubernde geht in einen zerbrechlichen Geisteszustand über und kanalisiert seine Kraft auf das Ziel, das 5 sec lang betäubt wird."},
[76715] = {name = "Trügerischer Schlag", description = "[q]Der Schlag verursacht 8287 to 8712 Schattenschaden und 3 sec lang alle 1 Sek. zusätzliche 1950 to 2050 Schattenschaden. Stapelbar."},
[76716] = {name = "Prügelstoß", description = "[q]Ein harter Stoß trifft den Gegner, verursacht 20004 to 27996 körperlichen Schaden und verringert 8 sec lang Angriffs- und Zaubertempo um 60%."},
[76717] = {name = "Meteor", description = "[q]Verursacht 104500 to 115500 Feuerschaden, der unter allen Zielen im Umkreis von 8 Metern um den Einschlagskrater aufgeteilt wird."},
[76718] = {name = "Finale Salve", description = "[q]Verursacht Feuerschaden bei in der Nähe befindlichen Feinden."},
[76719] = {name = "Feuerball", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[76720] = {name = "Wasserblitz", description = "[q]Überzieht einen Feind mit Wasser und verursacht Frostschaden. Verringert Angriffs- und Zaubertempo um 20%. Stapelbar."},
[76721] = {name = "Wasserdurchtränkt", description = "[q]Umgibt den Zaubernden mit einer Wasserblase. Diese Blase absorbiert 75000 Heilungspunkte. Hält 10 sec lang an."},
[76727] = {name = "Tödlicher Stoß", description = "[q]Ein heimtückischer Stoß, der Waffenschaden plus 550 verursacht und das Ziel verwundet, sodass die Wirksamkeit jeglicher Heilung 10 sec lang um 20% verringert wird."},
[76729] = {name = "Schlagwirbel", description = "[q]Ein Schlag des Wirbels verursacht in einem kegelförmigen Bereich von 5 Metern vor dem Zaubernden sofort 9750 to 10250 körperlichen Schaden."},
[76732] = {name = "Kettenfunken", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 3 Ziele aus und verursacht bei jedem Naturschaden."},
[76735] = {name = "Flammenwelle", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Feuerschaden zu."},
[76746] = {name = "Summon the Dreadshredder", description = "[q]Beschwört den Schredderschreck."},
[76748] = {name = "Geflügelte Füße", description = "Geflügelte Füße\nDie Bitte des Geists gibt Euch ein Gefühl der Dringlichkeit. Eure Füße fühlen sich leicht an und durch Eure Beine fließt mystische Energie."},
[76749] = {name = "Summon the Steelspark", description = "[q]Beschwört den Schredderschreck."},
[76764] = {name = "Sägeklinge", description = "[q]Wift eine riesige Verdammnissäge auf Kelsey Stahlfunken."},
[76765] = {name = "Schmelzschlag", description = "[q]Verursacht 15600 to 16400 Feuerschaden."},
[76766] = {name = "Flammenleiter", description = "[q]Der Flammenleiter wurzelt das Ziel fest und fügt seinen Verbündeten im Umkreis von 6 Metern 5 sec lang alle 1 Sek. 11212 to 11787 Feuerschaden zu."},
[76772] = {name = "Schreckenssäge", description = "[q]Fügt nahen Gegnern Schaden zu."},
[76779] = {name = "Felskracher", description = "[q]Der aufgestiegene Steinbrecher springt auf ein zufälliges Ziel, verursacht Schaden in einem Gebiet um das Ziel und ruft eine Felsspalte hervor."},
[76783] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[76790] = {name = "Blasenschild", description = "[q]Schützt den Zaubernden innerhalb einer Blase. Wann immer der Zaubernde Schaden erleidet, erleidet der Verursacher denselben Schaden in Form von Frostschaden. Zusätzlich wird seine Tempowertung 8 sec lang verringert."},
[76792] = {name = "Versteinerte Haut", description = "[q]Die Steinfäuste des aufgestiegenen Seinbrechers haben die Haut des Ziels versteinert, was 110% Waffenschaden verursacht und 15 sec lang 40000 Heilung absorbiert."},
[76794] = {name = "Wasserfontäne", description = "[q]Das Wasser schießt unter dem Ziel empor, verursacht 29250 to 30750 Frostschaden und verlangsamt Zauber- und Angriffstempo 6 sec lang um 50%."},
[76797] = {name = "Konzentrierter Geysir", description = "[q]Der konzentrierte Geysir verursacht 4 sec lang alle 0.3 Sek. 3900 to 4100 Frostschaden in einem 25 Meter großen Kegelförmigen Bereich vor dem Zaubernden."},
[76804] = {name = "Flammenwerfer", description = "[q]Schießt einen Flammenkegel aus dem Schredderschreck."},
[76807] = {name = "Aufschlitzen", description = "[q]Schneidet tief in das Ziel und verursacht so 10 sec lang pro Sekunde 1000 Schaden. Bis ui 5-mal stapelbar."},
[76813] = {name = "Welle der Heilung", description = "[q]Ruft Naturmagie herbei, um einen Verbündeten zu heilen."},
[76815] = {name = "Zorn", description = "[q]Schleudert einen Blitzschlag auf einen Feind und verursacht Naturschaden."},
[76816] = {name = "Tier füttern", description = "[q]Der satte Drache ist viel glücklicher, was seinen Schaden 10 sec lang um 25% erhöht."},
[76817] = {name = "Zwielichtatem", description = "[q]Verursacht in einem kegelförmigen Bereich von 25 Metern vor dem Zaubernden 29250 to 30750 Schattenschaden."},
[76820] = {name = "Verhexen", description = "[q]Transformiert einen Gegner in eine Krabbe, wodurch er 5 sec lang unfähig ist, anzugreifen oder Zauber zu wirken."},
[76822] = {name = "Feuerverzauberung", description = "[q]Angriffe haben eine Chance, einen Feuerschlag auszulösen."},
[76823] = {name = "Eisverzauberung", description = "[q]Angriffe haben eine Chance, einen Eisschlag auszulösen."},
[76825] = {name = "Eisschlag", description = "[q]Verursacht 8775 to 9225 Frostschaden und verringert das Bewegungstempo 5 sec lang um 25%."},
[76826] = {name = "Verwandlung", description = "[q]Verwandelt einen Feind in ein Schaf und zwingt ihn, 5 sec lang ziellos herumzuwandern. Während das Schaf herumwandert, kann es weder angreifen noch Zauber wirken. Jeglicher erlittene Schaden verwandelt das Ziel in seine normale Gestalt zurück. Es kann immer nur ein Ziel verwandelt werden. Wirkt nur bei Wildtieren, Drachen, Risen, Humanoiden und Tieren."},
[76843] = {name = "Gabelblitzschlag", description = "[q]Fügt Gegnern in einem kegelförmigen Bereich vor dem Zaubernden Naturschaden zu."},
[76850] = {name = "Spark Effect", description = "[q]Setzt eine massive elektrische Ladung frei, die Megs Schredderschreck jagt."},
[76852] = {name = "Stahlfunken", description = "[q]Fügt nahen Gegnern Schaden zu."},
[76859] = {name = "Harpunenkanone abfeuern", description = "[q]Zieht den Abzug der rostigen Harpunenkanone."},
[76860] = {name = "Magnetische Abstoßung", description = "[q]Stößt alle metallischen Objekte oder Goblins ab, die an Baunzers Stampferarm kleben."},
[76862] = {name = "Wutanfall", description = "[q]Erhöht until cancelled lang das Angriffstempo des Zaubernden um 30% und den von ihm verursachten körperlichen Schaden um 10%."},
[76870] = {name = "Saat des Chaos", description = "[q]Fügt allen Gegnern im Umkreis von 15 Metern 4440 to 5160 Schattenschaden zu und pflanzt eine Saat des Chaos, durch die Schaden verursachende Fähigkeiten Zielen zusätzlich 18037 to 20962 Schaden zufügen."},
[76881] = {name = "Harpune werfen", description = "[q]Feuert eine Harpune auf das Ziel und verursacht 2844 to 4156 Schaden."},
[76889] = {name = "Gefrierender Strahl", description = "[q]Friert Baunzers Feinde ein und verursacht so 263 to 437 Schaden."},
[76892] = {name = "Schlangensprung", description = "[q]Stürmt einen Feind an."},
