[78178] = {name = "Gefrierender Strahl", description = "[q]Friert Rosies Feinde ein und verursacht so 263 to 437 Schaden."},
[78181] = {name = "Magnetische Abstoßung", description = "[q]Stößt alle metallischen Objekte oder Gnome ab, die an Rosies Stampferarm kleben."},
[78194] = {name = "Magmastrahlen", description = "[q]Schießt einen Strom kleiner Feuerstrahlen auf einen zufälligen Feind. Diese Strahlen verursachen 70687 to 79312 Feuerschaden bei allen Feinden im Umkreis von 3 Metern. Zusätzlich hinterlassen die Strahlen einen bleibenden Feuereffekt, der allen in der Nähe befindlichen Feinden 37700 to 42300 Feuerschaden zufügt."},
[78196] = {name = "Meteor rufen", description = nil},
[78221] = {name = "Prasselnder Flammenatem", description = nil},
[78222] = {name = "Totem der Steinhaut", description = "[q]Beschwört ein Totem der Steinhaut mit 5 Gesundheit zu Füßen des Zaubernden. Das Totem schützt Gruppen- und Schlachtzugsmitglieder in einem Umkreis von 40 Metern, erhöht die Rüstung um 4076 . Hält 5 min lang an."},
[78223] = {name = "Absoluter Nullpunkt", description = "[q]Beschwört in der Nähe eines zufälligen Ziel eine Sphäre aus Energie unter dem Gefrierpunkt. Wenn die Sphäre sich einem Gegner auf 3 Meter nähert, explodiert sie, fügt allen Gegnern innerhalb von 5 Metern 94250 to 105750 Frostschaden zu und stößt sie zurück."},
[78225] = {name = "Säurenova", description = "[q]Fügt allen Gegnern 10 sec lang pro Sekunde 15000 Naturschaden zu."},
[78295] = {name = "Raketenschlag", description = "[q]Ein Schuss, ein Treffer."},
[78296] = {name = "Naga fressen", description = "[q]Lässt einen dominierten großen Hai gierig einen Wächter des Zin'jatar in den Pferchen verschlingen."},
[78297] = {name = "In Sicherheit bringen", description = "[q]Weist den dominierten großen Hai an, Euch zum Eingang der Tiefennebelgrotte zu bringen."},
[78331] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[78371] = {name = "Brocken schleudern", description = "[q]Schleudert einen massiven Felsbrocken in die Ferne. Fügt Feinden in einem Umkreis von 8 Metern um die Explosion 1350 to 1650 Schaden zu."},
[78372] = {name = "Rammen", description = "[q]Rammt alle Feinde, die sich vor dem Verwüster befinden, fügt ihnen 1900 to 2100 Schaden zu und stößt sie zurück."},
[78397] = {name = "Feuerfestes Netz", description = "[q]Macht einen Feind 2 sec lang bewegungsunfähig."},
[78403] = {name = "Geschmolzener Wutanfall", description = "[q]Da niemand in der Nähe ist, den er angreifen könnte, verfällt Magmaul in Raserei und sein verursachter Feuerschaden wird um 100% erhöht. 10-mal stapelbar."},
[78472] = {name = "Flammenatem", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Feuerschaden zu."},
[78491] = {name = "Welpenruf", description = "[q]Stößt einen imitierten Welpenruf aus, der nahe Feinde verwirrt und es ihnen 2 sec lang unmöglich macht, anzugreifen oder Zauber zu wirken."},
[78508] = {name = "Mal der Flammen", description = "[q]Erhöht den von einem Gegner erlittenen Feuerschaden 30 sec lang um 10."},
[78509] = {name = "Fackel werfen", description = "[q]Wirft eine Fackel, verursacht Feuerschaden und verbrennt das Ziel, was zusätzlich 10 sec lang Feuerschaden verursacht."},
[78515] = {name = "Sandvortex", description = nil},
[78521] = {name = "Drachenodem", description = "[q]Ziele in einem Kegel vor dem Zaubernden erleiden Feuerschaden und sind 1.50 sec lang desorientiert. Jeder direkte, Schaden verursachende Angriff belebt Ziele wieder."},
[78530] = {name = "Haut festigen", description = "[q]Verwandelt die Haut des Zaubernden in Stein und erhöht seine Rüstung 10 sec lang um 5000."},
[78535] = {name = "Ruf des Grabes", description = "[q]Fügt in der Nähe befindlichen Feinden nach 15 Sek. 20 Schattenschaden zu."},
[78542] = {name = "Spritzen", description = "[q]Ein Wasserschwall durchnässt alle nahen Gegner, verursacht Frostschaden und wirft sie ein Stück zurück."},
[78578] = {name = "Feuerbrandfalle", description = "[q]Eine Feuerfalle platzieren, die dem ersten sich nähernden Feind im Verlauf von 15 sec Feuerschaden zufügt. Die Falle bleibt 1 min lang bestehen."},
[78619] = {name = "Kinder von Todesschwinge", description = "[q]Die Angriffsgeschwindigkeit von Nefarian und Onyxia wird um 100% erhöht, wenn sie zusammen sind."},
[78620] = {name = "Kinder von Todesschwinge", description = "[q]Die Angriffsgeschwindigkeit von Nefarian und Onyxia wird um 100% erhöht, wenn sie zusammen sind."},
[78621] = {name = "Schattenflammenbeschuss", description = "[q]Fügt einem Gegner 36000 to 44000 Schattenfeuerschaden zu."},
[78622] = {name = "Heldenhafter Sprung", description = "[q]Springt durch die Luft und schmettert auf alle Feinde im Umkreis von 8 Metern um das Zielgebiet hinab. Verursacht körperlichen Schaden."},
[78646] = {name = "Knochenschild", description = "[q]Absorbiert Schaden.\n Nahkampf- oder Distanzangreifer erleiden 13 to 16 Schaden.\n Zaubernde Angreifer erleiden 13 to 16 Schaden."},
[78647] = {name = "Skelett beschwören", description = "[q]Beschwört 1 Skelett als Begleitung für den Zaubernden bis zur Freigabe."},
[78648] = {name = "Grimmiges Totem", description = "[q]Beschwört einen Zauberschutz, der 15 sec lang anhält und regelmäßig Schattenschaden bei nahen Feinden verursacht."},
[78660] = {name = "Verwüsten", description = "[q]Zerreißt die Rüstung des Ziels und verursacht den Effekt 'Rüstung zerreißen'. Zusätzlich werden 50% Waffenschaden verursacht."},
[78674] = {name = "Sternensog", description = "[q]Ihr vereint die Mächte der Sonne und des Mondes und schleudert einen vernichtenden Energiestoß auf das Ziel. Fügt dem Ziel 0 Zaubersturmschaden zu und erzeugt 15 Solar- oder Lunarenergie (der jeweils für Euch nützlichere Energiewert erhält den Bonus)."},
[78704] = {name = "Giftflasche", description = "[q]Verursacht alle 3 Sek. Naturschaden."},
[78710] = {name = "Bedrohlicher Schrei", description = "[q]Erhöht das Nahkampfangriffstempo des Zaubernden und seiner nahen Verbündeten 30 sec lang um 20%."},
[78722] = {name = "Wutanfall", description = "[q]Verringert die Zeit zwischen Euren Angriffen 10 sec lang um 150%."},
[78726] = {name = "Inaktiv", description = "[q]Der zwergische Golem ist inaktiv!"},
[78732] = {name = "Wirbelwasser", description = "[q]Führt sofort einen Wirbelangriff aus, der bis zu 1 nahe Ziele trifft. Die nächsten 6 sec lang werdet Ihr alle 1 Sek. einen eisigen Wirbelangriff durchführen. Während der Effekt 'Wirbelwasser' aktiv ist, könnt Ihr Euch bewegen, aber während dieser Zeit keine anderen Fähigkeiten einsetzen. Ihr fühlt weder Mitleid, noch Bedauern oder Furcht und es gibt keine Möglichkeit, Euch aufzuhalten, es sei denn, Ihr werdet getötet."},
[78740] = {name = "Aktiviert", description = "[q]Der zwergische Golem wurde aktiviert und entzieht pro Sekunde Energie!"},
[78744] = {name = "Blasenstrahl", description = "[q]Fügt einem Feind Frostschaden zu."},
[78746] = {name = "Herunterfahren", description = "[q]Dem Golem ist die Energie ausgegangen, er fährt herunter."},
[78747] = {name = "Hand von Azshara", description = "[q]Manipuliert die Kräfte der Tiefen und zieht das Ziel auf den Zaubernden zu."},
[78751] = {name = "Gedankenschinden", description = "[q]Fügt einem Feind Schattenschaden zu und verringert 3 sec lang sein Bewegungstempo."},
[78754] = {name = "Arkaner Schuss", description = "[q]Schießt auf einen Gegner, fügt Arkanschaden zu."},
[78770] = {name = "Totem des glühenden Magmas", description = "[q]Beschwört ein Magmatotem mit 5 Gesundheit zu Füßen des Zaubernden, das 20 Sek. lang anhält und Kreaturen innerhalb von 8 Metern alle 2 Sek. Feuerschaden zufügt."},
[78799] = {name = "Brandungswelle", description = "[q]Verursacht Frostschaden und stößt das Ziel zurück."},
[78801] = {name = "Wasserblitz", description = "[q]Schleudert ein wässriges Geschoss auf einen Feind und fügt mittleren Frostschaden zu."},
[78802] = {name = "Brechende Welle", description = "[q]Ihr beschwört einen wilden Taifun, der feindlichen Zielen bei Kontakt 508 to 591 Naturschaden zufügt und sie 5 Meter zurückstößt."},
[78807] = {name = "Zertrümmern", description = "[q]Schleudert die Stacheln, die aus Ozruk sprießen, auf Feinde im Umkreis von 15 Metern und verursacht 188500 to 211500 Schaden."},
[78808] = {name = "Sonnenstrahl", description = "[q]Fügt Feinden im Umkreis von 8 Metern um das Ziel Feuerschaden zu."},
[78810] = {name = "Schwelen", description = "[q]Glühende Kohlen verbrennen Angreifer. Hält 10 min lang an."},
[78823] = {name = "Befehlsruf", description = "[q]Erhöht die maximale Gesundheit aller Gruppenmitglieder im Umkreis von 20 Metern um 50. Hält 2 min lang an."},
[78826] = {name = "Entrinnen", description = "[q]Erhöht die Ausweichchance des Zaubernden 6 sec lang um 25%."},
[78827] = {name = "Klingensturm", description = "[q]Greift in einem Wirbel aus Stahl alle Feinde in einem Bereich von 8 Metern an, fügt jedem Feind mit beiden Nahkampfwaffen Waffenschaden zu."},
[78835] = {name = "Elementiumstachelschild", description = "[q]Lässt Stacheln aus Ozruks Körper hervorschießen. Jeder körperliche Angriff während dieses Effekts lässt den Angreifer 5 sec lang pro Sekunde für 0 Schaden bluten. Dieser Effekt ist bis zu 5-mal stapelbar. Hält 10 sec lang an."},
[78841] = {name = "Gezielter Schuss", description = "[q]Ein gezielter Schuss, der 100% Waffenschaden verursacht und erhaltene Heilung des Ziels um 50% verringert. Hält 10 sec lang an."},
[78842] = {name = "Biss des Fleischfressers", description = "[q]Fügt einem Feind 15 sec lang alle 3 Sek. körperlichen Schaden zu."},
[78855] = {name = "Flaggenträger von Sturmwind", description = "Flaggenträger von Sturmwind\nTrägt die Schlachtstandarte der Allianz."},
[78867] = {name = "Überwältigender Gestank", description = "[q]Verringert 10 sec lang die Intelligenz eines Gegners um 100."},
[78894] = {name = "Eingesprungenes Spalten", description = "[q]Springt auf das Ziel zu und fügt einem Gegner sowie seinen zwei nächsten Verbündeten 100% Waffenschaden zu."},
[78895] = {name = "Frosterfüllt", description = "Frosterfüllt\nGewährt Frostfähigkeiten."},
[78896] = {name = "Feuergetränkt", description = "Feuergetränkt\nGewährt Feuerfähigkeiten."},
[78903] = {name = "Erde erschüttern", description = "[q]Bereitet sich auf einen hinterhältigen Bodenstoß vor, der allen Gegnern im Umkreis von 4 Metern um den Einschlagsort 94250 to 105750 Schaden zufügt."},
[78904] = {name = "Verbrannte Erde", description = "[q]Verbrennt das Land."},
[78907] = {name = "Sternenfeuer", description = "[q]Fügt dem Ziel Arkanschaden zu."},
[78921] = {name = "Traumpeitscher", description = "[q]Verursacht 33 to 38 Naturschaden."},
[78939] = {name = "Elementiumbollwerk", description = "[q]Lädt die auf Ozruk montierten Elementiumplatten auf, was ihnen 10 sec lang eine Chance von 100% gibt, Zauber zu reflektieren."},
[78941] = {name = "Parasitäre Infektion", description = "[q]Verursacht alle 2 Sek. 23125 to 26875 Schaden. Infektöse Lavaparasiten haben sich bei Euch eingenistet und vermehren sich im kühlen Bette Eurer Eingeweide."},
[78943] = {name = "Wutanfall", description = "[q]Verringert die Zeit zwischen Euren Angriffen 1 min lang um 50%."},
[78965] = {name = "Zarte Berührung", description = "[q]Ruft heilige Magie herbei, um einen Verbündeten zu heilen."},
[78968] = {name = "Wässrige Verdammnis", description = "[q]Bringt den Zwielichtkultisten und gesichtslosen Aufseher in einem Umkreis von 40 Metern für 18000 to 22000 Schaden den wässrigen Tod."},
[78972] = {name = "Vortex", description = "[q]Erzeugt einen wirbelnden Vortex, der Zwielichtverehrer und gesichtslose Überwacher innerhalb eines Umkreis von 40 Metern um den Zaubernden einsaugt."},
[78986] = {name = "Marls Schild", description = "[q]Schützt Marl Wurmdorn."},
[79002] = {name = "Seismischer Splitter", description = "[q]Reißt ein Stück Fels aus dem Boden und wirft ihn auf die Position eines zufälligen Spielers. Wenn der Fels auf diesen Ort aufprallt, erleiden alle feindlichen oder freundlichen Einheiten in einem Umkreis von 3.5 Metern 942500 to 1057500 Schaden."},
[79010] = {name = "Empfindliche Stelle", description = "[q]Die empfindliche Rückseite von Magmauls Kopf ist freigelegt!\n Schaden, der an dieser Stelle verursacht wird, ist um 100% erhöht.\n Die Gesundheit wird von Kopf und Körper geteilt."},
[79023] = {name = "Sicherheitsvorkehrung: Einäscherung", description = "[q]Die Verteidigungsmaßnahme verursacht 4 sec lang alle 1 Sek. 14625 to 15375 Feuerschaden."},
[79050] = {name = "Energieschild", description = "[q]Fügt allen Gegnern im Umkreis von 5 Metern 47500 to 52500 Arkanschaden zu und stößt sie zurück. Zusätzlich wird der Zaubernde von einem Energieschild umhüllt. Dieser Schild verringert den gesamten erlittenen Schaden um 75% und gestattet es dem Zaubernden, zu fliegen."},
[79058] = {name = "Arkane Brillanz", description = "[q]Erfüllt das Ziel mit Brillanz, welche sein maximales Mana um 2126 und seine Zaubermacht um 6 % erhöht. Hält 1 hour lang an. Wenn sich das Ziel in Eurer Gruppe oder Eurem Schlachtzug befindet, werden alle Mitglieder den Effekt erhalten."},
[79060] = {name = "Mal der Wildnis", description = "[q]Belegt ein befreundetes Ziel mit einem Mal, welches seine Stärke, Beweglichkeit, Ausdauer und Intelligenz um 5% sowie alle Magiewiderstände um [( 85 <= 70 ? 85 / 2 : ( 85 <= 80 ? 85 / 2 + ( 85 - 70) / 2 * 5 : 85 / 2 + ( 85 - 70) / 2 * 5 + ( 85 - 80) / 2 * 7)) - 0.5] erhöht. Hält 1 hour lang an. Wenn sich das Ziel in Eurer Gruppe oder Eurem Schlachtzug befindet, werden alle Mitglieder den Effekt erhalten."},
[79085] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm Naturschaden zu."},
[79086] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 3 Ziele aus und verursacht bei jedem Naturschaden."},
[79094] = {name = "Seelendieb", description = "[q]Entzieht dem Ziel die Seele und verursacht so 5 sec lang alle 1 Sek. Schattenschaden."},
[79109] = {name = "Sandvortex", description = nil},
[79115] = {name = "Uralte Dunkelheit", description = "[q]Schleudert ein dunkles Geschoss auf das Ziel und verursacht Schattenschaden."},
[79116] = {name = "Landstreicherstecken", description = "[q]Was ist in der Tasche?"},
[79129] = {name = "Schrumpferei", description = "[q]Lässt einen Tentakelschrecken massiv schrumpfen. Die Reichweite beträgt 55 Meter."},
[79130] = {name = "Tieffrieren", description = "[q]Verursacht Frostschaden bei in der Nähe befindlichen Feinden und macht sie bis zu 1 min lang bewegungsunfähig."},
[79174] = {name = "Sand sprühen", description = "[q]Fügt Gegnern in einem kegelförmigen Bereich vor dem Zaubernden Schaden zu und erhöht 3 sec lang die Zeit zwischen ihren Angriffen."},
[79175] = {name = "Schlagen & Hacken", description = "[q]Hackt, würfelt und zerkleinert Euren Feind 5 sec lang."},
[79187] = {name = "Mehrfachschattenschuss", description = "[q]Feuert eine Salve von Geschossen auf einen Feind und seine nächsten Verbündeten ab, verursacht Schattenschaden"},
[79203] = {name = "Ausgeräuchert", description = "[q]Marion Wurmschwinge riecht Ärger."},
[79213] = {name = "Essenz verzehren", description = "[q]Verzehrt den nächsten gesichtslosen Aufseher, tötet ihn und überträgt 200% seiner Gesundheit auf den Zaubernden."},
[79222] = {name = "Wässriger Schlag", description = "[q]Bringt Hallazeal und seinen aufgestiegenen Zeloten den wässrigen Tod für 18000 to 22000 Schaden."},
[79223] = {name = "Erschütternder Spritzer", description = "[q]Unterbricht alle Feinde im Umkreis von 30 Metern um Hallazeal."},
[79232] = {name = "Hymne von Sira'kess", description = "[q]Erhöht das Angriffs- und Zaubertempo des Zaubernden um 25% und den von ihm verursachten Schaden um 25%."},
[79318] = {name = "Herrschaft", description = "[q]Beherrscht die Gedanken mehrerer naher Humanoide und übernimmt so 20 sec lang Kontrolle über ihre Körper."},
[79330] = {name = "Ermächtigen", description = "[q]Der Zaubernde wird mit der Zeit immer mächtiger, wodurch sich sein verursachter Schaden und sein Bewegungstempo erhöhen. Stapelbar."},
[79339] = {name = "Explodierende Asche", description = "[q]Verbrennt zufällig mehrere Feinde und überzieht sie mit explosiven Rückständen, die regelmäßig Feuerschaden verursachen. Die Rückstände detonieren nach Ablauf von 8 sec und verursachen 42750 to 47250 Feuerschaden in einem kleinem Bereich."},
[79340] = {name = "Summon Gravity Well", description = "[q]Beschwört ein Gebiet mit erdrückender Schwerkraft. Jede freundliche oder feindliche Einheit innerhalb von 10 Metern wird ins Zentrum gesogen. Dieser Zauber verursacht bleibenden Schaden bei allen Gegnern innerhalb des Gebiets, und der Schaden vergrößert sich, je näher das Ziel dem Zentrum kommt. Hält 20 Sek. lang an."},
[79345] = {name = "Blutfluch", description = "[q]Erhöht den erlittenen körperlichen Schaden des Ziels 15 sec lang um 5000."},
[79351] = {name = "Machtgriff", description = "[q]Fängt das Ziel in einem Energiefeld ein, hebt es in die Luft und rammt es in den Boden. Jedes Mal, wenn das Ziel in den Boden gerammt wird, erleidet es 70687 to 79312 Schaden. Dieser Schaden wird nicht durch Rüstung verringert. Hält 5 sec an."},
[79353] = {name = "Schatten der Feigheit", description = "[q]Fügt einem Gegner 30000 Schattenfeuerschaden zu und erhöht seinen erlittenen Schattenschaden um 100%."},
[79356] = {name = "Schlitternder Schleim", description = "[q]Überzieht das Ziel mit Schleim, wodurch es sich im Wasser schneller bewegen kann, aber pro Sekunde Naturschaden erleidet."},
[79379] = {name = "Kanonenkugelschlag", description = "[q]Verursacht 300% Waffenschaden."},
[79384] = {name = "Meeresschmettern", description = "[q]Wirft sich nach vorn, verursacht 300% Waffenschaden und mäht sich durch in der Nähe befindliche Feinde."},
[79400] = {name = "Laugenschuppengift", description = "[q]Verringert Stärke und intelligenz um 700."},
[79403] = {name = "Schleichen", description = "[q]Versetzt den Zauberer in Verstohlenheitsmodus, aber verlangsamt sein Bewegungstempo um 50%.Hält an, bis die Aktion abgebrochen wird."},
[79411] = {name = "Faulwasser", description = "[q]Verdirbt das Wasser um das Ziel herum und verursacht 15 sec lang pro Sekunde Schattenschaden."},
[79413] = {name = "Geheimes Scheinen", description = "[q]Fügt einem Gegner körperlichen Schaden zu."},
[79414] = {name = "Brüchige Berührung", description = "[q]Keine Rüstung."},
[79417] = {name = "Toxischer Biss", description = "[q]Verursacht 10 sec lang pro Sekunde Naturschaden."},
[79420] = {name = "Raserei", description = "[q]Steigert sich in eine Schwimmraserei hinein und flitzt um das Ziel herum."},
[79425] = {name = "Sensenschläge", description = "[q]Hackt, würfelt und zerkleinert Euren Feind 5 sec lang."},
[79429] = {name = "Totem des Elementarwiderstands", description = "[q]Beschwört ein Totem des Elementarwiderstands."},
[79430] = {name = "Raketennachbrenner", description = "[q]Feuert die Booster des überladenen Erntegolems. Erhöht das Bewegungstempo 5 sec lang um 100% und walzt alles nieder, was seinen Weg kreuzt."},
[79431] = {name = "Totem der Flammenzunge", description = "[q]Beschwört ein Totem des Elementarwiderstands."},
[79441] = {name = "Sandvortex", description = nil},
[79442] = {name = "Schnappkiefer", description = "[q]Ein schwerer Angriff, der 300% Schaden verursacht."},
[79443] = {name = "Sandschritt", description = "[q]Wirbelt den Sand um den Zaubernden herum auf und lässt ihn 1 sec lang unsichtbar werden."},
[79444] = {name = "Pfählen", description = "[q]Wirft einen Speer nach einem Gegner, der 188 to 211% Schaden sowie 9 sec lang alle 3 Sekunden weiteren Sschaden verursacht."},
[79449] = {name = "Bodycheck", description = "[q]Rammt den Körper gegen das Ziel, verursacht 15% der maximalen Gesundheit als Schaden und bewegt sowohl den Zaubernden als auch das Ziel über kurze Distanz."},
[79467] = {name = "Umbralheilung", description = "[q]Heilt das Ziel um 40% seiner gesamten Gesundheit."},
[79479] = {name = "Schnitzerstoß", description = "[q]Fügt allen Feinden in Nahkampfreichweite 1000% Waffenschaden zu."},
[79482] = {name = "Beeinträchtigt", description = "[q]Das unglaubliche Gewicht seiner Waffe hat sein Bewegungstempo um 50% verringert."},
[79487] = {name = "Sausendes Rad", description = "[q]Schleudert Euren Schild auf den Feind, verursacht Schaden und betäubt den ihn 1 sec lang."},
[79490] = {name = "Krankheitsübertragender Biss", description = "[q]Erhöht die Zeit zwischen Angriffen eines Feindes um 54% und verlangsamt sein Bewegungstempo um 25%. Hält 10 sec lang an."},
[79501] = {name = "Zielerfassung", description = "[q]Erfasse Ziel. Nach 4 sec wird Magmatron beginnen, seinen Flammenwerfer auf das Ziel zu kanalisieren, wobei 4 sec lang allen Zielen vor ihm alle 1 Sek. 63375 to 66625 Feuerschaden zugefügt wird."},
[79503] = {name = "Schlaghagel", description = "[q]Verringert den verursachten körperlichen Schaden des Zaubernden um 50%. Erhöht das Nahkampfangriffstempo um 200%."},
[79505] = {name = "Flammenwerfer", description = "[q]Der Flammenwerfer hat ein Ziel erfasst!"},
[79507] = {name = "Macht der Kvaldir", description = "[q]Erhöht 15 sec lang den vom Zaubernden verursachten körperlichen Schaden."},
[79508] = {name = "Sandsturm", description = "[q]Fügt nahen Gegnern Naturschaden zu und stößt sie zurück."},
[79510] = {name = "Sandsturm", description = "[q]Erschafft unter einem feindlichen Ziel einen Sandsturm."},
[79515] = {name = "Eingraben", description = "[q]Verschwindet im sandigen Boden des Ozeans."},
[79516] = {name = "Eruption", description = "[q]Springt aus dem Sand heraus, fügt nahen Feinden Schaden zu und stößt sie zurück."},
[79537] = {name = "Würgendes Seil", description = "[q]Würgt das Ziel mit einem Seil und verursacht 6 sec lang alle 2 Sek. Schaden in Höhe von 10% der maximalen Gesundheit des Ziels."},
[79539] = {name = "Brechende Welle", description = "[q]Ihr beschwört eine wilde Welle, die feindlichen Zielen bei Kontakt Frostschaden zufügt und sie 5 Meter zurückstößt."},
[79542] = {name = "Hypnotisieren", description = "[q]Hypnotisiert das Ziel, betäubt es 4 sec lang und lässt es tanzen."},
[79544] = {name = "Psionische Überladung", description = "[q]Beschießt das Ziel mit einem Strahl mentaler Energie und verursacht Schattenschaden."},
[79546] = {name = "Ankerangriff!", description = "[q]Schleudert ein Ankerseil nach einem Feind und fixiert ihn so eine kurze Zeit am Meeresboden."},
[79554] = {name = "Fischklatscher", description = "[q]Schlägt das Ziel mit Eurem Fisch und verursacht 110% Waffenschaden."},
[79556] = {name = "Fischwurf", description = "[q]Wirft Euren Fisch nach dem Ziel und verursacht 200% Waffenschaden."},
[79558] = {name = "Bioluminiszenz", description = "[q]Lässt eine Druckwelle aus Magie entstehen und fügt in der Nähe befindlichen Feinden Arkanschaden zu."},
[79559] = {name = "Luxschuppenlicht", description = "[q]Verringert 1 min lang die Ausweichchance eines Feindes um 20% und seine Beweglichkeit um 10%. Während dieses Effekts kann das Ziel weder 'Verstohlenheit' noch 'Unsichtbarkeit' anwenden."},
[79560] = {name = "Zerschmetternder Biss", description = "[q]Beißt die Rüstung eines Feindes und verringert sie mittels 'Rüstung zerreißen'. Bis zu 5-mal stapelbar. Hält 20 sec lang an."},
[79561] = {name = "Blutwahnsinn", description = "[q]Der Geschmack nach Blut macht den Zaubernden rasend und erhöht den verursachten Schaden 30 sec lang um 5%."},
[79563] = {name = "Eisrüstung", description = "[q]Erhöht die Rüstung um 2000 und den Frostwiderstand um 50. Wenn ein Feind den Zaubernden schlägt, kann 5 sec lang sein Bewegungstempo um 20% verlangsamt und die Zeit zwischen seinen Angriffen um 30% erhöht werden. Es kann immer nur ein Rüstungszauber zur selben Zeit auf dem Magier aktiv sein. Hält 30 min lang an."},
[79564] = {name = "Zwielichtfeuerball", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[79565] = {name = "Zwielichtschockwelle", description = "[q]Entfesselt eine Flammenwelle, die nahen Feinden Feuerschaden zufügt und ihr Bewegungstempo 6 sec lang verringert."},
[79566] = {name = "Wasserstrahl", description = "[q]Versprüht Wasser in alle Richtungen, fügt in der Nähe befindlichen Feinden Frostschaden zu und stößt sie zurück."},
[79571] = {name = "Kältekegel", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Frostschaden zu und verlangsamt ihr Bewegungstempo 4 sec lang."},
[79580] = {name = "Überkopfkracher", description = "[q]Fügt allen Feinden in einem kegelförmigen Bereich von 20 Metern vor dem Zaubernden 8750 to 11250 Schaden zu und stößt sie zurück."},
[79581] = {name = "Verärgertes Schnauben", description = "[q]Ein verärgertes Schnaufen verursacht Naturschaden und stößt das Ziel eine kurze Distanz zurück."},
[79582] = {name = "Barriere", description = "[q]Absorbiert 450000 Schaden. Wenn die Barriere bricht, wird ein Flammensturm entfesselt, der allen Feinden 151125 to 158875 Feuerschaden zufügt."},
[79583] = {name = "Pfählender Zug", description = "[q]Schleudert Euren Dreizack nach einem Ziel und zieht es zu Euch zurück. Verursacht 24 to 29 körperlichen Schaden und verspottet das Ziel."},
[79584] = {name = "Dolchfächer", description = "[q]Schleudert Dolche in alle richtungen und fügt so nahen Feinden körperlichen Schaden zu."},
[79589] = {name = "Fesselnde Ketten", description = "[q]Umschlingt das Ziel mit Ketten, verursacht alle 1 Sek. 18500 to 21500 körperlichen Schaden und betäubt das Ziel 5 sec lang."},
[79604] = {name = "Donnerknall", description = "[q]Fügt allen Feinden im Umkreis von 15 Metern 63750 to 86250 Naturschaden zu und verlangsamt das Angriffstempo um 20%."},
[79607] = {name = "Giftspritzer", description = "[q]Fügt allen Gegnern im Bereich des Gifts Naturschaden zu. Hält 10 sec lang an."},
[79610] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[79615] = {name = "Verschwinden", description = nil},
[79616] = {name = "Blutsauger", description = "[q]Entzieht einem Gegner im Verlauf von 5 sec Gesundheit und überträgt sie auf den Zaubernden."},
[79618] = {name = "Van Cleefs Verbündete", description = nil},
[79624] = {name = "Energiegenerator", description = "[q]Beschwört unter einem zufälligen befreundeten Ziel einen Energiegenerator herbei. Dieser Generator erhöht den Schaden aller befreundeten und feindlichen Ziele im Umkreis von 5 Metern um 50%. Hält 1 min lang an."},
[79628] = {name = "Energiegenerator", description = "[q]Beschwört unter einem zufälligen befreundeten Ziel einen Energiegenerator herbei. Dieser Generator erhöht den Schaden aller befreundeten und feindlichen Ziele im Umkreis von 5 Metern um 50%. Hält 1 min lang an."},
[79630] = {name = "Drakonidenansturm", description = "[q]Stürmt auf den am weitesten entfernten Gegner innerhalb von 80 Metern zu und betäubt ihn 5 sec lang."},
[79673] = {name = "Zone der Leere", description = "[q]Eine Zone der Leere beschwören, die Schattenschaden an Feinden verursacht, die sich in ihr befinden."},
[79675] = {name = "Mentaler Kampf", description = "[q]Zwingt das Ziel, sich selbst zu bekämpfen."},
[79687] = {name = "Zerreißendes Spalten", description = "[q]Fügt einem Gegner und seinen nächsten Verbündeten zusätzlich zum normalen Schaden noch 5 Zusatzschaden zu. Wirkt auf maximal 3 Ziele.Treffer reduzieren außerdem die Rüstung um 50."},
[79710] = {name = "Arkaner Annihilator", description = "[q]Fügt einem zufälligen feindlichen Ziel 78000 to 82000 Arkanschaden zu."},
[79719] = {name = "Markieren", description = "[q]Markiert die Position des Ziels. Erhöht die Distanzangriffskraft aller Angreifer gegen das Ziel um 20. Zudem kann das Ziel dieser Fähigkeit stets vom Scharfschützen gesehen werden, unabhängig davon, ob es Verstohlenheit oder Unsichtbarkeit anwendet. Hält 30 sec lang an."},
[79721] = {name = "Explosivschuss", description = "[q]Fügt einem Feind und jedem seiner in der Nähe befindlichen Verbündeten Waffenschaden und zusätzlich Feuerschaden zu."},
[79726] = {name = "Tickende Zeitbombe", description = "[q]Explodiert nach einigen Sekunde und verursacht Feuerschaden. Tick-tack."},
[79728] = {name = "Explosion", description = "[q]Verursacht Feuerschaden bei in der Nähe befindlichen Feinden."},
[79729] = {name = "Energieumwandlung", description = "[q]Machtumwandlung konvertiert Schaden gegen das Ziel in zusätzlichen Zauberschaden und Zaubertempo."},
[79732] = {name = "Schildhieb", description = "[q]Schlägt mit dem Schild des Zaubernden auf einen Feind ein, verursacht 10% Waffenschaden und stößt das Ziel zurück."},
[79733] = {name = "Befehlsruf", description = "[q]Erhöht die maximale Gesundheit aller Gruppenmitglieder im Umkreis von 20 Metern um 20. Hält 2 min lang an."},
[79735] = {name = "Konvertierte Energie", description = "[q]Die konvertierte Kraft erhöht den verursachten Zauberschaden um 10% und erhöht das Zaubertempo 30 sec lang um 10%. Stapelbar."},
[79765] = {name = "Blasenschlag", description = "[q]Verschießt eine Reihe Blasen, die einem Feind Frostschaden zufügen."},
[79782] = {name = "Kugel der Verderbnis", description = "[q]Fügt einem Feind Schattenschaden zu und verringert 5 sec lang sein Bewegungstempo."},
[79797] = {name = "Gewitter", description = "[q]Ruft einen BLitz hernieder, der allen Feinden im Umkreis von 15 Metern 3237 to 3762 Naturschaden zufügt und sie zurückstößt."},
[79810] = {name = "Frostspalten", description = "[q]Verursacht 110% Waffenschaden und verringert das Angriffs- und Zaubertempo eines Gegners sowie seiner nahen Verbündeten um 10%. Trifft bis zu 3 Ziele."},
[79811] = {name = "Dispersion", description = "[q]Löst Euch zu reiner Schattenenergie auf und verringert jeglichen erlittenen Schaden um 90%. Ihr könnt weder angreifen noch Zauber wirken, regeneriert jedoch 6 sec lang pro Sekunde 6% Mana. 'Dispersion' kann gewirkt werden, während Betäubungs-, Furcht- oder Stilleeffekte auf Euch wirken. Bei Anwendung werden alle wirkenden Verlangsamungs- und bewegungseinschränkenden Effekte entfernt und Ihr werdet für die Dauer des Effektes von 'Dispersion' gegen sie immun."},
[79821] = {name = "Gedankensicht von Zul'Gurub", description = "[q]Ermöglicht es Euch, durch die Augen des gefangenen Schmetterschwanzjungen zu sehen."},
[79822] = {name = "Heilige Nova", description = "[q]Verursacht eine Explosion heiligen Lichts um den Zaubernden herum und fügt allen Zielen in einem Umkreis von 10 Metern 24 to 29 Heiligschaden zu. Zusätzlich werden alle Gruppenmitglieder in einem Umkreis von 10 Metern um 360 to 440 geheilt. Dieser Effekt erzeugt keine Bedrohung."},
[79823] = {name = "Sternenregen", description = "[q]Fügt Gegnern in einem gewählten Gebiet 10 sec lang alle 2 Sek. Arkanschaden zu."},
[79824] = {name = "Treantverbündeten beschwören", description = "[q]Beschwört 1 Treantverbündeten als Begleitung für den Zaubernden bis zur Freigabe."},
[79825] = {name = "Faulenden Schwamm beschwören", description = "[q]Beschwört einen faulenden Schwamm."},
[79826] = {name = "Instabile Wolke", description = "[q]Verursacht im Verlauf von 15 sec Naturschaden.\n Bewegungstempo um 25% verringert."},
[79828] = {name = "Zerfleischen", description = "[q]Attackiert das Ziel mit Krallen, fügt 24 to 29 Schaden sowie 20 zusätzlichen Schaden im Verlauf von 15 sec zu."},
[79831] = {name = "Brandungswelle", description = "[q]Verursacht Frostschaden."},
[79833] = {name = "Mal der Wildnis", description = "[q]Erhöht die Stärke, Beweglichkeit, Ausdauer und Intelligenz eines befreundeten Ziels 1 hour lang um 5%. Wenn sich das Ziel in Eurer Gruppe oder Eurem Schlachtzug befindet, werden alle Mitglieder den Effekt erhalten."},
[79835] = {name = "Giftgetränkter Panzer", description = "[q]Ruft bei Nahkampf- und Zauberangriffen den Effekt 'Giftgetränkt' hervor, der alle 2 Sek. 20000 Naturschaden verursacht. Stapelbar."},
[79840] = {name = "Verhärten", description = "[q]Verstärkt den Chitinpanzer des Zaubernden. Verringert 20 sec lang jeglichen erlittenen Schaden um 5%."},
[79846] = {name = "Flammenstoß", description = "[q]Lässt eine Flammensäule niedergehen, die alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen 8 sec lang alle 2 Sek. zusätzlichen Schaden zufügt."},
[79847] = {name = "Druckwelle", description = "[q]Entfesselt eine Flammenwelle, die nahen Feinden Feuerschaden zufügt und ihr Bewegungstempo 6 sec lang verringert."},
[79848] = {name = "Glühende Rüstung", description = "[q]Verursacht Feuerschaden."},
[79849] = {name = "Glühende Rüstung", description = "[q]Verursacht Feuerschaden, wenn getroffen, erhöht Eure kritische Zaubertrefferchance um 3% und verringert die Chance, kritische Treffer zu erleiden, um 5%. Es kann immer nur eine Art Rüstungszauber gleichzeitig aktiv sein. Hält 10 min lang an."},
[79850] = {name = "Frostnova", description = "[q]Verursacht Frostschaden bei in der Nähe befindlichen Feinden und macht sie bis zu 4 sec lang bewegungsunfähig."},
[79851] = {name = "Dolchfächer", description = "[q]Wirft sofort Dolche nach allen nahen Feinden und verursacht 100% Waffenschaden."},
[79852] = {name = "Ausweiden", description = "[q]Finishing-Move, der Schaden verursacht."},
[79853] = {name = "Entrinnen", description = "[q]Erhöht die Ausweichchance des Zaubernden um 35%."},
[79854] = {name = "Feuerball", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[79855] = {name = "Feuerschlag", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[79856] = {name = "Flammenstoß", description = "[q]Lässt eine Flammensäule niedergehen, die alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen 8 sec lang alle 2 Sek. zusätzlichen Schaden zufügt."},
[79857] = {name = "Druckwelle", description = "[q]Entfesselt eine Flammenwelle, die nahen Feinden Feuerschaden zufügt und ihr Bewegungstempo 6 sec lang verringert."},
[79858] = {name = "Frostblitz", description = "[q]Fügt einem Feind Frostschaden zu und verringert 4 sec lang sein Bewegungstempo."},
[79859] = {name = "Eislanze", description = "[q]Fügt einem feindlichen Ziel Frostschaden zu. Eingefrorene Ziele erleiden den doppelten Schaden."},
[79860] = {name = "Blizzard", description = "[q]Ruft einen Blizzard herbei, der 8 sec lang anhält und Feinden im ausgewählten Gebiet alle 2 Sek. Frostschaden zufügt."},
[79862] = {name = "Meucheln", description = "[q]Fügt einem Gegner 150% Schaden zu, aber nur, wenn von hinten angegriffen wird."},
[79863] = {name = "Blutsturz", description = "[q]Verursacht Waffenschaden und erhöht den erlittenen körperlichen Schaden eines Gegners 10 sec lang um 20%."},
[79864] = {name = "Schattenschritt", description = "[q]Versucht, in die Schatten zu tauchen, um hinter dem Gegner zu erscheinen."},
[79865] = {name = "Frostrüstung", description = "[q]Umgibt den Zaubernden mit einer Frostschicht, die 30 min lang bestehen bleibt. Das dicke Eis erhöht die Rüstung, verlangsamt das Bewegungstempo von Nahkampfangreifern und erhöht die Zeit zwischen ihren Angriffen."},
[79866] = {name = "Tödlicher Wurf", description = "[q]Wirft einen Dolch auf ein gegnerisches Ziel und verursacht 24 to 29% Waffenschaden. Unterbricht außerdem das Wirken von Zaubern und verhindert 3 sec lang, dass diese Art Zauber gewirkt werden kann."},
[79868] = {name = "Arkanschlag", description = "[q]Überzieht einen Feind mit Energie und verursacht Arkanschaden. Mit jedem Wirken von 'Arkanschlag' werden der verursachte Schaden aller Arkanzauber um 10% und die Manakosten von 'Arkanschlag' um 150% erhöht. Dieser Effekt ist bis zu 4-mal stapelbar und hält 6 sec lang an oder bis ein Arkanzauber außer 'Arkanschlag' gewirkt wurde."},
[79870] = {name = "Wilde Attacke", description = "[q]Stürmt auf einen Gegner zu, macht ihn unbeweglich und unterbricht gewirkte Zauber. Hält until cancelled lang an."},
[79871] = {name = "Tollkühnheit", description = "[q]Immun gegen Furchteffekte.\n Alle Angriffe treffen kritisch.\n Rüstung um 35% verringert."},
[79872] = {name = "Schockwelle", description = "[q]Schickt dem Krieger eine Energiewelle voraus, verursacht Schaden und betäubt 4 sec lang alle Einheiten innerhalb eines kegelförmigen Bereichs von 10 Metern."},
[79875] = {name = "Ongolongoschmettern!", description = "[q]Stößt das Ziel zurück."},
[79877] = {name = "Blutdurst", description = "[q]Greift das Ziel sofort an und verursacht 120% normalen Waffenschaden. Zudem stellen erfolgreiche Nahkampfangriffe 1% der maximalen Gesundheit wieder her. Der Effekt hält 10 sec lang an."},
[79878] = {name = "Blutdurst", description = "[q]Greift das Ziel sofort an und verursacht 120% normalen Waffenschaden. Zudem stellen erfolgreiche Nahkampfangriffe 1% der maximalen Gesundheit wieder her. Der Effekt hält 10 sec lang an."},
[79879] = {name = "Elektrische Entladung", description = "[q]Fügt bis zu 3 Gegnern im Umkreis von 8 Metern 43875 to 46125 Naturschaden zu."},
[79880] = {name = "Verlangsamen", description = "[q]Erhöht die Zeit zwischen den Angriffen eines Gegners um 25%, verlangsamt sein Zaubertempo um 25% und sein Bewegungstempo um 25%. Hält 6 sec lang an."},
[79881] = {name = "Zerschmettern", description = "[q]Zerschmettert den Gegner und verursacht Waffenschaden plus zusätzlichen Schaden."},
[79883] = {name = "Zerschmetternder Wurf", description = "[q]Wirft Eure Waffe nach dem Feind. Verursacht Waffenschaden verringert die Rüstung des Ziels 10 sec lang um 20%."},
[79884] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm Naturschaden zu."},
[79885] = {name = "Herzstoß", description = "[q]Schlägt das Ziel und seinen nächsten Verbündeten sofort. Fügt dem Primärziel 110% Waffenschaden und dem Sekundärziel 55% Waffenschaden zu."},
[79886] = {name = "Lavaeruption", description = "[q]Ihr schleudert flüssige Lava auf das Ziel, die Feuerschaden verursacht."},
[79887] = {name = "Tod und Verfall", description = "[q]Fügt allen Zielen im betroffenen Gebiet 10 sec lang alle 1 Sek. Schattenschaden zu."},
[79888] = {name = "Blitzableiter", description = "[q]Infiziert einen Feind 15 sec lang, wodurch an allen in der Nähe befindlichen Verbündeten alle 2 Sek. Naturschaden verursacht wird."},
[79890] = {name = "Flammenschock", description = "[q]Verbrennt sofort einen Feind und fügt ihm dann 12 sec lang alle 3 Sek. zusätzlichen Feuerschaden zu."},
[79891] = {name = "Horn des Winters", description = "[q]Der Todesritter bläst das Horn des Winters und erhält 10 Runenmacht. Stärke und Beweglichkeit werden um 5 erhöht. Hält 2 min lang an."},
[79892] = {name = "Wasserschild", description = "[q]Der Zaubernde wird von 3 Wasserkugeln umgeben. Gegen den Zaubernden gerichtete Angriffe oder Zauber verursachen Frostschaden und verlangsamen das Zaubertempo des Angreifers. 3 Aufladungen. Hält 10 min lang an."},
[79893] = {name = "Blutwurm", description = "[q]Erschafft einen Blutwurm, der nahe Ziele angreift. Hält 8 sec lang an."},
[79894] = {name = "Todesgriff", description = "[q]Nutzt die Mächte unheiliger Energien, die alle Materie umgibt und bindet, um das Ziel zum Todesritter hinzuziehen und es zu zwingen, ihn 3 sec lang anzugreifen."},
[79895] = {name = "Froststoß", description = "[q]Schlägt das Ziel sofort und verursacht 110% Waffenschaden als Frostschaden."},
[79896] = {name = "Heulende Böe", description = "[q]Weht dem Ziel eine eisige Böe entgegen, die allen Zielen innerhalb von 10 Metern Frostschaden zufügt. Gefrorene Ziele erleiden dreifachen Schaden."},
[79897] = {name = "Eisige Berührung", description = "[q]Verursacht Frostschaden und verringert 8 sec lang das Nahkampf-, Distanzangriffs- und Zaubertempo des Ziels um 15%."},
[79899] = {name = "Eisketten", description = "[q]Umschlingt das Ziel mit eisigen Ketten und verringert sein Bewegungstempo auf 5% des Normalwerts. Das Ziel erhält im Verlauf von 10 sec pro Sekunde 10% seines Bewegungstempos zurück."},
[79900] = {name = "Instabiler Schild", description = "[q]Angriffe auf den instabilen Schild verursachen einen statischen Schock am Standort eines nahen Feindes. Der statische Schock fügt Feinden im Umkreis von 6 Metern um das Ziel 58500 to 61500 Naturschaden zu."},
[79901] = {name = "Blutseuche", description = "[q]Ein bösartiger Schlag, der Waffenschaden verursacht, das Ziel verseucht und ihm so im Verlauf von 12 sec Schattenschaden zufügt."},
[79902] = {name = "Seuchenstoß", description = "[q]Ein bösartiger Schlag, der Waffenschaden verursacht, das Ziel verseucht und im Verlauf von 12 sec 8000 Schattenschaden hervorruft."},
[79903] = {name = "Todesmantel", description = "[q]Fügt einem zufälligen nahen Ziel 24 to 29 Schattenschaden zu."},
[79904] = {name = "Sturmangriff", description = "[q]Stürmt auf einen Gegner zu, fügt ihm sowie seinen nahen Verbündeten 13423 to 15600 Schaden zu und stößt alle betroffenen Ziele zurück."},
[79905] = {name = "Totenerweckung", description = "[q]Erweckt einen Ghul, der an Eurer Seite kämpft. Steht kein humanoider Leichnam zur Verfügung, der Erfahrung oder Ehre gewährt, wird Leichenstaub benötigt, um den Zauber zu wirken. Ihr könnt nur maximal einen Ghul gleichzeitig beschwören. Hält 1 min lang"},
[79907] = {name = "Antimagisches Feld", description = "[q]Platziert ein großes, ortsgebundenes, antimagisches Feld, das den erlittenen Zauberschaden aller darin befindlichen Gruppen- oder Schlachtzugsmitglieder um 75% verringert. Das antimagische Feld hält 10 sec lang an oder bis es (10000 + 2 * Attack power) Zauberschaden absorbiert hat."},
[79909] = {name = "Geißelstoß", description = "[q]Ein unheiliger Stoß, der 150% Waffenschaden in Form von Schattenschaden verursacht."},
[79913] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 3 Ziele aus und verursacht bei jedem folgenden Ziel immer geringeren Naturschaden."},
[79914] = {name = "Wildgeist", description = "[q]Beschwört zwei Geisterwölfe, die unter dem Befehl des Schamanen stehen. Hält 45 sec lang an."},
[79921] = {name = "Schattenflamme", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden 23125 to 26875 Feuerschaden zu."},
[79922] = {name = "Schinden", description = "[q]Fügt allen Feinden im Umkreis von 4 Metern alle 0,25 Sek. 60% Waffenschaden zu. Hält 5 sec lang an."},
[79924] = {name = "Welle der Heilung", description = "[q]Ruft Naturmagie herbei, um einen Verbündeten zu heilen."},
[79925] = {name = "Frostschock", description = "[q]Fügt einem Feind Frostschaden zu und verringert 6 sec lang sein Bewegungstempo."},
[79927] = {name = "Erdschild", description = "[q]Schützt das Ziel mit einem Erdschild. Nahkampfangriffe heilen das geschützte Ziel, dieser Effekt tritt aber nur einmal alle paar Sekunden auf. 10 Aufladungen. Hält 10 min lang an."},
[79928] = {name = "Erdschild", description = "[q]Schützt das Ziel mit einem Erdschild. Nahkampfangriffe heilen das geschützte Ziel, dieser Effekt tritt aber nur einmal alle paar Sekunden auf."},
[79930] = {name = "Verderbnis", description = "[q]Verdirbt in der Nähe befindliche Feinde und fügt ihnen 15 sec lang alle 3 Sek. Schattenschaden zu."},
[79932] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[79933] = {name = "Blutsauger", description = "[q]Entzieht einem Gegner im Verlauf von 5 sec Gesundheit und überträgt sie auf den Zaubernden."},
[79934] = {name = "Dämonenrüstung", description = "[q]Beschützt den Zaubernden, erhöht seine Rüstung sowie die Menge an wiederhergestellter Gesundheit durch Zauber und Effekte um 20%. Es kann immer nur ein Rüstungszauber gleichzeitig aktiv sein. Hält 30 min lang an."},
[79937] = {name = "Feuerbrand", description = "[q]Verbrennt sofort einen Feind und fügt ihm dann 15 sec lang alle 3 Sek. zusätzlichen Feuerschaden zu."},
[79938] = {name = "Verbrennen", description = "[q]Fügt Eurem Ziel Feuerschaden zu."},
[79939] = {name = "Chaosblitz", description = "[q]Schleudert einen Blitz chaotischen Feuers auf den Gegner und verursacht Feuerschaden. Dem Chaosblitz kann nicht widerstanden werden und er durchbricht defensive Immunitäten."},
[79949] = {name = "Wasserschild", description = "[q]Der Zaubernde wird von 3 Wasserkugeln umgeben, die alle 5 Sek. 10 Mana gewähren. Trifft ein Zauber, ein Nahkampf- oder Distanzangriff den Zaubernden, erhält er 758 Mana und eine Wasserkugel wird aufgelöst. Alle paar Sekunden kann nur eine Wasserkugel aktiviert werden. Hält 10 min lang an. Es kann immer nur ein Elementarschild auf dem Schamanen aktiv sein."},
[79954] = {name = "Teufelsrüstung", description = "[q]Umgibt Euch mit Teufelsenergie, welche Eure Zaubermacht um 10% erhöht. Zusätzlich regeneriert Ihr alle 5 Sek. 2% Eurer gesamten Gesundheit. Es kann immer nur eine Art Rüstungszauber gleichzeitig aktiv sein. Hält 30 min lang an."},
[79955] = {name = "Feuerregen", description = "[q]Lässt einen Schmelzregen niedergehen, der alle Feinde im ausgewählten Gebiet verbrennt und ihnen 9 sec lang alle 3 Sek. Feuerschaden zufügt."},
[79956] = {name = "Fluch der Elemente", description = "[q]Verflucht das Ziel 30 sec lang und verringert so seinen Widerstand gegen Arkan-, Feuer-, Frost-, Natur- und Schattenschaden um 45, während der erlittene Magieschaden um 8% erhöht wird. Es kann immer nur jeweils ein Fluch pro Zauberndem auf einem beliebigen Ziel aktiv sein."},
[79960] = {name = "Heiliges Licht", description = "[q]Ruft heilige Magie herbei, um einen Verbündeten zu heilen."},
[79961] = {name = "Heiliger Schock", description = "[q]Überzieht das Ziel mit heiliger Energie und fügt einem Feind Heiligschaden zu."},
[79962] = {name = "Siegel der Rechtschaffenheit", description = "[q]Erfüllt den Zaubernden 30 min lang mit heiligem Geist, durch den jeder Einzelziel-Nahkampfangriff zusätzlichen Heiligschaden verursacht."},
[79963] = {name = "Aura der Konzentration", description = "[q]Verbündete im Umkreis von 40 Metern verlieren bei erlittenem Schaden 35% weniger Zauber- und Kanalisierungszeit."},
[79964] = {name = "Exorzismus", description = "[q]Fügt einem Gegner Heiligschaden zu."},
[79965] = {name = "Schild des Rächers", description = "[q]Schleudert einen heiligen Schild nach dem Feind, der ihm Heiligschaden zufügt, ihn benommen macht und dann auf weitere nahe Feinde überspringt. Trifft bis zu 2 Ziele insgesamt."},
[79966] = {name = "Weihe", description = "[q]Weiht das Land unter dem Zaubernden fügt 8 sec lang Feinden, die das Gebiet betreten, Heiligschaden zu."},
[79967] = {name = "Heiliger Schild", description = "[q]Erhöht die Blockchance 10 sec lang um 30% und verursacht, während der Effekt aktiv ist, mit jedem Blocken Heiligschaden. Jedes Blocken zehrt eine Aufladung auf. 3 Aufladungen."},
[79968] = {name = "Segen der Könige", description = "[q]Belegt ein befreundetes Ziel mit einem Segen, welcher 5 min lang seine Stärke, Beweglichkeit, Ausdauer und Intelligenz um 5% erhöht."},
[79970] = {name = "Göttlicher Sturm", description = "[q]Ein sofortiger Waffenangriff, der bei bis zu 4 Feinden innerhalb von 8 Metern Heiligschaden verursacht."},
[79971] = {name = "Hammer des Zorns", description = "[q]Schleudert einen Hammer, der einem Gegner Heiligschaden zufügt."},
[79972] = {name = "Siegel der Wahrheit", description = "[q]Erfüllt den Paladin mit heiliger Macht, durch den Angriffe auf Einzelziele den Effekt 'Tadel' auslösen, der im Verlauf von 15 sec [(0.01 * Spell power + 0.0193 * Attack power) * 5 */ 100] zusätzlichen Heiligschaden verursacht. 'Tadel' ist bis zu 5-mal stapelbar. Sobald 5 Stapel erreicht werden, verursacht jeder Angriff des Paladins zusätzlich 15% Waffenschaden als Heiligschaden. Es kann immer nur jeweils ein Siegel auf dem Paladin aktiv sein. Hält 5 min lang an.\n\n Die Entfesselung der Energie dieses Siegels fügt einem Feind (1 + 0.223 * Spell power + 0.142 * Attack power) Heiligschaden zu, dieser Schaden wird für jeden Stapel von 'Tadel' auf dem Ziel um 10% erhöht."},
[79975] = {name = "Gedankenkontrolle von Zul'Gurub", description = "[q]Erlaubt es Euch, das gefangene Schmetterschwanzjunge zu kontrollieren."},
[79976] = {name = "Aura der Vergeltung", description = "[q]Fügt jeder Kreatur, die den Zaubernden trifft, Heiligschaden zu."},
[79977] = {name = "Segen der Macht", description = "[q]Belegt ein befreundetes Ziel mit einem Segen, welcher 5 min lang seine Angriffskraft um 20%, seine Distanzangriffskraft um 10% erhöht und alle 5 Sek. 1 Mana regeneriert."},
[80003] = {name = "Schwarzer Pfeil", description = "[q]Feuert einen schwarzen Pfeil auf das Ziel ab, der Euren verursachten Schaden gegen das Ziel um 6% erhöht und 15 sec lang Schattenschaden verursacht."},
[80004] = {name = "Schlangenfalle", description = "[q]Eine Falle platzieren, die mehrere giftige Schlangen frei lässt, um den ersten Gegner anzugreifen, der sich nähert. Die Schlangen sterben nach 5 sec Die Falle besteht 1 min lang."},
[80009] = {name = "Schlangengift", description = "[q]Sticht das Ziel und verursacht 15 sec lang Naturschaden."},
[80011] = {name = "Giftgetränkt", description = "[q]Giftgetränkt zu sein verursacht alle 2 Sek. 20000 Naturschaden und lässt Zauber und Fähigkeiten dem Ziel 19500 to 20500 Naturschaden zufügen."},
[80015] = {name = "Zuverlässiger Schuss", description = "[q]Ein gezielter Schuss, der Waffenschaden plus (Ranged attack power * 0.1 + 0) verursacht."},
[80016] = {name = "Mal des Jägers", description = "[q]Belegt das Ziel mit dem Mal des Jägers. Erhöht die Distanzschaden aller Angreifer gegen dieses Ziel um 10%. Zudem kann das Ziel dieser Fähigkeit stets vom Jäger gesehen werden, auch wenn es in den Verstohlenheitsmodus geht oder unsichtbar wird. Das Ziel wird außerdem auf der Minikarte angezeigt. Hält 30 sec lang an."},
[80018] = {name = "Frostfalle", description = "[q]Eine Frostfalle platzieren, die 30 sec lang um sich herum eine Eisfläche bildet, sobald der erste Feind sich ihr nähert. Das Bewegungstempo aller Feinde in einem Umkreis von 10 Metern wird um 50% verlangsamt, solange sie sich im Wirkungsbereich befinden. Die Falle bleibt 30 sec lang bestehen. Es kann immer nur jeweils eine Falle aktiv sein."},
[80019] = {name = "Frostfallenaura", description = "[q]Eine Frostfalle platzieren, die 30 sec lang um sich herum eine Eisfläche bildet, sobald der erste Feind sich ihr nähert. Das Bewegungstempo aller Feinde in einem Umkreis von 10 Metern wird um 50% verlangsamt, solange sie sich im Wirkungsbereich befinden."},
[80021] = {name = "Netz", description = "[q]Macht einen Feind 4 sec lang bewegungsunfähig."},
[80022] = {name = "Netz durchbeißen", description = "[q]Beißt Euch durch die Netze der Trolle der Gurubashi. Funktioniert nicht bei Eisengewebten Netzen."},
[80028] = {name = "Fels bohren", description = "[q]Verursacht alle 3 Sek. 4000 Schaden. Bis zu 10-mal stapelbar. Hält 15 sec lang an."},
[80031] = {name = "Flammende Scherbe", description = "[q]Fügt einem Feind und seinem nächsten Verbündeten 110% des normalen Nahkampfschadens zu."},
[80035] = {name = "Rächende Wut", description = "[q]Der Tod eines Gefährten zieht einen Energieanstieg nach sich, erhöht den verursachten Schaden um 100% und das Angriffstempo 50%."},
[80038] = {name = "Magmaeruption", description = "[q]Schleudert geschmolzenes Gestein, das allen Gegnern im Umkreis von 2 Metern um das Zielgebiet 56550 to 63450 Feuerschaden zufügt."},
[80051] = {name = "Schreckliche Wunde", description = "[q]Ein bösartiger Angriff, der das Ziel stark bluten lässt, bis es um mehr als 90% seiner maximalen Gesundheit geheilt wird."},
[80053] = {name = "Giftprotokoll", description = "[q]Das Giftprotokoll erschafft alle 3 Sek. eine Giftbombe, die bei Kontakt mit ihrem Ziel oder nachdem sie zerstört wurde explodiert."},
[80058] = {name = "Zwielichtausbruch", description = "[q]Der Zwielichtausbruch verursacht Schattenschaden."},
[80065] = {name = "Tornado", description = "[q]Fügt nahen Gegnern Naturschaden zu und stößt sie zurück."},
[80066] = {name = "Tornado", description = "[q]Erschafft einen Tornado unter dem feindlichen Ziel."},
[80068] = {name = "Gewitter", description = "[q]Ihr ruft einen Blitzschlag hernieder, der Gegnern im Umkreis von 15 Metern Schaden zufügt. Alle Gegner in der Nähe erleiden Naturschaden und werden zurückgestoßen."},
[80074] = {name = "Eisengewebtes Netz", description = "[q]Macht einen Feind 4 sec lang bewegungsunfähig."},
[80084] = {name = "Wutanfall", description = "[q]Verringert die Zeit zwischen Euren Angriffen 30 sec lang um 50%. Verursachter Schaden um 50% erhöht."},
[80092] = {name = "Giftbombe", description = "[q]Fügt Gegnern im Umkreis von 6 Metern 195000 to 205000 Naturschaden zu."},
[80094] = {name = "Fixieren", description = "[q]Die Giftbombe ist auf Euch fixiert!"},
[80098] = {name = "Mekkadrillkanone", description = "[q]Mekkadrill ruft einen Satellitenangriff herab, der einem Feind Schaden zufügt."},
[80110] = {name = "Mekkadrillraketen", description = nil},
[80117] = {name = "Erdstachel", description = "[q]Zaubert einen Erdstachel, verursacht Naturschaden."},
[80127] = {name = "Flammenpuffer", description = "[q]Fügt einem Gegner 23125 to 26875 Feuerschaden zu und erhöht 8 sec lang seinen erlittenen Feuerschaden um 5%."},
[80130] = {name = "Schwanzpeitscher", description = "[q]Fügt Feinden, die sich innerhalb von 12 Metern um den Einschlagpunkt des Schwanzes befinden, 48750 to 81250 Schaden zu und stößt sie zurück."},
[80132] = {name = "Entfesselte Finsternis", description = "[q]Fügt einem Gegner Schattenschaden zu."},
[80145] = {name = "Durchdringender Griff", description = "[q]Ergreift einen in der Nähe befindlichen Feind und quetscht das Leben aus ihm heraus. Verursacht pro Sekunde 69375 to 80625 körperlichen Schaden."},
[80146] = {name = "Hinterkopfschlag", description = "[q]Eine Kopfnuss, die 150% Waffenschaden verursacht und die Ausweich- und Parierchance des Ziels 6 sec lang um 20% verringert."},
[80148] = {name = "Mekkadrillstrahl", description = "[q]Mekkadrill feuert seinen Mekkadrillstrahl ab, der dem anvisierten Feind und allen nahen Feinden Schaden zufügt."},
[80149] = {name = "Axt werfen", description = "[q]Wirft Eure Waffe nach einem Feind und verursacht 120% Waffenschaden."},
[80151] = {name = "Entzünden", description = "[q]Fügt dem Ziel 14250 to 15750 Feuerschaden zu sowie zusätzlich 10 sec lang pro Sekunde 5000 Feuerschaden."},
[80157] = {name = "Chemische Bombe", description = "[q]Feuert eine chemische Bombe auf ein zufälliges Ziel ab und verursacht an diesem Ort eine chemische Wolke."},
[80158] = {name = "Wut", description = "[q]Verursachter körperlicher Schaden um 100% erhöht."},
[80172] = {name = "Mekkadrilllaser", description = nil},
[80173] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[80174] = {name = "Teufelsblut", description = "[q]Der Feind wird in Teufelsblut gebadet. Erhöht den durch Feuer und Schatten erlittenen Schaden und ermöglicht das Hören der Einflüsterungen von Sargeras."},
[80180] = {name = "Bodycheck", description = "[q]Springt auf die Position eines zufälligen Ziels zu, fügt allen Gegnern im Umkreis von 5 Metern dieses Bereichs 37700 to 42300 Schaden zu und stößt sie zurück."},
[80182] = {name = "Aufwärtshaken", description = "[q]Fügt einem Gegner 110% Waffenschaden zu und stößt ihn zurück."},
[80183] = {name = "Sturmangriff", description = "[q]Ein Sturmangriff, der 8500 Schaden verursacht und das Ziel zurückstößt."},
[80195] = {name = "Schockwelle", description = "[q]Fügt Feinden in einem kegelförmigen Bereich von bis zu 20 Metern Reichweite 94250 to 105750 körperlichen Schaden zu."},
[80215] = {name = "Staubwind", description = "[q]Gestärkt von einem staubigen Zephyr. Angriffstempo um 10% erhöht."},
[80218] = {name = "Wilder Zorn", description = "[q]Erhöht den verursachten körperlichen Schaden des Zaubernden um 500%."},
[80221] = {name = "Niederschlagen", description = "[q]Wenn der Zaubernde einem Ziel Nahkampfschaden zufügt, wird der erlittene körperliche Schaden dieses Ziels 8 sec lang um 5% erhöht.Bis zu 20-mal stapelbar."},
[80266] = {name = "Talrendistarnung", description = "[q]Schleicht durch das hohe Gras."},
[80270] = {name = "Schattenflamme", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden 12487 to 14512 Feuerschaden zu."},
[80279] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[80295] = {name = "Fluch der Besserung", description = "[q]Verflucht einen Feind 15 sec lang und gewährt ihm eine Chance von 100% seine Ziele beim Einsatz einer offensiven Fähigkeit um 23125 to 26875 zu heilen."},
[80304] = {name = "Verjüngung", description = "[q]Heilt einen Verbündeten 12 sec lang alle 3 Sek."},
[80308] = {name = "Dämonenportal", description = "[q]Öffnet ein dämonisches Portal, das 5 sec lang pro Sekunde einen Wichtel beschwört."},
[80329] = {name = "Zeitraffer", description = "[q]In der Zeit eingefroren!\n Gesundheit um 10% verringert.\n Angriffs- und Zaubertempo verringert.Hält 3 sec lang an."},
[80332] = {name = "Verbrennen", description = "[q]Der Atem des Drakeadon stürmt zurück in sein Gesicht, woraufhin er 5 sec lang panikartig flieht."},
[80336] = {name = "Frostbeulen", description = "[q]Fügt allen Feinden 39375 to 50625 Frostschaden zu und erhöht die Zeit zwischen ihren Angriffen um 50%. Friert zudem die Münder der Ziele zu und bringt sie so 3 sec lang zum Schweigen."},
[80341] = {name = "Fleisch entzünden", description = "[q]Flammen absorbieren 50000 Schaden. Wenn der Schild die volle Menge Schade absorbiert, wird er sich entzünden und feindlichen Zielen 20812 to 24187 Schaden zufügen."},
[80344] = {name = "Teufelsfeuerblitz", description = "[q]Schleudert einen Flammenblitz auf einen Feind und verursacht Feuerschaden."},
[80345] = {name = "Ätzende Säure", description = "[q]Entzieht allen Zielen in einem kegelförmigen Bereich vor dem Zaubernden alle 1 Sek. 17500 to 22500 Leben.\n Rüstung um 30% verringert.\n Hält 15 sec lang an."},
[80354] = {name = "Temporale Verzerrung", description = "[q]Kann nicht von 'Zeitkrümmung' oder ähnlichen Effekten profitieren."},
[80362] = {name = "Zerschmetternder Biss", description = "[q]Beißt die Rüstung eines Feindes und verringert sie 20 sec lang um 10%."},
[80368] = {name = "Brutkraft: Rot", description = "[q]Verwundbar gegen Frost.\n Erfüllt mit der Essenz des roten Drachenschwarms."},
[80369] = {name = "Brutkraft: Grün", description = "[q]Verwundbar gegen Arkaneffekte.\n Erfüllt mit der Essenz des grünen Drachenschwarms."},
[80370] = {name = "Brutkraft: Schwarz", description = "[q]Verwundbar gegen Heiligeffekte.\n Erfüllt mit der Essenz des schwarzen Drachenschwarms."},
[80371] = {name = "Brutkraft: Blau", description = "[q]Verwundbar gegen Feuer.\n Erfüllt mit der Essenz des blauen Drachenschwarms."},
[80372] = {name = "Brutkraft: Bronze", description = "[q]Verwundbar gegen Natureffekte.\n Erfüllt mit der Essenz des bronzefarbenen Drachenschwarms."},
[80380] = {name = "Sandatem", description = "[q]Fügt Gegnern in einem kegelförmigen Bereich vor dem Zaubernden Natuschaden zu, verringert Ihr Angriffstempo und erhöht die Zeit zwischen ihren Angriffen und Zaubern. Hält 5 sec lang an."},
[80382] = {name = "Schmutz werfen", description = "[q]Blendet das Ziel und lässt es bis zu 2 sec lang verwirrt umherirren."},
[80383] = {name = "Waffe stehlen", description = "[q]Stielt einem Gegner die Waffe und zwingt ihn 3 sec lang, ohne Waffe zu kämpfen."},
[80384] = {name = "Landstreicherbrühe", description = "[q]Fügt allen Gegnern im einem kegelförmigen Bereich vor dem Zaubernden Naturschaden zu."},
[80390] = {name = "Tödlicher Stoß", description = "[q]Ein heimtückischer Stoß, der 250% Waffenschaden verursacht und das Ziel verwundet, sodass die Wirksamkeit jeglicher Heilung 10 sec lang um 20% verringert wird."},
[80391] = {name = "Druckwelle", description = "[q]Entfesselt eine Flammenwelle, die nahen Feinden Feuerschaden zufügt und ihr Bewegungstempo 6 sec lang verringert."},
[80392] = {name = "Spalten", description = "[q]Fügt einem Feind und seinen nächsten Verbündeten 100% des normalen Nahkampfschadens zu, wirkt auf bis zu 3 Ziele."},
[80418] = {name = "Immunität", description = "[q]Ziel ist allen Kreaturen gegenüber immun."},
[80453] = {name = "Schotterwelle", description = "[q]Spuckt Schotter auf alle Ziele in einem Halbkreis um Ouro herum, verursacht 7225 to 9775 Naturschaden und betäubt Feinde until cancelled lang. Von 'Sandböe' betroffene Ziele werden ignoriert."},
[80467] = {name = "Wutanfall", description = "[q]Erhöht den verursachten Schaden um 50%."},
[80471] = {name = "Trank der Stärke", description = "[q]Erhöht die Stärke des Zaubernden 2 min lang um 5%."},
[80472] = {name = "Elixier der Beweglichkeit", description = "[q]Erhöht die Beweglichkeit des Zaubernden 2 min lang um 5%."},
[80473] = {name = "Tonikum der Gesundheit", description = "[q]Erhöht die Ausdauer des Zaubernden 2 min lang um 5%."},
[80475] = {name = "Brandmal der Flusspfoten", description = "[q]Fügt einem Gegner Frostschaden sowie zusätzlich im Verlauf von 10 sec weiteren Frostschaden zu."},
[80512] = {name = "Dreck schleudern", description = "[q]Schleudert einen Klumpen Dreck nach dem Ziel und verursacht Schaden."},
[80513] = {name = "Zerquetschen", description = "[q]Verursacht 300% Waffenschaden."},
[80514] = {name = "Unsterbliche Raserei", description = "[q]Hackt, würfelt und zerkleinert Euren Feind."},
[80522] = {name = "Mmmrrrggglll demoralisieren", description = "[q]Verringert die Nahkampfangriffskraft von in der Nähe befindlichen Feinden 10 sec lang um 10%.\n Verursachter Magieschaden wird 10 sec lang um 10% veringert."},
[80546] = {name = "Gallschlag", description = "[q]Schießt Gift auf den Feind, fügt ihm Naturschaden und dann 10 sec lang alle 5 Sek. zusätzlichen Schaden zu."},
[80549] = {name = "Flammentropfen", description = "[q]Erschafft am Zielort einen Flammengeysir."},
[80555] = {name = "Schallspeer", description = "[q]Schickt dem Zaubernden eine Energiewelle voraus und verursacht bei allen Einheiten innerhalb eines kegelförmigen Bereichs von 35 Metern körperlichen Schaden."},
[80561] = {name = "Flammenschneide", description = "[q]Verursacht 110% Waffenschaden als Feuerschaden."},
[80563] = {name = "Erdriss", description = nil},
[80570] = {name = "Mimikry", description = "[q]Verwandelt den Nutzer in ein Ebenbild seines Ziels."},
[80572] = {name = "Energie!", description = "[q]Erhöht das Angriffs- und Zaubertempo des Zaubernden um 25% und den von ihm verursachten Schaden um 10%."},
[80576] = {name = "Schattenschritt", description = "[q]Versucht, in die Schatten zu tauchen, um hinter dem Gegner zu erscheinen."},
[80585] = {name = "Springendes Zustürmen", description = "[q]Springt das Ziel an und verursacht 100% Waffenschaden."},
[80588] = {name = "Verstümmeln", description = "[q]Greift sofort mit beiden Waffen an. Gewährt 2 Combopunkte."},
[80595] = {name = "Sprengfalleneffekt", description = "[q]Alle 2 Sek. Feuerschaden."},
[80596] = {name = "Sprengfalle", description = "[q]Eine Feuerfalle platzieren, die explodiert, wenn sich ein Feind nähert. Die Falle verursacht Feuerschaden und fügt zusätzlich allen Gegnern im Umkreis von 10 Metern im Verlauf von 10 sec Feuerschaden zu. Die Falle bleibt 30 sec lang bestehen."},
[80597] = {name = "Prügeln und Hauen", description = "[q]Erhöht 10 sec lang das Angriffstempo des Zaubernden um 30%."},
[80600] = {name = "Flammenstampfen", description = "[q]Fügt nahen Gegnern 24 to 29 Feuerschaden zu und stößt sie zurück."},
[80604] = {name = "Zerschmetternder Biss", description = "[q]Schnappt nach dem Ziel, verursacht 200% Waffenschaden und verringert die Rüstung des Ziels 10 sec lang um 50%."},
[80605] = {name = "Kopfstoß", description = "[q]Fügt nahen Gegnern normalen Schaden plus 3000 zu und stößt sie zurück."},
[80606] = {name = "Böses Spalten", description = "[q]Fügt einem Feind und seinen nächsten Verbündeten 330% des normalen Nahkampfschadens zu, wirkt auf bis zu 3 Ziele."},
[80607] = {name = "Flammenschritt", description = "[q]Versucht, in die Flammen einzutauchen, um hinter dem Gegner zu erscheinen."},
[80610] = {name = "Wassergleiten", description = "[q]Erhöht das Schwimmtempo 15 min lang um 50%. Gestattet Unterwasseratmung."},
[80613] = {name = "Zerschmetterndes Stampfen", description = "[q]Fügt in der Nähe befindlichen Feinden normalen Schaden plus 0 zu und betäubt sie 5 sec lang."},
[80616] = {name = "Salzsäure", description = "[q]Löst Felshaut auf und erhöht den erlittenen Schaden um einen erheblichen Betrag."},
[80618] = {name = "Kolossales Fegen", description = "[q]Das Ziel wird in die Luft geschleudert und erleidet 100% normalen Waffenschaden."},
[80638] = {name = "Totem der Verbrennung", description = "[q]Verbrennt einen Feind und verursacht 24 to 29 Schaden und zusätzlich alle 3 Sek. 4 Feuerschaden."},
[80645] = {name = "Avatar", description = "[q]Erhöht den vom Zaubernden zugefügten körperlichen Schaden um 25% und intensiviert seine Rüstung 15 sec lang um 25%."},
[80646] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der 27750 to 32250 Naturschaden verursacht. Der Blitz wird auf in der Nähe befindliche Feinde überspringen und mit jedem weiteren Ziel 50% mehr Schaden verursachen."},
[80648] = {name = "Sturmblitz", description = "[q]Fügt einem Feind und seinen umstehenden Verbündeten 27750 to 32250 Schaden zu und betäubt alle 4 sec lang."},
[80649] = {name = "Donnerknall", description = "[q]Fügt allen nahen Feinden 32375 to 37625 Naturschaden zu und verlangsamt das Angriffstempo um 20%."},
[80652] = {name = "Wirbelwind", description = "[q]Fügt umstehenden Feinden alle 1 Sek. 70687 to 79312 Schaden zu, verlangsamt das Bewegungstempo jedoch um 50%."},
