[81608] = {name = "Steintroggnetz", description = "[q]Nagelt einen Basilisken in Vorbereitung auf eine Zähmung am Boden fest."},
[81624] = {name = "Krieger des Drachenmals", description = "[q]Führt einen Krieger des Drachenmalklans."},
[81628] = {name = "Verderbenden Kultisten beschwören", description = "[q]Cho'gall beschwört zu seiner Unterstüzung aus den nahen Portalen verderbende Kultisten herbei!"},
[81630] = {name = "Zähflüssiges Gift", description = "[q]Fügt einem feindlichen Ziel sowie seinen Verbündeten im Umkreis von 5 Metern 13425 to 16575 Naturschaden sowie zusäzlich alle 2 Sek. 9250 to 10750 Naturschaden zu. Zudem wird das Bewegungstempo aller um 30% verringert."},
[81632] = {name = "Brocken werfen", description = "[q]Wirft einen Brocken, der Steindrachen 2250 to 2750 Schaden zufügt."},
[81642] = {name = "Staubflegel", description = "[q]Schmettert regelmäßig mit dem Schwanz auf den Boden."},
[81650] = {name = "Sündhafter Strahl", description = "[q]Schlägt einen Feind mit Schattenenergie, die auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 3 Ziele aus."},
[81651] = {name = "Übler Strahl", description = "[q]Schlägt einen Feind mit Schattenenergie, die auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 3 Ziele aus."},
[81653] = {name = "Borstennacken", description = "[q]Der Zaubernde ist von einer natürlichen Rüstung eines Borstenmantels umgeben, die Angreifern körperlichen Schaden zufügt."},
[81654] = {name = "Ungeschickter Schuss", description = "[q]Fügt einem Gegner sowie einem seiner nahen Verbündeten Feuerschaden zu und desorientiert den Zaubernden 3 sec lang."},
[81655] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[81677] = {name = "Heimtückischer Biss", description = "[q]Fügt einem Feind 50% Waffenschaden zu und lässt ihn pro Anwendung alle 2 Sek. für 1137 to 1462 Schaden bluten. Hält 15 sec lang an."},
[81678] = {name = "Augenpicken", description = "[q]Verringert 5 sec lang die Trefferchance um 10%."},
[81680] = {name = "Heulendes Kreischen", description = "[q]Das Bewegungstempo naher Feinde wird 5 sec lang um 10% verringert.\n Verringert die Angriffskraft naher Feinde um 10."},
[81685] = {name = "Verderbendes Schmettern", description = "[q]Feuert ein Geschoss auf ein zufälliges Ziel ab. Wenn das Geschoss auftrifft, fügt es allen Gegnern im Umkreis von 10 Metern um dieses Gebiet 11310 to 12690 Schattenschaden zu."},
[81690] = {name = "Blutgeruch", description = "[q]Verursacht alle 2 Sek. 1850 to 2150 Blutungsschaden. Nahe hungrige Krokilisken können Euer Blut riechen! Wenn sie jemanden angreifen, dann Euch."},
[81691] = {name = "Schwunghorn", description = "[q]Fügt einem Gegner 50% Waffenschaden zu und stößt ihn eine kurze Distanz zurück."},
[81695] = {name = "Fackel werfen", description = "[q]Wirft eine Fackel, verursacht Feuerschaden und verbrennt das Ziel, was zusätzlich 10 sec lang Feuerschaden verursacht."},
[81705] = {name = "Skalpschneider", description = "[q]Ein sofortiger Angriff, der 50% Waffenschaden verursacht und Intelligenz und Willenskraft 10 sec lang um 10% verringert."},
[81706] = {name = "Giftige Wut", description = "[q]Erhöht until cancelled lang den verursachten Schaden um 25%."},
[81707] = {name = "Windzorn", description = "[q]Gestärkt von einem sonderbaren Zephyr. Nahkampf-, Distanzangriffs- und Zauberzeit um 10% erhöht."},
[81711] = {name = "Fraktur", description = "[q]Wenn der Zaubernde einem Ziel Nahkampfschaden zufügt, wird die Rüstung dieses Ziels 6 sec lang um 5% verringert. Bis zu 20-mal stapelbar."},
[81713] = {name = "Verderbtheit", description = "[q]Fügt Gegnern im Umkreis von 20 Metern 73125 to 76875 Schattenschaden zu und ruft 'Verderbnis' hervor."},
[81753] = {name = "Bedrohlicher Schrei", description = "[q]Erhöht das Nahkampfangriffstempo des Zaubernden und seiner nahen Verbündeten 30 sec lang um 10%."},
[81754] = {name = "Motivieren", description = "[q]Fügt einem Feind Schattenschaden zu und erhöht das Bewegungstempo um 50%."},
[81757] = {name = "Vergossenes Bltu des Alten Gottes", description = "[q]Der getötete Anhänger des Alten Gottes vergießt sein verderbtes Blut. Verursacht bei Gegnern in einem Umkreis von 10 Metern 21937 to 23062 Schattenschaden und ruft 'verderbtes Blut' hervor."},
[81764] = {name = "Laterne werfen", description = "[q]Schleudert eine Laterne nach einem Feind, die Feuerschaden verursacht und unter seinen Füßen eine brennende Lohe hervorruft."},
[81767] = {name = "Laternenschein", description = "[q]Fügt allen Gegnern innerhalb der Flammen Feuerschaden zu. Hält 6 sec lang an."},
[81770] = {name = "Donnerstein", description = "[q]Schleudert einen Donnerstein auf den Feind und verursacht 9250 to 10750 Schaden."},
[81772] = {name = "Überstunden", description = "[q]Erhöht 6 sec lang das Angriffstempo des Zaubernden um 10% und den von ihm zugefügten körperlichen Schaden um 10%. Führt nach 6 sec zu Erschöpfung."},
[81773] = {name = "Erschöpft", description = "[q]Zu viele Überstunden haben das Ziel erschöpft und betäuben es 4 sec lang."},
[81790] = {name = "Für Gilneas!", description = "[q]Schneller, besser, stärker."},
[81850] = {name = "Seehorns Spalten", description = "[q]Fügt einem Gegner und seinen nächsten Verbündeten 2775 to 3225 Schaden zu, trifft bis zu 10 Ziele."},
[81886] = {name = "Handbomben werfen", description = "[q]Lässt Worgenpiraten explodieren."},
[81887] = {name = "Nach Beutebucht zurückkehren", description = "[q]Bittet Euren Wyvern, Euch nach Beutebucht zurückzubringen."},
[81888] = {name = "Rammen", description = "[q]Rammt alle Feinde, die sich innerhalb von 13 Metern vor dem Verwüster befinden, fügt ihnen 471 to 528 Schaden zu und stößt sie zurück."},
[81944] = {name = "Voodoobewegung", description = "[q]Levitiert..."},
[81947] = {name = "Buße", description = "[q]Reißt die Seele des Ziels aus dem Körper des Ziels und setzt sie der Geisterwelt aus."},
[81949] = {name = "Betäubendes Anspringen", description = "[q]Stürmt heran und stößt das Ziel um."},
[81954] = {name = "Verderbter Erdschild", description = "[q]Schützt das Ziel mit einem Erdschild. Nahkampfangriffe heilen das geschützte Ziel, dieser Effekt tritt aber nur einmal alle paar Sekunden auf. 10 Aufladungen. Hält 5 min lang an."},
[81995] = {name = "Flammenstoß", description = "[q]Lässt eine Flammensäule niedergehen, die alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen 8 sec lang alle 2 Sek. zusätzlichen Schaden zufügt."},
[81996] = {name = "Druckwelle", description = "[q]Entfesselt eine Flammenwelle, die nahen Feinden Feuerschaden zufügt und ihr Bewegungstempo 6 sec lang verringert."},
[82028] = {name = "Voodoojuju", description = "[q]Levitiert..."},
[82032] = {name = "Furor", description = "[q]Erhöht das Angriffstempo um 8%."},
[82033] = {name = "Teufelszornomanie", description = "[q]Türmt sich auf..."},
[82036] = {name = "Rückgratbrecher", description = "[q]Ergreift den Gegner und schmettert ihn auf den Boden."},
[82049] = {name = "Mor'ghors Schmettern", description = "[q]Springt auf einen nahen Feind und zerquetscht ihn."},
[82061] = {name = "Befehlsruf", description = "[q]Erhöht die maximale Gesundheit aller Gruppenmitglieder im Umkreis von 20 Metern um 2500. Hält 2 min lang an."},
[82064] = {name = "Feuerbrandpfeil", description = "[q]Schießt einen Flammenpfeil gegen einen Feind und fügt Waffenschaden plus 300 zu. Jeglicher zugefügte Schaden basiert auf Feuermagie."},
[82104] = {name = "Großer Massenteleport", description = "[q]Teleportiert die Ziele an einen angegebenen Ort."},
[82107] = {name = "Tieffrieren", description = "[q]Eingefroren und kann sich nicht bewegen."},
[82109] = {name = "Mondstrahl", description = "[q]Schießt auf einen Gegner und verursacht Arkanschaden."},
[82119] = {name = "Flammende Säule der Zerstörung", description = "[q]Lässt eine Flammensäule niedergehen, die alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen 1 sec lang alle 2 Sek. zusätzlichen Schaden zufügt."},
[82120] = {name = "Bodenzerkracher", description = "[q]Verursacht körperlichen Schaden bei in der Nähe befindlichen Feinden und betäubt sie 2 sec lang."},
[82133] = {name = "Flammende Zerstörung", description = "[q]Angriffe rufen eine flammende Zerstörung hervor, die 39000 to 41000 zusätzlichen Feuerschaden verursacht und in der Nähe eine Lohe verursacht."},
[82137] = {name = "Hilferuf", description = "[q]Rom'ogg ruft in der Nähe befindlichen Schergen zur Unterstützung!"},
[82226] = {name = "Klingensturm", description = "[q]Führt sofort einen Wirbelangriff aus, der alle nahen Ziele trifft. Die nächsten 6 sec lang werdet Ihr alle 1 Sek. einen Wirbelangriff durchführen. Während der Effekt 'Klingensturm' aktiv ist, könnt Ihr Euch bewegen, aber während dieser Zeit keine anderen Fähigkeiten einsetzen."},
[82228] = {name = "Ogerklingenstampfen", description = nil},
[82255] = {name = "Seele zerreißen", description = "[q]Ein Fragment Eurer Seele wird abgetrennt werden."},
[82263] = {name = "Verschmolzene Seelen", description = "[q]Verschmilzt die Seele mit dem Ziel, absorbiert 41625 to 48375 Gesundheit und erhöht den verursachten Schaden um 20%."},
[82285] = {name = "Elementare Stasis", description = "[q]Umhüllt in der Nähe befindliche Feinde mit Elementarenergie, lähmt sie und verringert 17 sec lang drastisch allen erlittenen Schaden."},
[82294] = {name = "Katapult abfeuern", description = "[q]Feuert einen explodierenden Donnerstein auf die Verstärkung der Steintroggs und Fungusschrecken mit einer Reichweite von bis zu 100 Metern."},
[82299] = {name = "Eiterblut", description = "[q]Lässt das Blut verderbter Kultisten verfaulen, lässt Blut des Alten Gottes aus dem geronnenen Blut entstehen und kocht das Blut aller lebenden verderbten Kultisten."},
[82361] = {name = "Verderbnis des Alten Gottes", description = "[q]Cho'gall wird von den Alten Göttern vollkommen verderbt und verursacht alle 2 Sek. 6337 to 6662 Schattenschaden. Verursacht 'Verderbtes Blut'."},
[82362] = {name = "Schattenhieb", description = "[q]Schreitet durch die Schatten, um hinter einem feindlichen Ziel aufzutauchen und 70000 Schattenschaden zu verursachen."},
[82376] = {name = "Frostschlag", description = "[q]Verursacht 110% Waffenschaden und verringert das Angriffs- und Zaubertempo eines Gegners sowie seiner nahen Verbündeten um 10%."},
[82395] = {name = "Berserker", description = "[q]Erhöht das Angriffs- und Bewegungstempo des Zaubernden um 150% sowie seinen verursachten Schaden um 500%. Zudem wird er gegen Spotteffekte immun."},
[82411] = {name = "Entkräftender Strahl", description = "[q]Der entkräftende Strahl reduziert verursachte Heilung und Schaden um 75% und verursacht 10 sec lang alle 1 Sek. 10725 to 11275 Schattenschaden."},
[82414] = {name = "Verfinsterte Geschöpfe", description = "[q]Cho'gall beschwört verfinsterte Geschöpfe, um ihm beizustehen."},
[82415] = {name = "Dämpfende Welle", description = "[q]Fügt allen Gegnern im Umkreis von 60 Metern 9425 to 10575 Schattenschaden zu. Zudem absorbieren Ziele bis zu 15000 Heilung."},
[82425] = {name = "Heulen der Dunkelheit", description = "[q]Fügt nahen Zielen 26513 to 30812 Schattenschaden zu und reißt sie aus der Geisterwelt."},
[82506] = {name = "Fünfzig Peitschenhiebe", description = "[q]Nahkampfwaffenschwünge verursachen fünfzig Peitschenhiebe, die dem Ziel normalen Schaden plus 5000 zufügen."},
[82524] = {name = "Furor des Cho'gall", description = "[q]Cho'gall greift das Ziel an, verursacht massiven Schatten- und körperlichen Schaden und erhöht den erlittenen Schatten- und körperlichen Schaden."},
[82528] = {name = "Versteinernder Schlag", description = "[q]Betäubt 6 sec lang alle in der Nähe befindlichen Feinde."},
[82529] = {name = "Augenstrahl", description = "[q]Verursacht Naturschaden."},
[82531] = {name = "Einhüllende Dunkelheit", description = "[q]Dunkle Energien rekonstruieren den Körper des Ziels. Ruft until cancelled lang alle 3 Sek. Heilung hervor."},
[82547] = {name = "Brocken werfen", description = nil},
[82548] = {name = "Unversehrbrakeit des Zwielichts", description = "[q]Unnatürliche Energien machen den Zaubernden immun gegen jeden Schaden."},
[82589] = {name = "Rauchqualmbomben", description = "[q]Verlangsamt das Ziel um 60% und verhindert 8 sec lang alle feindlichen Aktionen."},
[82599] = {name = "Verplankt!", description = "[q]Schlägt einen Gegner nieder, verursacht leichten Schaden und betäubt ihn 1 sec lang."},
[82608] = {name = "Heulendes Kreischen", description = "[q]Verringert das Bewegungstempo naher Feinde um 10% und ihre Angriffskraft um 10%. Hält 5 sec lang an."},
[82610] = {name = "Stachelebersturm", description = "[q]Verursacht 300% Waffenschaden und stößt das Ziel zurück."},
[82611] = {name = "Rächender Stoß", description = "[q]Der entfesselte, ungebändigte Furor der Elemente."},
[82615] = {name = "Fokussierter Schlag", description = "[q]Schlägt schnell auf den Feind ein und verursacht 50% Waffenschaden."},
[82616] = {name = "Fokussierte Schläge", description = "[q]Führt 5 sec lang schnelle Angriffe auf einen Feind aus."},
[82617] = {name = "Heben", description = "[q]Verursacht Schaden und wirft das Ziel über die Schulter des Zaubernden."},
[82619] = {name = "Goblindrachenwerfer", description = "[q]Fügt allen Gegnern in einem kegelförmigen Bereich vor dem Ingenieur 8 sec lang Feuerschaden zu."},
[82622] = {name = "Seuche der Zeitalter", description = "[q]Schleudert ein verfluchtes Seuchengeschoss nach einem Feind, das 8840 to 11960 Naturschaden verursacht und dann auf weitere nahe Feinde überspringt."},
[82625] = {name = "Knochen werfen", description = "[q]Wirft einen Knochen auf einen Feind und verursacht körperlichen Schaden."},
[82630] = {name = "Blut der Alten Gottes verzehren", description = "[q]Das vergossene Blut des Alten Gottes wird von Cho'gall aufgenommen, was ihn noch weiter verderbt!"},
[82631] = {name = "Aegis der Flamme", description = "[q]Umgibt den Zaubernden mit einem leuchtenden Flammenschild, der bis zu 2000000 Schaden absorbiert und die Unterbrechung von Zaubern verhindert, solange er aktiv ist."},
[82632] = {name = "Tödliches Gift", description = "[q]Bei jedem Schlag besteht die Chance, den Gegner 2 Sek. lang mit Naturschaden zu vergiften. Ist bis zu 5-mal auf dem gleichen Ziel stapelbar."},
[82635] = {name = "Flammender Sprung", description = "[q]Springt auf das Ziel zu, fügt einem Gegner sowie seinen zwei nächsten Verbündeten 25% Waffenschaden zu und ruft eine Druckwelle hervor, die zusätzlichen Feuerschaden verursacht."},
[82636] = {name = "Aufsteigende Flammen", description = "[q]Ruft die Energien der Elementarebene der Flammen und erhöht den verursachten Schaden. Die Ermächtigungsrate erhöht sich mit der Zeit, wenn das Kanalisieren des Wirkenden nicht unterbrochen wird."},
[82641] = {name = "Lavatropfen", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[82644] = {name = "Flammender Schleim", description = "[q]Fügt allen Gegnern innerhalb des flammenden Schleims Schaden zu. Hält 10 sec lang an."},
[82654] = {name = "Witwentoxin", description = "[q]Ein vergifteter Schuss, der jegliche erhaltene Heilung des Ziels 30 sec lang verringert."},
[82660] = {name = "Brennendes Blut", description = "[q]Fügt einem Gegner alle 2 Sek. zunehmenden Feuerschaden zu. Wenn das Feuer brennt werden in der Nähe befindliche Verbündete mit der Energie des Feuers erfüllt."},
[82664] = {name = "Brennender Hass", description = "[q]Fügt einem Gegner Feuerschaden zu und verringert 3 sec lang sein Bewegungstempo."},
[82665] = {name = "Herz aus Eis", description = "[q]Eine eisige Kälte ergreift das Herz des Ziels und verursacht alle 2 Sek. zunehmenden Frostschaden. Wenn das Ziel jedoch mit anderen Verbündeten in Kontakt kommt, werden sie mit Frostenergie erfüllt."},
[82668] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[82669] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[82670] = {name = "Schädelkracher", description = "[q]Fügt einem Feind einen harten Schlag auf den Schädel zu, verursacht dabei Waffenschaden plus zusätzlichen Schaden und betäubt ihn 2 sec lang."},
[82671] = {name = "Klingenschildstachel", description = "[q]Ein klingenbewehrter Schild gewährt dem Ziel Schutz, Angreifer erleiden bei einem Treffer Schaden."},
[82699] = {name = "Wasserbombe", description = "[q]Fügt Feinden innerhalb von 6 Metern 14625 to 15375 Schaden zu."},
[82703] = {name = "Sog der Gezeiten", description = "[q]Manipuliert die Kräfte der Tiefen und zieht das Ziel auf den Zaubernden zu."},
[82704] = {name = "Schnappen und stoßen", description = "[q]Verursacht Schaden und wirft das Ziel über die Schulter des Zaubernden."},
[82705] = {name = "Pips Mixtur", description = "[q]Wenn Ihr Schaden erleidet, während Ihr mehr als 10000 Gesundheit besitzt, kann Eure Gesundheit nicht unter 1 gesenkt werden."},
[82706] = {name = "Tiefschlaf", description = "[q]Diese Kreatur schläft tief und fest. Sollte sie gestört werden, könnte etwas Furchtbares passieren."},
[82707] = {name = "Rückzug", description = "[q]Springt vom gewählten Ziel fort."},
[82708] = {name = "Flammenpfeil", description = "[q]Schießt einen Flammenpfeil auf einen Feind ab und verursacht Feuerschaden."},
[82710] = {name = "Schleimkappe", description = "[q]Haftet dem Ziel eine Schleimkappe an, verursacht Schaden und versucht sein Gehirn zu übernehmen."},
[82715] = {name = "In die Schatten flüchten", description = "[q]Versucht, in die Schatten zu flüchten. Verschwindet nach 4 sec außer Sicht."},
[82717] = {name = "Verschwinden", description = "[q]Versetzt den Zaubernden 4 sec lang in den Verstohlenheitsmodus."},
[82718] = {name = "Wasserlinge beschwören", description = "[q]Beschwört drei wässrige Diener, die dem Zauberer im Kampf helfen."},
[82723] = {name = "Zwielichtdruckwelle", description = "[q]Kanalisiert seine Energie in eine Zwielichtdruckwelle und fügt Feinden im Umkreis von 10 Metern Schattenschaden zu."},
[82738] = {name = "Felsregen", description = "[q]Lässt im Zielgebiet Felsen und Schutt herabhageln."},
[82742] = {name = "Pfählender Zug", description = "[q]Schleudert Euren Dreizack nach einem Ziel und zieht es zu Euch zurück. Verursacht körperlichen Schaden und verspottet das Ziel."},
[82744] = {name = "Versiegelte Lippen", description = "[q]Fügt dem Ziel 9724 to 11076 Feuerschaden zu und verschweißt seinen Mund."},
[82746] = {name = "Vergletscherung", description = "[q]Fügt allen nahen Gegnern massiven Frostschaden zu, der mit zunehmendem Abstand vom Zaubernden abnimmt. Alle durchnässten Gegner werden eingefroren und erleiden zusätzlichen Schaden."},
[82750] = {name = "Serum der Folter", description = "[q]Fügt einem Ziel alle 1 Sek. 8415 to 9585 Naturschaden zu, das daraufhin von schrecklichen Schmerzen geplagt flüchtet."},
[82752] = {name = "Hydrolanze", description = "[q]Fügt einem zufälligen Ziel 180000 to 220000 Frostschaden zu."},
[82753] = {name = "Aderlassritual", description = "[q]Lässt das Ziel stark bluten und verursacht alle 1 Sek. 9350 to 10650 Schaden."},
[82757] = {name = "Bösartige Egel beschwören", description = "[q]Beschwört bösartige Egel."},
[82759] = {name = "Mixtur der genutzten Wut", description = "[q]Verringert die Zeit zwischen Euren Angriffen 30 sec lang um 50%. Verursachter Schaden um 25% erhöht."},
[82760] = {name = "Verhexen", description = "[q]Verwandelt den Gegner in einen Frosch. Während es verhext ist, kann das Ziel nicht angreifen oder Zauber wirken. Erlittener Schaden kann den Effekt unterbrechen. Hält 6 sec lang an. Es kann jeweils nur ein Ziel verhext sein. Kann nur auf Humanoide und Wildtiere angewandt werden."},
[82762] = {name = "Wasserdurchtränkt", description = "[q]Durchnässt einen Feind mit eisigem Wasser und reduziert sein Bewegungstempo um 25%. Während sie durchnässt sind, sind Feinde gegen bestimmte Frostattacken empfindlich. Sich einer Flamme auszusetzen kann diesen Effekt entfernen."},
[82763] = {name = "Zerschmettern", description = "[q]Wirft einen Feind zu Boden und verursacht körperlichen Schaden."},
[82764] = {name = "Zurechtstutzen", description = "[q]Verursacht 150% des normalen Nahkampfschadens und reduziert das Bewegungstempo des feindlichen Ziels um 30%. Hält 10 sec lang an."},
[82765] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[82768] = {name = "Infektiöse Seuche", description = "[q]Infiziert einen Feind 10 sec lang, wodurch an allen in der Nähe befindlichen Verbündeten alle 3 Sek. Naturschaden verursacht wird."},
[82771] = {name = "Deepholm - Stormstone Visual", description = nil},
[82772] = {name = "Gefroren", description = "[q]Gefriert einen durchnässten Gegner, der dadurch betäubt wird und alle 2 Sek. 80000 Frostschaden erleidet. Hält 10 sec lang an."},
[82777] = {name = "Flammensog", description = "[q]Fügt Feinden in einem kegelförmigen Bereich von 18 Metern vor dem Zaubernden 3 sec lang alle 1 Sek. 69375 to 80625 Feuerschaden zu."},
[82780] = {name = "Deepholm - Stormstone Visual", description = nil},
[82789] = {name = "Dolchfächer", description = "[q]Verursacht 18750 to 31250 Schaden."},
[82791] = {name = "Krokilisk rufen", description = "[q]Beschwört einen Krokilisken als Begleitung für den Zaubernden bis zur Freigabe."},
[82792] = {name = "Falken rufen", description = "[q]Beschwört einen Falken als Begleitung für den Zaubernden bis zur Freigabe."},
[82794] = {name = "Gabelfeuerschuss", description = "[q]Feuert mehrere Geschosse ab, die 3 Zielen 100% Waffenschaden zufügen und dann zusätzlich alle 2 Sek. 9250 to 10750 Schaden verursachen."},
