[84052] = {name = "Zwielichtschild", description = "[q]Schützt den Zaubernden 8 sec lang vor allen Angriffen und Zaubern, die Zeit zwischen Angiffen wird während dieser Zeit jedoch um 100% erhöht."},
[84092] = {name = "Wirbelsturmwinde", description = "[q]Hüllt das Ziel in einen Zyklon ein, der sein Zaubertempo um 500% erhöht."},
[84106] = {name = "Tanzende Flammen", description = "[q]Bündel flüssigen Feuers erscheinen überall um den Drachen herum und ermöglichen ihm, 'Feuerballbeschuss' zu wirken."},
