[84297] = {name = "Korrosiver Speichel", description = "[q]Schleckt den Feind ab, verursacht Naturschaden und bedeckt ihn mit Speichel, der pro Anwendung 5% seiner Rüstung wegätzt. Bis zu 5-mal stapelbar."},
[84298] = {name = "Optische Verbindung", description = "[q]Kanalisiert einen Strahl dunkler Energie auf das Ziel und verursacht 12 sec lang Schattenschaden."},
[84306] = {name = "Durchnässen", description = "[q]Verursacht Frostschaden, zudem wird 20 sec lang das Bewegungstempo verringert sowie der erlittene Frostschaden erhöht. Bis zu 5-mal stapelbar."},
