[84931] = {name = "Erdsplitter", description = "[q]Entsendet eine Reihe Stacheln in Richtung eines zufälligen Spielers. Sobald die Stacheln ihren Zielort erreichen, werden sie in voller Größe aus dem Boden ausbrechen und allen Gegnern im Umkreis von 5 Metern alle 0.5 Sek. 9425 to 10575 Schaden zufügen. Hält 10 Sek. lang an."},
[84948] = {name = "Gravitationsschmettern", description = "[q]Fängt einen Feind in einer Gravitationsblase, hebt ihn vom Boden und quetscht 6.50 sec lang alle 0.5 Sek. 9% seiner maximalen Gesundheit aus ihm heraus. Wenn der Effekt endet, stürzt das Ziel zu Boden."},
