[85401] = {name = "Verzehrende Flamme", description = "[q]Brennt!"},
[85411] = {name = "Verlassenen erwecken", description = "[q]Lässt Gefallene als Verlassene auferstehen."},
[85412] = {name = "Blutlache", description = "[q]Der gesichtslose Wächter verwandelt sich langsam in eine Blutlache und fügt so allen Feinden im Umkreis von 10 Metern pro Sekunde 19500 to 20500 Schattenschaden zu."},
[85415] = {name = "Zerfleischen", description = "[q]Zerfleischt das Ziel, verursacht körperlichen Schaden und zusätzlichen Schaden über 8 sec."},
[85419] = {name = "Dröhnendes Gebrüll", description = "[q]Stößt ein dröhnendes Brüllen aus, das Gegnern im Umkreis von 5 Metern körperlichen Schaden zufügt."},
[85422] = {name = "Wachstumsverstärkung", description = "[q]Beschwört 5 sec lang pro Sekunde eine gefräßige Ranke in der Nähe."},
[85424] = {name = "Geistexplosion", description = "[q]Feuert einen Schwall spektraler Energie auf den Feind und verursacht Schattenschaden."},
[85467] = {name = "Lauern", description = "[q]Diese Kreatur kann nur Gegner angreifen, wenn sie nicht hinschauen."},
[85482] = {name = "Schattensalve", description = "[q]Verursacht 4875 to 5125 Schattenschaden und erhöht 30 sec lang den erlittenen Schattenschaden um 10%."},
[85521] = {name = "Böse Aegis", description = "[q]Erhöht die Rüstung 8 sec lang um 30%."},
[85524] = {name = "Macht der Verlassenen", description = "[q]Erhöht das Angriffstempo 8 sec lang um 30%."},
[85525] = {name = "Lordaerons Ruf", description = "[q]Erhöht das Angriffstempo 8 sec lang um 30%."},
[85527] = {name = "Anspornender Schrei", description = "[q]Erhöht die maximale Gesundheit von Einheiten im Umkreis von 6 Metern um 10%."},
[85529] = {name = "Portal formen", description = "[q]Erschafft ein Portal, das regelmäßig gesichtslose Diener beschwört."},
[85536] = {name = "Spektralexplosion", description = "[q]Fügt einem Gegner Schattenschaden zu."},
[85544] = {name = "Schattenblitz", description = "[q]Verursacht 29250 to 30750 Schattenschaden."},
[85556] = {name = "Zwielichtverschiebung", description = "[q]Während der Phasenverschiebung gegen allen Schaden immun. Sucht alle 3 Sek. neue Angriffziele. Durch Angriffe wird auch das Ziel phasenverschoben und absorbiert alle erhaltene Heilung."},
[85564] = {name = "Verschobene Realität", description = "[q]Absorbiert 25000 Heilung."},
[85573] = {name = "Ohrenbetäubende Winde", description = "[q]Fügt allen in der Zone befindlichen Spielern zunehmenden Naturschaden zu und bringt sie zum Schweigen. Dieser Effekt kann nur gewirkt werden, wenn sich in Bewegungsreichweite keine gültigen Ziele befinden."},
[85575] = {name = "Schattenheilung", description = "[q]Heilt nahe Verbündete um 100% des verursachten Schattenschadens."},
[85576] = {name = "Verwitternde Winde", description = "[q]Fügt allen in der Zone befindlichen Spielern zunehmenden Schattenschaden zu und verringert ihre erhaltene Heilung um 100%. Dieser Effekt kann nur gewirkt werden, wenn sich in Bewegungsreichweite keine gültigen Ziele befinden."},
[85584] = {name = "Hinrichtung", description = "[q]Richter, Geschworene und Henker."},
[85591] = {name = "Glühen des Lichtbrunnens", description = "[q]Erstrahlt hell."},
[85593] = {name = "Leichter Beschuss", description = "[q]Greift eine Leichenbestie mit Lichtblitzen an."},
[85625] = {name = "Spalten", description = "[q]Fügt einem Feind und seinen nächsten Verbündeten 110% des normalen Nahkampfschadens zu, wirkt auf bis zu 3 Ziele."},
[85643] = {name = "Gedankenexplosion", description = "[q]Die Gedankenexplosion verursacht 5 sec lang bei Feinden in einem Umkreis von 10 Metern 8287 to 8712 Schattenschaden."},
[85644] = {name = "Überschatteter Speer", description = "[q]Erfüllt die Waffe des Zaubernden mit Schattenenergie, die erfolgreichen Angriffen eine Chance verleiht, einen zusätzlichen Nahkampfwaffenschwung auszulösen, der Schattenschaden verursacht."},
[85664] = {name = "Umbralflammen", description = "[q]Die Umbralflammen fügen alle 4 sec lang alle 1 Sek. Feinden in einem Umkreis von 6 Metern 7800 to 8200 Schattenschaden zu."},
[85681] = {name = "Stiefelwurf", description = "[q]Wirft einen Stiefel auf einen Feind und verursacht körperlichen Schaden."},
[85687] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[85691] = {name = "Durchdringendes Heulen", description = "[q]Macht ein feindliches Ziel 4 sec lang benommen."},
[85692] = {name = "Verdammnisblitz", description = "[q]Schleudert einen Schattenblitz auf den Feind und verursacht 1188 Schattenschaden."},
[85695] = {name = "Kommunikation mit Geistern", description = nil},
[85700] = {name = "Drachenmalstoß", description = "[q]Ein starker Angriff, der den Nahkampfschaden um 10% erhöht."},
[85709] = {name = "Gesegnet vom Feuer", description = "[q]Erhaltet den Segen der Feuergeister."},
[85710] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[85711] = {name = "Gesegnet von der Luft", description = "[q]Erhaltet den Segen der Windgeister."},
[85712] = {name = "Gesegnet von der Erde", description = "[q]Erhaltet den Segen der Erde.\n Schaden um 50% erhöht.\n Zeit zwischen Angriffen um 100% verringert."},
[85713] = {name = "Berauschendes Gift", description = "[q]Fügt einem Gegner Naturschaden zu und vergiftet ihn mit einer berauschenden Substanz, durch welche Schaden verursachende Fähigkeiten zusätzlichen Naturschaden verursachen."},
[85714] = {name = "Lavaeruption", description = "[q]Ihr schleudert flüssige Lava auf das Ziel, die 58 to 67 Feuerschaden verursacht. Ist das Ziel von 'Flammenschock' betroffen, wird 'Lavaeruption' einen kritischen Treffer erzielen."},
[85715] = {name = "Kettenblitzschlag", description = "[q]Trifft einen Feind mit einem Blitzschlag, der auf einen in der Nähe befindlichen Feind überspringt. Der Zauber wirkt sich auf bis zu 3 Ziele aus und verursacht bei jedem folgenden Ziel immer geringeren Naturschaden."},
[85717] = {name = "Feuerblüte", description = "[q]Erschafft eine feurige Blüte und verursacht bei allen Feinden im Gebiet Feuerschaden."},
[85729] = {name = "Popcornexplosion", description = "[q]Schleudert regelmäßig Maiskolben, deren Popcornexplosion körperlichen Schaden verursacht."},
[85733] = {name = "Fauliger Saft", description = "[q]Das klebrige Innere dieser verfaulten Frucht verursacht Naturschaden und verringert das Bewegungstempo 5 sec lang um 50%."},
[85737] = {name = "Durchdringende Verderbnis", description = "[q]Verringert das Angriffs- und Zaubertempo eines Feindes um 40% und verlangsamt Bewegungen um 40%. Hält 8 sec lang."},
[85740] = {name = "Zephyr", description = "[q]Erhöht das Bewegungstempo naher Spieler um 70%."},
[85746] = {name = "Werfen", description = "[q]Wirft Eure Waffe und fügt dem Ziel 150% ihres Schadens zu."},
[85756] = {name = "Schießen", description = "[q]Schießt auf den Feind und fügt körperlichen Schaden zu."},
[85762] = {name = "Stampfen", description = "[q]Betäubt alle Feinde im Umkreis von 45 Metern 10 sec lang."},
[85781] = {name = "Kommunikation mit Geistern", description = nil},
[85786] = {name = "Kältekegel", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Frostschaden zu und verlangsamt ihr Bewegungstempo 8 sec lang."},
[85790] = {name = "Verderbte Seele", description = "[q]Eure Seele wird verderbt und wird beim Erreichen von 5 Stapeln brechen."},
[85791] = {name = "Düsterwolkenzorn", description = "[q]Erhöht den verursachten Schaden und das Angriffstempo."},
[85800] = {name = "Schattenflamme", description = "[q]Ziele in einem kegelförmigen Bereich vor dem Zaubernden erleiden Feuerschaden und 3 Sek. lang alle 3 Sekunden zusätzlichen Feuerschaden."},
[85802] = {name = "Sandatem", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Arkanschaden zu und verlangsamt ihr Bewegungstempo und erhöht die Zeit zwischen ihren Angriffen. Hält until cancelled lang an."},
[85812] = {name = "Ruf des Häuptlings", description = nil},
[85835] = {name = "Zermalmen", description = "[q]Ein bösartiger Angriff, der körperlichen Schaden verursacht."},
[85838] = {name = "Seelenstärke der Ahnen", description = "[q]Ahnengeister verringern den vom Ziel erlittenen Schaden um 100%."},
[85840] = {name = "Donnerknall", description = nil},
[85842] = {name = "Überlebensinstinkte", description = "[q]Eure Überlebensinstinkte lösen einen Adrenalinrausch aus, der durch Euren Körper rast! Immer dran denken, so groß wie möglich aussehen und was auch immer Ihr tut, nicht davonlaufen!"},
[85844] = {name = "Seuchenschleim", description = "[q]Verursacht 9 sec lang alle 3 Sek. 30 Naturschaden."},
[85846] = {name = "Seuchenfass", description = "[q]Schleudert ein Seuchenfass in die Ferne."},
[85850] = {name = "Leerenseele", description = "[q]Schleudert regelmäßig ein Leerengeschoss, das beim Aufschlag auf den Boden Schattenschaden verursacht."},
[85855] = {name = "Beben", description = "[q]Fügt allen Gegnern im Umkreis von 15 Metern Naturschaden zu. Dieser Effekt wirkt nur auf Einheiten am Boden."},
[85867] = {name = "Troglodytenschlag", description = "[q]Fügt dem Ziel 200% Schaden zu."},
[85873] = {name = "Strom der Dankbarkeit", description = "[q]Erhöht das Schwimmtempo 30 sec lang um 70%."},
[85874] = {name = "Sylvanas' Lied", description = "[q]Die Kampfkraft wird durch Sylvanas Gesang erhöht."},
[85910] = {name = "Reparaturen", description = "[q]Repariert das Ziel 30 sec lang um 5200 Gesundheit."},
[85915] = {name = "Raketenbeschuss des Todesklingenschredders", description = "[q]Fügt Gegnern im Radius von 100 Metern 5250 to 6750 Feuerschaden zu."},
[85944] = {name = "Hinrichtung", description = "[q]Richter, Geschworene und Henker."},
[85950] = {name = "Verlassenen erwecken", description = "[q]Lässt Gefallene als Verlassene auferstehen."},
[85962] = {name = "Feuerschlag", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[85963] = {name = "Flammenstoß", description = "[q]Lässt eine Flammensäule niedergehen, die alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen 8 sec lang alle 2 Sek. zusätzlichen Schaden zufügt."},
[85964] = {name = "Druckwelle", description = "[q]Entfesselt eine Flammenwelle, die nahen Feinden Feuerschaden zufügt und ihr Bewegungstempo 6 sec lang verringert."},
[85965] = {name = "Heilige Nova", description = "[q]Verursacht eine Explosion heiligen Lichts um den Zaubernden herum und fügt allen Zielen in einem Umkreis von 10 Metern 24 to 29 Heiligschaden zu. Zusätzlich werden alle Gruppenmitglieder in einem Umkreis von 10 Metern um 360 to 440 geheilt. Dieser Effekt erzeugt keine Bedrohung."},
[85966] = {name = "Psychischer Schrei", description = "[q]Stößt einen psychischen Schrei aus, der 4 sec lang bis zu 5 in der Nähe befindliche Feinde in die Flucht schlägt."},
[86000] = {name = "Fluch von Gul'dan", description = "[q]Beschwört einen Meteor auf das gegnerische Ziel herab, der 1793 Schattenflammenschaden verursacht und in einem Umkreis von 4 Metern eine magische Aura hervorruft, welche die kritische Trefferchance der Fähigkeiten von Dämonendienern von Hexenmeistern gegen alle Ziele innerhalb dieser Aura um 10% erhöht. Die Aura hält 15 sec lang an."},
[86002] = {name = "Stinkabsorbtion", description = "[q]Von einer Krankheitswolke geschützt. Absorbiert 120 Schaden."},
[86013] = {name = "Zwielichtmeteorit", description = "[q]Verursacht an Feinden im Umkreis von 6 Metern 292500 to 307500 Schattenschaden auf alle Ziele verteilt."},
[86027] = {name = "Furor des Cho'gall", description = "[q]Cho'gall greift das Ziel an, verursacht massiven Schatten- und körperlichen Schaden und erhöht den erlittenen Schatten- und körperlichen Schaden."},
[86032] = {name = "Up to the Citadel: Cho'gall's Backhand", description = nil},
[86042] = {name = "Erde entfesseln", description = "[q]Entfesselt die Kraft der Erde und verursacht Naturschaden."},
[86064] = {name = "Wandernde Schatten", description = "[q]Fügt allen Gegnern im Effektbereich Schattenschaden zu. Hält 6 sec lang an."},
[86070] = {name = "Rüstung durchstechen", description = "[q]Verringert die Rüstung eines Feindes 6 sec lang um 50%."},
[86071] = {name = "Säurewolke", description = "[q]Spritzt Säure auf den Standort des Ziels und erschafft eine Wolke, die Feinden im Effektbereich pro Sekunde Naturschaden zufügt. Hält 8 sec lang an."},
[86072] = {name = "Egelgift", description = "[q]Entzieht umstehenden Gegnern Gesundheit und überträgt sie auf den Zaubernden."},
[86073] = {name = "Flammenwerfer", description = "[q]Fügt allen Gegnern in einem kegelförmigen Bereich vor dem Zaubernden until cancelled lang Feuerschaden zu."},
[86079] = {name = "Bodenhieb", description = "[q]Schickt dem Zaubernden eine Energiewelle voraus, verursacht Schaden und betäubt 2 sec lang alle Einheiten in einem frontalen kegelförmigen Bereich von 7 Metern."},
[86082] = {name = "Permafrost", description = "[q]Kanalisiert eine Frostwelle auf das aktuelle Ziel und verursacht 3 sec lang in einem kegelförmigen Bereich in Richtung dieses Ziels 47500 to 52500 Frostschaden."},
[86085] = {name = "Verstümmeln", description = "[q]Greift sofort mit beiden Waffen an. Gewährt 2 Combopunkte."},
[86087] = {name = "Brennende Lohe", description = "[q]Fügt allen Gegnern innerhalb der Flammen Feuerschaden zu. Hält 15 sec lang an."},
[86088] = {name = "Dynamit werfen", description = "[q]Schleudert einen Dynamitstange nach einem Feind, die Feuerschaden verursacht und unter seinen Füßen eine brennende Lohe hervorruft."},
[86090] = {name = "Hochenergieschuss", description = "[q]Ein gezielter Schuss auf den Gegner, der erheblichen Schaden verursacht."},
[86180] = {name = "Dunkle Seuche", description = nil},
[86182] = {name = "Schneidender Orkan", description = "[q]Scharfe Winde wehen an einem zufälligen Gegner vorbei und verursachen 28500 to 31500 Naturschaden."},
[86193] = {name = "Windschlag", description = "[q]Dreht sich langsam um die Plattform und verheert sekündlich den Bereich vor dem Zaubernden. Fügt allen Feinden, der sich in diesem Bereich befindet, 47500 to 52500 Naturschaden zu und stößt sie zurück. Hält 6 sec lang an."},
[86202] = {name = "Zwielichtverschiebung", description = "[q]Ihr befindet Euch im Zwielicht!"},
[86205] = {name = "Beruhigende Brise", description = nil},
[86237] = {name = "Pirschen", description = "[q]Versetzt den Zaubernden in den Verstohlenheitsmodus. Hält an, bis die Aktion abgebrochen wird."},
[86249] = {name = "Werfen", description = "[q]Wirft Eure Waffe und fügt dem Ziel 150% ihres Schadens zu."},
[86281] = {name = "Toxische Sporen", description = "[q]Sporen schießen 5 Sek. lang pro Sekunde aus dem Zaubernden hervor und infizieren jeden Feind innerhalb von 10 Metern mit einem tödlichen Toxin. Dieses Toxin verursacht 5 sec lang 20000 Naturschaden pro Sekunde. Bis zu 25-mal stapelbar."},
[86292] = {name = "Wirbelsturmschild", description = "[q]Die tosenden Winde des Zyklons verursachen Naturschaden und verringern das Bewegungs-, Angriffs- und Zaubertempo naher Feinde um 85%."},
[86307] = {name = "Stärke sammeln", description = nil},
[86331] = {name = "Blitzschlag", description = "[q]Deckt einen Feind mit Blitzschlägen ein und fügt ihm Naturschaden zu."},
[86340] = {name = "Sturm beschwören", description = nil},
[86345] = {name = "Sprung", description = "[q]Springt das ausgewählte Ziel an."},
[86360] = {name = "Zwielichtverschiebung", description = "[q]Zwielichtdrachenflammen erreichen im Zwielichtreich volle Stärke."},
[86369] = {name = "Zwielichtdruckwelle", description = "[q]Die Zwielichtdruckwelle fügt Feinden im Umkreis von 8 Metern um das Ziel 34125 to 35875 Schattenschaden zu."},
[86374] = {name = "Weitreichender Wirbelwind", description = "[q]Verursacht 150% Waffenschaden und stößt nahe Feinde zurück."},
[86405] = {name = "Flugmuschel", description = "[q]Schleudert eine Muschel nach einem feindlichen Ziel und verursacht 24 to 29 Schaden."},
[86407] = {name = "Schillerndes Gefängnis", description = "[q]Fügt einem Gegner Schaden zu und verringert sein Bewegungstempo um 60%."},
[86408] = {name = "Blendende Zerstörung", description = "[q]Die blendende Zerstörung verursacht an Feinden im Umkreis von 12 Metern 14625 to 15375 Schattenschaden und befördert das Ziel in das Zwielichtreich!"},
[86561] = {name = "Theldurins Faust", description = "[q]Räumt mit der Faust jedes Hindernis aus dem Weg."},
[86570] = {name = "Schieferschlaghagel", description = "[q]Prügelt schnell auf den Feind ein und verursacht 50% Waffenschaden."},
[86572] = {name = "Blutrotes Starren", description = "[q]Verursacht 3 sec lang pro Sekunde Arkanschaden."},
[86574] = {name = "Pilzstampfer", description = "[q]Fügt nahen Feinden 100 Naturschaden zu und lässt in ihrer Nähe Pilze sprießen."},
[86581] = {name = "Versteinernder Blick", description = "[q]Versteinert das Ziel langsam und verringert so das Bewegungs-, Angriffs- sowie Zaubertempo um 10%. Beim Erreichen von 5 Stapeln ist das Ziel vollkommen versteinert und wird 5 Sek. lang betäubt."},
[86582] = {name = "Blutfluch", description = "[q]Erhöht den erlittenen körperlichen Schaden eines Feindes 20 sec lang um 1000."},
[86583] = {name = "Phasenverschobene Rüstung", description = "[q]Verringert die Rüstung eines Gegners 8 sec lang um 50%.\n Fügt einem Gegner 8 sec lang alle 2 Sek. Naturschaden zu."},
[86585] = {name = "Zwielichtflammen", description = "[q]Fügt nahen Gegnern Feuerschaden zu und hinterlässt ein Flammenfeld, das Gegnern in seinem Bereich 10 sec lang Schaden zufügt."},
[86593] = {name = "Winzige Zähne!", description = "[q]Babykrokilisken verursachen Schaden über Zeit und verringern das Bewegungstempo des Ziels."},
[86603] = {name = "Verstohlenheit", description = "[q]Schleicht herum."},
[86604] = {name = "Bösartiges Schlitzen", description = "[q]Ein bösartiger Angriff, der durch das feindliche Ziel schlitzt und sofort 100% normalen Waffenschaden verursacht. Lässt das Ziel 6 sec lang alle 2 Sek. um 5% der maximalen Gesundheit bluten lässt."},
[86606] = {name = "Donnernder Ansturm", description = "[q]Stürmt auf einen Gegner zu und verursacht 150% Waffenschaden."},
[86610] = {name = "Kristalliner Tritt", description = "[q]Tritt Euer Ziel, verursacht 200% Waffenschaden und stößt es zurück."},
[86620] = {name = "Unerträglicher Ruf", description = "[q]Ein unerträglicher Ruf wird entfesselt, der allen in der Nähe befindlichen Feinden 114 to 126 körperlichen Schaden zufügt und 5 sec lang das Nahkampf-, Distanzangriffs- und Zaubertempo um 60% verringert."},
[86633] = {name = "Überhitzen", description = "[q]Verbrennt einen Feind und verursacht 50 Feuerschaden und zusätzlich 9 sec lang alle 3 Sek. 50 Feuerschaden."},
[86636] = {name = "Feuerregen", description = "[q]Lässt einen Schmelzregen niedergehen, der alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen 9 sec lang alle 3 Sek. 4 Feuerschaden zufügt."},
[86639] = {name = "Smaragdregen", description = "[q]Fügt allen Gegnern nahe dem Zaubernden 10 sec lang Naturschaden zu."},
[86676] = {name = "Versteinernder Blick", description = "[q]Betäubt alle sichtbaren Ziele."},
[86677] = {name = "Kristallblitz", description = "[q]Teleportiert den Zaubernden hinter das Ziel. Befreit den Zaubernden auch von jeglichen Fesseln."},
[86695] = {name = "Wirbelwind", description = "[q]Fügt umstehenden Gegnern alle 2 Sek. körperlichen Schaden zu, verringert jeddoch das Bewegungstempo um 40%."},
[86699] = {name = "Schockwelle", description = "[q]Schickt dem Krieger eine Energiewelle voraus, die Schaden verursacht und alle feindlichen Ziele innerhalb von 10 Metern kurzzeitig betäubt."},
[86717] = {name = "Drachenbann", description = "[q]Schießt auf einen Gegner und verursacht Feuerschaden."},
[86722] = {name = "Spott", description = "[q]Verspottet ein Ziel und erhöht so die Chance, dass es den Verspottenden angreift."},
[86725] = {name = "Umgeschichtete Erde", description = "[q]Fügt allen Gegnern im Umkreis von 10 Metern Naturschaden zu."},
[86726] = {name = "Wilde Stecherei", description = "[q]Der Zaubernde beginnt, wild um sich zu stechen. Das Angriffstempo ist stark erhöht, allerdings ist die Trefferchance um 35% verringert."},
[86729] = {name = "Feger", description = "[q]Fügt einem Feind und seinem nächsten Verbündeten 110% des normalen Nahkampfschadens zu."},
[86733] = {name = "Schinderwirbel", description = "[q]Schlägt schnell auf den Feind ein und verursacht 50% Waffenschaden."},
[86735] = {name = "Goldmine initiieren", description = "[q]Initiand Goldmine ist im Haus... äh, in den Tiefen!"},
[86736] = {name = "Wutanfall", description = "[q]Erhöht 5 min lang das Angriffstempo des Zaubernden um 30% und den von ihm verursachten körperlichen Schaden um 10%."},
[86738] = {name = "Böser blauer Fleck", description = "[q]Schmettert das gegnerische Ziel zu Boden, verursacht 20% des normalen Waffenschadens und hinterlässt eine schwere Prellung, die den erlittenen körperlichen Schaden um 20% erhöht."},
[86740] = {name = "Unter die Gürtellinie", description = "[q]Ein Schlag unter die Gürtellinie, der 75% des normalen Waffenschadens verursacht, alle Rüstung ignoriert und das Ziel 2 sec lang betäubt."},
[86744] = {name = "Kettenhaken", description = "[q]Verursacht 150 körperlichen Schaden und zieht das Ziel zum Zaubernden."},
[86765] = {name = "Rüstungsschmelze", description = "[q]Schmilzt die Rüstung eines feindlichen Ziels und reduziert seine Rüstung, wodurch deren Wirksamkeit 6 sec lang um 50% verringert wird."},
[86766] = {name = "Merkuriale Absorption", description = "[q]Zehrt einen Zauber auf, absorbiert seine Magie und verwendet sie gegen den Zaubernden."},
[86771] = {name = "Felssplitter", description = "[q]Der Erdelementar explodiert in Felssplitter und verursacht 27 to 33 Naturschaden Gegnern innerhalb von 5 Metern."},
[86780] = {name = "Schattengefängnis", description = "[q]Sperrt das Ziel ein."},
[86785] = {name = "Sickerndes Quecksilber", description = "[q]Verursacht 20 sec lang pro Sekunde Schaden. Das Ziel wird transformiert sobald der Zauber 5-mal Stapel erreicht."},
[86788] = {name = "Blackout", description = "[q]Der Blackout absorbiert 75000 Heilung auf das Ziel. Wenn 'Blackout' entfernt wird oder ausläuft, verursacht er 975000 to 1025000 Schattenschaden bei Feinden in einem Umkreis von 8 Metern, wobei der Schaden zwischen allen von 'Blackout' betroffenen Zielen aufgeteilt wird."},
[86798] = {name = "Kristalline Geschosse", description = "[q]Schleudert Kristallgeschosse auf das Ziel, die Arkanschaden verursachen."},
[86800] = {name = "Wilde Energie", description = "[q]Fügt dem Ziel 13 to 15 Arkanschaden zu."},
