[86820] = {name = "Wahnsinniger Sprint", description = "[q]Der Zaubernde rennt wie wild auf das gegnerische Ziel zu, das Bewegungstempo wird um 60% erhöht und 3 sec lang wird der Nahkampfschaden um 20 erhöht."},
[86830] = {name = "Bevorstehender Untergang", description = "[q]Vollführt einen derart teuflischen Schlag, dass er schriftlich nicht hinreichend beschrieben werden kann. Auch betroffene Ziele werden demzufolge das Ausmaß der Auswirkungen dieses Zaubers kaum verstanden haben, wenn seine blendende Brillianz sie überwältigt wie eine Flutwelle der Zerstörung."},
[86840] = {name = "Verschlingende Flammen", description = "[q]'Verschlingende Flammen' verursacht alle 1 Sek. 90000 Schattenschaden. Der Schaden lässt nach, je weiter das Ziel vom Ursprung der verschlingenden Flammen entfernt ist."},
[86845] = {name = "Schatteneruption", description = "[q]Schatten brechen in alle Richtungen aus und bilden in der Nähe zufällig positionierte Schattenpfützen. Diese Pfützen verursachen beim Einschlag Schattenschaden."},
[86861] = {name = "Erdbeben", description = "[q]Ihr lasst die Erde an einem ausgewählten Zielort beben und brechen, wodurch Feinde in einem Umkreis von 8 Metern alle 1 Sek. Naturschaden erleiden. Hält 10 sec lang an."},
[86867] = {name = "Qual der zerrissenen Erde", description = "[q]Erhöht den erlittenen Schaden um 25% und den verursachten Schaden um 50%. Hält 1 min lang an."},
[86881] = {name = "Kristallbeschuss", description = "[q]Fügt allen Gegnern im Umkreis von 5 Metern um das Zielgebiet alle 0,5 Sek. 9500 to 10500 Schaden zu. Hält 4 sec lang an."},
[86911] = {name = "Instabiles Erdungsfeld", description = "[q]Asaad kanalisiert und erzeugt ein Erdungsfeld, das ihn beschützt, während er Blitze vom Himmel ruft."},
