[89713] = {name = "ES ITS DER EINZIGE WEG", description = nil},
[89734] = {name = "Faulige Aura", description = "[q]Verdorbenes Essen verursacht pro Sek. Naturschaden."},
[89751] = {name = "Teufelssturm", description = "[q]Eure Teufelswache vollführt waghalsige Axtangriffe, die 6 sec lang allen Zielen im Umkreis von 8 Metern alle 1 Sek. Waffenschaden plus [((Spell power * .50) * 2) * 0.33 + 130 ] zufügen. Während des Teufelssturms kann die Teufelswache keine anderen Aktionen ausführen."},
[89766] = {name = "Axtwurf", description = "[q]Die Teufelswache schleudert ihre Axt und betäubt das Ziel 4 sec lang."},
[89769] = {name = "Explodieren", description = "[q]Verursacht Feuerschaden und stößt nahe Feinde zu Boden."},
[89773] = {name = "Zerfleischen", description = "[q]Verursacht alle 2 Sek. 154649 to 179728 Schaden.\n Das Ziel wird nach 30 sec bis zum Tode zerfleischt sein."},
[89786] = {name = "Kanonenschuss", description = "[q]Feuert einen Schuss auf die Versorgungsboote ab."},
[89792] = {name = "Flüchten", description = "[q]Flüchtet zum Meister. 'Flüchten' entfernt alle Betäubungs- und Verlangsamungseffekte."},
[89798] = {name = "Meisterabenteurerauszeichnung", description = "[q]Nefarian hat seinem Dank dafür Ausdruck verliehen, dass Ihr Euch um seine schwächsten Kreationen gekümmert habt, und Euch einen neuen Titel verliehen."},
[89808] = {name = "Magie herausbrennen", description = "[q]Brennt schädigende Magie aus dem Ziel heraus und entfernt 1 schädlichen Zauber von einem Freund."},
[89812] = {name = "Extremes Schleichen", description = "Dieser Worgen schleicht so gut, dass selbst das größte Leuchtfeuer der Welt ihn nicht sichtbar machen könnte."},
[89829] = {name = "Geölt", description = "[q]Dieser Schredder wurde geölt! Sucht einen, der es nötiger hat!"},
[89905] = {name = "Giftiger Biss", description = "[q]Spuckt Gift auf den Feind, fügt ihm Naturschaden und dann 10 sec lang alle 2 Sek. zusätzlichen Schaden zu."},
[89918] = {name = "Hämmern", description = "[q]Schlägt auf den Boden, verursacht Schaden und destabilisiert für eine kurze Zeit den Boden."},
[89932] = {name = "Fraktur", description = "[q]Bricht den Boden vor dem Zaubernden auf und erzeugt nacht 2 Sek. eine Eruption aus Flammen."},
[89942] = {name = "Zauberschutz des Weltenbrechers", description = "[q]Alle 5 Sekunden 50000 Gesundheit regenerieren."},
[89972] = {name = "Windschlag", description = "[q]Verursacht Naturschaden und stößt vor dem Zaubernden befindliche Gegner zurück."},
[90028] = {name = "Essenz entfesseln", description = "[q]Entfesselt eine Flut von Zwielichtenergie, die alle nahen Feinde trifft und alle 1 Sek. Schaden in Höhe von 10% ihrer maximalen Gesundheit verursacht."},
[90045] = {name = "Unbezähmbar", description = "[q]Erlangt durch einen Schub reiner Willenskraft die Kontrolle zurück, wird gegen alle Fesseln und Einschränkungen immun, bis sie aufgehalten wird. Verursacht bei Feinden im Umkreis von 8 Metern 40000 Schattenschaden und stößt sie zurück."},
[90046] = {name = "Sturmvortex", description = "[q]Fügt nahen Gegnern Naturschaden zu und stößt sie zurück."},
[90047] = {name = "Renegatenstärke", description = "[q]Erfüllt das Ziel mit der Stärke der Defias. Erhöht verursachten körperlichen Schaden um 200%, reduziert erlittenen Schaden von Zaubern und Fähigkeiten mit Flächenwirkung um 90% und erhöht die Gesundheit um 300%."},
[90052] = {name = "Kältekegel", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Frostschaden zu und verlangsamt ihr Bewegungstempo 8 sec lang."},
[90053] = {name = "Arkane Explosion", description = "[q]Lässt eine Druckwelle aus Magie entstehen und fügt in der Nähe befindlichen Feinden Arkanschaden zu."},
[90054] = {name = "Demoralisierender Ruf", description = "[q]Verringert 20 sec lang die Nahkampfangriffskraft von Gegnern in der Nähe."},
[90063] = {name = "Überkopfkracher", description = "[q]Schlägt mit einem großen Baumstamm auf den Boden vor sich und verursacht 300% Waffenschaden bei in der Nähe befindlichen Gegnern."},
[90067] = {name = "Feuriger Atem", description = "[q]Feuert einen Blitz feurigen Atems auf den Feind und fügt allen Eindringlingen des Schwarzfels 13125 to 16875 Schaden zu."},
[90073] = {name = "Druckwelle", description = "[q]Entfesselt eine Flammenwelle, die nahen Feinden Feuerschaden zufügt und ihr Bewegungstempo 6 sec lang verringert."},
[90075] = {name = "Schwarzes Spalten", description = "[q]Fügt einem Gegner und seinen nächsten Verbündeten Waffenschaden plus zusätzlichem Schaden zu. Trifft bis zu 3 Ziele. Gegner, die von dieser Fähigkeit getroffen wurden, erleiden zusätzlich 12 sec lang alle 3 Sek. Schattenschaden."},
[90083] = {name = "Zwielichtatem", description = "[q]Verursacht in einem kegelförmigen Bereich von 25 Metern vor dem Zaubernden 57600 to 62400 Schattenschaden."},
[90098] = {name = "Axt im Kopp", description = "[q]Fügt einem Feind 15 sec lang alle 3 Sek. körperlichen Schaden zu."},
[90099] = {name = "Beobachten", description = "[q]Konzentriert sich auf den Feind und erhöht den erlittenen Schaden."},
[90107] = {name = "Essenz absorbieren", description = "[q]Mit der absorbierten Macht einer reinen Zwielichtessenz erfüllt.\n Schaden um 10% erhöht.\n Gesundheit um 10% erhöht."},
[90125] = {name = "Flammenatem", description = "[q]Fügt allen Feinden vor dem Zaubernden 95000 to 105000 Feuerschaden zu."},
