[90255] = {name = "Feuernova", description = "[q]Verursacht Feuerschaden bei in der Nähe befindlichen Feinden."},
[90257] = {name = "Großbrand", description = "[q]Setzt einen Feind in Brand, fügt ihm 5 sec lang Feuerschaden zu und versetzt ihn in Panik. Solange das Ziel betroffen ist, versengen die Flammen in regelmäßigen Abständen auch seine in der Nähe befindlichen Verbündeten und verursachen Feuerschaden."},
[90272] = {name = "Heldenhafter Sprung", description = "[q]Springt durch die Luft und schmettert auf alle Feinde im Umkreis von 8 Metern um das Zielgebiet hinab. Verursacht 24 to 29% Waffenschaden."},
[90325] = {name = "Zerschmettern", description = "[q]Zerschmettert den Gegner und verursacht Waffenschaden plus zusätzlichen Schaden."},
[90333] = {name = "Magmaspalte", description = "[q]Bricht den Boden vor dem Zaubernden auf und erzeugt nacht 2 Sek. eine Eruption aus Flammen."},
[90373] = {name = "Lavafluss", description = nil},
[90391] = {name = "Lavateich", description = "[q]Fügt nahen Gegnern Feuerschaden zu."},
[90398] = {name = "Seeschwall", description = "[q]Trifft einen Feind mit einem Wasserblitz, der auf andere nahe Feinde überspringt. Der Zauber trifft bis zu 3 Ziele und verursacht bei jedem von ihnen Frostschaden. Schaden erhöht sich mit jedem Sprung."},
[90409] = {name = "Teiche aktivieren", description = nil},
