[90447] = {name = "Giftwolke", description = "[q]Erschafft eine Giftwolke unter Calders Leichnam."},
[90522] = {name = "Wasserhülle", description = "[q]Absorbiert 50000 Schaden. Wenn die Wasserhülle bricht, explodiert sie und fügt Feinden in einem Umkreis von 8 Metern 48750 to 51250 Frostschaden zu."},
[90550] = {name = "Arkanbeschuss", description = "[q]Schleudert mehrere Geschosse auf das feindliche Ziel und verursacht Arkanschaden."},
