[90625] = {name = "Erdstachel", description = "[q]Beschwört einen Erdstachel, der am Zielort ausbricht und 4675 to 6325 Natuschaden verursacht."},
[90662] = {name = "Schwerer Schlag", description = "[q]Wird gegen einen Feind geschwungen, verursacht Waffenschaden plus 50 und stößt Gegner zurück."},
[90673] = {name = "Schattengespinst", description = "[q]Verursacht an allen Feinden in einem kegelförmigen Bereich vor dem Zaubernden 24375 to 25625 Schattenschaden."},
[90677] = {name = "Wässriger Schild", description = "[q]Legt einen Schild um den Zaubernden, der Schaden in Höhe von bis zu 50% seiner maximalen Gesundheit absorbiert."},
