[90783] = {name = "Tödlicher Wurf", description = "[q]Wirft einen Dolch auf ein gegnerisches Ziel und verursacht 24 to 29% Waffenschaden. Unterbricht außerdem das Wirken von Zaubern und verhindert 3 sec lang, dass diese Art Zauber gewirkt werden kann."},
[90789] = {name = "Seuchenbiss", description = "[q]Fügt einem Feind alle 2 Sek. Naturschaden zu und erhöht seinen erlittenen körperlichen Schaden um 10%. Hält 8 sec lang an."},
[90846] = {name = "Flammenschock", description = "[q]Verursacht 6337 to 6662 Feuerschaden sowie zusätzlich 8 sec lang alle 1 Sek. 2437 to 2561 Feuerschaden."},
[90905] = {name = "Eingesprungenes Spalten", description = "[q]Springt das Ziel an, fügt ihm und seinen nächsten zwei Verbündeten 150% Waffenschaden zu und stößt sie zu Boden."},
[90908] = {name = "Mandibelschmettern", description = "[q]Zermalmt das Ziel, verursacht 150% körperlichen Schaden."},
