[90928] = {name = "Sturmangriff", description = "[q]Stürmt auf das Ziel zu und bewegt sich schnell zu seinem Standort."},
[90929] = {name = "Tollkühnheit", description = "[q]Immun gegen Furchteffekte.\n Alle Angriffe treffen kritisch.\n Rüstung um 35% verringert."},
[90932] = {name = "Wutzone", description = "[q]Erzeugt ein magisches Feld. Einheiten innerhalb des Feldes verursachen 50% zusätzlichen Schaden."},
[90934] = {name = "Quest Credit: Duarn", description = nil},
[90938] = {name = "Blutgeschoss", description = "[q]Fügt einem Gegner Schaden zu und verringert 4 sec lang sein Bewegungstempo."},
[90946] = {name = "Blutwäsche", description = "[q]Erhöht 6 sec lang das Angriffs- und Zaubertempo eines Verbündeten um 30%."},
[90950] = {name = "Verschlingende Flammen", description = "[q]'Verschlingende Flammen' verursacht alle 1 Sek. 40000 Schattenschaden. Der Schaden lässt nach, je weiter das Ziel vom Ursprung der verschlingenden Flammen entfernt ist."},
[90951] = {name = "Finsterer Stoß", description = "[q]Verursacht 50% Waffenschaden."},
[90954] = {name = "Camouflage", description = "[q]Wird eins mit der Umgebung und kann nicht zum Ziel von Distanzangriffen werden."},
[90956] = {name = "Schattenschritt", description = "[q]Versucht, in die Schatten zu tauchen, um hinter dem Gegner zu erscheinen."},
[90958] = {name = "Entrinnen", description = "[q]Erhöht die Ausweichchance des Zaubernden um 35%."},
[90960] = {name = "Wirbelnde Klingen", description = "[q]Umgibt den Feind mit wirbelnden Klingen. Verursacht bei allen in der Nähe befindlichen Einheiten körperlichen Schaden."},
