[90980] = {name = "Spalten", description = "[q]Fügt einem Feind und seinen nächsten Verbündeten 150% des normalen Nahkampfschadens zu, wirkt auf bis zu 3 Ziele."},
[90994] = {name = "Tunnel", description = "[q]Gräbt sich unter dem Zaubernden in die Erde, wodurch er until cancelled lang verschwindet."},
[91004] = {name = "Heiliges Feuer", description = "[q]Verzehrt einen Feind mit Flammen, verbrennt ihn und fügt ihm im Verlauf von 8 sec alle 2 Sek. zusätzlichen Feuerschaden zu."},
[91009] = {name = "Renegatenstärke", description = "[q]Erfüllt das Ziel mit der Stärke der Defias. Erhöht verursachten körperlichen Schaden um 200%, reduziert erlittenen Schaden von Zaubern und Fähigkeiten mit Flächenwirkung um 90% und erhöht die Gesundheit um 300%."},
[91017] = {name = "Welle der Heilung", description = "[q]Ruft Naturmagie herbei, um einen Verbündeten zu heilen."},
[91018] = {name = "Kettenheilung", description = "[q]Erfüllt einen verwundeten Verbündeten mit Heilenergie, die sich auf einen anderen in der Nähe befindlichen Verbündeten ausbreitet. Der Zauber wirkt sich auf bis zu 3 Ziele aus, aber die Heilenergie wird bei jedem folgenden Ziel um 50% reduziert."},
[91032] = {name = "Trunkenheit", description = "[q]Erhöht die Zeit zwischen den Angriffen eines Gegners und 30% und verlangsamt sein Bewegungstempo um 50%. Hält 10 sec lang an."},
[91034] = {name = "Bedrohlicher Schrei", description = "[q]Erhöht das Nahkampfangriffstempo des Zaubernden 30 sec lang um 50%."},
[91036] = {name = "Motivieren", description = "[q]Verursacht bei einem Feind Schattenschaden und erhöht das Bewegungstempo um 50%."},
[91038] = {name = "Werfen", description = "[q]Wirft Eure Waffe, verursacht 150% Waffenschaden."},
[91045] = {name = "Aufwärtshaken", description = "[q]Fügt einem Feind 200% Waffenschaden zu und stößt ihn zurück."},
[91050] = {name = "Hinterkopfschlag", description = "[q]Eine Kopfnuss, die 150% Waffenschaden verursacht und die Ausweich- und Parierchance des Ziels 6 sec lang um 50% verringert."},
[91053] = {name = "Raserei", description = "[q]Erhöht 6 sec lang das Angriffstempo des Zaubernden um 100%."},
[91061] = {name = "Bloody Explosion - Hands", description = nil},
[91064] = {name = "Schlitzende Klauen", description = "[q]Fügt dem gewählten feindlichen Ziel 75% Waffenschaden zu."},
[91077] = {name = "Wilder Sprung", description = "[q]Der wilde Spuk springt sein Ziel an, wobei das Angriffstempo des Spuks 5 sec lang um 50% erhöht wird."},
[91088] = {name = "Fleisch verzehren", description = "[q]Entzieht dem Ziel 20% seiner maximalen Gesundheit und gewährt einen Teil der entzogenen Gesundheit dem Zaubernden."},
[91090] = {name = "Klaue", description = "[q]Beharkt einen Gegner mit Klauen und verursacht Schaden."},
[91099] = {name = "Verrottende Galle", description = "[q]Der Leichenfresser sammelt Galle an und schleudert sie auf das Ziel. Verursacht alle 1 Sek. 2000 Naturschaden und verringert den verursachten Schaden und die verursachte Heilung des Ziels um 25%."},
[91121] = {name = "Tektonische Erschütterung.", description = "[q]Das kommt davon, wenn man explodierende Schafe haut."},
