[91213] = {name = "Zerkrachen", description = "[q]Fügt Gegnern im Umkreis von 10 Metern 19000 to 21000 körperlichen Schaden zu."},
[91220] = {name = "Erschauerndes Gebrüll", description = "[q]Ein lautes, markerschütterndes Gebrüll erhallt und versetzt alle in der Nähe befindlichen Feinden 3 sec lang in Angst. Erlittener Schaden hebt den Effekt auf."},
[91233] = {name = "Gemeiner Kick", description = nil},
[91263] = {name = "Fallen sprengen", description = "[q]Macht alle mystischen Fallen scharf und lässt sie nach 0 explodieren."},
[91277] = {name = "Brennende Seele", description = "[q]Diese Seele brennt."},
[91289] = {name = "Knochenschild", description = "[q]Der Zaubernde wird von 3 wirbelnden Knochen umgeben. Solange wenigstens 1 Knochen verbleibt, wird der erlittene Schaden um 20% verringert und der mit allen Angriffen, Zaubern und Fähigkeiten verursachte Schaden um 2% erhöht. Jeder erfolgreiche, Schaden verursachende Angriff verbraucht 1 Knochen. Hält 5 min lang an."},
[91291] = {name = "Blutsauger", description = "[q]Entzieht einem Feind im Verlauf von 5 sec insgesamt 30000 Gesundheit und überträgt sie auf den Zaubernden."},
[91294] = {name = "Wirbelnde Klinge", description = "[q]Greift in der Nähe befindliche Feinde mit einem Wirbel aus Stahl an, der 8 sec lang anhält und normalen Schaden plus 110 verursacht."},
[91297] = {name = "Welpen beschwören", description = "[q]Beschwört 3 Flammenschuppenjunge, die Euch 1 min beistehen."},
[91303] = {name = "Bekehrung", description = "[q]Cho'gall lockt die geistig Schwachen und bringt das Ziel dazu, den Zauber 'Verehren' auf ihn zu kanalisieren. Cho'gall zu verehren verleiht ihm alle 3 Sek. 'Finstere Gebete'. 'Verehren' kann unterbrochen werden."},
[91304] = {name = "Schattenpeitsche", description = "[q]Peitscht Schimaeron mit Schattenenergie, verursacht überwältigende Schmerzen und unterbricht die Fähigkeit 'Fehde'."},
[91307] = {name = "Höhnende Schatten", description = "[q]Fügt allen Gegnern pro Sekunde 2000 Schattenschaden zu."},
[91309] = {name = "Sturmangriff", description = "[q]Stürmt auf den am weitesten entfernten Gegner zu, verursacht normalen Schaden plus 150, und verursacht dann Furcht bei allen in der Nähe befindlichen Zielen."},
[91313] = {name = "Flammenstoß", description = "[q]Schickt dem Golem eine Flammenwelle voraus, die in einem kegelförmigen Bereich von 10 Metern Feuerschaden verursacht. Hält until cancelled an."},
[91314] = {name = "Heben", description = "[q]Verursacht Schaden und wirft das Ziel über die Schulter des Zaubernden."},
[91324] = {name = "Schattenhafte Berührung", description = "[q]Schattenhände greifen zu und verursachen beim Ziel 15 sec lang alle 3 6000 Schattenschaden."},
[91325] = {name = "Schattenvortex", description = "[q]Erschafft eine dunkle Pfütze, die im Verlauf von 10 sec bei Feinden im Gebiet 60000 Schattenschaden verursacht."},
[91335] = {name = "Zerschmettern", description = "[q]Zerschmettert den Gegner und verursacht Waffenschaden plus zusätzlichen Schaden."},
[91348] = {name = "Weichklopfen", description = "[q]Macht das Ziel empfindlich, verringert die Rüstung pro Stapel um 3% und verursacht alle 1 Sek. 150 körperlichen Schaden. Bis zu 15-mal stapelbar. Hält 10 sec lang an."},
[91408] = {name = "Wirbelwind", description = "[q]Fügt nahen Feinden pro Sekunde 18850 to 21150 Schaden zu, verringert das Bewegungstempo jedoch um 35%."},
[91415] = {name = "Raserei", description = "[q]Erhöht das Angriffstempo um 60%.\n Erhöht den Schaden um 60%."},
[91419] = {name = "Knochenwirbel", description = "[q]Wirft einen Knochen, der einem zufälligen feindlichen Ziel 3 sec lang alle 1 Sek. 9000 körperlichen Schaden zufügt."},
[91463] = {name = "Schildwall", description = "[q]Verringert den vom Zaubernden erlittenen körperlichen und magischen Schaden 10 sec lang um 75%."},
[91522] = {name = "Dunkle Berührung", description = "[q]Fügt einem feindlichen Ziel 40% des normalen Waffenschadens als Schattenschaden zu."},
[91554] = {name = "Stinkwolke", description = "[q]Eine faulige Krankheitswolke umgibt die Leiche und verursacht 10 sec lang pro Sekunde 8000 Schaden."},
[91561] = {name = "Krankheitswolke", description = "[q]Eine Krankheitswolke umgibt den Träger, die Gegnern im Umkreis von 5 Metern alle 3 Sek. 1950 to 2050 Naturschaden zufügt."},
[91563] = {name = "Lebende Bombe", description = "[q]Platziert eine feurige Bombe an einem Feind. Nach 6 Sek explodiert die Bombe und fügt dem Ziel und allen seinen nahen Verbündeten 20000 Feuerschaden zu."},
[91565] = {name = "Feenfeuer", description = "[q]Verringert die Rüstung des Ziels 5 min lang um 4%. Während der Wirkungsdauer kann das Ziel weder Verstohlenheit anwenden noch unsichtbar werden. Bis zu 3-mal stapelbar."},
[91571] = {name = "Schreddergeschoss", description = "[q]Fügt Zielen in einem kleinen Radius um das Einschlagsgebiet 23797 to 33202 Schaden zu. Kann sehr schnell hintereinander abgefeuert werden."},
[91574] = {name = "Zerstörergeschoss", description = "[q]Fügt Zielen in einem großen Radius um das Einschlagsgebiet 200400 to 279600 Feuerschaden zu. Das Laden eines neues Zerstörergeschosses benötigt etwas Zeit."},
[91594] = {name = "Wedding Phase Aura", description = nil},
[91601] = {name = "Instant Boulder Poof Spawn", description = nil},
[91611] = {name = "Gargoylestoß", description = "[q]Fügt einem Gegner 7400 to 8600 Naturschaden zu."},
[91614] = {name = "Zakahns Schlangen", description = nil},
[91622] = {name = "Arkanbeschuss", description = "[q]Schleudert mehrere Geschosse auf das feindliche Ziel und verursacht 17100 to 18900 Arkanschaden."},
[91623] = {name = "Arkane Explosion", description = "[q]Lässt eine Druckwelle aus Magie entstehen und fügt in der Nähe befindlichen Feinden 13500 to 16500 Arkanschaden zu."},
[91624] = {name = "Magie verstärken", description = "[q]Erhöht den verursachten Feuer-, Frost- und Arkanschaden des Zaubernden 8 sec lang um 50%."},
[91625] = {name = "Explodieren", description = "[q]Verursacht Feuerschaden und stößt nahe Feinde zu Boden."},
[91629] = {name = "Knochenschild", description = "[q]Absorbiert 100000 Schaden. Absorbieren die Knochen die gesamte Menge, zerbrechen sie und fügen feindlichen Zielen im Umkreis von 15 Metern 30000 Schaden zu."},
[91646] = {name = "Sonnenblume pflanzen", description = "[q]Erschafft eine Sonnenblume, die Solarkraft aus dem Himmel konzentriert. Solarkraft wird verwendet, um andere Pflanzen zu erschaffen."},
[91649] = {name = "Speier pflanzen", description = "[q]Erschafft einen Speier, der Säure auf angreifende Ghule spuckt."},
[91668] = {name = "Instabile Stärke", description = "[q]Ändert 8 sec lang zufällig den vom Zaubernden verursachten körperlichen Schaden."},
[91677] = {name = "Eitrige Spucke", description = "[q]Schießt Gift auf den Feind, fügt ihm Naturschaden und dann 10 sec lang alle 2 Sek. zusätzlichen Schaden zu."},
[91704] = {name = "Felsnuss pflanzen", description = "[q]Erschafft eine Felsnuss, die Angriffe angreifender Ghule absorbiert."},
[91710] = {name = "Schlingranke pflanzen", description = "[q]Erschafft eine Schlingranke, die einen angreifenden Ghul ergreift und langsam tötet."},
[91723] = {name = "Schnitterstoß", description = "[q]Verursacht in einem kegelförmigen Bereich vor dem Schnitterprototypen 13875 to 16125 körperlichen Schaden. Erzeugt 20 Dampfkraft."},
[91726] = {name = "Schnittersturmangriff", description = "[q]Stürmt auf einen Gegner zu, verursacht 27750 to 32250 körperlichen Schaden und betäubt ihn 4 sec lang."},
[91727] = {name = "Hochdruckschlag", description = "[q]Hoher Dampfdruck wird in einem mächtigen Angriff entladen. Verursacht 18500 to 21500 körperlichen Schaden und stößt das Ziel zurück."},
[91731] = {name = "Obsidianverbindung", description = "[q]Eine fortschrittliche Obisidionlegierung macht den Schnitterprototypen immun gegen Feuerschaden."},
[91737] = {name = "Feuer!", description = "[q]Verursacht alle 3 Sek. Feuerschaden. Die Einheit wird sich nach 30 sec selbstzerstören."},
[91801] = {name = "Tödlicher Stoß", description = "[q]Verursacht 120% Waffenschaden und verwundet das Ziel, verringert außerdem die Wirksamkeit jeglicher Heilung 5 sec lang um 25%."},
[91815] = {name = "Geschmolzener Schild", description = "[q]Geschmolzener Stahl schützt den Zaubernden vor magischen Angriffen und fügt allein in der Nähe befindlichen Feinden Feuerschaden zu. Erlittener Magieschaden um 50% verringert."},
[91830] = {name = "Fixieren", description = "[q]Fixiert sich auf das aktuelle Ziel des Zaubernden und lässt den Zaubernden nur dieses Ziel angreifen."},
[91849] = {name = "Griff des Todes", description = "[q]Ruft alle in der Nähe befindlichen Feinde zu Lord Victor Nefarius Standort."},
[91857] = {name = "Überladener Energiegenerator", description = "[q]Beschwört unter einem zufälligen befreundeten Ziel einen Energiegenerator herbei. Dieser Generator erhöht den Schaden aller befreundeten und feindlichen Ziele im Umkreis von 5 Metern um 50%. Hält 1 min lang an."},
[91871] = {name = "Blitzladung", description = "[q]Erlittener Schaden um 33% erhöht.\n Verursachter Schaden um 10% erhöht.\n Tempo um 10% erhöht. Das Tempo ist mit ähnlichen Effekten nicht stapelbar."},
[91933] = {name = "Einschüchterndes Gebrüll", description = "[q]Brüllt laut einen Feind an, lähmt ihn 6 sec lang mit Schrecken und lässt alle anderen in der Nähe befindlichen Feinde voller Furcht flüchten."},
[91977] = {name = "Ungebetener Gast", description = "[q]Feuert ein Geschoss auf ein zufälliges Ziel ab. Wenn das Geschoss auftrifft, fügt es allen Gegnern im Umkreis von 5 Metern um dieses Gebiet Schattenschaden zu."},
[91978] = {name = "Kalte Füße", description = "[q]Verursacht bei Feinden eheliche Nervosität und friert sie fest!"},
[91979] = {name = "Kalte Füße", description = "[q]Dunkle Hochzeitsglocken läuten das Ende aller Tage ein. Lässt in der Nähe befindliche Feinde vor Angst erstarren."},
[91980] = {name = "tickerexplosion", description = nil},
[91981] = {name = "Ticker Bomb Invis Removal", description = nil},
[91997] = {name = "Schattenblitz", description = "[q]Schleudert einen Blitz aus dunkler Magie auf einen Feind und verursacht Schattenschaden."},
[91998] = {name = "Fels werfen", description = "[q]Schleudert einen Felsen auf einen Gegner, fügt körperlichen Schaden zu."},
[92001] = {name = "Schild des Rufers", description = "[q]Der Schild des Rufers schützt gegen viele Arten der Gruppenkontrolle."},
[92007] = {name = "Wirbelnder Dampf", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Frostschaden zu und verlangsamt ihr Bewegungstempo 2 sec lang."},
[92008] = {name = "Kondensierender Dampf", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Frostschaden zu und verlangsamt ihr Bewegungstempo 2 sec lang."},
[92011] = {name = "Eiskalter Dampf", description = "[q]Fügt Feinden in einem kegelförmigen Bereich vor dem Zaubernden Frostschaden zu und verlangsamt ihr Bewegungstempo 2 sec lang."},
[92013] = {name = "Kondensierung", description = "[q]Zieht Kraft aus den wirbelnden Dämpfen. Kon-den-sa-tion."},
[92023] = {name = "Umschließende Schatten", description = "[q]Nefarian hält das ausgewählte Ziel fest und verhindert so, dass es sich bewegt."},
[92025] = {name = "Siedendes Blut", description = "[q]Bringt das Blut aller Feinde im Umkreis von 10 Metern zum Kochen und verursacht Schattenschaden."},
[92038] = {name = "Dampfanimus", description = "[q]Die Dämpfe haben sich vereint und könnten sich jederzeit zerstreuen, wobei sie schweren Schaden verursachen und Feinde zurückstoßen."},
[92042] = {name = "Vereinigen", description = "[q]Verursacht Frostschaden und stößt nahe Feinde zurück."},
[92048] = {name = "Schattenmacht", description = "[q]Der Blitzableiter wird von Nefarius mit den Schatten verschmolzen, was ihn zu einer Schattenmacht werden lässt. Die Schattenmacht fügt allen nahen Verbündeten Schattenschaden zu, basierend auf ihrem Abstand zur Quelle."},
[92067] = {name = "Statische Überladung", description = "[q]Lädt einen Gegner mit Energie auf, die seinen nahen Verbündeten Stromschläge versetzt, durch welche sie 10 sec lang alle 2 Sek. 9250 to 10750 Naturschaden erleiden. Durch starke Gravitationsfelder lösen sich diese Energien gefahrlos auf."},
[92072] = {name = "Pfählender Zug", description = "[q]Schleudert Euren Dreizack nach einem Ziel und zieht es zu Euch zurück. Verursacht 24 to 29 körperlichen Schaden und verspottet das Ziel."},
[92075] = {name = "Gravitationskern", description = "[q]Zerquetscht einen Feind mit heftiger Schwerkraft, verursacht alle 2 Sek. 10000 körperlichen Schaden. Solange das Ziel betroffen ist, behindert das Gravitationsfeld regelmäßig die Bewegung und die Aktionen in der Nähe befindlicher Verbündeter."},
[92100] = {name = "Giftige Mischung", description = "[q]Ein mächtiges Gebräu, dass es Feinden unmöglich macht, sich zu bewegen oder zu reagieren."},
[92118] = {name = "Zündung", description = "[q]Ignites the ground in multiple directions. DO NOT LOCALIZE"},
[92144] = {name = "Feuriges Schlitzen", description = "[q]Fügt einem feindlichen Ziel 75% des normalen Nahkampfschadens als Feuerschaden zu."},
[92156] = {name = "Cancel Phase - Zone-Specific 02", description = nil},
[92157] = {name = "Kürbisbombe pflanzen", description = "[q]Erschafft eine Kürbisbombe, die explodiert und alle in der Nähe befindlichen Ghule vernichtet."},
[92177] = {name = "Armageddon", description = "[q]Die Einheit zerstört sich selbst."},
[92212] = {name = "Flammenstoß", description = "[q]Erzeugt in der Nähe eines feindlichen Ziels eine feurige Explosion, die 195000 to 205000 Feuerschaden verursacht und den Boden am Einschlagsort in Brand setzt."},
[92249] = {name = "Ruf des Lebens", description = "[q]Ruft die heiligen Energien des Lebens aus dem ganzen Hochland. Todesschwinge kann nicht umhin, diese Störung zu bemerken... sowie den Verantwortlichen."},
[92265] = {name = "Kristallsturm", description = "[q]Fügt allen Gegnern im Sichtfeld alle 0.5 Sek. 9425 to 10575 Naturschaden zu. Hält 6 sec lang an."},
[92266] = {name = "Strahlungsblitz", description = "[q]Fügt einem Feind Naturschaden zu und hat die Chance, ihn zu bestrahlen."},
[92307] = {name = "Frostleuchtfeuer", description = "[q]Markiert das Ziel, von einer gefrorenen Kugel verfolgt zu werden."},
[92426] = {name = "Paralysieren", description = "[q]Betäubt 8 sec lang alle Feinde. Wenn dieser Effekt vollständig durchläuft, verursacht er 47500 to 52500 Arkanschaden. Dieser Effekt wird entfernt, wenn das Ziel Schaden erleidet."},
[92440] = {name = "Friersythie pflanzen", description = "[q]Erschafft eine Friersythie, die kalte Geschosse auf angreifende Ghule abfeuert und ihr Bewegungs- und Angriffstempo verringert."},
[92548] = {name = "Vergletscherung", description = "[q]Fügt allen nahen Gegnern massiven Frostschaden zu."},
[92552] = {name = "Detonation", description = "[q]Fügt nahen Feinden 175 to 225 Feuerschaden zu."},
[92573] = {name = "Biss der Natur beschwören", description = "[q]Beschwört 3 Naturgewalten, um dem Zaubernden 20 sec lang im Kampf zu helfen."},
[92596] = {name = "Wiedergewonnenes Mana", description = "[q]Gewinnt Mana zurück."},
[92603] = {name = "Ende des Alptraums", description = "[q]Ein traumatisches Ereignis führt dazu, dass das Opfer des Alptraums wahnsinnig wird."},
[92604] = {name = "Adrenalin", description = "[q]Euer Herz hämmert in Eurer Brust. Bewegungstempo um 50% erhöht."},
[92608] = {name = "Erwürgen", description = "[q]Erstickt. Verliert pro Sek. 3% der maximalen Gesundheit."},
[92614] = {name = "Abwehr", description = "[q]Erhöht bei Aktivierung Parierchance um 100%, reduziert die Chance, von Distanzangriffen getroffen zu werden um 100% und gewährt eine Chance von 100%, Zauber abzuwehren. Hält 10 sec lang an."},
[92619] = {name = "Rückenschlitzer", description = "[q]Taucht in die Schatten und schlitzt den Rücken des Feindes. Verursacht 10 sec lang körperlichen Schaden."},
[92622] = {name = "Tödliche Klingen", description = "[q]Vanessa springt blitzschnell von Ziel zu Ziel und verursacht schwere Wunden."},
[92677] = {name = "Nerven", description = "[q]Das nervige Scheusal verursacht zu viel Lärm! Erhöht Eure Geräusche um 10."},
[92681] = {name = "Phasenverschiebung", description = "[q]Verschiebt das nervige Scheusal aus seiner Existenz und macht es gegen Zauber immun."},
[92685] = {name = "Neeeerv!", description = "[q]Ihr werdet von einem nervigen Scheusal gestört!"},
[92713] = {name = "Gedankenschlag", description = "[q]Fügt einem Gegner Schattenschaden zu."},
[92716] = {name = "Schattengetränkt", description = "[q]Gewährt Schattenfähigkeiten.\n Immun gegen Spott."},
[92733] = {name = "Zeugs abfeuern", description = "[q]Feuert zufälliges explosives Zeugs aus der Umgebung der Krazzwerke ab. Reichweite 150 Meter."},
[92754] = {name = "Von Dunkelheit umhüllt", description = nil},
[92789] = {name = "Kristallformationsexplosion", description = nil},
[92796] = {name = "Klauenwirbel", description = "[q]Erhöht Euer Angriffstempo um 40%. Zusätzlich treffen Angriffe einen weiteren in der Nähe befindlichen Gegner. Hält 12 sec lang an."},
[92831] = {name = "Schwarze Flasche werfen", description = nil},
[92857] = {name = "Spionieren", description = "[q]Versetzt den Zaubernden in den Verstohlenheitsmodus. Hält an, bis die Aktion abgebrochen wird."},
[92888] = {name = "Sirenenlied", description = "[q]Bringt alle nahen Feinde zum Tanzen!"},
[92895] = {name = "Salzige Romanze", description = "[q]Umklammert den Feind in einer salzigen Romanze. Verursacht 15 sec lang alle 3 Sek. Frostschaden."},
[92915] = {name = "Feuerschlag", description = "[q]Überzieht einen Zwielichtschattendrachen mit Feuerschaden."},
[92916] = {name = "Segen der Lebensbinderin", description = "[q]Heilt sechs Sekunden lang sowohl Euch selbst als auch den anvisierten Zinnoberroten Vorposten. Stellt auch Mana wieder her."},
[92917] = {name = "Schleimgetränkt", description = nil},
[92918] = {name = "Finaler Schlag", description = "[q]Stürzt sich auf einen geschwächten Zwielichtschattendrache, reißt ihm den Hals raus und gibt ihm den Rest."},
[92930] = {name = "Dunkler Schlick", description = "[q]Fügt allen Gegnern im Umkreis von 3 alle 0,5 Sek. 15000 Schattenschaden zu. Hält 30 sec lang an."},
[92945] = {name = "Degenstich", description = "[q]Fügt einem Feind 100% des normalen Waffenschadens zu."},
[93017] = {name = "Zwielichtriss", description = "[q]Kanalisiert einen Zwielichtriss, der an Feinden in anderen Reichen Schaden verursacht."},
[93059] = {name = "Sturmschild", description = "Absorbiert 450000 Schaden.\nFügt allen nahen Gegnern pro Sekunde 28500 to 31500 Naturschaden zu."},
[93091] = {name = "Gebet der Heilung", description = "[q]Ruft heilige Magie herbei, um in der Nähe befindliche Gruppenmitglieder zu heilen."},
[93094] = {name = "Erneuerung", description = "[q]Heilt einen Verbündeten 15 sec lang alle 3 Sek."},
[93245] = {name = "Feuerkraft", description = "[q]Die Stärke des Feuerelementars schwindet, wenn seine Gesundheit sinkt."},
[93276] = {name = "Feuerhülle", description = "[q]Immun gegen Schaden!"},
[93277] = {name = "Zerreißender Sturm", description = "[q]Umgibt einen Feind mit peitschenden Winden, die 30 sec lang alle 2 Sek. 6000 Schaden verursachen. Zusätzliche Anwendungen erhöhen zwar den Schmerz, doch die Kraft der Erde kann den Winden entgegenwirken."},
[93278] = {name = "Blitzschock", description = "[q]Verursacht 28350 to 31650 Naturschaden."},
[93301] = {name = "Schattenmacht", description = "[q]Die Stärke des Schattenlords schwindet, wenn seine Gesundheit sinkt."},
[93306] = {name = "Zerstäuben", description = "[q]Fügt einem Feind 37000 to 43000 Naturschaden zu, der seine Rüstung unkörperlich werden lässt, sodass sie ihren Träger 10 sec lang nicht schützen kann."},
[93311] = {name = "Schattenpanzer", description = "[q]Immun gegen Schaden!"},
[93325] = {name = "Schockwelle", description = "[q]Sendet vom Zaubernden aus eine Schockwelle nach vorne, die 58500 to 71500 Schaden verursacht und alle feindlichen Ziele innerhalb von 15 Metern in einem kegelförmigen Bereich vor dem Zaubernden 3 sec lang betäubt."},
[93327] = {name = "Erdbestattung", description = "[q]Erdrückt den Feind in einer irdenen Umklammerung und verursacht 30 sec lang alle 2 Sek. 6000 Schaden. Zusätzliche Anwendungen erhöhen zwar den Schmerz, doch starke Winde können dem Effekt entgegenwirken."},
[93331] = {name = "Frostwirbel", description = "[q]Wirbelt herum und lässt eisige Geschosse auf in der Nähe befindliche Feinde los, die 11875 to 13125 Frostschaden verursachen und das Bewegungstempo um 30% verringern."},
[93335] = {name = "Eisiger Schleier", description = "[q]Unterkühlte Luft umgibt den Zaubernden, reduziert jeglichen erlittenen Schaden um 50% und verhindert, dass Zauber unterbrochen werden. Absorbiert Umgebungshitze, aber große Flammen könnten den Schleier vernichten."},
[93336] = {name = "Zauberschutz der Einäscherung", description = "[q]Unterkühlte Luft umgibt den Zaubernden und verursacht bei allen Angreifern Schaden. Irgendein Schutz gegen die große Flamme wäre nötig, um sicher durch die Barriere hindurch anzugreifen."},
[93362] = {name = "Flammenstoß", description = "[q]Lässt eine Flammensäule niedergehen, die alle Feinde in einem ausgewählten Gebiet verbrennt und ihnen 8 sec lang alle 5 Sek. zusätzlichen Schaden zufügt."},
[93373] = {name = "Klingenorkan", description = "[q]Schnell wirbelnde Klingen fügen vor dem Angreifer befindlichen Feinden 4 sec lang alle 0.5 Sek. 70% Waffenschaden zu."},
[93377] = {name = "Zwielichtbruch", description = "[q]Ruft Zwielichtausbrüche hervor, die bei nahen Feinden vor dem Zaubernden Schaden verursachen."},
[93381] = {name = "Kältedurchdrungen", description = "[q]Kühlt Angreifer ab."},
[93423] = {name = "Ersticken", description = "[q]Baron Ashbury beginnt, alle in der Nähe befindlichen Feinde mit den Schatten zu ersticken. Verursacht alle 1 Sek. Schattenschaden."},
[93449] = {name = "Bomben", description = "[q]Bombardiert alle Feinde in einem ausgewählten Gebiet und fügt Feuerschaden zu."},
[93456] = {name = "Rauchbombe", description = "[q]Wirft eine Rauchbombe auf den Boden. Diese verursacht Schaden und verringert die Trefferchance von Spielern, die im Rauch stehen."},
[93468] = {name = "Hinrichtungsaufschub", description = "[q]Baron Ashbury hat Mittleid mit Euch, jedoch nur genug, um Euch noch weiterhin Schmerzen zuzufügen! Heilt alle in der Nähe befindlichen Feinde um 0% der Gesundheit."},
[93473] = {name = "Sandsturm", description = "[q]Wirbelnde Sande umgeben Euch!"},
[93479] = {name = "Maschinenpistoleneinschlag", description = "[q]Fügt einen schwren Angriff auf eine feindliche Einheit aus."},
[93490] = {name = "Algen", description = "[q]Erschafft am Zielort einen Algenteich, der Naturschaden verursacht und Feinde verlangsamt."},
[93492] = {name = "Rammen", description = "[q]Rammt den Gegner, verursacht körperlichen Schaden und stößt Gegner in einem Kegel vor dem Zaubernden zurück."},
[93494] = {name = "Nachstrom", description = "[q]Mobus' mächtiger Schwanz verursacht körperlichen Schaden und stößt hinter ihm stehende Feinde zurück."},
[93497] = {name = "Blasenansturm", description = "[q]Stürmt in einem Regen aus Blasen auf das Ziel zu und verursacht Frostschaden."},
[93500] = {name = "Blasenstrahl", description = "[q]Schießt in einem kegelförmigen Bereich vor dem Zaubernden Blasen ab, die Frostschaden verursachen."},
[93505] = {name = "Frostmixtur beschwören", description = "[q]Beschwört eine frostige Mixtur, die Gegnern Frostschaden zufügt."},
[93506] = {name = "Antimagiepuls", description = "[q]Bannt Magie von in der Nähe befindlichen Feinden und entfernt 1 nützlichen magischen Effekt."},
[93507] = {name = "Magmafesseln", description = "[q]Verringert das Bewegungstempo von in der Nähe befindlichen Feinden 10 sec lang um 60%."},
[93508] = {name = "Massiver Ausbruch", description = "[q]Fügt nahen Gegnern Feuerschaden zu."},
[93512] = {name = "Eruption", description = "[q]Fügt allen nahen Gegnern Feuerschaden zu und stößt sie zurück."},
[93513] = {name = "Flammenpuffer", description = "[q]Fügt einem Gegner Feuerschaden zu."},
[93514] = {name = "Rüstungsschmelze", description = "[q]Schmilzt die Rüstung des Ziels und verringert sie dadurch 30 sec lang um 10%."},
[93515] = {name = "Sturmangriff", description = "[q]Stürmt auf einen Gegner zu und verursacht normalen Schaden plus 1800 to 2200."},
[93520] = {name = "Pistolensperrfeuer", description = "[q]Überzieht einen kegelförmigen Bereich vor dem Zaubernden mit einem konstanten Kugelhagel. Alle Feinde erleiden pro Sekunde, in der sie sich in diesem Gebiet aufhalten, 30000 Schattenschaden. Hält 6 sec lang an."},
[93527] = {name = "Eissplitter", description = "[q]Eissplitter bersten in alle Richtungen, verursachen Frostschaden und erschaffen Eisfelder, die zusätzlichen Frostschaden verursachen."},
[93544] = {name = "Zwielichtatem", description = "[q]Verursacht in einem kegelförmigen Bereich vor dem Zaubernden Schattenschaden."},
[93545] = {name = "Leerenschlag", description = "[q]Fügt allen Gegnern innerhalb des Schattenrisses nach 5 Sek. Schattenschaden zu."},
[93546] = {name = "Zwielichtriss", description = "[q]Fügt allen Gegnern innerhalb des Schattenrisses nach 5 Sek. Schattenschaden zu."},
[93553] = {name = "Zwielichtzone", description = "[q]Erschafft eine Zone aus Zwielichtmacht. Verringert erlittenen magischen Schaden aller Einheiten in der Zone um 75%."},
[93556] = {name = "Entfesselte Magie", description = "[q]Entfesselt Zwielichtmacht, um in der Nähe befindlichen Feinden schweren Magieschaden zuzufügen."},
[93561] = {name = "Steinmantel", description = "[q]Umgibt den Zaubernden mit einem Steinmantel, der eintreffenden Schaden absorbiert. Solange der Mantel hält, wird der verursachte Schaden erhöht."},
[93562] = {name = "Eigenartiges Gift beschwören", description = "[q]Beschwört eine Sonnenkugel."},
[93568] = {name = "Spucken", description = "[q]Dormus' Lieblingskamel bespuckt das Ziel und erschafft so eine Pfütze, die Feinden in der Nähe Naturschaden zufügt."},
[93578] = {name = "Sande der Zeit", description = "[q]Seht die Sande der Zeit! Reduziert die Tempowertung einiger nahen Feinde im Umkreis von 60 Metern 15 sec lang."},
[93581] = {name = "Schmerz und Leid", description = "[q]Baron Ashbury fügt dem Ziel Schmerz und Leid zu, was alle 1 Sek. zunehmenden Schattenschaden verursacht!"},
[93585] = {name = "Serum der Folter", description = "[q]Fügt einem Ziel alle 2 Sek. 6077 to 6922 Naturschaden zu, das daraufhin von schrecklichen Schmerzen geplagt flüchtet."},
[93587] = {name = "Ade", description = "[q]Lässt das Ziel stark bluten und verursacht alle 1 Sek. 2337 to 2662 Schaden."},
[93589] = {name = "Krankheitsodem", description = "[q]Ein verseuchter Atem geht vom Zaubernden aus und verursacht 2850 to 3150 Naturschaden und 10 sec lang alle 2 Sek. 950 to 1050 Naturschaden."},
[93590] = {name = "Flammenatem", description = "[q]Fügt Gegnern in einem kegelförmigen Bereich vor dem Zaubernden Feuerschaden zu."},
[93592] = {name = "Gürtlogeddon", description = "[q]Entfesselt eine Flammenhölle auf alle nahen Feinde."},
[93598] = {name = "Stachelansturm", description = "[q]Stürm den Feind an und verursacht körperlichen Schaden."},
[93610] = {name = "Massive Schockwelle", description = "[q]Stampft auf den Boden und verursacht eine massive Schockwelle, die bei Zielen in einem kegelförmigen Bereich vor dem Zaubernden körperlichen Schaden verursacht und sie zu Boden wirft."},
[93611] = {name = "Schwarzer Atem", description = "[q]Erschafft eine Pfütze aus schwarzem Schlamm, der allen Feinden im Umkreis Schattenschaden zufügt."},
[93621] = {name = "Dunkles Wispern", description = "[q]Dunkle Wisper dringen in den Kopf des Ziels ein und zwingen es, dem Willen von Julak-Doom zu folgen. Entfernt, wenn die Gesundheit unter 90% sinkt."},
[93629] = {name = "Verfluchte Kugeln", description = "[q]Schießt mit verfluchten Kugeln auf einen zufälligen Feind und verursacht 47500 to 52500 Schattenschaden. Zusätzlich erleidet das Ziel 15 sec lang sich erhöhenden Schattenschaden."},
[93653] = {name = "Hochzeitshymne", description = "[q]Spielt einen Hochzeitsmarsch, welcher das Mana und die Gesundheit naher Verbündeter regeneriert."},
[93655] = {name = "Dampfschlag", description = "[q]Fügt einem Feind 21 to 28 Feuerschaden zu und stößt ihn zurück."},
[93661] = {name = "Arkanbeschuss", description = "[q]Schleudert mehrere Geschosse auf das feindliche Ziel und verursacht Arkanschaden."},
