[98714] = {name = "Glutlache", description = "[q]Speit einem Feind ein Feuergeschoss entgegen, das dort, wo es auftrifft, eine Glutlache hinterlässt, die allen im Bereich befindlichen Gegnern Feuerschaden zufügt."},
[98716] = {name = "Entzünden", description = "[q]Zündet einen Feind an, sodass er vor Angst flieht und 7 sec lang regelmäßig Feuerschaden erleidet. Es kann immer nur ein Ziel von 'Entzünden' betroffen sein."},
[98719] = {name = "Furor", description = "[q]Erhöht Tempo um 100%, Größe um 50% und reduziert die Abklingzeit aller Spezialfähigkeiten um 100%."},
