[98994] = {name = "Sternensog", description = "[q]Ihr vereint die Mächte von Mond und Sonne und schleudert so einen vernichtenden Energieschlag auf das Ziel. Fügt dem Ziel 33 to 38 Zaubersturmschaden zu."},
[98995] = {name = "Sternensog", description = "[q]Ihr vereint die Mächte von Mond und Sonne und schleudert so einen vernichtenden Energieschlag auf das Ziel. Fügt dem Ziel 33 to 38 Zaubersturmschaden zu."},
