[99101] = {name = "Wurfspeerbresche", description = nil},
[99112] = {name = "Supernova", description = "[q]Der Sohn der Flamme hat Sulfuras erreicht und wurde mit Macht erfüllt, was eine Supernova auslöst, die allen Feinden 487500 to 512500 Feuerschaden zufügt."},
